import{_ as t,a}from"./chunks/tfi2cadt01_multi_tfrpm01.BfScS0iL.js";import{_ as r,c as s,a8 as o,o as i}from"./chunks/framework.BDnHobkS.js";const _=JSON.parse('{"title":"TFI2CADT01 - Перетворювач адреси I²C","description":"","frontmatter":{},"headers":[],"relativePath":"uk/sensor_bus/translator_tfi2cadt.md","filePath":"uk/sensor_bus/translator_tfi2cadt.md"}'),n={name:"uk/sensor_bus/translator_tfi2cadt.md"};function d(l,e,c,h,p,f){return i(),s("div",null,e[0]||(e[0]=[o('<h1 id="tfi2cadt01-перетворювач-адреси-i2c" tabindex="-1">TFI2CADT01 - Перетворювач адреси I²C <a class="header-anchor" href="#tfi2cadt01-перетворювач-адреси-i2c" aria-label="Permalink to &quot;TFI2CADT01 - Перетворювач адреси I²C&quot;">​</a></h1><p><a href="https://github.com/ThunderFly-aerospace/TFI2CADT01" target="_blank" rel="noreferrer">TFI2CADT01</a> is an address translator module that is compatible with Pixhawk and PX4.</p><p>Перетворення адрес дозволяє кільком пристроям I2C з однаковою адресою співіснувати в мережі I2C. Модуль може знадобитися, якщо використовуються кілька пристроїв, які мають однакову заводську адресу.</p><p>Модуль має вхідну та вихідну сторони. Датчик підключається до головного пристрою на одній стороні. На вихідній стороні можуть бути підключені датчики, адреси яких потрібно перетворити. Модуль містить дві пари конекторів, кожна пара відповідає за різні перетворення.</p><p><img src="'+t+'" alt="TFI2CADT - i2c address translator"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p><a href="https://github.com/ThunderFly-aerospace/TFI2CADT01" target="_blank" rel="noreferrer">TFI2CADT01</a> is designed as open-source hardware with GPLv3 license. It is commercially available from <a href="https://www.thunderfly.cz/" target="_blank" rel="noreferrer">ThunderFly</a> company or from <a href="https://www.tindie.com/products/thunderfly/tfi2cadt01-i2c-address-translator/" target="_blank" rel="noreferrer">Tindie eshop</a>.</p></div><h2 id="метод-перетворення-адрес" tabindex="-1">Метод перетворення адрес <a class="header-anchor" href="#метод-перетворення-адрес" aria-label="Permalink to &quot;Метод перетворення адрес&quot;">​</a></h2><p>TFI2CADT01 виконує операцію XOR на викликаній адресі. Таким чином, нову адресу пристрою можна знайти, взявши оригінальну адресу та застосувавши операцію XOR з значенням, вказаним на модулі. За замовчуванням, вихід 1 виконує XOR зі значенням 0x08, а другий порт - з 0x78. Коротко заданої каси можна змінити значення XOR на 0x0f для першого та 0x7f для другого порту.</p><p>Якщо вам потрібне власне значення для перетворення адрес, зміна конфігураційних резисторів дозволяє встановити будь-яке значення XOR.</p><h2 id="приклад-використання" tabindex="-1">Приклад використання <a class="header-anchor" href="#приклад-використання" aria-label="Permalink to &quot;Приклад використання&quot;">​</a></h2><p>The tachometer sensor <a href="./../sensor/thunderfly_tachometer.html">TFRPM01</a> can be set to two different addresses using a solder jumper. Якщо автопілот має три шини, тільки 6 датчиків можуть бути підключені і жодна шина не залишається вільною (2 доступні адреси * 3 порти I2C). У деяких мультикоптерах або рішеннях VTOL є необхідність вимірювати оберти хвилину RPM 8 або більше елементів. The <a href="https://www.tindie.com/products/thunderfly/tfi2cadt01-i2c-address-translator/" target="_blank" rel="noreferrer">TFI2CADT01</a> is highly recommended in this case.</p><p><img src="'+a+'" alt="Multiple sensors"></p><p>Наступна схема показує, як підключити 6 TFRPM01 до однієї шини автопілота. Додавши ще один TFI2CADT01, до тієї ж шини можна підключити ще 4 пристрої.</p><p><a href="https://mermaid-js.github.io/mermaid-live-editor/edit#pako:eNptkd9rwjAQx_-VcE8dtJB2ukEfBLEWfJCJy8CHvgRznQH7gzSBDfF_33VZB2oCyf3I576XcBc4dgohh08j-xMTRdUyWuX2I6LNErY7zJh0tuv1ubNP_7csSRZsudlHS22GHlGxAduhM3fEfrdNI1GS4emK8a85fwSyGyC9A0S5yVbrg_DZKfLtCxH9JsjhaU7VvI7pfK3_NCg_NXmO3pwl5uYt9D0yAXoWoFNP4yM9H-kspJ0FtF8CdObpURtiaNA0UisaymWsrsCesMEKcnIV1tKdbQVVeyXU9UpaXCttOwO5NQ5jGKf1_t0ep9gzhZY04sYnrz9BI4mU" target="_blank" rel="noreferrer"><img src="https://mermaid.ink/img/pako:eNptkd9rwjAQx_-VcE8dtJB2ukEfBLEWfJCJy8CHvgRznQH7gzSBDfF_33VZB2oCyf3I576XcBc4dgohh08j-xMTRdUyWuX2I6LNErY7zJh0tuv1ubNP_7csSRZsudlHS22GHlGxAduhM3fEfrdNI1GS4emK8a85fwSyGyC9A0S5yVbrg_DZKfLtCxH9JsjhaU7VvI7pfK3_NCg_NXmO3pwl5uYt9D0yAXoWoFNP4yM9H-kspJ0FtF8CdObpURtiaNA0UisaymWsrsCesMEKcnIV1tKdbQVVeyXU9UpaXCttOwO5NQ5jGKf1_t0ep9gzhZY04sYnrz9BI4mU" alt="Connection of multiple sensors"></a></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>TFI2CADT01 does not contain any I2C buffer or accelerator. As it adds additional capacitance on the bus, we advise combining it with some bus booster, e.g. <a href="https://github.com/ThunderFly-aerospace/TFI2CEXT01" target="_blank" rel="noreferrer">TFI2CEXT01</a>.</p></div><h3 id="інші-ресурси" tabindex="-1">Інші ресурси <a class="header-anchor" href="#інші-ресурси" aria-label="Permalink to &quot;Інші ресурси&quot;">​</a></h3><ul><li>Datasheet of <a href="https://www.analog.com/media/en/technical-documentation/data-sheets/4317fa.pdf" target="_blank" rel="noreferrer">LTC4317</a></li></ul>',17)]))}const b=r(n,[["render",d]]);export{_ as __pageData,b as default};
