import{_ as e,c as r,a8 as l,o as i}from"./chunks/framework.BDnHobkS.js";const d=JSON.parse('{"title":"Апаратне забезпечення та налаштування датчика","description":"","frontmatter":{},"headers":[],"relativePath":"uk/sensor/index.md","filePath":"uk/sensor/index.md"}'),t={name:"uk/sensor/index.md"};function s(o,a,n,h,m,p){return i(),r("div",null,a[0]||(a[0]=[l('<h1 id="апаратне-забезпечення-та-налаштування-датчика" tabindex="-1">Апаратне забезпечення та налаштування датчика <a class="header-anchor" href="#апаратне-забезпечення-та-налаштування-датчика" aria-label="Permalink to &quot;Апаратне забезпечення та налаштування датчика&quot;">​</a></h1><p>Цей розділ описує обов&#39;язкові та необов&#39;язкові датчики та їх налаштування/конфігурацію.</p><h2 id="загальнии-огляд" tabindex="-1">Загальний огляд <a class="header-anchor" href="#загальнии-огляд" aria-label="Permalink to &quot;Загальний огляд&quot;">​</a></h2><p>Системи на основі PX4 використовують датчики для оцінки стану транспортного засобу, що необхідно для стабілізації та увімкнення автономного керування. Інформація про стан транспортного засобу включає: позицію/висоту, курс, швидкість, швидкість польоту, орієнтацію (відносно чогось), швидкість обертання в різних напрямках, рівень заряду батареї тощо.</p><p>PX4 <em>мінімально потребує</em> гіроскоп, акселерометр, магнетометр (компас) та барометр для вимірювання вищевказаних станів. Дрони літакового типу, а також апарати VTOL <em>повинні</em> також включати датчик швидкості польоту. Для увімкнення усіх автоматичних режимів, а також деяких ручних/допоміжних режимів, потрібна GPS або інша позиційна система.</p><p><a href="./../flight_controller/pixhawk_series.html">Серія Pixhawk</a> контролерів польоту вже має мінімальний набір сенсорів (часто також це мають і інші платформи контролерів). До контролера можна підключити додаткові/зовнішні датчики — рекомендовано зовнішній GPS та компас, а також датчик швидкості повітря для ВПП та літаків з фіксованим крилом.</p><h2 id="теми-датчиків" tabindex="-1">Теми датчиків <a class="header-anchor" href="#теми-датчиків" aria-label="Permalink to &quot;Теми датчиків&quot;">​</a></h2><p>Обов&#39;язково (включено в Pixhawk серії FCs):</p><ul><li><a href="./../sensor/accelerometer.html">Акселерометр</a> — Вимірює зміну прискорення.</li><li><a href="./../sensor/gyroscope.html">Гіроскоп</a> — Вимірює орієнтацію.</li><li><a href="./../gps_compass/magnetometer.html">Магнітометр (Компас)</a> — Вимірює напрямок/напрям. Рекомендується зовнішній компас!</li><li><a href="./../sensor/barometer.html">Барометри</a> — Вимірює висоту (за допомогою атмосферного тиску).</li></ul><p>Рекомендовано:</p><ul><li><a href="./../sensor/airspeed.html">Датчики швидкості повітря</a> — Вимірює швидкість повітря. Високо рекомендується для ВТОЛ та фіксованих крил, оскільки це єдиний механізм виявлення строба.</li><li><a href="./../gps_compass/">GNSS (GPS)</a> — Вимірює глобальне положення. Потрібно для місій, а також деяких інших автоматичних та ручних/допоміжних режимів.</li><li><a href="./../gps_compass/rtk_gps.html">RTK GNSS (GPS)</a> — GNSS з точністю на рівні сантиметрів. Деякі налаштування також дозволяють визначати напрямок руху за допомогою GNSS, а не за допомогою магнітометра.</li></ul><p>Необов&#39;язково:</p><ul><li><a href="./../sensor/rangefinders.html">Датчики відстані (дальніміри)</a> — Вимірює відстань до цілі. Посадка, уникнення об&#39;єктів і слідування за місцевістю.</li><li><a href="./../sensor/optical_flow.html">Оптичний потік</a> — Оцінює швидкість за допомогою камери, спрямованої вниз, та датчика відстані, спрямованого вниз. Дозволяє отримати більш точне фіксування позиції, ніж просто GPS, і може використовуватися усередині приміщень, коли сигнал GPS недоступний.</li><li><a href="./../sensor/tachometers.html">Тахометри (лічильники обертів)</a> — Використовується лише для реєстрації.</li></ul><p>Інші варіанти включають:</p><ul><li><a href="./../advanced_config/imu_factory_calibration.html">Калібрування фабрики IMU/Компасу</a> — Збереження налаштувань калібрування в постійну пам&#39;ять.</li><li><a href="./../advanced_config/sensor_thermal_calibration.html">Компенсація Температурних Датчиків</a> — Компенсувати датчики для змін температури.</li></ul>',15)]))}const f=e(t,[["render",s]]);export{d as __pageData,f as default};
