import{_ as r,a as o,b as a,c as t}from"./chunks/pico_configurator.arjPCV-i.js";import{_ as i,c as l,a8 as n,o as c}from"./chunks/framework.BDnHobkS.js";const b=JSON.parse('{"title":"ARK Electron Microhard Серійне Телеметрійне Радіо","description":"","frontmatter":{},"headers":[],"relativePath":"uk/telemetry/ark_microhard_serial.md","filePath":"uk/telemetry/ark_microhard_serial.md"}'),d={name:"uk/telemetry/ark_microhard_serial.md"};function h(s,e,m,p,u,f){return c(),l("div",null,e[0]||(e[0]=[n('<h1 id="ark-electron-microhard-серіине-телеметріине-радіо" tabindex="-1">ARK Electron Microhard Серійне Телеметрійне Радіо <a class="header-anchor" href="#ark-electron-microhard-серіине-телеметріине-радіо" aria-label="Permalink to &quot;ARK Electron Microhard Серійне Телеметрійне Радіо&quot;">​</a></h1><p><em>ARK Electron Microhard Serial Telemetry Radios</em> integrate the <a href="http://microhardcorp.com/P900.php" target="_blank" rel="noreferrer">Microhard Pico Serial</a> P900 RF module. Це може бути використано для активації комунікації MAVLink між радіо на транспортному засобі та GCS.</p><p>Радіостанції Microhard Pico Serial - це радіостанції з вихідною потужністю (до) 1 Вт, які підтримують режими точка-точка, точка-багато точок та мережеві режими. Радіопристрої Microhard Pico також можуть бути замовлені з шифруванням AES-256.</p><p>Приблизний діапазон з вихідною потужністю, встановленою на рівні 1 Вт, становить 8 км (5 миль), коли використовуються налаштування за замовчуванням. Одне радіо земної станції може бути використано для зв&#39;язку з кількома транспортними засобами за допомогою точка-багатоточної або мережевої топології меш. Транспортні засоби повинні мати різні ідентифікатори MAVLINK.</p><p><img src="'+r+'" alt="Microhard Radio"></p><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><ul><li><a href="https://arkelectron.com/product/1w-900mhz-serial-telemetry-air-radio/" target="_blank" rel="noreferrer">1W 900MHz Serial Telemetry Radio</a> (vehicle)</li><li><a href="https://arkelectron.com/product/1w-900mhz-serial-telemetry-ground-radio/" target="_blank" rel="noreferrer">1W 900MHz USB Serial Telemetry Radio</a> (ground station)</li><li><a href="https://arkelectron.com/product/1w-2400mhz-serial-telemetry-radio/" target="_blank" rel="noreferrer">1W 2.4GHz Serial Telemetry Radio</a> (vehicle)</li><li><a href="https://arkelectron.com/product/1w-2400mhz-usb-serial-telemetry-radio/" target="_blank" rel="noreferrer">1W 2.4GHz USB Serial Telemetry Radio</a> (ground station)</li></ul><h2 id="з-єднання" tabindex="-1">З&#39;єднання <a class="header-anchor" href="#з-єднання" aria-label="Permalink to &quot;З&#39;єднання&quot;">​</a></h2><h3 id="транспортнии-радіопередавач" tabindex="-1">Транспортний радіопередавач <a class="header-anchor" href="#транспортнии-радіопередавач" aria-label="Permalink to &quot;Транспортний радіопередавач&quot;">​</a></h3><p>Connect the vehicle radio to the flight controller <code>TELEM1</code> port. Для цієї мети надається кабель телеметрії з 6 контактами JST GH стандарту Pixhawk.</p><p>Радіо може бути живлений за допомогою телеметричного кабелю, якщо вихідна потужність встановлена менше 100 мВт. Для вищих рівнів виводу радіо повинно бути окремо живлене через 2-контактний роз&#39;єм Molex Nano-Fit (тобто від батареї).</p><p><img src="'+o+'" alt="Microhard Radio on Vehicle"></p><h3 id="радіо-наземноі-станціі" tabindex="-1">Радіо наземної станції <a class="header-anchor" href="#радіо-наземноі-станціі" aria-label="Permalink to &quot;Радіо наземної станції&quot;">​</a></h3><p>Підключіть земельне радіо до земельної станції за допомогою USB C. Радіо не потребує окремого живлення при використанні USB PD (може бути постачана потужність 1 Вт).</p><h2 id="налаштування-конфігурація" tabindex="-1">Налаштування/Конфігурація <a class="header-anchor" href="#налаштування-конфігурація" aria-label="Permalink to &quot;Налаштування/Конфігурація&quot;">​</a></h2><p>Радіостанції налаштовані за замовчуванням на використання режиму peer-to-peer та швидкість передачі 57600 бод. This allows them to connect to the PX4 <code>TELEM1</code> port and <em>QGroundControl</em> <strong>without any further configuration</strong>.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>You can use a different baud rate, mode or flight controller port. Єдине &quot;вимога&quot; - земний радіопередавач, повітряний радіопередавач, PX4 та QGroundControl повинні мати однакову швидкість передачі даних.</p></div><p><a href="./../telemetry/microhard_serial.html#configuration">Microhard Serial Telemetry Radios &gt; Configuration</a> explains how to configure the radios, <em>QGroundControl</em>, and PX4.</p><p>The ARK Electron radios must be connected to the computer running the <em>PicoConfig</em> configuration tool as described below:</p><ul><li><p>For vehicle radio configuration you will have to connect an FTDI adapter between the radio&#39;s 3 pin JST-GH Config port and a Windows PC running <em>Pico Config</em> (the radio must be powered, which you can do from battery or the data connection to the flight-controller&#39;s <code>TELEM1</code> port).</p><p><img src="'+a+'" alt="Ark Microhard Serial - Ports"></p><p><em>Pico Config</em> will automatically detect the radio. Налаштуйте швидкість передачі даних (бод-швидкість) так, щоб вона відповідала PX4 (і радіо земної станції).</p></li><li><p>З&#39;єднання USB C радіостанції наземної станції може бути використане для налаштування радіо (а також для телеметричних даних). <em>Pico Config</em> will automatically detect and connect to the configuration port. Налаштуйте параметри так, щоб швидкість передачі даних відповідала PX4.</p></li></ul><p>Як тільки радіостанції та PX4 будуть налаштовані на використання одного темпу передачі, ви зможете підключити QGroundControl до транспортного засобу через радіо.</p><h3 id="конфігурація-за-замовчуванням" tabindex="-1">Конфігурація за замовчуванням <a class="header-anchor" href="#конфігурація-за-замовчуванням" aria-label="Permalink to &quot;Конфігурація за замовчуванням&quot;">​</a></h3><p>The default radio configuration as shipped is shown in <em>PicoConfig</em> below.</p><p><img src="'+t+'" alt="Pico Config"></p><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="https://arkelectron.com/wp-content/uploads/2021/04/PicoConfig-1.7.zip" target="_blank" rel="noreferrer">Pico Config 1.7</a> - Radio configuration tool</li></ul>',26)]))}const P=i(d,[["render",h]]);export{b as __pageData,P as default};
