import{_ as e,c as o,a8 as a,o as i}from"./chunks/framework.BDnHobkS.js";const h=JSON.parse('{"title":"Motion Capture (MoCap)","description":"","frontmatter":{},"headers":[],"relativePath":"zh/computer_vision/motion_capture.md","filePath":"zh/computer_vision/motion_capture.md"}'),n={name:"zh/computer_vision/motion_capture.md"};function r(s,t,m,p,l,c){return i(),o("div",null,t[0]||(t[0]=[a('<h1 id="motion-capture-mocap" tabindex="-1">Motion Capture (MoCap) <a class="header-anchor" href="#motion-capture-mocap" aria-label="Permalink to &quot;Motion Capture (MoCap)&quot;">​</a></h1><p>Motion Capture (MoCap) is a <a href="https://en.wikipedia.org/wiki/Computer_vision" target="_blank" rel="noreferrer">computer vision</a> technique for estimating the 3D <em>pose</em> (position and orientation) of a vehicle using a positioning mechanism that is <em>external</em> to the vehicle. It is commonly used to navigate a vehicle in situations where GPS is absent (e.g. indoors), and provides position relative to a <em>local</em> coordinate system.</p><p><em>MoCap</em> systems most commonly detect motion using infrared cameras, but other types of cameras, Lidar, or Ultra Wideband (UWB) may also be used.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p><em>MoCap</em> is conceptually similar to <a href="./../computer_vision/visual_inertial_odometry.html">Visual Inertial Odometry (VIO)</a>. 主要区别在于，在 VIO 中，视觉系统运行在无人机上。 额外还需要利用无人机上 IMU 提供速度信息。</p></div><h2 id="mocap-资源" tabindex="-1">MoCap 资源 <a class="header-anchor" href="#mocap-资源" aria-label="Permalink to &quot;MoCap 资源&quot;">​</a></h2><p>有关 MoCap 的信息，请参阅：</p><ul><li><a href="./../ros/external_position_estimation.html">Using Vision or Motion Capture Systems for Position Estimation</a>. </li><li><a href="./../tutorials/motion-capture.html">Flying with Motion Capture (VICON, NOKOV, Optitrack)</a>. </li><li><a href="./../advanced_config/tuning_the_ecl_ekf.html#external-vision-system">EKF &gt; External Vision System</a></li></ul>',7)]))}const d=e(n,[["render",r]]);export{h as __pageData,d as default};
