import{_ as a}from"./chunks/sensor_airspeed.D2028Lv6.js";import{_ as t,c as r,a8 as i,o}from"./chunks/framework.BDnHobkS.js";const u=JSON.parse('{"title":"Airspeed Calibration","description":"","frontmatter":{},"headers":[],"relativePath":"zh/config/airspeed.md","filePath":"zh/config/airspeed.md"}'),s={name:"zh/config/airspeed.md"};function n(l,e,c,d,p,h){return o(),r("div",null,e[0]||(e[0]=[i('<h1 id="airspeed-calibration" tabindex="-1">Airspeed Calibration <a class="header-anchor" href="#airspeed-calibration" aria-label="Permalink to &quot;Airspeed Calibration&quot;">​</a></h1><div class="info custom-block"><p class="custom-block-title">INFO</p><p><a href="./../sensor/airspeed.html">Airspeed sensors</a> are highly recommended for Fixed-wing and VTOL vehicles.</p></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Unlike most other sensor drivers, the airspeed sensor drivers are not automatically started. Before calibration they must be <a href="./../advanced_config/parameters.html">enabled via the corresponding parameter</a>:</p><ul><li>Sensirion SDP3X (<a href="./../advanced_config/parameter_reference.html#SENS_EN_SDP3X">SENS_EN_SDP3X</a>)</li><li>TE MS4525 (<a href="./../advanced_config/parameter_reference.html#SENS_EN_MS4525DO">SENS_EN_MS4525DO</a>)</li><li>TE MS5525 (<a href="./../advanced_config/parameter_reference.html#SENS_EN_MS5525DS">SENS_EN_MS5525DS</a>)</li><li>Eagle Tree airspeed sensor (<a href="./../advanced_config/parameter_reference.html#SENS_EN_ETSASPD">SENS_EN_ETSASPD</a>)</li></ul></div><h2 id="执行校准" tabindex="-1">执行校准 <a class="header-anchor" href="#执行校准" aria-label="Permalink to &quot;执行校准&quot;">​</a></h2><p>To calibrate the airspeed sensor:</p><ol><li><p>Start <em>QGroundControl</em> and connect the vehicle.</p></li><li><p>Enable the airspeed sensors if not already done (as in <em>warning</em> above).</p></li><li><p>Select <strong>&quot;Q&quot; icon &gt; Vehicle Setup &gt; Sensors</strong> (sidebar) to open <em>Sensor Setup</em>.</p></li><li><p>Click the <strong>Airspeed</strong> sensor button.</p><p><img src="'+a+'" alt="Airspeed calibration"></p></li><li><p>Shield the sensor from the wind (i.e. cup it with your hand). Take care not to block any of its holes.</p></li><li><p>Click <strong>OK</strong> to start the calibration.</p></li><li><p>Once asked for, blow into the tip of the pitot tube to signal the end of calibration.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Blowing into the tube is also a basic check that the dynamic and static ports are installed correctly. If they are swapped then the sensor will read a large negative differential pressure when you blow into the tube, and the calibration will abort with an error.</p></div></li></ol><p>:::</p><ol start="8"><li><em>QGroundControl</em> then tells you if the calibration was successful or not.</li></ol><h2 id="更多信息" tabindex="-1">更多信息 <a class="header-anchor" href="#更多信息" aria-label="Permalink to &quot;更多信息&quot;">​</a></h2><ul><li><a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/setup_view/sensors_px4.html#airspeed" target="_blank" rel="noreferrer">QGroundControl User Guide &gt; Sensors</a></li></ul>',10)]))}const S=t(s,[["render",n]]);export{u as __pageData,S as default};
