import{_ as d,a as r,b as e,c as a}from"./chunks/ark_pab_back.C-9W7U4S.js";import{_ as o,c as l,a8 as h,o as i}from"./chunks/framework.BDnHobkS.js";const D=JSON.parse('{"title":"ARK Pixhawk Autopilot Bus Carrier","description":"","frontmatter":{},"headers":[],"relativePath":"zh/flight_controller/ark_pab.md","filePath":"zh/flight_controller/ark_pab.md"}'),c={name:"zh/flight_controller/ark_pab.md"};function n(b,t,u,s,P,_){return i(),l("div",null,t[0]||(t[0]=[h('<h1 id="ark-pixhawk-autopilot-bus-carrier" tabindex="-1">ARK Pixhawk Autopilot Bus Carrier <a class="header-anchor" href="#ark-pixhawk-autopilot-bus-carrier" aria-label="Permalink to &quot;ARK Pixhawk Autopilot Bus Carrier&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://arkelectron.com/contact-us/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The <a href="https://arkelectron.gitbook.io/ark-documentation/flight-controllers/ark-pixhawk-autopilot-bus-carrier" target="_blank" rel="noreferrer">ARK Pixhawk Autopilot Bus (PAB) Carrier</a> is a USA-built flight controller carrier board, based on the <a href="https://github.com/pixhawk/Pixhawk-Standards" target="_blank" rel="noreferrer">Pixhawk Autopilot Bus open source standard</a>.</p><p>The PAB form factor enables the ARK PAB Carrier to be used with any <a href="./../flight_controller/pixhawk_autopilot_bus.html">PAB-compatible flight controller</a>, such as the <a href="./../flight_controller/ark_v6x.html">ARKV6X</a>.</p><p><img src="'+d+'" alt="ARKPAB Main Photo"></p><h3 id="where-to-buy" tabindex="-1">Where To Buy <a class="header-anchor" href="#where-to-buy" aria-label="Permalink to &quot;Where To Buy&quot;">​</a></h3><p>Order From <a href="https://arkelectron.com/product/ark-pixhawk-autopilot-bus-carrier/" target="_blank" rel="noreferrer">Ark Electronics</a> (US)</p><h2 id="特性" tabindex="-1">特性 <a class="header-anchor" href="#特性" aria-label="Permalink to &quot;特性&quot;">​</a></h2><ul><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-010%20Pixhawk%20Autopilot%20Bus%20Standard.pdf?_ga=2.20605755.2081055420.1671562222-391294592.1671562222" target="_blank" rel="noreferrer">Pixhawk Autopilot Bus (PAB) Form Factor</a></li><li>USA Built</li></ul><h2 id="连接器" tabindex="-1">连接器 <a class="header-anchor" href="#连接器" aria-label="Permalink to &quot;连接器&quot;">​</a></h2><ul><li>PAB Board to Board Interface <ul><li>100 Pin Hirose DF40</li><li>40 Pin Hirose DF40</li></ul></li><li>Dual Digital Power Module Inputs <ul><li>5V Input</li><li>I2C Power Monitor</li><li>6 Pin Molex CLIK-Mate</li></ul></li><li>Ethernet <ul><li>100Mbps</li><li>Built in Magnetics</li><li>4 Pin JST-GH</li></ul></li><li>Full GPS Plus Safety Switch Port <ul><li>10 Pin JST-GH</li></ul></li><li>Basic GPS Port <ul><li>6 Pin JST-GH</li></ul></li><li>Dual CAN Ports <ul><li>4 Pin JST-GH</li></ul></li><li>Triple Telemetry Ports with Flow - Control <ul><li>6 Pin JST-GH</li></ul></li><li>Eight PWM Outputs <ul><li>10 Pin JST-GH</li></ul></li><li>UART/I2C Port <ul><li>6 Pin JST-GH</li></ul></li><li>I2C Port <ul><li>4 Pin JST-GH</li></ul></li><li>PPM RC Port <ul><li>3 Pin JST-GH</li></ul></li><li>DSM RC Port <ul><li>3 Pin JST-ZH</li></ul></li><li>SPI Port <ul><li>11 Pin JST-GH</li></ul></li><li>ADIO Port <ul><li>8 Pin JST-GH</li></ul></li><li>调试接口 <ul><li>10 Pin JST-SH</li></ul></li></ul><h2 id="尺寸" tabindex="-1">尺寸 <a class="header-anchor" href="#尺寸" aria-label="Permalink to &quot;尺寸&quot;">​</a></h2><ul><li>Without Flight Controller Module <ul><li>74.0mm x 43.5mm x 12.0mm</li><li>22g</li></ul></li></ul><h2 id="电源" tabindex="-1">电源 <a class="header-anchor" href="#电源" aria-label="Permalink to &quot;电源&quot;">​</a></h2><ul><li>5V input on <code>POWER1</code>, <code>POWER2</code>, <code>USB C</code>, and the <code>USB JST-GH</code> connector <ul><li>Input is prioritized in the following order: POWER1 &gt; POWER2 &gt; USB</li><li><code>USB C</code> and the <code>USB JST-GH</code> are in parallel</li><li>Overvoltage protection at 5.8V</li><li>Undervoltage protection at 3.9V</li></ul></li><li><code>VDD_5V_HIPOWER</code> and <code>VDD_5V_PERIPH</code> can each provide a total of 1.5A across all the connectors</li></ul><h2 id="leds" tabindex="-1">LEDS <a class="header-anchor" href="#leds" aria-label="Permalink to &quot;LEDS&quot;">​</a></h2><ul><li>There are two LEDs on the ARK PAB <ul><li><code>Red</code> is the ethernet power LED</li><li><code>Green</code> is the ethernet activity LED</li></ul></li></ul><h2 id="针脚定义" tabindex="-1">针脚定义 <a class="header-anchor" href="#针脚定义" aria-label="Permalink to &quot;针脚定义&quot;">​</a></h2><p><img src="'+r+'" alt="ARKPAB Pinout"></p><h2 id="power1" tabindex="-1">POWER1 <a class="header-anchor" href="#power1" aria-label="Permalink to &quot;POWER1&quot;">​</a></h2><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td><code>VBRICK1</code></td><td>+5.0V</td></tr><tr><td>2</td><td><code>VBRICK1</code></td><td>+5.0V</td></tr><tr><td>3</td><td>I2C1_SCL</td><td>+3.3V</td></tr><tr><td>4（黑）</td><td>I2C1_SDA</td><td>+3.3V</td></tr><tr><td>6</td><td><code>GND</code></td><td>GND</td></tr><tr><td>6</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><h2 id="power2" tabindex="-1">POWER2 <a class="header-anchor" href="#power2" aria-label="Permalink to &quot;POWER2&quot;">​</a></h2><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td><code>VBRICK2</code></td><td>+5.0V</td></tr><tr><td>2</td><td><code>VBRICK2</code></td><td>+5.0V</td></tr><tr><td>3</td><td>I2C2_SCL</td><td>+3.3V</td></tr><tr><td>4（黑）</td><td>I2C2_SDA</td><td>+3.3V</td></tr><tr><td>6</td><td><code>GND</code></td><td>GND</td></tr><tr><td>6</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><h2 id="pwm" tabindex="-1">PWM <a class="header-anchor" href="#pwm" aria-label="Permalink to &quot;PWM&quot;">​</a></h2><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td>VDD_SERVO (Not Connected)</td><td>+5.0V</td></tr><tr><td>2</td><td>FMU_CH1</td><td>+3.3V</td></tr><tr><td>3</td><td>FMU_CH2</td><td>+3.3V</td></tr><tr><td>4（黑）</td><td>FMU_CH3</td><td>+3.3V</td></tr><tr><td>6</td><td>FMU_CH4</td><td>+3.3V</td></tr><tr><td>6</td><td>FMU_CH5</td><td>+3.3V</td></tr><tr><td>7</td><td>FMU_CH6</td><td>+3.3V</td></tr><tr><td>8 (blk)</td><td>FMU_CH7</td><td>+3.3V</td></tr><tr><td>9 (blk)</td><td>FMU_CH8</td><td>+3.3V</td></tr><tr><td>10 (blk)</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><h2 id="gps1" tabindex="-1">GPS1 <a class="header-anchor" href="#gps1" aria-label="Permalink to &quot;GPS1&quot;">​</a></h2><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td><code>VDD_5V_PERIPH</code></td><td>+5.0V</td></tr><tr><td>2</td><td>USART1_TX_GPS1</td><td>+3.3V</td></tr><tr><td>3</td><td>USART1_RX_GPS1</td><td>+3.3V</td></tr><tr><td>4（黑）</td><td>I2C1_SCL</td><td>+3.3V</td></tr><tr><td>6</td><td>I2C1_SDA</td><td>+3.3V</td></tr><tr><td>6</td><td>nSAFETY_SWITCH_IN</td><td>+3.3V</td></tr><tr><td>7</td><td>nSAFETY_SWITCH_LED_OUT</td><td>+3.3V</td></tr><tr><td>8 (blk)</td><td><code>3V3_FMU</code></td><td>+3.3V</td></tr><tr><td>9 (blk)</td><td>BUZZER</td><td>+5.0V</td></tr><tr><td>10 (blk)</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><h2 id="gps2" tabindex="-1">GPS2 <a class="header-anchor" href="#gps2" aria-label="Permalink to &quot;GPS2&quot;">​</a></h2><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td><code>VDD_5V_HIPOWER</code></td><td>+5.0V</td></tr><tr><td>2</td><td>UART8_TX_GPS2</td><td>+3.3V</td></tr><tr><td>3</td><td>UART8_RX_GPS2</td><td>+3.3V</td></tr><tr><td>4（黑）</td><td>I2C2_SCL</td><td>+3.3V</td></tr><tr><td>6</td><td>I2C2_SDA</td><td>+3.3V</td></tr><tr><td>6</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><h2 id="telem1" tabindex="-1">TELEM1 <a class="header-anchor" href="#telem1" aria-label="Permalink to &quot;TELEM1&quot;">​</a></h2><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td><code>VDD_5V_HIPOWER</code></td><td>+5.0V</td></tr><tr><td>2</td><td>UART7_TX</td><td>+3.3V</td></tr><tr><td>3</td><td>UART7_RX</td><td>+3.3V</td></tr><tr><td>4（黑）</td><td>UART7_CTS</td><td>+3.3V</td></tr><tr><td>6</td><td>UART7_RTS</td><td>+3.3V</td></tr><tr><td>6</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><h2 id="telem2" tabindex="-1">TELEM2 <a class="header-anchor" href="#telem2" aria-label="Permalink to &quot;TELEM2&quot;">​</a></h2><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td><code>VDD_5V_PERIPH</code></td><td>+5.0V</td></tr><tr><td>2</td><td>UART5_TX</td><td>+3.3V</td></tr><tr><td>3</td><td>UART5_RX</td><td>+3.3V</td></tr><tr><td>4（黑）</td><td>UART5_CTS</td><td>+3.3V</td></tr><tr><td>6</td><td>UART5_RTS</td><td>+3.3V</td></tr><tr><td>6</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><h2 id="telem3" tabindex="-1">TELEM3 <a class="header-anchor" href="#telem3" aria-label="Permalink to &quot;TELEM3&quot;">​</a></h2><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td><code>VDD_5V_HIPOWER</code></td><td>+5.0V</td></tr><tr><td>2</td><td>USART2_TX</td><td>+3.3V</td></tr><tr><td>3</td><td>USART2_RX</td><td>+3.3V</td></tr><tr><td>4（黑）</td><td>USART2_CTS</td><td>+3.3V</td></tr><tr><td>6</td><td>USART2_RTS</td><td>+3.3V</td></tr><tr><td>6</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><h2 id="uart4-i2c3" tabindex="-1">UART4/I2C3 <a class="header-anchor" href="#uart4-i2c3" aria-label="Permalink to &quot;UART4/I2C3&quot;">​</a></h2><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td><code>VDD_5V_PERIPH</code></td><td>+5.0V</td></tr><tr><td>2</td><td>UART4_TX</td><td>+3.3V</td></tr><tr><td>3</td><td>UART4_RX</td><td>+3.3V</td></tr><tr><td>4（黑）</td><td>I2C3_SCL</td><td>+3.3V</td></tr><tr><td>6</td><td>I2C3_SDA</td><td>+3.3V</td></tr><tr><td>6</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><h2 id="i2c3" tabindex="-1">I2C3 <a class="header-anchor" href="#i2c3" aria-label="Permalink to &quot;I2C3&quot;">​</a></h2><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td><code>VDD_5V_PERIPH</code></td><td>+5.0V</td></tr><tr><td>2</td><td>I2C3_SCL</td><td>+3.3V</td></tr><tr><td>3</td><td>I2C3_SDA</td><td>+3.3V</td></tr><tr><td>4（黑）</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><h2 id="can1" tabindex="-1">CAN1 <a class="header-anchor" href="#can1" aria-label="Permalink to &quot;CAN1&quot;">​</a></h2><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td><code>VDD_5V_HIPOWER</code></td><td>+5.0V</td></tr><tr><td>2</td><td>CAN1_H</td><td>+3.3V</td></tr><tr><td>3</td><td>CAN1_L</td><td>+3.3V</td></tr><tr><td>4（黑）</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><h2 id="can2" tabindex="-1">CAN2 <a class="header-anchor" href="#can2" aria-label="Permalink to &quot;CAN2&quot;">​</a></h2><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td><code>VDD_5V_PERIPH</code></td><td>+5.0V</td></tr><tr><td>2</td><td>CAN2_H</td><td>+3.3V</td></tr><tr><td>3</td><td>CAN2_L</td><td>+3.3V</td></tr><tr><td>4（黑）</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><h2 id="usb" tabindex="-1">USB <a class="header-anchor" href="#usb" aria-label="Permalink to &quot;USB&quot;">​</a></h2><p>All signals in parallel with USB C connector</p><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td><code>VBUS_IN</code></td><td>+5.0V</td></tr><tr><td>2</td><td>USB_N</td><td>+3.3V</td></tr><tr><td>3</td><td>USB_P</td><td>+3.3V</td></tr><tr><td>4（黑）</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><h2 id="eth" tabindex="-1">ETH <a class="header-anchor" href="#eth" aria-label="Permalink to &quot;ETH&quot;">​</a></h2><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td>ETH_RD_N</td><td>+50.0V Tolerant</td></tr><tr><td>2</td><td>ETH_RD_P</td><td>+50.0V Tolerant</td></tr><tr><td>3</td><td>ETH_TD_N</td><td>+50.0V Tolerant</td></tr><tr><td>4（黑）</td><td>ETH_TD_P</td><td>+50.0V Tolerant</td></tr></tbody></table><h2 id="adio" tabindex="-1">ADIO <a class="header-anchor" href="#adio" aria-label="Permalink to &quot;ADIO&quot;">​</a></h2><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td><code>VDD_5V_PERIPH</code></td><td>+5.0V</td></tr><tr><td>2</td><td>FMU_CAP</td><td>+3.3V</td></tr><tr><td>3</td><td>BOOTLOADER</td><td>+3.3V</td></tr><tr><td>4（黑）</td><td>FMU_RST_REQ</td><td>+3.3V</td></tr><tr><td>6</td><td>nARMED</td><td>+3.3V</td></tr><tr><td>6</td><td>ADC1_3V3</td><td>+3.3V</td></tr><tr><td>7</td><td>ADC1_6V6</td><td>+3.3V</td></tr><tr><td>8 (blk)</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><h2 id="rc-sbus" tabindex="-1">RC/SBUS <a class="header-anchor" href="#rc-sbus" aria-label="Permalink to &quot;RC/SBUS&quot;">​</a></h2><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td><code>VDD_5V_SBUS_RC</code></td><td>+5.0V</td></tr><tr><td>2</td><td>USART6_RX_SBUS_IN</td><td>+3.3V</td></tr><tr><td>3</td><td>USART6_TX</td><td>+3.3V</td></tr><tr><td>4（黑）</td><td><code>VDD_3V3_SPEKTRUM</code></td><td>+3.3V</td></tr><tr><td>6</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><h2 id="ppm" tabindex="-1">PPM <a class="header-anchor" href="#ppm" aria-label="Permalink to &quot;PPM&quot;">​</a></h2><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td><code>VDD_5V_PPM_RC</code></td><td>+5.0V</td></tr><tr><td>2</td><td>DSM_INPUT/FMU_PPM_INPUT</td><td>+3.3V</td></tr><tr><td>3</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><h2 id="dsm" tabindex="-1">DSM <a class="header-anchor" href="#dsm" aria-label="Permalink to &quot;DSM&quot;">​</a></h2><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td><code>VDD_3V3_SPEKTRUM</code></td><td>+3.3V</td></tr><tr><td>2</td><td><code>GND</code></td><td>GND</td></tr><tr><td>3</td><td>DSM_INPUT/FMU_PPM_INPUT</td><td>+3.3V</td></tr></tbody></table><h2 id="spi6" tabindex="-1">SPI6 <a class="header-anchor" href="#spi6" aria-label="Permalink to &quot;SPI6&quot;">​</a></h2><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td><code>VDD_5V_PERIPH</code></td><td>+5.0V</td></tr><tr><td>2</td><td>SPI6_SCK</td><td>+3.3V</td></tr><tr><td>3</td><td>SPI6_MISO</td><td>+3.3V</td></tr><tr><td>4（黑）</td><td>SPI6_MOSI</td><td>+3.3V</td></tr><tr><td>6</td><td>SPI6_nCS1</td><td>+3.3V</td></tr><tr><td>6</td><td>SPI6_nCS2</td><td>+3.3V</td></tr><tr><td>7</td><td>SPIX_nSYNC</td><td>+3.3V</td></tr><tr><td>8 (blk)</td><td>SPI6_DRDY1</td><td>+3.3V</td></tr><tr><td>9 (blk)</td><td>SPI6_DRDY2</td><td>+3.3V</td></tr><tr><td>10 (blk)</td><td>SPI6_nRESET</td><td>+3.3V</td></tr><tr><td>11 (blk)</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><h2 id="调试接口" tabindex="-1">调试接口 <a class="header-anchor" href="#调试接口" aria-label="Permalink to &quot;调试接口&quot;">​</a></h2><p>The <a href="./../debug/system_console.html">PX4 System Console</a> and <a href="./../debug/swd_debug.html">SWD interface</a> run on the <strong>FMU Debug</strong> port.</p><p>The pinouts and connector comply with the <a href="./../debug/swd_debug.html#pixhawk-debug-full">Pixhawk Debug Full</a> interface defined in the <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a> interface (JST SM10B connector).</p><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td><code>Vtref</code></td><td>+3.3V</td></tr><tr><td>2</td><td>Console TX (OUT)</td><td>+3.3V</td></tr><tr><td>3</td><td>Console RX (IN)</td><td>+3.3V</td></tr><tr><td>4（黑）</td><td><code>SWDIO</code></td><td>+3.3V</td></tr><tr><td>6</td><td><code>SWCLK</code></td><td>+3.3V</td></tr><tr><td>6</td><td><code>SWO</code></td><td>+3.3V</td></tr><tr><td>7</td><td>NFC GPIO</td><td>+3.3V</td></tr><tr><td>8 (blk)</td><td>PH11</td><td>+3.3V</td></tr><tr><td>9 (blk)</td><td>nRST</td><td>+3.3V</td></tr><tr><td>10 (blk)</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><p>For information about using this port see:</p><ul><li><a href="./../debug/swd_debug.html">SWD Debug Port</a></li><li><a href="./../debug/system_console.html">PX4 System Console</a> (Note, the FMU console maps to USART3).</li></ul><p><img src="'+e+'" alt="ARKPAB Top Down Photo"></p><p><img src="'+a+'" alt="ARKPAB Bottom Photo"></p><h2 id="see-also" tabindex="-1">See Also <a class="header-anchor" href="#see-also" aria-label="Permalink to &quot;See Also&quot;">​</a></h2><ul><li><a href="https://arkelectron.gitbook.io/ark-documentation/flight-controllers/ark-pixhawk-autopilot-bus-carrier" target="_blank" rel="noreferrer">ARK Pixhawk Autopilot Bus Carrier</a> (ARK Docs)</li></ul>',68)]))}const p=o(c,[["render",n]]);export{D as __pageData,p as default};
