import{_ as r}from"./chunks/pix32_hero.BGwOX7hT.js";import{_ as l,c as o,j as e,a as i,G as s,a8 as n,B as h,o as d}from"./chunks/framework.BDnHobkS.js";const w=JSON.parse('{"title":"Holybro pix32 Flight Controller (Discontinued)","description":"","frontmatter":{},"headers":[],"relativePath":"zh/flight_controller/holybro_pix32.md","filePath":"zh/flight_controller/holybro_pix32.md"}'),p={name:"zh/flight_controller/holybro_pix32.md"};function c(u,t,b,m,f,g){const a=h("Badge");return d(),o("div",null,[t[0]||(t[0]=e("h1",{id:"holybro-pix32-flight-controller-discontinued",tabindex:"-1"},[i("Holybro pix32 Flight Controller (Discontinued) "),e("a",{class:"header-anchor",href:"#holybro-pix32-flight-controller-discontinued","aria-label":'Permalink to "Holybro pix32 Flight Controller (Discontinued)"'},"​")],-1)),s(a,{type:"info",text:"Discontinued"}),t[1]||(t[1]=n('<div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://holybro.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The Holybro<sup>®</sup> <a href="https://holybro.com/collections/autopilot-flight-controllers/products/pix32pixhawk-flight-controller" target="_blank" rel="noreferrer">pix32 autopilot</a> (also known as &quot;Pixhawk 2&quot;, and formerly as HKPilot32) is based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk<sup>®</sup>-project</a> <strong>FMUv2</strong> open hardware design. This board is based on hardware version Pixhawk 2.4.6. It runs the PX4 flight stack on the <a href="https://nuttx.apache.org/" target="_blank" rel="noreferrer">NuttX</a> OS.</p><p><img src="'+r+'" alt="pix32"></p><p>As a CC-BY-SA 3.0 licensed Open Hardware design, schematics and design files should be <a href="https://github.com/PX4/Hardware" target="_blank" rel="noreferrer">available here</a>.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>The Holybro pix32 is software compatible with the <a href="./../flight_controller/pixhawk.html">3DR Pixhawk 1</a>. It is not connector compatible, but is otherwise physically very similar to the 3DR Pixhawk or mRo Pixhawk.</p></div><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="主要特性" tabindex="-1">主要特性 <a class="header-anchor" href="#主要特性" aria-label="Permalink to &quot;主要特性&quot;">​</a></h2><ul><li>Main System-on-Chip: <a href="http://www.st.com/web/en/catalog/mmc/FM141/SC1169/SS1577/LN1789" target="_blank" rel="noreferrer">STM32F427</a><ul><li>CPU: 32-bit STM32F427 Cortex<sup>®</sup> M4 core with FPU</li><li>RAM: 168 MHz/256 KB</li><li>Flash: 2 MB</li></ul></li><li>Failsafe System-on-Chip: STM32F103</li><li>传感器： <ul><li>ST Micro L3GD20 3-axis 16-bit gyroscope</li><li>ST Micro LSM303D 3-axis 14-bit accelerometer / magnetometer</li><li>Invensense<sup>®</sup> MPU 6000 3-axis accelerometer/gyroscope</li><li>MEAS MS5611 气压计</li></ul></li><li>尺寸/重量 <ul><li>Size: 81x44x15mm</li><li>Weight: 33.1g</li></ul></li><li>GPS: u-blox<sup>®</sup> super precision Neo-7M with compass</li><li>Input Voltage: 2~10s (7.4~37V)</li></ul><h3 id="连接" tabindex="-1">连接 <a class="header-anchor" href="#连接" aria-label="Permalink to &quot;连接&quot;">​</a></h3><ul><li>1x I2C</li><li>2x CAN</li><li>3.3 and 6.6V ADC inputs</li><li>5x UART (serial ports), one high-power capable, 2x with HW flow control</li><li>Spektrum DSM / DSM2 / DSM-X® Satellite compatible input up to DX8 (DX9 and above not supported)</li><li>Futaba<sup>®</sup> S.BUS compatible input and output</li><li>PPM sum signal</li><li>RSSI (PWM or voltage) input</li><li>SPI</li><li>External microUSB port</li><li>Molex PicoBlade connectors</li></ul><h2 id="购买渠道" tabindex="-1">购买渠道 <a class="header-anchor" href="#购买渠道" aria-label="Permalink to &quot;购买渠道&quot;">​</a></h2><p><a href="https://holybro.com/collections/autopilot-flight-controllers/products/pix32pixhawk-flight-controller" target="_blank" rel="noreferrer">shop.holybro.com</a></p><h3 id="配件" tabindex="-1">配件 <a class="header-anchor" href="#配件" aria-label="Permalink to &quot;配件&quot;">​</a></h3><ul><li><a href="https://holybro.com/products/digital-air-speed-sensor" target="_blank" rel="noreferrer">Digital airspeed sensor</a></li><li><a href="https://hobbyking.com/en_us/apm-pixhawk-wireless-wifi-radio-module.html" target="_blank" rel="noreferrer">Hobbyking<sup>®</sup> Wifi Telemetry</a></li><li><a href="./../telemetry/holybro_sik_radio.html">HolyBro SiK Telemetry Radio (EU 433 MHz, US 915 MHz)</a></li></ul><h2 id="编译固件" tabindex="-1">编译固件 <a class="header-anchor" href="#编译固件" aria-label="Permalink to &quot;编译固件&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make px4_fmu-v2_default</span></span></code></pre></div><h2 id="调试接口" tabindex="-1">调试接口 <a class="header-anchor" href="#调试接口" aria-label="Permalink to &quot;调试接口&quot;">​</a></h2><p>See <a href="./../flight_controller/pixhawk.html#debug-ports">3DR Pixhawk 1 &gt; Debug Ports</a>.</p><h2 id="引脚和原理图" tabindex="-1">引脚和原理图 <a class="header-anchor" href="#引脚和原理图" aria-label="Permalink to &quot;引脚和原理图&quot;">​</a></h2><p>The board is based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk project</a> <strong>FMUv2</strong> open hardware design.</p><ul><li><a href="https://raw.githubusercontent.com/PX4/Hardware/master/FMUv2/PX4FMUv2.4.5.pdf" target="_blank" rel="noreferrer">FMUv2 + IOv2 schematic</a> -- Schematic and layout</li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>As a CC-BY-SA 3.0 licensed Open Hardware design, all schematics and design files are <a href="https://github.com/PX4/Hardware" target="_blank" rel="noreferrer">available</a>.</p></div><h2 id="串口映射" tabindex="-1">串口映射 <a class="header-anchor" href="#串口映射" aria-label="Permalink to &quot;串口映射&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>设备</th><th>Port</th></tr></thead><tbody><tr><td>UART1</td><td>/dev/ttyS0</td><td>IO debug</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (flow control)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2 (flow control)</td></tr><tr><td>UART4</td><td></td><td></td></tr><tr><td>UART7</td><td>CONSOLE</td><td></td></tr><tr><td>UART8</td><td>SERIAL4</td><td></td></tr></tbody></table>',26))])}const y=l(p,[["render",c]]);export{w as __pageData,y as default};
