import{_ as e,a as r,b as o,c as a}from"./chunks/mro_control_pinouts.COJ70kob.js";import{_ as l,c as i,a8 as n,o as s}from"./chunks/framework.BDnHobkS.js";const _=JSON.parse('{"title":"mRo Control Zero F7 Flight Controller","description":"","frontmatter":{},"headers":[],"relativePath":"zh/flight_controller/mro_control_zero_f7.md","filePath":"zh/flight_controller/mro_control_zero_f7.md"}'),d={name:"zh/flight_controller/mro_control_zero_f7.md"};function c(p,t,h,m,u,b){return s(),i("div",null,t[0]||(t[0]=[n('<h1 id="mro-control-zero-f7-flight-controller" tabindex="-1">mRo Control Zero F7 Flight Controller <a class="header-anchor" href="#mro-control-zero-f7-flight-controller" aria-label="Permalink to &quot;mRo Control Zero F7 Flight Controller&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://store.mrobotics.io/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The <em>mRo Control Zero F7<sup>®</sup></em> is a new flight controller from mRo.</p><p><img src="'+e+'" alt="mRo Control Zero F7"></p><p>It is a no-compromise triple IMU commercial grade flight controller. It includes 8x PWM outputs (DShot capable), 3x IMUs, 1x Magnetometer, 1x Barometric Pressure Sensor (Altimeter), 6x UART, and SD Card, all packed into a 32mm x 20mm PCB. The PWMs are bidirectional, EMI protected, and level shifted to 5V logic levels. All accessed using front and rear 30pin Molex PicoClasp connectors. A durable plastic case, conformal board coating, and optional temperature calibration included.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="主要特性" tabindex="-1">主要特性 <a class="header-anchor" href="#主要特性" aria-label="Permalink to &quot;主要特性&quot;">​</a></h2><ul><li><p>微处理器：</p><ul><li>32-bit STM32F777 Cortex<sup>®</sup> M4 core with FPU rev. 3</li><li>216 MHz/512 KB RAM/2 MB Flash</li><li>F-RAM Cypress MF25V02-G 256-Kbit nonvolatile memory (Flash memory that performs as fast as RAM)</li></ul></li><li><p>传感器：</p><ul><li><a href="https://www.bosch-sensortec.com/bst/products/all_products/bmi088_1" target="_blank" rel="noreferrer">Bosch BMI088</a> 3-axis accelerometer/gyroscope (internally vibration dampened)</li><li><a href="https://www.invensense.com/products/motion-tracking/6-axis/icm-20602/" target="_blank" rel="noreferrer">Invensense ICM-20602</a> 3-axis accelerometer/gyroscope</li><li><a href="https://www.invensense.com/products/motion-tracking/9-axis/icm-20948/" target="_blank" rel="noreferrer">Invensense ICM-20948</a> 3-axis accelerometer/gyroscope/magnetometer</li><li><a href="https://www.infineon.com/cms/en/product/sensor/pressure-sensors/pressure-sensors-for-iot/dps310/" target="_blank" rel="noreferrer">Infineon DPS310 barometer</a> (So smooth and NO more light sensitivity)</li></ul></li><li><p>接口：</p><ul><li>6x UART (serial ports total), 3x with HW flow control, 1x FRSky Telemetry (D or X types), 1x Console and 1x GPS+I2C</li><li>8x PWM outputs (all DShot capable)</li><li>1x CAN</li><li>1x I2C</li><li>1x SPI</li><li>Spektrum DSM / DSM2 / DSM-X® Satellite compatible input and binding</li><li>Futaba S.BUS® &amp; S.BUS2® compatible input</li><li>FRSky Telemetry port output</li><li>Graupner SUMD</li><li>Yuneec ST24</li><li>PPM sum input signal</li><li>1x JTAG (TC2030 Connector)</li><li>1x RSSI (PWM or voltage) input</li><li>Tricolor LED</li></ul></li><li><p>Weight and Dimensions (Uncased):</p><ul><li>Weight: 5.3g (0.19oz)</li><li>Width: 20mm (0.79&quot;)</li><li>Length: 32mm (1.26&quot;)</li></ul></li><li><p>电源系统</p><ul><li>3x Ultra low noise LDO voltage regulator</li></ul></li></ul><h2 id="购买渠道" tabindex="-1">购买渠道 <a class="header-anchor" href="#购买渠道" aria-label="Permalink to &quot;购买渠道&quot;">​</a></h2><ul><li><a href="https://store.mrobotics.io/mRo-Control-Zero-F7-p/mro-ctrl-zero-f7.htm" target="_blank" rel="noreferrer">mRo Control Zero</a></li></ul><h2 id="编译固件" tabindex="-1">编译固件 <a class="header-anchor" href="#编译固件" aria-label="Permalink to &quot;编译固件&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make mro_ctrl-zero-f7</span></span></code></pre></div><h2 id="debug-ports" tabindex="-1">Debug Ports <a class="header-anchor" href="#debug-ports" aria-label="Permalink to &quot;Debug Ports&quot;">​</a></h2><h3 id="console-port" tabindex="-1">Console Port <a class="header-anchor" href="#console-port" aria-label="Permalink to &quot;Console Port&quot;">​</a></h3><p>The <a href="./../debug/system_console.html">PX4 System Console</a> runs on <code>USART7</code> using the pins listed below. This is a standard serial pinout, designed to connect to a <a href="https://www.digikey.com/en/products/detail/TTL-232R-3V3/768-1015-ND/1836393" target="_blank" rel="noreferrer">3.3V FTDI</a> cable (5V tolerant).</p><table tabindex="0"><thead><tr><th>mRo control zero f7</th><th></th><th>FTDI</th><th></th></tr></thead><tbody><tr><td>17</td><td>USART7 Tx</td><td>5</td><td>FTDI RX （黄色）</td></tr><tr><td>19</td><td>USART7 Rx</td><td>4</td><td>FTDI TX （橙色）</td></tr><tr><td>6</td><td>USART21 GND</td><td>1</td><td>FTDI GND (黑色)</td></tr></tbody></table><h3 id="swd-port" tabindex="-1">SWD Port <a class="header-anchor" href="#swd-port" aria-label="Permalink to &quot;SWD Port&quot;">​</a></h3><p>The <a href="./../debug/swd_debug.html">SWD port</a> (JTAG) for FMU debugging is a TC2030 debug connector, as shown below.</p><p><img src="'+r+'" alt="mro swd port"></p><p>You can use the <a href="https://www.tag-connect.com/" target="_blank" rel="noreferrer">Tag Connect</a> cable <a href="https://www.tag-connect.com/product/tc2030-idc-nl" target="_blank" rel="noreferrer">TC2030 IDC NL</a> below (with associated <a href="https://www.tag-connect.com/product/tc2030-clip-retaining-clip-board-for-tc2030-nl-cables" target="_blank" rel="noreferrer">retaining clip</a>) to attach to either a BlackMagic probe or a ST-LINK V2 debugger.</p><p><img src="'+o+'" alt="tc2030 idc nl cable"></p><p>There is also an <a href="https://www.tag-connect.com/product/arm20-ctx-20-pin-to-tc2030-idc-adapter-for-cortex" target="_blank" rel="noreferrer">ARM20-CTX 20-Pin to TC2030-IDC adapter</a> that can be used with other debug probes.</p><h2 id="针脚定义" tabindex="-1">针脚定义 <a class="header-anchor" href="#针脚定义" aria-label="Permalink to &quot;针脚定义&quot;">​</a></h2><p><img src="'+a+'" alt="mRo Control Zero F7"></p><h2 id="串口映射" tabindex="-1">串口映射 <a class="header-anchor" href="#串口映射" aria-label="Permalink to &quot;串口映射&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>设备</th><th>Port</th></tr></thead><tbody><tr><td>USART2</td><td>/dev/ttyS0</td><td>TELEM1 (flow control)</td></tr><tr><td>USART3</td><td>/dev/ttyS1</td><td>TELEM2 (flow control)</td></tr><tr><td>UART4</td><td>/dev/ttyS2</td><td>GPS1</td></tr><tr><td>USART6</td><td>/dev/ttyS3</td><td>Flex port (can be configured as SPI or UART with Flow Control).</td></tr><tr><td>UART7</td><td>/dev/ttyS4</td><td>CONSOLE</td></tr><tr><td>UART8</td><td>/dev/ttyS5</td><td>Free serial port (typically for FrSky telemetry)</td></tr></tbody></table><h2 id="更多信息" tabindex="-1">更多信息 <a class="header-anchor" href="#更多信息" aria-label="Permalink to &quot;更多信息&quot;">​</a></h2><ul><li><a href="https://mrobotics.io/introducing-the-new-mro-control-zero-autopilot/" target="_blank" rel="noreferrer">Introducing the new mRo Control Zero Autopilot</a> (blog)</li><li><a href="https://mrobotics.io/mrocontrolzero/" target="_blank" rel="noreferrer">Quick Start Guide</a></li></ul>',30)]))}const w=l(d,[["render",c]]);export{_ as __pageData,w as default};
