import{_ as e,a as r}from"./chunks/pixhawk3_pro_debug_ports.Cr0unQys.js";import{_ as a,c as o,a8 as i,o as d}from"./chunks/framework.BDnHobkS.js";const k=JSON.parse('{"title":"Pixhawk 3 Pro (Discontinued)","description":"","frontmatter":{},"headers":[],"relativePath":"zh/flight_controller/pixhawk3_pro.md","filePath":"zh/flight_controller/pixhawk3_pro.md"}'),s={name:"zh/flight_controller/pixhawk3_pro.md"};function l(n,t,h,p,c,u){return d(),o("div",null,t[0]||(t[0]=[i('<h1 id="pixhawk-3-pro-discontinued" tabindex="-1">Pixhawk 3 Pro (Discontinued) <a class="header-anchor" href="#pixhawk-3-pro-discontinued" aria-label="Permalink to &quot;Pixhawk 3 Pro (Discontinued)&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://store-drotek.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The Pixhawk<sup>®</sup> 3 Pro is based on the FMUv4 hardware design (Pixracer) with some upgrades and additional features. The board was designed by <a href="https://drotek.com" target="_blank" rel="noreferrer">Drotek<sup>®</sup></a> and PX4.</p><p><img src="'+e+'" alt="Pixhawk 3 Pro hero image"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The main hardware documentation is here: <a href="https://drotek.gitbook.io/pixhawk-3-pro/hardware" target="_blank" rel="noreferrer">https://drotek.gitbook.io/pixhawk-3-pro/hardware</a></p></div><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>This autopilot is <a href="./../flight_controller/autopilot_pixhawk_standard.html">supported</a> by the PX4 maintenance and test teams.</p></div><h2 id="总览" tabindex="-1">总览 <a class="header-anchor" href="#总览" aria-label="Permalink to &quot;总览&quot;">​</a></h2><ul><li>Microcontroller: <strong>STM32F469</strong>; Flash size is <strong>2MiB</strong>, RAM size is <strong>384KiB</strong></li><li><strong>ICM-20608-G</strong> gyro / accelerometer</li><li><strong>MPU-9250</strong> gyro / accelerometer / magnetometer</li><li><strong>LIS3MDL</strong> compass</li><li>Sensors connected via two SPI buses (one high rate and one low-noise bus)</li><li>Two I2C buses</li><li>Two CAN buses</li><li>Voltage / battery readings from two power modules</li><li>FrSky<sup>®</sup> Inverter</li><li>8 Main + 6 AUX PWM outputs (Separate IO chip, PX4IO)</li><li>microSD (logging)</li><li>S.BUS / Spektrum / SUMD / PPM input</li><li>JST GH user-friendly connectors: same connectors and pinouts as Pixracer</li></ul><h2 id="where-to-buy" tabindex="-1">Where to buy <a class="header-anchor" href="#where-to-buy" aria-label="Permalink to &quot;Where to buy&quot;">​</a></h2><p>From <a href="https://store.drotek.com/" target="_blank" rel="noreferrer">Drotek store</a> (EU) :</p><ul><li><a href="https://store.drotek.com/autopilots/844-pixhawk-3-pro-pack.html" target="_blank" rel="noreferrer">Pixhawk 3 Pro (Pack)</a></li><li><a href="https://store.drotek.com/autopilots/821-pixhawk-pro-autopilot-8944595120557.html" target="_blank" rel="noreferrer">Pixhawk 3 Pro</a></li></ul><p>From <a href="https://www.readymaderc.com" target="_blank" rel="noreferrer">readymaderc</a> (USA) :</p><ul><li><a href="https://www.readymaderc.com/products/details/pixhawk-3-pro-flight-controller" target="_blank" rel="noreferrer">Pixhawk 3 Pro</a></li></ul><h2 id="编译固件" tabindex="-1">编译固件 <a class="header-anchor" href="#编译固件" aria-label="Permalink to &quot;编译固件&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make px4_fmu-v4pro_default</span></span></code></pre></div><h2 id="调试接口" tabindex="-1">调试接口 <a class="header-anchor" href="#调试接口" aria-label="Permalink to &quot;调试接口&quot;">​</a></h2><p>The board has FMU and IO debug ports as shown below.</p><p><img src="'+r+'" alt="Debug Ports"></p><p>The pinouts and connector comply with the <a href="./../debug/swd_debug.html#pixhawk-debug-mini">Pixhawk Debug Mini</a> interface defined in the <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a> (JST SM06B connector).</p><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td>VCC TARGET SHIFT</td><td>+3.3V</td></tr><tr><td>2</td><td>UART7 Tx</td><td>+3.3V</td></tr><tr><td>3</td><td>UART7 Rx</td><td>+3.3V</td></tr><tr><td>4（黑）</td><td>SWDIO</td><td>+3.3V</td></tr><tr><td>6</td><td>SWCLK</td><td>+3.3V</td></tr><tr><td>6</td><td>GND</td><td>GND</td></tr></tbody></table><p>For information about wiring and using this port see:</p><ul><li><a href="./../debug/swd_debug.html">SWD Debug Port</a></li><li><a href="./../debug/system_console.html#pixhawk_debug_port">PX4 System Console</a> (Note, the FMU console maps to UART7).</li></ul><h2 id="串口映射" tabindex="-1">串口映射 <a class="header-anchor" href="#串口映射" aria-label="Permalink to &quot;串口映射&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>设备</th><th>Port</th></tr></thead><tbody><tr><td>UART1</td><td>/dev/ttyS0</td><td>WiFi</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (flow control)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2 (flow control)</td></tr><tr><td>UART4</td><td></td><td></td></tr><tr><td>UART7</td><td>CONSOLE</td><td></td></tr><tr><td>UART8</td><td>SERIAL4</td><td></td></tr></tbody></table>',26)]))}const g=a(s,[["render",l]]);export{k as __pageData,g as default};
