import{_ as e}from"./chunks/pixhawk6x-rt.Bpl6o5Dy.js";import{_ as a}from"./chunks/pixhawk6x_exploded_diagram.BSBem3D0.js";import{_ as r}from"./chunks/pixhawk6x_wiring_diagram.BpS__0X1.js";import{_ as o,c as i,a8 as l,o as n}from"./chunks/framework.BDnHobkS.js";const x=JSON.parse('{"title":"Holybro Pixhawk 6X-RT","description":"","frontmatter":{},"headers":[],"relativePath":"zh/flight_controller/pixhawk6x-rt.md","filePath":"zh/flight_controller/pixhawk6x-rt.md"}'),s={name:"zh/flight_controller/pixhawk6x-rt.md"};function d(h,t,p,c,u,m){return n(),i("div",null,t[0]||(t[0]=[l('<h1 id="holybro-pixhawk-6x-rt" tabindex="-1">Holybro Pixhawk 6X-RT <a class="header-anchor" href="#holybro-pixhawk-6x-rt" aria-label="Permalink to &quot;Holybro Pixhawk 6X-RT&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://holybro.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p><em>Pixhawk 6X-RT</em><sup>®</sup> is the latest update to the successful family of Pixhawk® flight controllers designed and made in collaboration with Holybro<sup>®</sup>, NXP&#39;s mobile robotics team and the PX4 team based on NXP&#39;s open source reference design.</p><p>It is based on the <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-012%20Pixhawk%20Autopilot%20v6X%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk​​® Autopilot FMUv6X Standard</a>, <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-010%20Pixhawk%20Autopilot%20Bus%20Standard.pdf" target="_blank" rel="noreferrer">Autopilot Bus Standard</a>, and <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Connector Standard</a>.</p><p>Equipped with a high performance NXP i.mx RT1176 dual core Processor, modular design, triple redundancy, temperature-controlled IMU board, isolated sensor domains, delivering incredible performance, reliability, and flexibility.</p><p><img src="'+e+'" width="350px" title="Pixhawk6X-RT Upright Image"> <img src="'+a+'" width="300px" title="Pixhawk6X Exploded Image"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>This autopilot is <a href="./../flight_controller/autopilot_pixhawk_standard.html">supported</a> by the PX4 maintenance and test teams.</p></div><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to &quot;简介&quot;">​</a></h2><p>Inside the Pixhawk®​ 6X-RT, you can find an NXP i.mx RT1176, paired with sensor technology from Bosch®​​, InvenSense®​,​ giving you flexibility and reliability for controlling any autonomous vehicle, suitable for both academic and commercial applications.</p><p>The Pixhawk® 6X-RT&#39;s i.mx RT1176 Crossover dual-core MCU contain an Arm® Cortex®-M7 core running up to 1GHz and Arm® Cortex®-M4 core running up to 400MHz, has 2MB SRAM and external XIP Flash with 64MB. The PX4 Autopilot takes advantage of the increased power and RAM. Thanks to the increased processing power, developers can be more productive and efficient with their development work, allowing for complex algorithms and models.</p><p>The FMUv6X open standard includes high-performance, low-noise IMUs on board, designed for better stabilization. Triple redundant IMU &amp; double redundant barometer on separate buses. When PX4 detects a sensor failure, the system seamlessly switches to another to maintain flight control reliability.</p><p>An independent LDO powers every sensor set with independent power control. A vibration isolation System to filter out high-frequency vibration and reduce noise to ensure accurate readings, allowing vehicles to reach better overall flight performances.</p><p>External sensor bus (SPI5) has two chip select lines and data-ready signals for additional sensors and payload with SPI-interface, and with an integrated Microchip Ethernet PHY, high-speed communication with mission computers via ethernet is now possible.</p><p>The Pixhawk®​ 6X-RT is perfect for developers at corporate research labs, startups, academics (research, professors, students), and commercial application.</p><h2 id="key-design-points" tabindex="-1">Key Design Points <a class="header-anchor" href="#key-design-points" aria-label="Permalink to &quot;Key Design Points&quot;">​</a></h2><ul><li>High performance <a href="https://www.nxp.com/products/processors-and-microcontrollers/arm-microcontrollers/i-mx-rt-crossover-mcus/i-mx-rt1170-1-ghz-crossover-mcu-with-arm-cortex-cores:i.MX-RT1170" target="_blank" rel="noreferrer">NXP i.MX RT1170 1GHz Crossover MCU</a> with Arm® Cortex® cores</li><li>Hardware secure element <a href="https://www.nxp.com/products/security-and-authentication/authentication/edgelock-se051-proven-easy-to-use-iot-security-solution-with-support-for-updatability-and-custom-applets:SE051" target="_blank" rel="noreferrer">NXP EdgeLock SE051</a> an extension to the widely trusted EdgeLock SE050 Plug &amp; Trust secure element family, supports applet updates in the field and delivers proven security certified to CC EAL 6+, with AVA_VAN.5 up to the OS level, for strong protection against the most recent attack scenarios. E.g, to securely store operator ID or certificates.</li><li>Modular flight controller: separated IMU, FMU, and Base system connected by a 100-pin &amp; a 50-pin Pixhawk®​ Autopilot Bus connector.</li><li>Redundancy: 3x IMU sensors &amp; 2x Barometer sensors on separate buses</li><li>Triple redundancy domains: Completely isolated sensor domains with separate buses and separate power control</li><li>Newly designed vibration isolation system to filter out high frequency vibration and reduce noise to ensure accurate readings</li><li>Ethernet interface for high-speed mission computer integration</li><li>IMUs are temperature-controlled by onboard heating resistors, allowing optimum working temperature of IMUs&amp;#x20</li></ul><h3 id="processors-sensors" tabindex="-1">Processors &amp; Sensors <a class="header-anchor" href="#processors-sensors" aria-label="Permalink to &quot;Processors &amp; Sensors&quot;">​</a></h3><ul><li>FMU Processor: NXP i.MX RT1176 <ul><li>32 Bit Arm® Cortex®-M7, 1GHz</li><li>32 Bit Arm® Cortex®-M4, 400MHz secondary core</li><li>64MB external flash memory</li><li>2MB RAM</li></ul></li><li>NXP EdgeLock SE051 hardware secure element <ul><li>IEC62443-4-2 certified for the applicable requirements</li><li>46 kB user memory with personalization options to go up to 104 kB</li><li>Groundbreaking CC EAL6+ certified solution for IoT deployments</li><li>AES and 3DES encryption and decryption</li></ul></li><li>IO 处理器：STM32F100 <ul><li>32 位 Arm® Cortex®-M3，24MHz，8KB SRAM</li></ul></li><li>On-board sensors <ul><li>Accel/Gyro: ICM-20649 or BMI088</li><li>Accel/Gyro: ICM-42688-P</li><li>Accel/Gyro: ICM-42670-P</li><li>Mag: BMM150</li><li>Barometer: 2x BMP388</li></ul></li></ul><h3 id="electrical-data" tabindex="-1">Electrical data <a class="header-anchor" href="#electrical-data" aria-label="Permalink to &quot;Electrical data&quot;">​</a></h3><ul><li>Voltage Ratings: <ul><li>Max input voltage: 6V</li><li>USB Power Input: 4.75~5.25V</li><li>Servo Rail Input: 0~36V</li></ul></li><li>Current Ratings: <ul><li><code>TELEM1</code> output current limiter: 1.5A</li><li>All other port combined output current limiter: 1.5A</li></ul></li></ul><h3 id="mechanical-data" tabindex="-1">Mechanical data <a class="header-anchor" href="#mechanical-data" aria-label="Permalink to &quot;Mechanical data&quot;">​</a></h3><ul><li>尺寸 <ul><li>Flight Controller Module: 38.8 x 31.8 x 14.6mm</li><li>Standard Baseboard: 52.4 x 103.4 x 16.7mm</li><li>Mini Baseboard: 43.4 x 72.8 x 14.2 mm</li></ul></li><li>重量 <ul><li>Flight Controller Module: 23g</li><li>Standard Baseboard: 51g</li><li>Mini Baseboard: 26.5g</li></ul></li></ul><h3 id="接口" tabindex="-1">接口 <a class="header-anchor" href="#接口" aria-label="Permalink to &quot;接口&quot;">​</a></h3><ul><li><p>16- PWM servo outputs</p></li><li><p>R/C input for Spektrum / DSM</p></li><li><p>Dedicated R/C input for PPM and S.Bus input</p></li><li><p>Dedicated analog / PWM RSSI input and S.Bus output</p></li><li><p>4个通用串行口</p><ul><li>3 with full flow control</li><li>1 with separate 1.5A current limit (Telem1)</li><li>1 with I2C and additional GPIO line for external NFC reader</li></ul></li><li><p>2 GPS ports</p><ul><li>1 full GPS plus Safety Switch Port</li><li>1 basic GPS port</li></ul></li><li><p>1 I2C port</p></li><li><p>1 Ethernet port</p><ul><li>Transformerless Applications</li><li>100Mbps</li></ul></li><li><p>1 SPI bus</p><ul><li>2 chip select lines</li><li>2 data-ready lines</li><li>1 SPI SYNC line</li><li>1 SPI reset line</li></ul></li><li><p>2 CAN Buses for CAN peripheral</p><ul><li>CAN Bus has individual silent controls or ESC RX-MUX control</li></ul></li><li><p>2 Power input ports with SMBus</p><ul><li>1 AD &amp; IO port</li><li>2 个模拟输入接口</li><li>1 PWM/Capture input</li><li>2 Dedicated debug and GPIO lines</li></ul></li><li><p>其它特性:</p><ul><li>Operating &amp; storage temperature: -40 ~ 85°c</li></ul></li></ul><h2 id="购买渠道" tabindex="-1">购买渠道 <a class="header-anchor" href="#购买渠道" aria-label="Permalink to &quot;购买渠道&quot;">​</a></h2><p>Order from <a href="https://holybro.com/products/fmuv6x-rt-developer-edition" target="_blank" rel="noreferrer">Holybro</a>.</p><h2 id="组装-设置" tabindex="-1">组装 / 设置 <a class="header-anchor" href="#组装-设置" aria-label="Permalink to &quot;组装 / 设置&quot;">​</a></h2><p>The <a href="./../assembly/quick_start_pixhawk6x.html">Pixhawk 6X Wiring Quick Start</a> provides instructions on how to assemble required/important peripherals including GPS, Power Module etc.</p><h2 id="connections" tabindex="-1">Connections <a class="header-anchor" href="#connections" aria-label="Permalink to &quot;Connections&quot;">​</a></h2><p>Sample Wiring Diagram</p><p><img src="'+r+'" alt="Pixhawk 6X Wiring Overview"></p><h2 id="针脚定义" tabindex="-1">针脚定义 <a class="header-anchor" href="#针脚定义" aria-label="Permalink to &quot;针脚定义&quot;">​</a></h2><ul><li><a href="https://docs.holybro.com/autopilot/pixhawk-6x/pixhawk-baseboard-pinout" target="_blank" rel="noreferrer">Holybro Pixhawk Baseboard Pinout</a></li><li><a href="https://docs.holybro.com/autopilot/pixhawk-6x/pixhawk-mini-baseboard-pinout" target="_blank" rel="noreferrer">Holybro Pixhawk Mini-Baseboard Pinout</a></li></ul><p>备注：</p><ul><li>The <a href="./../camera/fc_connected_camera.html#camera-capture-configuration">camera capture pin</a> (<code>PI0</code>) is pin 2 on the AD&amp;IO port, marked above as <code>FMU_CAP1</code>.</li></ul><h2 id="串口映射" tabindex="-1">串口映射 <a class="header-anchor" href="#串口映射" aria-label="Permalink to &quot;串口映射&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>设备</th><th>Port</th></tr></thead><tbody><tr><td>UART1</td><td>/dev/ttyS0</td><td>Debug</td></tr><tr><td>UART3</td><td>/dev/ttyS1</td><td>GPS</td></tr><tr><td>UART4</td><td>/dev/ttyS2</td><td>TELEM1</td></tr><tr><td>UART5</td><td>/dev/ttyS3</td><td>GPS2</td></tr><tr><td>UART6</td><td>/dev/ttyS4</td><td>PX4IO</td></tr><tr><td>UART8</td><td>/dev/ttyS5</td><td>TELEM2</td></tr><tr><td>UART10</td><td>/dev/ttyS6</td><td>TELEM3</td></tr><tr><td>UART11</td><td>/dev/ttyS7</td><td>External</td></tr></tbody></table><h2 id="尺寸" tabindex="-1">尺寸 <a class="header-anchor" href="#尺寸" aria-label="Permalink to &quot;尺寸&quot;">​</a></h2><p><a href="https://docs.holybro.com/autopilot/pixhawk-6x/dimensions" target="_blank" rel="noreferrer">Pixhawk 6X Dimensions</a></p><h2 id="额定电压" tabindex="-1">额定电压 <a class="header-anchor" href="#额定电压" aria-label="Permalink to &quot;额定电压&quot;">​</a></h2><p><em>Pixhawk 6X-RT</em> can be triple-redundant on the power supply if three power sources are supplied. The three power rails are: <strong>POWER1</strong>, <strong>POWER2</strong> and <strong>USB</strong>. The <strong>POWER1</strong> &amp; <strong>POWER2</strong> ports on the Pixhawk 6X uses the 6 circuit <a href="https://www.molex.com/molex/products/part-detail/pcb_receptacles/5024430670" target="_blank" rel="noreferrer">2.00mm Pitch CLIK-Mate Wire-to-Board PCB Receptacle</a>.</p><p><strong>Normal Operation Maximum Ratings</strong></p><p>Under these conditions all power sources will be used in this order to power the system:</p><ol><li><strong>POWER1</strong> and <strong>POWER2</strong> inputs (4.9V to 5.5V)</li><li><strong>USB</strong> input (4.75V to 5.25V)</li></ol><p><strong>Absolute Maximum Ratings</strong></p><p>Under these conditions the system will not draw any power (will not be operational), but will remain intact.</p><ol><li><strong>POWER1</strong> and <strong>POWER2</strong> inputs (operational range 4.1V to 5.7V, 0V to 10V undamaged)</li><li><strong>USB</strong> input (operational range 4.1V to 5.7V, 0V to 6V undamaged)</li><li>Servo input: VDD_SERVO pin of <strong>FMU PWM OUT</strong> and <strong>I/O PWM OUT</strong> (0V to 42V undamaged)</li></ol><p><strong>Voltage monitoring</strong></p><p>Digital I2C battery monitoring is enabled by default (see <a href="./../assembly/quick_start_pixhawk6x.html#power">Quickstart &gt; Power</a>).</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Analog battery monitoring via an ADC is not supported on this particular board, but may be supported in variations of this flight controller with a different baseboard.</p></div><h2 id="编译固件" tabindex="-1">编译固件 <a class="header-anchor" href="#编译固件" aria-label="Permalink to &quot;编译固件&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make px4_fmu-v6xrt_default</span></span></code></pre></div><p><a id="debug_port"></a></p><h2 id="调试接口" tabindex="-1">调试接口 <a class="header-anchor" href="#调试接口" aria-label="Permalink to &quot;调试接口&quot;">​</a></h2><p>The <a href="./../debug/system_console.html">PX4 System Console</a> and <a href="./../debug/swd_debug.html">SWD interface</a> run on the <strong>FMU Debug</strong> port.</p><p>The pinouts and connector comply with the <a href="./../debug/swd_debug.html#pixhawk-debug-full">Pixhawk Debug Full</a> interface defined in the <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a> interface (JST SM10B connector).</p><table tabindex="0"><thead><tr><th>针脚</th><th>信号</th><th>电压</th></tr></thead><tbody><tr><td>1（红）</td><td><code>Vtref</code></td><td>+3.3V</td></tr><tr><td>2</td><td>Console TX (OUT)</td><td>+3.3V</td></tr><tr><td>3</td><td>Console RX (IN)</td><td>+3.3V</td></tr><tr><td>4（黑）</td><td><code>SWDIO</code></td><td>+3.3V</td></tr><tr><td>6</td><td><code>SWCLK</code></td><td>+3.3V</td></tr><tr><td>6</td><td><code>SWO</code></td><td>+3.3V</td></tr><tr><td>7</td><td>NFC GPIO</td><td>+3.3V</td></tr><tr><td>8 (blk)</td><td>PH11</td><td>+3.3V</td></tr><tr><td>9 (blk)</td><td>nRST</td><td>+3.3V</td></tr><tr><td>10 (blk)</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><p>For information about using this port see:</p><ul><li><a href="./../debug/swd_debug.html">SWD Debug Port</a></li><li><a href="./../debug/system_console.html">PX4 System Console</a> (Note, the FMU console maps to USART3).</li></ul><h2 id="外部设备" tabindex="-1">外部设备 <a class="header-anchor" href="#外部设备" aria-label="Permalink to &quot;外部设备&quot;">​</a></h2><ul><li><a href="https://holybro.com/products/digital-air-speed-sensor" target="_blank" rel="noreferrer">Digital Airspeed Sensor</a></li><li><a href="https://holybro.com/collections/telemetry-radios?orderby=date" target="_blank" rel="noreferrer">Telemetry Radio Modules</a></li><li><a href="./../sensor/rangefinders.html">Rangefinders/Distance sensors</a></li></ul><h2 id="支持的平台-机身" tabindex="-1">支持的平台/机身 <a class="header-anchor" href="#支持的平台-机身" aria-label="Permalink to &quot;支持的平台/机身&quot;">​</a></h2><p>Any multicopter / airplane / rover or boat that can be controlled with normal RC servos or Futaba S-Bus servos. The complete set of supported configurations can be seen in the <a href="./../airframes/airframe_reference.html">Airframes Reference</a>.</p><h2 id="更多信息" tabindex="-1">更多信息 <a class="header-anchor" href="#更多信息" aria-label="Permalink to &quot;更多信息&quot;">​</a></h2><ul><li><a href="./../advanced_config/bootloader_update_v6xrt.html">Update Pixhawk 6X-RT Bootloader</a></li><li><a href="https://docs.holybro.com/" target="_blank" rel="noreferrer">Holybro Docs</a> (Holybro)</li><li><a href="./../assembly/quick_start_pixhawk6x.html">Pixhawk 6X Wiring QuickStart</a></li><li><a href="./../power_module/holybro_pm02d.html">PM02D Power Module</a></li><li><a href="./../power_module/holybro_pm03d.html">PM03D Power Module</a></li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-012%20Pixhawk%20Autopilot%20v6X%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Autopilot FMUv6X Standard</a>.</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-010%20Pixhawk%20Autopilot%20Bus%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Autopilot Bus Standard</a>.</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a>.</li></ul>',67)]))}const k=o(s,[["render",d]]);export{x as __pageData,k as default};
