import{_ as e,a as r}from"./chunks/pinmap.mPaKziDO.js";import{_ as a,c as o,a8 as l,o as i}from"./chunks/framework.BDnHobkS.js";const m=JSON.parse('{"title":"ThePeach FCC-R1","description":"","frontmatter":{},"headers":[],"relativePath":"zh/flight_controller/thepeach_r1.md","filePath":"zh/flight_controller/thepeach_r1.md"}'),s={name:"zh/flight_controller/thepeach_r1.md"};function n(p,t,d,h,c,u){return i(),o("div",null,t[0]||(t[0]=[l('<h1 id="thepeach-fcc-r1" tabindex="-1">ThePeach FCC-R1 <a class="header-anchor" href="#thepeach-fcc-r1" aria-label="Permalink to &quot;ThePeach FCC-R1&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://thepeach.kr/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p><strong>ThePeach FCC-R1</strong> is an advanced autopilot designed and made in <strong>ThePeach</strong>.</p><p>It is based on the <strong>Pixhawk-project FMUv3</strong> open hardware design and runs <strong>PX4</strong> on <strong>Nuttx OS</strong>.</p><p><img src="'+e+'" alt="ThePeachR1"></p><h2 id="产品规格" tabindex="-1">产品规格 <a class="header-anchor" href="#产品规格" aria-label="Permalink to &quot;产品规格&quot;">​</a></h2><ul><li><p>Main Processor: STM32F427VIT6</p><ul><li>32bit ARM Cortex-M4, 168 MHz 256 KB RAM 2 MB Flash memory</li></ul></li><li><p>IO Processor: STM32F100C8T6</p><ul><li>ARM Cortex-M3, 32bit ARM Cortex-M3, 24 MHz, 8KB SRAM</li></ul></li><li><p>On-board sensors</p><ul><li>加速度计 / 陀螺仪：ICM-20602</li><li>Accel/Gyro/Mag: MPU-9250</li><li>气压计：MS5611</li></ul></li><li><p>接口</p><ul><li>8+6 PWM output (8 from IO, 6 from FMU)</li><li>Spektrum DSM / DSM2 / DSM-X Satellite compatible input</li><li>Futaba S.BUS compatible input and output</li><li>PPM sum signal input</li><li>Analogue / PWM RSSI input</li><li>S.BUS伺服输出</li><li>Safety switch/LED</li><li>4x UART: TELEM1, TELEM2(Raspberry Pi CM3+), GPS, SERIAL4</li><li>1x I2C Ports</li><li>1x CAN bus</li><li>Analog inputs for voltage / Current of 1 battery</li></ul></li><li><p>Interfaces For Raspberry Pi CM3+</p><ul><li>VBUS</li><li>DDR2 Connector: Raspberry Pi CM3+</li><li>1x UART</li><li>2x USB</li><li>1x Raspberry Pi Camera</li></ul></li><li><p>Mechanical</p><ul><li>Dimensions: 49.2 x 101 x 18.2mm</li><li>Weight: 100g</li></ul></li></ul><h2 id="连接器" tabindex="-1">连接器 <a class="header-anchor" href="#连接器" aria-label="Permalink to &quot;连接器&quot;">​</a></h2><p><img src="'+r+'" alt="pinmaptop"></p><h2 id="串口映射" tabindex="-1">串口映射 <a class="header-anchor" href="#串口映射" aria-label="Permalink to &quot;串口映射&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>设备</th><th>Port</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>IO Processor Debug</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (flow control)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2 (Raspberry pi cm3+)</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>GPS1</td></tr><tr><td>USART6</td><td>/dev/ttyS4</td><td>PX4IO</td></tr><tr><td>UART7</td><td>/dev/ttys5</td><td>Debug console</td></tr><tr><td>UART8</td><td>/dev/ttyS6</td><td>TELEM4</td></tr></tbody></table><h2 id="额定电压" tabindex="-1">额定电压 <a class="header-anchor" href="#额定电压" aria-label="Permalink to &quot;额定电压&quot;">​</a></h2><p><strong>ThePeach FCC-R1</strong> can be double-redundant on the power supply if two power sources are supplied. The two power rails are: <strong>POWER</strong> and <strong>USB</strong>.</p><p>注意：</p><ol><li>The output power rails <strong>FMU PWM OUT</strong> and <strong>I/O PWM OUT</strong> do not power the flight controller board (and are not powered by it). You must supply power to one of <strong>POWER</strong> or <strong>USB</strong> or the board will be unpowered.</li><li>The USB do not power the <strong>Raspberry Pi CM3+</strong>. You must supply power to <strong>POWER</strong> or the Raspberry Pi CM3+ will be unpowered.</li></ol><p><strong>Normal Operation Maximum Ratings</strong></p><p>Under these conditions, all power sources will be used in this order to power the system:</p><ol><li>POWER input (5V to 5.5V)</li><li>USB input (4.75V to 5.25V)</li></ol><p><strong>Absolute Maximum Ratings</strong></p><p>Under these conditions, all power sources cause permanent damage to the flight controller.</p><ol><li><p>POWER input (5.5V Over)</p></li><li><p>USB input (5.5V Over)</p></li></ol><h2 id="编译固件" tabindex="-1">编译固件 <a class="header-anchor" href="#编译固件" aria-label="Permalink to &quot;编译固件&quot;">​</a></h2><p>To build PX4 for this target:</p><div class="language-jsx vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">jsx</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">make thepeach_r1_default</span></span></code></pre></div><h2 id="where-to-buy" tabindex="-1">Where to buy <a class="header-anchor" href="#where-to-buy" aria-label="Permalink to &quot;Where to buy&quot;">​</a></h2><p>Order from <a href="http://thepeach.shop/" target="_blank" rel="noreferrer">ThePeach</a></p>',26)]))}const P=a(s,[["render",n]]);export{m as __pageData,P as default};
