import{_ as a}from"./chunks/difficulty_hard.N1nnc_xq.js";import{_ as e}from"./chunks/remote_control.ss1psCxn.js";import{_ as o,c as i,a8 as r,o as l}from"./chunks/framework.BDnHobkS.js";const M=JSON.parse('{"title":"Manual Mode (Fixed-wing)","description":"","frontmatter":{},"headers":[],"relativePath":"zh/flight_modes_fw/manual.md","filePath":"zh/flight_modes_fw/manual.md"}'),d={name:"zh/flight_modes_fw/manual.md"};function n(c,t,s,_,h,m){return l(),i("div",null,t[0]||(t[0]=[r('<h1 id="manual-mode-fixed-wing" tabindex="-1">Manual Mode (Fixed-wing) <a class="header-anchor" href="#manual-mode-fixed-wing" aria-label="Permalink to &quot;Manual Mode (Fixed-wing)&quot;">​</a></h1><p><img src="'+a+'" title="Hard to fly" width="30px"> <img src="'+e+'" title="Manual/Remote control required" width="30px"> </p><p><em>Manual mode</em> sends manual stick input directly to control allocation for fully manual control.</p><p>This is the hardest mode to fly, because nothing is stabilised. Unlike <a href="./../flight_modes_fw/acro.html">Acro Mode</a>, if the roll-pitch stick is centered the vehicle will not automatically stop rotating around the axis — the pilot actually has to move the stick to apply force in the other direction.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>这是唯一忽略 FMU（命令通过安全协处理器发送） 的飞行模式。 该模式提供了一个安全机制，允许在 FMU 固件故障时通过遥控器完全控制油门，升降舵，副翼和方向舵。</p></div><h2 id="技术描述" tabindex="-1">技术描述 <a class="header-anchor" href="#技术描述" aria-label="Permalink to &quot;技术描述&quot;">​</a></h2><p>RC mode where stick input is sent directly to control allocation (for &quot;fully&quot; manual control).</p><p>这是唯一忽略 FMU（命令通过安全协处理器发送） 的飞行模式。 该模式提供了一个安全机制，允许在 FMU 固件故障时通过遥控器完全控制油门，升降舵，副翼和方向舵。</p><h2 id="参数" tabindex="-1">参数 <a class="header-anchor" href="#参数" aria-label="Permalink to &quot;参数&quot;">​</a></h2><table tabindex="0"><thead><tr><th>参数</th><th>描述</th></tr></thead><tbody><tr><td><a id="FW_MAN_P_SC"></a><a href="./../advanced_config/parameter_reference.html#FW_MAN_P_SC">FW_MAN_P_SC</a></td><td>手动俯仰控制比例。 在完全手动模式下应用于所需俯仰舵机指令的比例因子。 此参数允许调整控制舵偏的偏转。 默认：1.0</td></tr><tr><td><a id="FW_MAN_R_SC"></a><a href="./../advanced_config/parameter_reference.html#FW_MAN_R_SC">FW_MAN_R_SC</a></td><td>手动滚转控制比例。 在完全手动模式下应用于所需横滚舵机指令的比例因子。 此参数允许调整控制舵偏的偏转。 默认：1.0</td></tr><tr><td><a id="FW_MAN_Y_SC"></a><a href="./../advanced_config/parameter_reference.html#FW_MAN_Y_SC">FW_MAN_Y_SC</a></td><td>手动偏航控制比例。 在完全手动模式下应用于所需偏航舵机指令的比例因子。 此参数允许调整控制舵偏的偏转。 默认：1.0</td></tr></tbody></table>',10)]))}const g=o(d,[["render",n]]);export{M as __pageData,g as default};
