import{_ as t,a as r,b as a}from"./chunks/smartap-pdb-current-sensor.6lLV32Mi.js";import{_ as o,c as i,a8 as n,o as s}from"./chunks/framework.BDnHobkS.js";const f=JSON.parse('{"title":"Sky-Drones SmartAP PDB","description":"","frontmatter":{},"headers":[],"relativePath":"zh/power_module/sky-drones_smartap-pdb.md","filePath":"zh/power_module/sky-drones_smartap-pdb.md"}'),l={name:"zh/power_module/sky-drones_smartap-pdb.md"};function p(d,e,h,m,u,c){return s(),i("div",null,e[0]||(e[0]=[n('<h1 id="sky-drones-smartap-pdb" tabindex="-1">Sky-Drones SmartAP PDB <a class="header-anchor" href="#sky-drones-smartap-pdb" aria-label="Permalink to &quot;Sky-Drones SmartAP PDB&quot;">​</a></h1><p><a href="https://sky-drones.com/power/smartap-pdb.html" target="_blank" rel="noreferrer">SmartAP PDB</a> (Power Distribution Board) is used to simplify the distribution of power from one or more batteries to ESCs (motors), flight controller, and other peripherals. It also acts as a <a href="./../power_module/">power module</a>, enabling battery voltage and current measurements. SmartAP PDB makes connecting high-power lines easier and much more reliable.</p><p><img src="'+t+'" alt="SmartAP PDB"></p><h2 id="产品规格" tabindex="-1">产品规格 <a class="header-anchor" href="#产品规格" aria-label="Permalink to &quot;产品规格&quot;">​</a></h2><ul><li>Size: 65x65 mm, 4x M3 mounting holes</li><li>Input voltage up to 60 Volts (14S)</li><li>Capability to handle extremenly high currents (peak current up to 400A)</li><li>Power input from the main battery, possibility to connect up to 2 independent batteries</li><li>12 pairs of pads (6 on top, 6 on bottom) for powering up to 12 motors</li><li>Integrated voltage and current sensors with L/C filters</li><li>Precise Hall Effect based current measurements</li><li>Integrated DC-DC converter from 10-60 V input (up to 14S battery) to 5V / 5A output to power peripherals</li><li>Integrated DC-DC converter from 10-60 V input (up to 14S battery) to 12V / 5A output to power peripherals</li><li>5V and 12V power output terminals (standard 2.54mm/0.1&quot; connectors)</li><li>Integrated electromagnetic sounder (buzzer)</li><li>Power output for the flight controller (both 5V regulated and battery voltage level output)</li></ul><h2 id="size-and-weight" tabindex="-1">Size and Weight <a class="header-anchor" href="#size-and-weight" aria-label="Permalink to &quot;Size and Weight&quot;">​</a></h2><ul><li>Length: 65mm</li><li>Width: 65mm</li><li>Height: 14mm</li><li>Weight: 16g</li></ul><h2 id="px4-配置" tabindex="-1">PX4 配置 <a class="header-anchor" href="#px4-配置" aria-label="Permalink to &quot;PX4 配置&quot;">​</a></h2><p><a href="./../config/battery.html">Battery Estimation Tuning</a> describes how to configure the battery setting for a power module.</p><p>The key configuration settings are:</p><ul><li>Voltage divider: 15.51</li><li>Amps per volt: 36.00</li></ul><h2 id="where-to-buy" tabindex="-1">Where to buy <a class="header-anchor" href="#where-to-buy" aria-label="Permalink to &quot;Where to buy&quot;">​</a></h2><p><a href="https://sky-drones.com/parts/smartap-pdb.html" target="_blank" rel="noreferrer">SmartAP PDB</a></p><h2 id="wiring-pinout" tabindex="-1">Wiring / Pinout <a class="header-anchor" href="#wiring-pinout" aria-label="Permalink to &quot;Wiring / Pinout&quot;">​</a></h2><p>SmartAP Power Distribution Board pinout diagram is shown below.</p><p><img src="'+r+'" alt="SmartAP PDB"></p><p>The two big pads are intended for the main battery connection. Up to two independent batteries can be connected using the thick wires (e.g. 8-10 AWG) to be able to handle high current loads and higher total capacity. Up to 12 ESCs can be connected using the 6 smaller pads on top, bottom, left and right sides.</p><h2 id="voltage-and-current-sensors" tabindex="-1">Voltage and current sensors <a class="header-anchor" href="#voltage-and-current-sensors" aria-label="Permalink to &quot;Voltage and current sensors&quot;">​</a></h2><p>SmartAP PDB has integrated voltage and current sensors. The current sensor is located on the bottom side of the PDB.</p><p><img src="'+a+'" alt="SmartAP PDB"></p><h2 id="更多信息" tabindex="-1">更多信息 <a class="header-anchor" href="#更多信息" aria-label="Permalink to &quot;更多信息&quot;">​</a></h2><ul><li><a href="https://sky-drones.com/power/smartap-pdb.html" target="_blank" rel="noreferrer">Buy SmartAP PDB</a></li><li><a href="https://docs.sky-drones.com/avionics/smartap-pdb" target="_blank" rel="noreferrer">Documentation</a></li></ul>',22)]))}const P=o(l,[["render",p]]);export{f as __pageData,P as default};
