import{_ as e,c as a,a8 as o,o as i}from"./chunks/framework.BDnHobkS.js";const h=JSON.parse('{"title":"Community Supported Simulators","description":"","frontmatter":{},"headers":[],"relativePath":"zh/simulation/community_supported_simulators.md","filePath":"zh/simulation/community_supported_simulators.md"}'),r={name:"zh/simulation/community_supported_simulators.md"};function s(n,t,l,m,d,u){return i(),a("div",null,t[0]||(t[0]=[o('<h1 id="community-supported-simulators" tabindex="-1">Community Supported Simulators <a class="header-anchor" href="#community-supported-simulators" aria-label="Permalink to &quot;Community Supported Simulators&quot;">​</a></h1><p>This section contains information about <em>community-supported</em> simulations</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>These simulators are not maintained, tested, or supported, by the core development team. 他们可能与当前版本的 PX4 工作, 也可能不工作。</p><p>See <a href="./../dev_setup/dev_env.html">Toolchain Installation</a> for information about the environments and tools supported by the core development team.</p></div><p>这些工具有来自其社区的不同程度的支持 (有些得到很好的支持，有些则没有) 。 Questions about these tools should be raised on the <a href="./../contribute/support.html#forums-and-chat">discussion forums</a></p><table tabindex="0"><thead><tr><th>仿真器</th><th>描述</th></tr></thead><tbody><tr><td><a href="./../sim_flightgear/">FlightGear</a></td><td><p>A simulator that provides physically and visually realistic simulations. In particular it can simulate many weather conditions, including thunderstorms, snow, rain and hail, and can also simulate thermals and different types of atmospheric flows. <a href="./../sim_flightgear/multi_vehicle.html">Multi-vehicle simulation</a> is also supported.</p> <p><strong>Supported Vehicles:</strong> Plane, Autogyro, Rover</p></td></tr><tr><td><a href="./../sim_jmavsim/">JMAVSim</a></td><td><p>A simple multirotor/quad simulator. This was previously part of the PX4 development toolchain but was removed in favour of <a href="./../sim_gazebo_gz/">Gazebo</a>.</p> <p><strong>Supported Vehicles:</strong> Quad</p></td></tr><tr><td><a href="./../sim_jsbsim/">JSBSim</a></td><td><p>A simulator that provides advanced flight dynamics models. This can be used to model realistic flight dynamics based on wind tunnel data.</p> <p><strong>Supported Vehicles:</strong> Plane, Quad, Hex</p></td></tr><tr><td><a href="./../sim_airsim/">AirSim</a></td><td><p>A cross platform simulator that provides physically and visually realistic simulations. This simulator is resource intensive, and requires a very significantly more powerful computer than the other simulators described here.</p><p><strong>Supported Vehicles:</strong> Iris (MultiRotor model and a configuration for PX4 QuadRotor in the X configuration).</p></td></tr><tr><td><a href="./../sim_sih/">Simulation-In-Hardware</a> (SIH)</td><td><p>An alternative to HITL that offers a hard real-time simulation directly on the hardware autopilot. This simulator is implemented in C++ as a PX4 module directly in the Firmware <a href="https://github.com/PX4/PX4-Autopilot/tree/main/src/modules/simulation/simulator_sih" target="_blank" rel="noreferrer">code</a>. </p><p><strong>Supported Vehicles:</strong> Plane, Quad, Tailsitter</p></td></tr></tbody></table>',5)]))}const c=e(r,[["render",s]]);export{h as __pageData,c as default};
