import{_ as a,a as e,b as r}from"./chunks/cuav_p8_connect.BrRXGyfL.js";import{_ as o,c as i,a8 as d,o as n}from"./chunks/framework.BDnHobkS.js";const g=JSON.parse('{"title":"CUAV P8 Telemetry Radio","description":"","frontmatter":{},"headers":[],"relativePath":"zh/telemetry/cuav_p8_radio.md","filePath":"zh/telemetry/cuav_p8_radio.md"}'),l={name:"zh/telemetry/cuav_p8_radio.md"};function p(s,t,c,h,m,u){return n(),i("div",null,t[0]||(t[0]=[d('<h1 id="cuav-p8-telemetry-radio" tabindex="-1">CUAV P8 Telemetry Radio <a class="header-anchor" href="#cuav-p8-telemetry-radio" aria-label="Permalink to &quot;CUAV P8 Telemetry Radio&quot;">​</a></h1><p>CUAV P8 Radio is a long range (&gt;60km) and high data rate (375 Kbps) remote data transmission module for drones that works plug-and-play with PX4.</p><p>It supports multiple modes such as point-to-point, point-to-multipoint, and relay communication.</p><p><img src="'+a+'" alt="CUAV P8 Radio"></p><h2 id="主要特性" tabindex="-1">主要特性 <a class="header-anchor" href="#主要特性" aria-label="Permalink to &quot;主要特性&quot;">​</a></h2><ul><li>Long range: &gt;60km (depending on the antenna and environment, up to 100 km).</li><li>Supports point-to-point, point-to-multipoint, and repeater modes.</li><li>Up to 2W power (fixed frequency 2W; frequency hopping 1W)</li><li>Up to 345 Kbps transfer rate.</li><li>Supports 12v~60V operating voltage.</li><li>Unit can operate either as ground station modem or aircraft modem.</li><li>Independent power supply for more stable operation</li><li>USB Type-C port, integrated USB to UART converter</li></ul><h2 id="购买渠道" tabindex="-1">购买渠道 <a class="header-anchor" href="#购买渠道" aria-label="Permalink to &quot;购买渠道&quot;">​</a></h2><ul><li><a href="https://www.cuav.net/en/p8-2/" target="_blank" rel="noreferrer">CUAV store</a></li><li><a href="https://www.alibaba.com/product-detail/Free-shipping-CUAV-UAV-P8-Radio_1600324379418.html?spm=a2747.manage.0.0.2dca71d2bY4B0M" target="_blank" rel="noreferrer">CUAV alibaba</a></li></ul><h2 id="px4-配置" tabindex="-1">PX4 配置 <a class="header-anchor" href="#px4-配置" aria-label="Permalink to &quot;PX4 配置&quot;">​</a></h2><p>The CUAV P8 Radio comes pre-configured (baud rate 57600, broadcast mode) for use with PX4. It should require no additional setup if connected to <code>TELEM1</code> OR <code>TELEM2</code>.</p><p>On some flight controllers, or if used with a different serial port, you may need to <a href="./../peripherals/mavlink_peripherals.html">configure the port for MAVLink communication</a>.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p><a href="https://doc.cuav.net/data-transmission/p8-radio/en/config.html" target="_blank" rel="noreferrer">P8 Configuration</a> provides full information about radio configuration, if required.</p></div><h2 id="针脚定义" tabindex="-1">针脚定义 <a class="header-anchor" href="#针脚定义" aria-label="Permalink to &quot;针脚定义&quot;">​</a></h2><p><img src="'+e+'" alt="P8 pinouts"></p><h3 id="data-port" tabindex="-1">Data Port <a class="header-anchor" href="#data-port" aria-label="Permalink to &quot;Data Port&quot;">​</a></h3><table tabindex="0"><thead><tr><th>引脚</th><th>C-RTK GPS 6P</th><th>引脚</th><th>Pixhawk standard pins</th></tr></thead><tbody><tr><td>1</td><td>5V+(NC)</td><td>1</td><td>VCC</td></tr><tr><td>2</td><td>RX</td><td>2</td><td>TX</td></tr><tr><td>3</td><td>TX</td><td>3</td><td>RX</td></tr><tr><td>4</td><td>RTS:</td><td>4</td><td>RTS:</td></tr><tr><td>5</td><td>CTS</td><td>5</td><td>CTS</td></tr><tr><td>6</td><td>GND</td><td>6</td><td>GND</td></tr></tbody></table><h2 id="布线" tabindex="-1">布线 <a class="header-anchor" href="#布线" aria-label="Permalink to &quot;布线&quot;">​</a></h2><p><img src="'+r+'" alt="P8 wiring"></p><p>Connect the CUAV P8 Radio to the <code>TELEM1</code>/<code>TELEM2</code> interface of the flight controller and use a battery or BEC to power the module. The required cables are included in the package.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>CUAV P8 Radio does not support power supply from the flight controller, it needs to be connected to a 12~60v battery or BEC.</p></div><h2 id="more-information" tabindex="-1">More information <a class="header-anchor" href="#more-information" aria-label="Permalink to &quot;More information&quot;">​</a></h2><p><a href="http://manual.cuav.net/data-transmission/p8-radio/p8-user-manual-en.pdf" target="_blank" rel="noreferrer">P8 manual</a></p><p><a href="https://doc.cuav.net/data-transmission/p8-radio/en/" target="_blank" rel="noreferrer">CUAV P8 Radio</a> (Official Guide)</p>',23)]))}const P=o(l,[["render",p]]);export{g as __pageData,P as default};
