import{_ as e}from"./chunks/v5__01.BYtbY4Vm.js";import{_ as t,a as o,b as a,c as r,d as i,e as l}from"./chunks/v5__quickstart_07.DFnbisE-.js";import{_ as s,c as n,o as c,ab as h}from"./chunks/framework.CUflZczI.js";const k=JSON.parse('{"title":"CUAV V5+ Wiring Quick Start","description":"","frontmatter":{},"headers":[],"relativePath":"en/assembly/quick_start_cuav_v5_plus.md","filePath":"en/assembly/quick_start_cuav_v5_plus.md"}'),d={name:"en/assembly/quick_start_cuav_v5_plus.md"},u=h('<h1 id="cuav-v5-wiring-quick-start" tabindex="-1">CUAV V5+ Wiring Quick Start <a class="header-anchor" href="#cuav-v5-wiring-quick-start" aria-label="Permalink to &quot;CUAV V5+ Wiring Quick Start&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://store.cuav.net/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>This quick start guide shows how to power the <a href="./../flight_controller/cuav_v5_plus.html">CUAV V5+</a> flight controller and connect its most important peripherals.</p><p><img src="'+e+'" alt="V5+ AutoPilot - Hero Image"></p><h2 id="wiring-chart-overview" tabindex="-1">Wiring Chart Overview <a class="header-anchor" href="#wiring-chart-overview" aria-label="Permalink to &quot;Wiring Chart Overview&quot;">​</a></h2><p>The image below shows how to connect the most important sensors and peripherals (except the motor and servo outputs). We&#39;ll go through each of these in detail in the following sections.</p><p><img src="'+t+'" alt="V5+ AutoPilot"></p><table><thead><tr><th style="text-align:left;">Main interface</th><th style="text-align:left;">Function</th></tr></thead><tbody><tr><td style="text-align:left;">Power1</td><td style="text-align:left;">Connect power module. Power input with <em>analog</em> voltage and current detection. Do not use a Digital PM on this connector!</td></tr><tr><td style="text-align:left;">Power2</td><td style="text-align:left;">Connect i2c smart battery.</td></tr><tr><td style="text-align:left;">TF CARD</td><td style="text-align:left;">SD card for log storage (card pre-inserted in factory).</td></tr><tr><td style="text-align:left;">M1~M8</td><td style="text-align:left;">PWM outputs. Can be used to control motors or servos.</td></tr><tr><td style="text-align:left;">A1~A6</td><td style="text-align:left;">PWM outputs. Can be used to control motors or servos.</td></tr><tr><td style="text-align:left;">DSU7</td><td style="text-align:left;">Used for FMU debug, reading debug information.</td></tr><tr><td style="text-align:left;">I2C1/I2C2</td><td style="text-align:left;">Connect an I2C device such as an external compass.</td></tr><tr><td style="text-align:left;">CAN1/CAN2</td><td style="text-align:left;">Connect UAVCAN devices such as CAN GPS.</td></tr><tr><td style="text-align:left;">TYPE-C(USB)</td><td style="text-align:left;">Connect to a computer for communication between the flight controller and the computer, such as loading firmware.</td></tr><tr><td style="text-align:left;">SBUS OUT</td><td style="text-align:left;">Connect SBUS devices (e.g. camera gimbals).</td></tr><tr><td style="text-align:left;">GPS&amp;SAFETY</td><td style="text-align:left;">Connect to Neo GPS, which includes GPS, safety switch, buzzer interface.</td></tr><tr><td style="text-align:left;">TELEM1/TELEM2</td><td style="text-align:left;">Connect to the Telemetry System.</td></tr><tr><td style="text-align:left;">DSM/SBUS/RSSI</td><td style="text-align:left;">Includes DSM, SBUS, RSSI signal input interface, DSM interface can be connected to DSM satellite receiver, SBUS interface to SBUS remote control receiver, RSSI for signal strength return module.</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">INFO</p><p>For more interface information, please read <a href="http://manual.cuav.net/V5-Plus.pdf" target="_blank" rel="noreferrer">V5+ Manual</a>.</p></div><p><img src="'+o+'" alt="V5+ AutoPilot"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>If the controller cannot be mounted in the recommended/default orientation (e.g. due to space constraints) you will need to configure the autopilot software with the orientation that you actually used: <a href="./../gps_compass/rtk_gps.html">Flight Controller Orientation</a>.</p></div><h2 id="gps-compass-safety-switch-led" tabindex="-1">GPS + Compass + Safety Switch + LED <a class="header-anchor" href="#gps-compass-safety-switch-led" aria-label="Permalink to &quot;GPS + Compass + Safety Switch + LED&quot;">​</a></h2><p>The recommended GPS module is the <em>Neo v2 GPS</em>, which contains GPS, compass, safety switch, buzzer, LED status light.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Other GPS modules may not work (see <a href="./../flight_controller/cuav_v5_nano.html#compatibility_gps">this compatibility issue</a>)).</p></div><p>The GPS/Compass module should be <a href="./../assembly/mount_gps_compass.html">mounted on the frame</a> as far away from other electronics as possible, with the direction marker towards the front of the vehicle (<em>Neo v2 GPS</em> arrow is in the same direction as the flight control arrow). Connect to the flight control GPS interface using a cable.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>If you use the <a href="http://doc.cuav.net/gps/neo-series-gnss/en/neo-v2-pro.html" target="_blank" rel="noreferrer">NEO V2 PRO GNSS (CAN GPS)</a>, please use the cable to connect to the flight control CAN interface.</p></div><p><img src="'+a+'" alt="V5+ AutoPilot"></p><h2 id="safety-switch" tabindex="-1">Safety Switch <a class="header-anchor" href="#safety-switch" aria-label="Permalink to &quot;Safety Switch&quot;">​</a></h2><p>The dedicated safety switch that comes with the V5+ is only required if you are not using the recommended <em>Neo V2 GPS</em> (which has an inbuilt safety switch).</p><p>If you are flying without the GPS you must attach the switch directly to the <code>GPS1</code> port in order to be able to arm the vehicle and fly (if you use the old 6-pin GPS, please read the definition of the bottom interface to change the line).</p><h2 id="buzzer" tabindex="-1">Buzzer <a class="header-anchor" href="#buzzer" aria-label="Permalink to &quot;Buzzer&quot;">​</a></h2><p>If you do not use the recommended GPS, the buzzer may not work.</p><h2 id="radio-control" tabindex="-1">Radio Control <a class="header-anchor" href="#radio-control" aria-label="Permalink to &quot;Radio Control&quot;">​</a></h2><p>A remote control (RC) radio system is required if you want to manually control your vehicle (PX4 does not require a radio system for autonomous flight modes). You will need to select a compatible transmitter/receiver and then bind them so that they communicate (read the instructions that come with your specific transmitter/receiver).</p><p>The figure below shows how you can access your remote receiver (please find the SBUS cable in the kit).</p><p><img src="'+r+'" alt="V5+ AutoPilot"></p><h2 id="spektrum-satellite-receivers" tabindex="-1">Spektrum Satellite Receivers <a class="header-anchor" href="#spektrum-satellite-receivers" aria-label="Permalink to &quot;Spektrum Satellite Receivers&quot;">​</a></h2><p>The V5+ has a dedicated DSM cable. If using a Spektrum satellite receiver, this should be connected to the flight controller DSM/SBUS/RSSI interface.</p><h2 id="power" tabindex="-1">Power <a class="header-anchor" href="#power" aria-label="Permalink to &quot;Power&quot;">​</a></h2><p>The V5+ kit includes the <em>HV_PM</em> module, which supports 2~14S LiPo batteries. Connect the 6pin connector of the <em>HW_PM</em> module to the flight control <code>Power1</code> interface.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>The supplied power module is unfused. Power <strong>must</strong> be turned off while connecting peripherals.</p></div><p><img src="'+t+'" alt="V5+ AutoPilot"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The power module is not a power source for peripherals connected to the PWM outputs. If you&#39;re connecting servos/actuators you will need to separately power them using a BEC.</p></div><h2 id="telemetry-system-optional" tabindex="-1">Telemetry System (Optional) <a class="header-anchor" href="#telemetry-system-optional" aria-label="Permalink to &quot;Telemetry System (Optional)&quot;">​</a></h2><p>A telemetry system allows you to communicate with, monitor, and control a vehicle in flight from a ground station (for example, you can direct the UAV to a particular position, or upload a new mission).</p><p>The communication channel is via Telemetry Radios. The vehicle-based radio should be connected to either the <code>TELEM1</code> or <code>TELEM2</code> port (if connected to these ports, no further configuration is required). The other radio is connected to your ground station computer or mobile device (usually via USB).</p><p><img src="'+i+'" alt="V5+ AutoPilot"></p><p><a id="sd_card"></a></p><h2 id="sd-card-optional" tabindex="-1">SD Card (Optional) <a class="header-anchor" href="#sd-card-optional" aria-label="Permalink to &quot;SD Card (Optional)&quot;">​</a></h2><p>An <a href="./../getting_started/px4_basic_concepts.html#sd-cards-removable-memory">SD card</a> is inserted in the factory (you do not need to do anything).</p><h2 id="motors" tabindex="-1">Motors <a class="header-anchor" href="#motors" aria-label="Permalink to &quot;Motors&quot;">​</a></h2><p>Motors/servos are connected to the MAIN and AUX ports in the order specified for your vehicle in the <a href="./../airframes/airframe_reference.html">Airframes Reference</a>.</p><p><img src="'+l+'" alt="V5+ AutoPilot"></p><h2 id="pinouts" tabindex="-1">Pinouts <a class="header-anchor" href="#pinouts" aria-label="Permalink to &quot;Pinouts&quot;">​</a></h2><p>Download <strong>V5+</strong> pinouts from <a href="http://manual.cuav.net/V5-Plus.pdf" target="_blank" rel="noreferrer">here</a>.</p><h2 id="further-information" tabindex="-1">Further Information <a class="header-anchor" href="#further-information" aria-label="Permalink to &quot;Further Information&quot;">​</a></h2><ul><li><a href="./../frames_multicopter/dji_f450_cuav_5plus.html">Airframe build-log using CUAV v5+ on a DJI FlameWheel450</a></li><li><a href="http://manual.cuav.net/V5-Plus.pdf" target="_blank" rel="noreferrer">CUAV V5+ Manual</a> (CUAV)</li><li><a href="http://doc.cuav.net/flight-controller/v5-autopilot/en/v5+.html" target="_blank" rel="noreferrer">CUAV V5+ docs</a> (CUAV)</li><li><a href="https://docs.google.com/spreadsheets/d/1-n0__BYDedQrc_2NHqBenG1DNepAgnHpSGglke-QQwY/edit#gid=912976165" target="_blank" rel="noreferrer">FMUv5 reference design pinout</a> (CUAV)</li><li><a href="https://github.com/cuav" target="_blank" rel="noreferrer">CUAV Github</a> (CUAV)</li><li><a href="https://github.com/cuav/hardware/tree/master/V5_Autopilot/V5%2B/V5%2BBASE" target="_blank" rel="noreferrer">Base board design reference</a> (CUAV)</li></ul>',47),p=[u];function m(f,g,b,y,_,S){return c(),n("div",null,p)}const C=s(d,[["render",m]]);export{k as __pageData,C as default};
