import{_ as e,c as o,o as t,ab as i}from"./chunks/framework.CUflZczI.js";const d=JSON.parse('{"title":"Motion Capture (MoCap)","description":"","frontmatter":{},"headers":[],"relativePath":"en/computer_vision/motion_capture.md","filePath":"en/computer_vision/motion_capture.md"}'),a={name:"en/computer_vision/motion_capture.md"},n=i('<h1 id="motion-capture-mocap" tabindex="-1">Motion Capture (MoCap) <a class="header-anchor" href="#motion-capture-mocap" aria-label="Permalink to &quot;Motion Capture (MoCap)&quot;">​</a></h1><p>Motion Capture (MoCap) is a <a href="https://en.wikipedia.org/wiki/Computer_vision" target="_blank" rel="noreferrer">computer vision</a> technique for estimating the 3D <em>pose</em> (position and orientation) of a vehicle using a positioning mechanism that is <em>external</em> to the vehicle. It is commonly used to navigate a vehicle in situations where GPS is absent (e.g. indoors), and provides position relative to a <em>local</em> coordinate system.</p><p><em>MoCap</em> systems most commonly detect motion using infrared cameras, but other types of cameras, Lidar, or Ultra Wideband (UWB) may also be used.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p><em>MoCap</em> is conceptually similar to <a href="./../computer_vision/visual_inertial_odometry.html">Visual Inertial Odometry (VIO)</a>. The main difference is that in VIO the vision system runs on the vehicle, and additionally makes use of the vehicle IMU to provide velocity information.</p></div><h2 id="mocap-resources" tabindex="-1">MoCap Resources <a class="header-anchor" href="#mocap-resources" aria-label="Permalink to &quot;MoCap Resources&quot;">​</a></h2><p>For information about MoCap see:</p><ul><li><a href="./../ros/external_position_estimation.html">Using Vision or Motion Capture Systems for Position Estimation</a>. </li><li><a href="./../tutorials/motion-capture.html">Flying with Motion Capture (VICON, NOKOV, Optitrack)</a>. </li><li><a href="./../advanced_config/tuning_the_ecl_ekf.html#external-vision-system">EKF &gt; External Vision System</a></li></ul>',7),s=[n];function r(c,m,l,p,u,h){return t(),o("div",null,s)}const f=e(a,[["render",r]]);export{d as __pageData,f as default};
