import{_ as s,c as a,o as i,ab as e}from"./chunks/framework.CUflZczI.js";const g=JSON.parse('{"title":"Advanced Linux Installation Use-Cases","description":"","frontmatter":{},"headers":[],"relativePath":"en/dev_setup/dev_env_advanced_linux.md","filePath":"en/dev_setup/dev_env_advanced_linux.md"}'),n={name:"en/dev_setup/dev_env_advanced_linux.md"},t=e(`<h1 id="advanced-linux-installation-use-cases" tabindex="-1">Advanced Linux Installation Use-Cases <a class="header-anchor" href="#advanced-linux-installation-use-cases" aria-label="Permalink to &quot;Advanced Linux Installation Use-Cases&quot;">​</a></h1><h2 id="using-jtag-programming-adapters" tabindex="-1">Using JTAG Programming Adapters <a class="header-anchor" href="#using-jtag-programming-adapters" aria-label="Permalink to &quot;Using JTAG Programming Adapters&quot;">​</a></h2><p>Linux users need to explicitly allow access to the USB bus for JTAG programming adapters.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>For Archlinux: replace the group plugdev with uucp in the following commands</p></div><p>Run a simple <code>ls</code> in <code>sudo</code> mode to ensure the commands below succeed:</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ls</span></span></code></pre></div><p>Then with <code>sudo</code> rights temporarily granted, run this command:</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">cat</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> &gt;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> $HOME</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">/rule.tmp</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> &lt;&lt;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">_EOF</span></span>
<span class="line"><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"># All 3D Robotics (includes PX4) devices</span></span>
<span class="line"><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">SUBSYSTEM==&quot;usb&quot;, ATTR{idVendor}==&quot;26AC&quot;, GROUP=&quot;plugdev&quot;</span></span>
<span class="line"><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"># FTDI (and Black Magic Probe) Devices</span></span>
<span class="line"><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">SUBSYSTEM==&quot;usb&quot;, ATTR{idVendor}==&quot;0483&quot;, GROUP=&quot;plugdev&quot;</span></span>
<span class="line"><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"># Olimex Devices</span></span>
<span class="line"><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">SUBSYSTEM==&quot;usb&quot;,  ATTR{idVendor}==&quot;15ba&quot;, GROUP=&quot;plugdev&quot;</span></span>
<span class="line"><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">_EOF</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> mv</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> $HOME</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">/rule.tmp</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /etc/udev/rules.d/10-px4.rules</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /etc/init.d/udev</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> restart</span></span></code></pre></div><p>The user needs to be added to the group <strong>plugdev</strong>:</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> usermod</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -a</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -G</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> plugdev</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> $USER</span></span></code></pre></div>`,10),l=[t];function p(d,h,o,r,c,k){return i(),a("div",null,l)}const F=s(n,[["render",p]]);export{g as __pageData,F as default};
