import{_ as a,a as o,b as r}from"./chunks/kakuteh7_debug_swd_port.BBweMAHx.js";import{_ as d,E as i,c as l,J as n,m as t,a as s,ab as h,o as u}from"./chunks/framework.CUflZczI.js";const C=JSON.parse('{"title":"Holybro Kakute H7","description":"","frontmatter":{},"headers":[],"relativePath":"en/flight_controller/kakuteh7.md","filePath":"en/flight_controller/kakuteh7.md"}'),p={name:"en/flight_controller/kakuteh7.md"},c=t("h1",{id:"holybro-kakute-h7",tabindex:"-1"},[s("Holybro Kakute H7 "),t("a",{class:"header-anchor",href:"#holybro-kakute-h7","aria-label":'Permalink to "Holybro Kakute H7"'},"​")],-1),m=h('<div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://holybro.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The <a href="https://holybro.com/products/kakute-h7" target="_blank" rel="noreferrer">Holybro Kakute H7</a> is full of features including dual plug-and-play 4in1 ESC ports, HD camera plug, barometer, OSD, 6x UARTs, full Blackbox MicroSD card slot, 5V and 9V BEC, easy soldering layout, and much more.</p><p>The Kakute H7 builds upon the best features of its predecessor, the <a href="./../flight_controller/kakutef7.html">Kakute F7</a>, and further improves on hardware components and layout. Dual plug&#39;n&#39;play 4-in-1 ESC connectors simplify support for x8 and Octocopter configurations, keeping assembly simple and clean.</p><p>The board also has an on-board barometer, LED &amp; buzzer pad, and I2C pad (SDA &amp; SCL) for external GPS/Magnetometers.</p><p><img src="'+a+'" alt="Kakute h7"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="key-features" tabindex="-1">Key Features <a class="header-anchor" href="#key-features" aria-label="Permalink to &quot;Key Features&quot;">​</a></h2><ul><li>MCU: STM32H743 32-bit processor running at 480 MHz</li><li>IMU: MPU6000</li><li>Barometer: BMP280</li><li>OSD: AT7456E</li><li>Onboard Bluetooth chip: Disabled with PX4</li><li>2x JST-SH1.0_8pin port (For Single or 4in1 ESCs, x8/Octocopter plug &amp; play compatible)</li><li>1x JST-GH1.25_6pin port (For HD System like Caddx Vista &amp; Air Unit)</li><li>Battery input voltage: 2S - 8S</li><li>BEC 5V 2A Cont.</li><li>BEC 9V 1.5A Cont.</li><li>Mounting: 30.5 x 30.5mm/Φ4mm hole with Φ3mm Grommets</li><li>Dimensions: 35x35mm</li><li>Weight: 8g</li></ul><h2 id="where-to-buy" tabindex="-1">Where to Buy <a class="header-anchor" href="#where-to-buy" aria-label="Permalink to &quot;Where to Buy&quot;">​</a></h2><p>The board can be bought from one of the following shops (for example):</p><ul><li><a href="https://holybro.com/products/kakute-h7" target="_blank" rel="noreferrer">Holybro</a></li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>The <em>Kakute H7</em> is designed to work with the <em>Tekko32</em> 4-in-1 ESC and they can be bought in combination.</p></div><h2 id="connectors-and-pins" tabindex="-1">Connectors and Pins <a class="header-anchor" href="#connectors-and-pins" aria-label="Permalink to &quot;Connectors and Pins&quot;">​</a></h2><p>This is the silkscreen for the <em>Kakute H7</em>, showing the top of the board:</p><img src="'+o+'" width="380px" title="Kakute h7"><table><thead><tr><th>Pin</th><th>Function</th><th>PX4 default</th></tr></thead><tbody><tr><td>B+</td><td>Battery positive voltage (2S-8S)</td><td></td></tr><tr><td>SDA, SCL</td><td>I2C connection (for peripherals)</td><td></td></tr><tr><td>5V</td><td>5V output (2A max)</td><td></td></tr><tr><td>3V3</td><td>3.3V output (0.25A max)</td><td></td></tr><tr><td>VI</td><td>Video input from FPV camera</td><td></td></tr><tr><td>VO</td><td>Video output to video transmitter</td><td></td></tr><tr><td>CAM</td><td>To camera OSD control</td><td></td></tr><tr><td>G or GND</td><td>Ground</td><td></td></tr><tr><td>RSI</td><td>Analog RSSI (0-3.3V) input from receiver</td><td></td></tr><tr><td>R1, T1</td><td>UART1 RX and TX</td><td>TELEM1</td></tr><tr><td>R3, T3</td><td>UART3 RX and TX</td><td>NuttX debug console</td></tr><tr><td>R4, T4</td><td>UART4 RX and TX</td><td>GPS1</td></tr><tr><td>R6, T6</td><td>UART6 RX and TX (R6 also located in the GH plug)</td><td>RC port</td></tr><tr><td>R7</td><td>UART7 RX (RX is located in the plugs for use with 4-in-1 ESCs)</td><td>DShot telemetry</td></tr><tr><td>LED</td><td>WS2182 addressable LED signal wire (not tested)</td><td></td></tr><tr><td>Z-</td><td>Piezo buzzer negative leg (Connect buzzer positive leg to 5V pad)</td><td></td></tr><tr><td>M1 to M4</td><td>Motor signal outputs (located in plug for use in 4-in-1 ESCs)</td><td></td></tr><tr><td>M5 to M8</td><td>Motor signal outputs (located in plug for use in 4-in-1 ESCs)</td><td></td></tr><tr><td>Boot</td><td>Bootloader button</td><td></td></tr></tbody></table><p><a id="bootloader"></a></p><h2 id="px4-bootloader-update" tabindex="-1">PX4 Bootloader Update <a class="header-anchor" href="#px4-bootloader-update" aria-label="Permalink to &quot;PX4 Bootloader Update&quot;">​</a></h2><p>The board comes pre-installed with <a href="https://github.com/betaflight/betaflight/wiki" target="_blank" rel="noreferrer">Betaflight</a>. Before PX4 firmware can be installed, the <em>PX4 bootloader</em> must be flashed. Download the <a href="https://github.com/PX4/PX4-user_guide/raw/v1.15/assets/flight_controller/kakuteh7/holybro_kakuteh7_bootloader.hex" target="_blank" rel="noreferrer">kakuteh7_bl.hex</a> bootloader binary and read <a href="./../advanced_config/bootloader_update_from_betaflight.html">this page</a> for flashing instructions.</p><h2 id="building-firmware" tabindex="-1">Building Firmware <a class="header-anchor" href="#building-firmware" aria-label="Permalink to &quot;Building Firmware&quot;">​</a></h2><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make holybro_kakuteh7_default</span></span></code></pre></div><h2 id="installing-px4-firmware" tabindex="-1">Installing PX4 Firmware <a class="header-anchor" href="#installing-px4-firmware" aria-label="Permalink to &quot;Installing PX4 Firmware&quot;">​</a></h2><p>The firmware can be installed in any of the normal ways:</p><ul><li><p>Build and upload the source</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make holybro_kakuteh7_default upload</span></span></code></pre></div></li><li><p><a href="./../config/firmware.html">Load the firmware</a> using <em>QGroundControl</em>. You can use either pre-built firmware or your own custom firmware.</p></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>If you are loading the pre-built firmware via QGroundcontrol, you must use QGC Daily or QGC version newer than 4.1.7.</p></div><h2 id="px4-configuration" tabindex="-1">PX4 Configuration <a class="header-anchor" href="#px4-configuration" aria-label="Permalink to &quot;PX4 Configuration&quot;">​</a></h2><p>In addition to the <a href="./../config/">basic configuration</a>, the following parameters are important:</p><table><thead><tr><th>Parameter</th><th>Setting</th></tr></thead><tbody><tr><td><a href="./../advanced_config/parameter_reference.html#SYS_HAS_MAG">SYS_HAS_MAG</a></td><td>This should be disabled since the board does not have an internal mag. You can enable it if you attach an external mag.</td></tr></tbody></table><h2 id="serial-port-mapping" tabindex="-1">Serial Port Mapping <a class="header-anchor" href="#serial-port-mapping" aria-label="Permalink to &quot;Serial Port Mapping&quot;">​</a></h2><table><thead><tr><th>UART</th><th>Device</th><th>Port</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>TELEM1</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>Debug Console</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>GPS1</td></tr><tr><td>USART6</td><td>/dev/ttyS4</td><td>RC SBUS</td></tr><tr><td>UART7</td><td>/dev/ttyS5</td><td>ESC telemetry (DShot)</td></tr></tbody></table><h2 id="debug-port" tabindex="-1">Debug Port <a class="header-anchor" href="#debug-port" aria-label="Permalink to &quot;Debug Port&quot;">​</a></h2><h3 id="system-console" tabindex="-1">System Console <a class="header-anchor" href="#system-console" aria-label="Permalink to &quot;System Console&quot;">​</a></h3><p>UART3 RX and TX are configured for use as the <a href="./../debug/system_console.html">System Console</a>.</p><h3 id="swd" tabindex="-1">SWD <a class="header-anchor" href="#swd" aria-label="Permalink to &quot;SWD&quot;">​</a></h3><p>The <a href="./../debug/swd_debug.html">SWD interface</a> (JTAG) pins are:</p><ul><li><code>SWCLK</code>: Test Point 2 (Pin 72 on the CPU)</li><li><code>SWDIO</code>: Test Point 3 (Pin 76 on CPU)</li><li><code>GND</code>: As marked on board</li><li><code>VDD_3V3</code>: As marked on board</li></ul><p>These are shown below.</p><p><img src="'+r+'" alt="SWD Pins on Kakute H7 - CLK SWO"></p>',39);function b(f,g,k,_,S,y){const e=i("Badge");return u(),l("div",null,[c,n(e,{type:"tip",text:"PX4 v1.13"}),m])}const v=d(p,[["render",b]]);export{C as __pageData,v as default};
