import{_ as a,a as r,b as o,c as l,d as i}from"./chunks/bottom.dvipQsek.js";import{_ as d,E as n,c as s,J as h,m as t,a as c,ab as p,o as f}from"./chunks/framework.CUflZczI.js";const w=JSON.parse('{"title":"ModalAI Flight Core v1","description":"","frontmatter":{},"headers":[],"relativePath":"en/flight_controller/modalai_fc_v1.md","filePath":"en/flight_controller/modalai_fc_v1.md"}'),m={name:"en/flight_controller/modalai_fc_v1.md"},g=t("h1",{id:"modalai-flight-core-v1",tabindex:"-1"},[c("ModalAI Flight Core v1 "),t("a",{class:"header-anchor",href:"#modalai-flight-core-v1","aria-label":'Permalink to "ModalAI Flight Core v1"'},"​")],-1),u=p('<div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://forum.modalai.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The ModalAI <a href="https://modalai.com/flight-core" target="_blank" rel="noreferrer">Flight Core v1</a> (<a href="https://docs.modalai.com/flight-core-datasheet" target="_blank" rel="noreferrer">Datasheet</a>) is a flight controller for PX4, made in the USA. The Flight Core can be paired with ModalAI <a href="https://modalai.com/voxl" target="_blank" rel="noreferrer">VOXL</a> (<a href="https://docs.modalai.com/voxl-datasheet/" target="_blank" rel="noreferrer">Datasheet</a>) for obstacle avoidance and GPS-denied navigation, or used independently as a standalone flight controller.</p><p><img src="'+a+'" alt="FlightCoreV1"></p><p>Flight Core is identical to the PX4 Flight Controller portion of <a href="https://www.modalai.com/voxl-flight" target="_blank" rel="noreferrer">VOXL Flight</a> (<a href="https://docs.modalai.com/voxl-flight-datasheet/" target="_blank" rel="noreferrer">Datasheet</a>) which integrates the VOXL Companion Computer and Flight Core into a single PCB.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="specification" tabindex="-1">Specification <a class="header-anchor" href="#specification" aria-label="Permalink to &quot;Specification&quot;">​</a></h2><table><thead><tr><th style="text-align:left;">Feature</th><th style="text-align:left;">Details</th></tr></thead><tbody><tr><td style="text-align:left;">Weight</td><td style="text-align:left;">6 g</td></tr><tr><td style="text-align:left;">MCU</td><td style="text-align:left;">216MHz, 32-bit ARM M7 <a href="https://www.st.com/en/microcontrollers-microprocessors/stm32f765ii.html" target="_blank" rel="noreferrer">STM32F765II</a></td></tr><tr><td style="text-align:left;">Memory</td><td style="text-align:left;">256Kb FRAM</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">2Mbit Flash</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">512Kbit SRAM</td></tr><tr><td style="text-align:left;">Firmware</td><td style="text-align:left;"><a href="https://github.com/PX4/PX4-Autopilot/tree/release/1.15/boards/modalai/fc-v1" target="_blank" rel="noreferrer">PX4</a></td></tr><tr><td style="text-align:left;">IMUs</td><td style="text-align:left;"><a href="https://www.invensense.com/products/motion-tracking/6-axis/icm-20602/" target="_blank" rel="noreferrer">ICM-20602</a> (SPI1)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">ICM-42688 (SPI2)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;"><a href="https://www.bosch-sensortec.com/bst/products/all_products/bmi088_1" target="_blank" rel="noreferrer">BMI088</a> (SPI6)</td></tr><tr><td style="text-align:left;">Barometer</td><td style="text-align:left;"><a href="https://www.bosch-sensortec.com/products/environmental-sensors/pressure-sensors/bmp388/" target="_blank" rel="noreferrer">BMP388</a> (I2C4)</td></tr><tr><td style="text-align:left;">Secure Element</td><td style="text-align:left;"><a href="https://www.nxp.com/products/security-and-authentication/authentication/plug-and-trust-the-fast-easy-way-to-deploy-secure-iot-connections:A71CH" target="_blank" rel="noreferrer">A71CH</a> (I2C4)</td></tr><tr><td style="text-align:left;">microSD Card</td><td style="text-align:left;"><a href="./../dev_log/logging.html#sd-cards">Information on supported cards</a></td></tr><tr><td style="text-align:left;">Inputs</td><td style="text-align:left;">GPS/Mag</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Spektrum</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Telemetry</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">CAN bus</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">PPM</td></tr><tr><td style="text-align:left;">Outputs</td><td style="text-align:left;">6 LEDs (2xRGB)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">8 PWM Channels</td></tr><tr><td style="text-align:left;">Extra Interfaces</td><td style="text-align:left;">3 serial ports</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">I2C</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">GPIO</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">INFO</p><p>More detailed hardware documentation can be found <a href="https://docs.modalai.com/flight-core-datasheet/" target="_blank" rel="noreferrer">here</a>.</p></div><h2 id="dimensions" tabindex="-1">Dimensions <a class="header-anchor" href="#dimensions" aria-label="Permalink to &quot;Dimensions&quot;">​</a></h2><p><img src="'+r+'" alt="FlightCoreV1Dimensions"></p><h2 id="px4-firmware-compatibility" tabindex="-1">PX4 Firmware Compatibility <a class="header-anchor" href="#px4-firmware-compatibility" aria-label="Permalink to &quot;PX4 Firmware Compatibility&quot;">​</a></h2><p><em>Flight Core v1</em> is fully compatible with the official PX4 Firmware from PX4 v1.11.</p><p>ModalAI maintains a <a href="https://github.com/modalai/px4-firmware/tree/modalai-1.11" target="_blank" rel="noreferrer">branched PX4 version</a> for PX4 v1.11. This includes UART ESC support and improvements in VIO and VOA that are planned to be upstreamed.</p><p>More information about the firmware can be found <a href="https://docs.modalai.com/flight-core-firmware/" target="_blank" rel="noreferrer">here</a>.</p><h2 id="qgroundcontrol-support" tabindex="-1">QGroundControl Support <a class="header-anchor" href="#qgroundcontrol-support" aria-label="Permalink to &quot;QGroundControl Support&quot;">​</a></h2><p>This board supported in QGroundControl 4.0 and later.</p><h2 id="availability" tabindex="-1">Availability <a class="header-anchor" href="#availability" aria-label="Permalink to &quot;Availability&quot;">​</a></h2><ul><li><a href="https://modalai.com/flight-core" target="_blank" rel="noreferrer">Flight Core Complete Kit</a></li><li><a href="https://modalai.com/flight-core" target="_blank" rel="noreferrer">Flight Core integrated with VOXL Companion Computer on a single PCB</a></li><li><a href="https://modalai.com/flight-deck" target="_blank" rel="noreferrer">Flight Core integrated with VOXL Companion Computer and Obstacle Avoidance Cameras (VOXL Flight Deck)</a> (<a href="https://docs.modalai.com/voxl-flight-deck-platform-datasheet/" target="_blank" rel="noreferrer">Datasheet</a>)</li><li><a href="https://shop.modalai.com/products/voxl-flight-deck-r1" target="_blank" rel="noreferrer">Flight Core assembled with VOXL and cameras</a></li></ul><h2 id="quick-start" tabindex="-1">Quick Start <a class="header-anchor" href="#quick-start" aria-label="Permalink to &quot;Quick Start&quot;">​</a></h2><h3 id="orientation" tabindex="-1">Orientation <a class="header-anchor" href="#orientation" aria-label="Permalink to &quot;Orientation&quot;">​</a></h3><p>The diagram below shows the recommended orientation, which corresponds to <code>ROTATION_NONE</code> starting with PX4 v1.11.</p><p><img src="'+o+'" alt="FlightCoreV1Orientation"></p><h3 id="connectors" tabindex="-1">Connectors <a class="header-anchor" href="#connectors" aria-label="Permalink to &quot;Connectors&quot;">​</a></h3><p>Detailed information about the pinouts can be found <a href="https://docs.modalai.com/flight-core-datasheet-connectors" target="_blank" rel="noreferrer">here</a>.</p><p><img src="'+l+'" alt="FlightCoreV1Top"></p><table><thead><tr><th>Connector</th><th>Summary</th></tr></thead><tbody><tr><td>J1</td><td>VOXL Communications Interface Connector (TELEM2)</td></tr><tr><td>J2</td><td>Programming and Debug Connector</td></tr><tr><td>J3</td><td>USB Connector</td></tr><tr><td>J4</td><td>UART2, UART ESC (TELEM3)</td></tr><tr><td>J5</td><td>Telemetry Connector (TELEM1)</td></tr><tr><td>J6</td><td>VOXL-Power Management Input / Expansion</td></tr><tr><td>J7</td><td>8-Channel PWM Output Connector</td></tr><tr><td>J8</td><td>CAN Bus Connector</td></tr><tr><td>J9</td><td>PPM RC In</td></tr><tr><td>J10</td><td>External GPS &amp; Magnetometer Connector</td></tr><tr><td>J12</td><td>RC input, Spektrum/SBus/UART Connector</td></tr><tr><td>J13</td><td>I2C Display (Spare Sensor Connector) / Safety Button Input</td></tr></tbody></table><p><img src="'+i+'" alt="FlightCoreV1Bottom"></p><h3 id="user-guide" tabindex="-1">User Guide <a class="header-anchor" href="#user-guide" aria-label="Permalink to &quot;User Guide&quot;">​</a></h3><p>The full user guide is available <a href="https://docs.modalai.com/flight-core-manual/" target="_blank" rel="noreferrer">here</a>.</p><h3 id="how-to-build" tabindex="-1">How to Build <a class="header-anchor" href="#how-to-build" aria-label="Permalink to &quot;How to Build&quot;">​</a></h3><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make modalai_fc-v1</span></span></code></pre></div><h2 id="serial-port-mapping" tabindex="-1">Serial Port Mapping <a class="header-anchor" href="#serial-port-mapping" aria-label="Permalink to &quot;Serial Port Mapping&quot;">​</a></h2><table><thead><tr><th>UART</th><th>Device</th><th>Port</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>GPS1 (J10)</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM3 (J4)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>Debug Console (J2)</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>Expansion UART (J6)</td></tr><tr><td>UART5</td><td>/dev/ttyS4</td><td>TELEM2, Primary VOXL Communications (J1)</td></tr><tr><td>USART6</td><td>/dev/ttyS5</td><td>RC (J12)</td></tr><tr><td>UART7</td><td>/dev/ttyS6</td><td>TELEM1 (J5)</td></tr><tr><td>UART8</td><td>/dev/ttyS7</td><td>N/A</td></tr></tbody></table><h2 id="support" tabindex="-1">Support <a class="header-anchor" href="#support" aria-label="Permalink to &quot;Support&quot;">​</a></h2><p>Please visit the <a href="https://forum.modalai.com/category/10/flight-core" target="_blank" rel="noreferrer">ModalAI Forum</a> for more information.</p>',36);function b(y,_,x,C,v,k){const e=n("Badge");return f(),s("div",null,[g,h(e,{type:"tip",text:"PX4 v1.11"}),u])}const T=d(m,[["render",b]]);export{w as __pageData,T as default};
