import{_ as a,a as r,b as l,c as o}from"./chunks/voxl-flight-bottom.Di9udeWe.js";import{_ as d,E as i,c as n,J as s,m as t,a as h,ab as c,o as p}from"./chunks/framework.CUflZczI.js";const C=JSON.parse('{"title":"ModalAI VOXL Flight","description":"","frontmatter":{},"headers":[],"relativePath":"en/flight_controller/modalai_voxl_flight.md","filePath":"en/flight_controller/modalai_voxl_flight.md"}'),f={name:"en/flight_controller/modalai_voxl_flight.md"},m=t("h1",{id:"modalai-voxl-flight",tabindex:"-1"},[h("ModalAI VOXL Flight "),t("a",{class:"header-anchor",href:"#modalai-voxl-flight","aria-label":'Permalink to "ModalAI VOXL Flight"'},"​")],-1),g=c('<div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://forum.modalai.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The ModalAI <a href="https://modalai.com/voxl-flight" target="_blank" rel="noreferrer">VOXL Flight</a> (<a href="https://docs.modalai.com/voxl-flight-datasheet" target="_blank" rel="noreferrer">Datasheet</a>) is one of the first computing platforms to combine the power and sophistication of Snapdragon with the flexibility and ease of use of PX4 on an STM32F7. Made in the USA, VOXL Flight supports obstacle avoidance and GPS-denied (indoor) navigation fused with a PX4 flight controller on a single PCB.</p><p><img src="'+a+'" alt="VOXL-Flight"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="specifications" tabindex="-1">Specifications <a class="header-anchor" href="#specifications" aria-label="Permalink to &quot;Specifications&quot;">​</a></h2><h3 id="system" tabindex="-1">System <a class="header-anchor" href="#system" aria-label="Permalink to &quot;System&quot;">​</a></h3><table><thead><tr><th style="text-align:left;">Feature</th><th style="text-align:left;">Details</th></tr></thead><tbody><tr><td style="text-align:left;">Weight</td><td style="text-align:left;">26 g</td></tr></tbody></table><h3 id="companion-computer" tabindex="-1">Companion Computer <a class="header-anchor" href="#companion-computer" aria-label="Permalink to &quot;Companion Computer&quot;">​</a></h3><table><thead><tr><th style="text-align:left;">Feature</th><th style="text-align:left;">Details</th></tr></thead><tbody><tr><td style="text-align:left;">Base Operation System</td><td style="text-align:left;">Linux Yocto Jethro with 3.18 kernel. Additional Linux Operating Systems can be used by running Docker on VOXL, details <a href="https://docs.modalai.com/docker-on-voxl/" target="_blank" rel="noreferrer">here</a></td></tr><tr><td style="text-align:left;">Compute</td><td style="text-align:left;">Qualcomm Snapdragon 821 w/ 4GB LPDDR4 1866MHz, Snapdragon 821 <a href="https://developer.qualcomm.com/download/sd820e/qualcomm-snapdragon-820e-processor-apq8096sge-device-specification.pdf" target="_blank" rel="noreferrer">Datasheet</a>, <a href="https://developer.qualcomm.com/hardware/apq-8096sg/tools" target="_blank" rel="noreferrer">Docs</a></td></tr><tr><td style="text-align:left;">CPU</td><td style="text-align:left;">Quad-core CPU up to 2.15GHz</td></tr><tr><td style="text-align:left;">GPU</td><td style="text-align:left;">Adreno 530 GPU at 624MHz</td></tr><tr><td style="text-align:left;">Compute DSP</td><td style="text-align:left;">Hexagon compute DSP (cDSP) 825MHz</td></tr><tr><td style="text-align:left;">Sensor DSP</td><td style="text-align:left;">Hexagon sensor DSP (sDSP) 700MHz</td></tr><tr><td style="text-align:left;">Video</td><td style="text-align:left;">4k30 Video Capture h.264/5 w/ 720p FPV</td></tr><tr><td style="text-align:left;">Camera Interfaces</td><td style="text-align:left;">Support for MIPI-CSI2, USB UVC, HDMI</td></tr><tr><td style="text-align:left;">Wi-Fi</td><td style="text-align:left;">Pre-certified Wi-Fi module <a href="https://fccid.io/PPD-QCNFA324" target="_blank" rel="noreferrer">QCNFA324 FCC ID:PPD-QCNFA324</a>, QCA6174A modem, 802.11ac 2x2 Dual-band, Bluetooth 4.2 (dual-mode)</td></tr><tr><td style="text-align:left;">4G LTE</td><td style="text-align:left;"><a href="https://www.modalai.com/collections/voxl-add-ons/products/voxl-lte" target="_blank" rel="noreferrer">Optional add-on module</a></td></tr><tr><td style="text-align:left;">Microhard pDDL</td><td style="text-align:left;"><a href="https://www.modalai.com/collections/voxl-add-ons/products/voxl-microhard-modem-usb-hub" target="_blank" rel="noreferrer">Optional add-on module</a></td></tr><tr><td style="text-align:left;">GNSS</td><td style="text-align:left;">WGR7640 10Hz</td></tr><tr><td style="text-align:left;">I/O</td><td style="text-align:left;">1x USB3.0 OTG (ADB port), 1x USB2.0 (expansion port), 2x UART, 3x I2C, additional GPIO and SPI can be configured</td></tr><tr><td style="text-align:left;">Storage</td><td style="text-align:left;">32GB (UFS 2.0), Micro SD Card</td></tr><tr><td style="text-align:left;">Software</td><td style="text-align:left;">Docker, OpenCV 2.4.11, 3.4.6, 4.2, ROS Indigo, Qualcomm Machine Vision SDK, see <a href="https://gitlab.com/voxl-public" target="_blank" rel="noreferrer">GitLab</a> for lots of open source examples!</td></tr><tr><td style="text-align:left;">IMUs</td><td style="text-align:left;">ICM-42688 (SPI10), ICM-20948 (SPI1)</td></tr><tr><td style="text-align:left;">Barometer</td><td style="text-align:left;">BMP280</td></tr></tbody></table><h3 id="flight-controller" tabindex="-1">Flight Controller <a class="header-anchor" href="#flight-controller" aria-label="Permalink to &quot;Flight Controller&quot;">​</a></h3><table><thead><tr><th style="text-align:left;">Feature</th><th style="text-align:left;">Details</th></tr></thead><tbody><tr><td style="text-align:left;">MCU</td><td style="text-align:left;">216MHz, 32-bit ARM M7 <a href="https://www.st.com/en/microcontrollers-microprocessors/stm32f765ii.html" target="_blank" rel="noreferrer">STM32F765II</a></td></tr><tr><td style="text-align:left;">Memory</td><td style="text-align:left;">256Kb FRAM</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">2Mbit Flash</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">512Kbit SRAM</td></tr><tr><td style="text-align:left;">Firmware</td><td style="text-align:left;"><a href="https://github.com/PX4/PX4-Autopilot/tree/release/1.15/boards/modalai/fc-v1" target="_blank" rel="noreferrer">PX4</a></td></tr><tr><td style="text-align:left;">IMUs</td><td style="text-align:left;"><a href="https://www.invensense.com/products/motion-tracking/6-axis/icm-20602/" target="_blank" rel="noreferrer">ICM-20602</a> (SPI1)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">ICM-42688 (SPI2)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;"><a href="https://www.bosch-sensortec.com/bst/products/all_products/bmi088_1" target="_blank" rel="noreferrer">BMI088</a> (SPI6)</td></tr><tr><td style="text-align:left;">Barometer</td><td style="text-align:left;"><a href="https://www.bosch-sensortec.com/products/environmental-sensors/pressure-sensors/bmp388/" target="_blank" rel="noreferrer">BMP388</a> (I2C4)</td></tr><tr><td style="text-align:left;">Secure Element</td><td style="text-align:left;"><a href="https://www.nxp.com/products/security-and-authentication/authentication/plug-and-trust-the-fast-easy-way-to-deploy-secure-iot-connections:A71CH" target="_blank" rel="noreferrer">A71CH</a> (I2C4)</td></tr><tr><td style="text-align:left;">microSD Card</td><td style="text-align:left;"><a href="./../dev_log/logging.html#sd-cards">Information on supported cards</a></td></tr><tr><td style="text-align:left;">Inputs</td><td style="text-align:left;">GPS/Mag</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Spektrum</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Telemetry</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">CAN bus</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">PPM</td></tr><tr><td style="text-align:left;">Outputs</td><td style="text-align:left;">6 LEDs (2xRGB)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">8 PWM Channels</td></tr><tr><td style="text-align:left;">Extra Interfaces</td><td style="text-align:left;">3 serial ports</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">I2C</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">GPIO</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">INFO</p><p>More detailed hardware documentation can be found <a href="https://docs.modalai.com/voxl-flight-datasheet/" target="_blank" rel="noreferrer">here</a>.</p></div><h2 id="dimensions" tabindex="-1">Dimensions <a class="header-anchor" href="#dimensions" aria-label="Permalink to &quot;Dimensions&quot;">​</a></h2><p><img src="'+r+'" alt="FlightCoreV1Dimensions"></p><p><a href="https://storage.googleapis.com/modalai_public/modal_drawings/M0019_VOXL-Flight.zip" target="_blank" rel="noreferrer">3D STEP File</a></p><h2 id="px4-firmware-compatibility" tabindex="-1">PX4 Firmware Compatibility <a class="header-anchor" href="#px4-firmware-compatibility" aria-label="Permalink to &quot;PX4 Firmware Compatibility&quot;">​</a></h2><p><em>VOXL Flight</em> is fully compatible with the official PX4 Firmware from PX4 v1.11.</p><p>ModalAI maintains a <a href="https://github.com/modalai/px4-firmware/tree/modalai-1.11" target="_blank" rel="noreferrer">branched PX4 version</a> for PX4 v1.11. This includes UART ESC support and improvements in VIO and VOA that are planned to be upstreamed.</p><p>More information about the firmware can be found <a href="https://docs.modalai.com/flight-core-firmware/" target="_blank" rel="noreferrer">here</a>.</p><h2 id="qgroundcontrol-support" tabindex="-1">QGroundControl Support <a class="header-anchor" href="#qgroundcontrol-support" aria-label="Permalink to &quot;QGroundControl Support&quot;">​</a></h2><p>This board supported in QGroundControl 4.0 and later.</p><h2 id="availability" tabindex="-1">Availability <a class="header-anchor" href="#availability" aria-label="Permalink to &quot;Availability&quot;">​</a></h2><ul><li><a href="https://modalai.com/voxl-flight" target="_blank" rel="noreferrer">VOXL Flight Complete Kit</a></li><li><a href="https://www.modalai.com/products/voxl-flight?variant=31707275362355" target="_blank" rel="noreferrer">VOXL Flight Board</a> (only)</li><li><a href="https://modalai.com/flight-deck" target="_blank" rel="noreferrer">VOXL Flight integrated with Obstacle Avoidance Cameras (VOXL Flight Deck)</a> (<a href="https://docs.modalai.com/voxl-flight-deck-platform-datasheet/" target="_blank" rel="noreferrer">Datasheet</a>)</li><li><a href="https://www.modalai.com/collections/development-drones/products/voxl-m500" target="_blank" rel="noreferrer">VOXL Flight in a ready to fly VOXL m500 Development Drone</a> (<a href="https://docs.modalai.com/voxl-m500-reference-drone-datasheet/" target="_blank" rel="noreferrer">Datasheet</a>)</li></ul><h2 id="quick-start" tabindex="-1">Quick Start <a class="header-anchor" href="#quick-start" aria-label="Permalink to &quot;Quick Start&quot;">​</a></h2><p>A quickstart from the vendor is located <a href="https://docs.modalai.com/voxl-flight-quickstart/" target="_blank" rel="noreferrer">here</a>.</p><h3 id="voxl-vision-px4" tabindex="-1">voxl-vision-px4 <a class="header-anchor" href="#voxl-vision-px4" aria-label="Permalink to &quot;voxl-vision-px4&quot;">​</a></h3><p>The VOXL Flight runs <a href="https://gitlab.com/voxl-public/modal-pipe-architecture/voxl-vision-px4" target="_blank" rel="noreferrer">voxl-vision-px4</a> on the companion computer portion of the hardware serving as a sort of MAVLink proxy. For details, the source code is available <a href="https://gitlab.com/voxl-public/modal-pipe-architecture/voxl-vision-px4" target="_blank" rel="noreferrer">here</a></p><h3 id="connectors" tabindex="-1">Connectors <a class="header-anchor" href="#connectors" aria-label="Permalink to &quot;Connectors&quot;">​</a></h3><p>Detailed information about the pinouts can be found <a href="https://docs.modalai.com/voxl-flight-datasheet-connectors/" target="_blank" rel="noreferrer">here</a>.</p><h4 id="top" tabindex="-1">Top <a class="header-anchor" href="#top" aria-label="Permalink to &quot;Top&quot;">​</a></h4><p><img src="'+l+'" alt="VOXLFlightTop"></p><p><em>Note: 1000 Series connectors accessible from the STM32/PX4</em></p><table><thead><tr><th>Connector</th><th>Summary</th><th>Used By</th></tr></thead><tbody><tr><td>J2</td><td>Hires 4k Image Sensor (CSI0)</td><td>Snapdragon - Linux</td></tr><tr><td>J3</td><td>Stereo Image Sensor (CSI1)</td><td>Snapdragon - Linux</td></tr><tr><td>J6</td><td>Cooling Fan Connector</td><td>Snapdragon - Linux</td></tr><tr><td>J7</td><td>BLSP6 (GPIO) and BLSP9 (UART)</td><td>Snapdragon - Linux</td></tr><tr><td>J13</td><td>Expansion B2B</td><td>Snapdragon - Linux</td></tr><tr><td>J14</td><td>Integrated GNSS Antenna Connection</td><td>Snapdragon - Linux</td></tr><tr><td>J1001</td><td>Programming and Debug/UART3</td><td>STM32 - PX4</td></tr><tr><td>J1002</td><td>UART ESC, UART2/TELEM3</td><td>STM32 - PX4</td></tr><tr><td>J1003</td><td>PPM RC In</td><td>STM32 - PX4</td></tr><tr><td>J1004</td><td>RC Input, Spektrum/SBus/UART6</td><td>STM32 - PX4</td></tr><tr><td>J1006</td><td>USB 2.0 Connector (PX4/QGroundControl)</td><td>STM32 - PX4</td></tr><tr><td>J1007</td><td>8-Channel PWM/DShot Output</td><td>STM32 - PX4</td></tr><tr><td>J1008</td><td>CAN Bus</td><td>STM32 - PX4</td></tr><tr><td>J1009</td><td>I2C3, UART4</td><td>STM32 - PX4</td></tr><tr><td>J1010</td><td>Telemetry (TELEM1)</td><td>STM32 - PX4</td></tr><tr><td>J1011</td><td>I2C2, Safety Button Input</td><td>STM32 - PX4</td></tr><tr><td>J1012</td><td>External GPS &amp; Mag, UART1, I2C1</td><td>STM32 - PX4</td></tr><tr><td>J1013</td><td>Power Input, I2C3</td><td>STM32 - PX4 (powers whole system)</td></tr></tbody></table><h4 id="bottom" tabindex="-1">Bottom <a class="header-anchor" href="#bottom" aria-label="Permalink to &quot;Bottom&quot;">​</a></h4><p><img src="'+o+'" alt="VOXLFlightBottom"></p><p><em>Note: 1000 Series connectors accessible from the STM32/PX4</em></p><table><thead><tr><th>Connector</th><th>Summary</th><th>Used By</th></tr></thead><tbody><tr><td>J4</td><td>Tracking/Optic Flow Image Sensor (CSI2)</td><td>Snapdragon - Linux</td></tr><tr><td>J8</td><td>USB 3.0 OTG</td><td>Snapdragon - Linux, <strong>adb</strong></td></tr><tr><td>J10</td><td>BLSP7 UART and I2C off-board</td><td>Snapdragon - Linux</td></tr><tr><td>J11</td><td>BLSP12 UART and I2C off-board</td><td>Snapdragon - Linux</td></tr><tr><td>VOXL microSD</td><td></td><td>Snapdragon - Linux</td></tr><tr><td>PX4 microSD</td><td>32Gb Max</td><td>STM32 - PX4</td></tr><tr><td>Wi-Fi Antennas</td><td>Included</td><td>Snapdragon - Linux</td></tr></tbody></table><h3 id="user-guide" tabindex="-1">User Guide <a class="header-anchor" href="#user-guide" aria-label="Permalink to &quot;User Guide&quot;">​</a></h3><p>The full user guide is available <a href="https://docs.modalai.com/voxl-flight-quickstart" target="_blank" rel="noreferrer">here</a>.</p><h3 id="how-to-build" tabindex="-1">How to Build <a class="header-anchor" href="#how-to-build" aria-label="Permalink to &quot;How to Build&quot;">​</a></h3><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make modalai_fc-v1</span></span></code></pre></div><h2 id="serial-port-mapping" tabindex="-1">Serial Port Mapping <a class="header-anchor" href="#serial-port-mapping" aria-label="Permalink to &quot;Serial Port Mapping&quot;">​</a></h2><p><em>Note: mappings shown are for the PX4 controlled interfaces only</em></p><table><thead><tr><th>UART</th><th>Device</th><th>Port</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>GPS1 (J1012)</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM3 (J1002)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>Debug Console (J1001)</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>Expansion UART (J6)</td></tr><tr><td>UART5</td><td>/dev/ttyS4</td><td>UART between PX4 and Companion Computer</td></tr><tr><td>USART6</td><td>/dev/ttyS5</td><td>RC (J1004)</td></tr><tr><td>UART7</td><td>/dev/ttyS6</td><td>TELEM1 (J1010)</td></tr><tr><td>UART8</td><td>/dev/ttyS7</td><td>N/A</td></tr></tbody></table><h2 id="support" tabindex="-1">Support <a class="header-anchor" href="#support" aria-label="Permalink to &quot;Support&quot;">​</a></h2><p>Please visit the <a href="https://forum.modalai.com/category/8/voxl-flight" target="_blank" rel="noreferrer">ModalAI Forum</a> for more information.</p>',47);function u(x,b,y,S,P,_){const e=i("Badge");return p(),n("div",null,[m,s(e,{type:"tip",text:"PX4 v1.11"}),g])}const M=d(f,[["render",u]]);export{C as __pageData,M as default};
