import{_ as t,a as e,b as i}from"./chunks/mro_x21_wiring.D9V6_9jN.js";import{_ as a,c as r,o,ab as l}from"./chunks/framework.CUflZczI.js";const S=JSON.parse('{"title":"mRo-X2.1 Autopilot","description":"","frontmatter":{},"headers":[],"relativePath":"en/flight_controller/mro_x2.1.md","filePath":"en/flight_controller/mro_x2.1.md"}'),s={name:"en/flight_controller/mro_x2.1.md"},n=l('<h1 id="mro-x2-1-autopilot" tabindex="-1">mRo-X2.1 Autopilot <a class="header-anchor" href="#mro-x2-1-autopilot" aria-label="Permalink to &quot;mRo-X2.1 Autopilot&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://store.mrobotics.io/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The <a href="http://www.mRobotics.io/" target="_blank" rel="noreferrer">mRo-X2.1 autopilot</a> is based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk<sup>®</sup>-project</a> <strong>FMUv2</strong> open hardware design. It runs PX4 on the <a href="https://nuttx.apache.org/" target="_blank" rel="noreferrer">NuttX</a> OS.</p><p><img src="'+t+'" alt="mRo X2.1"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="quick-summary" tabindex="-1">Quick Summary <a class="header-anchor" href="#quick-summary" aria-label="Permalink to &quot;Quick Summary&quot;">​</a></h2><ul><li>Main System-on-Chip: <a href="http://www.st.com/web/en/catalog/mmc/FM141/SC1169/SS1577/LN1789" target="_blank" rel="noreferrer">STM32F427</a><ul><li>CPU: STM32F427VIT6 ARM<sup>®</sup> microcontroller - Revision 3</li><li>IO: STM32F100C8T6 ARM<sup>®</sup> microcontroller</li></ul></li><li>Sensors: <ul><li>Invensense<sup>®</sup> MPU9250 9DOF</li><li>Invensense ICM-20602 6DOF</li><li>MEAS MS5611 barometer</li></ul></li><li>Dimensions/Weight <ul><li>Size: 36mm x 50mm (Can be ordered with vertical, horizontal or no headers installed)</li><li>Mounting Points: 30.5mm x 30.5mm 3.2mm diameter</li><li>Weight: 10.9g</li></ul></li></ul><p>The diagram below provides a side-by-side comparison with a Pixhawk 1. The mRo features almost identical hardware and connectivity but has a much smaller footprint. Major differences are updated sensors and Rev 3 FMU.</p><p><img src="'+e+'" alt="Mro Pixhawk 1 vs X2.1 comparison"></p><h2 id="connectivity" tabindex="-1">Connectivity <a class="header-anchor" href="#connectivity" aria-label="Permalink to &quot;Connectivity&quot;">​</a></h2><ul><li>2.54mm headers:</li><li>GPS (UART4) with I2C</li><li>CAN Bus</li><li>RC input</li><li>PPM input</li><li>Spektrum input</li><li>RSSI input</li><li>sBus input</li><li>sBus output</li><li>Power input</li><li>Buzzer output</li><li>LED output</li><li>8 x Servo outputs</li><li>6 x Aux outputs</li><li>Offboard microUSB connector</li><li>Kill Pin output <em>(Currently not supported by firmware)</em></li><li>AirSpeed Sensor</li><li>USART2 (Telem 1)</li><li>USART3 (Telem 2)</li><li>UART7 (Console)</li><li>UART8 (OSD)</li></ul><h2 id="px4-bootloader-issue" tabindex="-1">PX4 BootLoader Issue <a class="header-anchor" href="#px4-bootloader-issue" aria-label="Permalink to &quot;PX4 BootLoader Issue&quot;">​</a></h2><p>By default a mRo X2.1 might come preconfigured for ArduPilot<sup>®</sup> rather than PX4. This can be seen during firmware update when the board is recognized as FMUv2 instead of X2.1.</p><p>In this case you must update the BootLoader using <a href="https://github.com/PX4/PX4-user_guide/raw/v1.15/assets/hardware/BL_Update_X21.zip" target="_blank" rel="noreferrer">BL_Update_X21.zip</a>. If this correction is not carried out your compass direction will be wrong and the secondary IMU will not be detected.</p><p>The update steps are:</p><ol><li>Download and extract <a href="https://github.com/PX4/PX4-user_guide/raw/v1.15/assets/hardware/BL_Update_X21.zip" target="_blank" rel="noreferrer">BL_Update_X21.zip</a>.</li><li>Find the folder <em>BL_Update_X21</em>. This contains a <strong>bin</strong> file and a subfolder named <strong>/etc</strong> containing an <strong>rc.txt</strong> file</li><li>Copy these files to your micro SD card&#39;s root directory and insert it into the mRO x2.1</li><li>Power on the mRO x2.1 Wait for it to boot and then reboot 1 time.</li></ol><h2 id="availability" tabindex="-1">Availability <a class="header-anchor" href="#availability" aria-label="Permalink to &quot;Availability&quot;">​</a></h2><p>This product can be ordered at the <a href="https://store.mrobotics.io/mRo-X2-1-Rev-2-p/m10021a.htm" target="_blank" rel="noreferrer">mRobotics<sup>®</sup> Store</a>.</p><h2 id="wiring-guide" tabindex="-1">Wiring Guide <a class="header-anchor" href="#wiring-guide" aria-label="Permalink to &quot;Wiring Guide&quot;">​</a></h2><p><img src="'+i+'" alt="mRo_X2.1_Wiring"></p><h2 id="building-firmware" tabindex="-1">Building Firmware <a class="header-anchor" href="#building-firmware" aria-label="Permalink to &quot;Building Firmware&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make mro_x21_default</span></span></code></pre></div><h2 id="schematics" tabindex="-1">Schematics <a class="header-anchor" href="#schematics" aria-label="Permalink to &quot;Schematics&quot;">​</a></h2><p>The board is documented on the mRo hardware repo: <a href="https://github.com/mRoboticsIO/Hardware/blob/master/X2.1/Docs/x21_V2_schematic.pdf" target="_blank" rel="noreferrer">x21_V2_schematic.pdf</a>.</p><h2 id="serial-port-mapping" tabindex="-1">Serial Port Mapping <a class="header-anchor" href="#serial-port-mapping" aria-label="Permalink to &quot;Serial Port Mapping&quot;">​</a></h2><table><thead><tr><th>UART</th><th>Device</th><th>Port</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>IO debug</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>SERIAL1</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>GPS/I2C</td></tr><tr><td>USART6</td><td>/dev/ttyS4</td><td>PX4IO</td></tr><tr><td>UART7</td><td>/dev/ttyS5</td><td>SERIAL5 CONSOLE</td></tr><tr><td>UART8</td><td>/dev/ttyS6</td><td>SERIAL4</td></tr></tbody></table>',28),d=[n];function p(u,h,c,m,b,_){return o(),r("div",null,d)}const v=a(s,[["render",p]]);export{S as __pageData,v as default};
