import{_ as e,a as t,b as r,c as a}from"./chunks/pixhawk4_debug_port.BFdkyuia.js";import{_ as o}from"./chunks/pixhawk4_logo_view.C3CsFYwh.js";import{_ as i,c as s,o as l,ab as n}from"./chunks/framework.CUflZczI.js";const x=JSON.parse('{"title":"Holybro Pixhawk 4","description":"","frontmatter":{},"headers":[],"relativePath":"en/flight_controller/pixhawk4.md","filePath":"en/flight_controller/pixhawk4.md"}'),d={name:"en/flight_controller/pixhawk4.md"},h=n('<h1 id="holybro-pixhawk-4" tabindex="-1">Holybro Pixhawk 4 <a class="header-anchor" href="#holybro-pixhawk-4" aria-label="Permalink to &quot;Holybro Pixhawk 4&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://holybro.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p><em>Pixhawk 4</em><sup>®</sup> is an advanced autopilot designed and made in collaboration with Holybro<sup>®</sup> and the PX4 team. It is optimized to run PX4 v1.7 and later, and is suitable for academic and commercial developers.</p><p>It is based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk-project</a> <strong>FMUv5</strong> open hardware design and runs PX4 on the <a href="https://nuttx.apache.org/" target="_blank" rel="noreferrer">NuttX</a> OS.</p><p><img src="'+e+'" width="200px" title="Pixhawk4 Upright Image"> <img src="'+o+'" width="420px" title="Pixhawk4 Image"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>This autopilot is <a href="./../flight_controller/autopilot_pixhawk_standard.html">supported</a> by the PX4 maintenance and test teams.</p></div><h2 id="quick-summary" tabindex="-1">Quick Summary <a class="header-anchor" href="#quick-summary" aria-label="Permalink to &quot;Quick Summary&quot;">​</a></h2><ul><li>Main FMU Processor: STM32F765 <ul><li>32 Bit Arm® Cortex®-M7, 216MHz, 2MB memory, 512KB RAM</li></ul></li><li>IO Processor: STM32F100 <ul><li>32 Bit Arm® Cortex®-M3, 24MHz, 8KB SRAM</li></ul></li><li>On-board sensors: <ul><li>Accel/Gyro: ICM-20689</li><li>Accel/Gyro: BMI055 or ICM20602</li><li>Magnetometer: IST8310</li><li>Barometer: MS5611</li></ul></li><li>GPS: u-blox Neo-M8N GPS/GLONASS receiver; integrated magnetometer IST8310</li><li>Interfaces: <ul><li>8-16 PWM outputs (8 from IO, 8 from FMU)</li><li>3 dedicated PWM/Capture inputs on FMU</li><li>Dedicated R/C input for CPPM</li><li>Dedicated R/C input for Spektrum / DSM and S.Bus with analog / PWM RSSI input</li><li>Dedicated S.Bus servo output</li><li>5 general purpose serial ports</li><li>3 I2C ports</li><li>4 SPI buses</li><li>Up to 2 CANBuses for dual CAN with serial ESC</li><li>Analog inputs for voltage / current of 2 batteries</li></ul></li><li>Power System: <ul><li>Power module output: 4.9~5.5V</li><li>USB Power Input: 4.75~5.25V</li><li>Servo Rail Input: 0~36V</li></ul></li><li>Weight and Dimensions: <ul><li>Weight: 15.8g</li><li>Dimensions: 44x84x12mm</li></ul></li><li>Other Characteristics: <ul><li>Operating temperature: -40 ~ 85°c</li></ul></li></ul><p>Additional information can be found in the <a href="https://github.com/PX4/PX4-user_guide/raw/v1.15/assets/flight_controller/pixhawk4/pixhawk4_technical_data_sheet.pdf" target="_blank" rel="noreferrer">Pixhawk 4 Technical Data Sheet</a>.</p><h2 id="where-to-buy" tabindex="-1">Where to Buy <a class="header-anchor" href="#where-to-buy" aria-label="Permalink to &quot;Where to Buy&quot;">​</a></h2><p>Order from <a href="https://holybro.com/products/pixhawk-4" target="_blank" rel="noreferrer">Holybro</a>.</p><h2 id="connectors" tabindex="-1">Connectors <a class="header-anchor" href="#connectors" aria-label="Permalink to &quot;Connectors&quot;">​</a></h2><p><img src="'+t+'" alt="Pixhawk 4 connectors"></p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>The <strong>DSM/SBUS RC</strong> and <strong>PPM RC</strong> ports are for RC receivers only. These are powered! NEVER connect any servos, power supplies or batteries (or to any connected receiver).</p></div><h2 id="pinouts" tabindex="-1">Pinouts <a class="header-anchor" href="#pinouts" aria-label="Permalink to &quot;Pinouts&quot;">​</a></h2><p>Download <em>Pixhawk 4</em> pinouts from <a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Pixhawk4-Pinouts.pdf" target="_blank" rel="noreferrer">here</a>.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Connector pin assignments are left to right (i.e. Pin 1 is the left-most pin). The exception is the <a href="#debug_port">debug port(s)</a> (pin 1 is the right-most, as shown below).</p></div><h2 id="serial-port-mapping" tabindex="-1">Serial Port Mapping <a class="header-anchor" href="#serial-port-mapping" aria-label="Permalink to &quot;Serial Port Mapping&quot;">​</a></h2><table><thead><tr><th>UART</th><th>Device</th><th>Port</th></tr></thead><tbody><tr><td>UART1</td><td>/dev/ttyS0</td><td>GPS</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (flow control)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2 (flow control)</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>TELEM4</td></tr><tr><td>USART6</td><td>/dev/ttyS4</td><td>RC SBUS</td></tr><tr><td>UART7</td><td>/dev/ttyS5</td><td>Debug Console</td></tr><tr><td>UART8</td><td>/dev/ttyS6</td><td>PX4IO</td></tr></tbody></table><h2 id="dimensions" tabindex="-1">Dimensions <a class="header-anchor" href="#dimensions" aria-label="Permalink to &quot;Dimensions&quot;">​</a></h2><p><img src="'+r+'" alt="Pixhawk 4 Dimensions"></p><h2 id="voltage-ratings" tabindex="-1">Voltage Ratings <a class="header-anchor" href="#voltage-ratings" aria-label="Permalink to &quot;Voltage Ratings&quot;">​</a></h2><p><em>Pixhawk 4</em> can be triple-redundant on the power supply if three power sources are supplied. The three power rails are: <strong>POWER1</strong>, <strong>POWER2</strong> and <strong>USB</strong>.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The output power rails <strong>FMU PWM OUT</strong> and <strong>I/O PWM OUT</strong> (0V to 36V) do not power the flight controller board (and are not powered by it). You must supply power to one of <strong>POWER1</strong>, <strong>POWER2</strong> or <strong>USB</strong> or the board will be unpowered.</p></div><p><strong>Normal Operation Maximum Ratings</strong></p><p>Under these conditions all power sources will be used in this order to power the system:</p><ol><li><strong>POWER1</strong> and <strong>POWER2</strong> inputs (4.9V to 5.5V)</li><li><strong>USB</strong> input (4.75V to 5.25V)</li></ol><p><strong>Absolute Maximum Ratings</strong></p><p>Under these conditions the system will not draw any power (will not be operational), but will remain intact.</p><ol><li><strong>POWER1</strong> and <strong>POWER2</strong> inputs (operational range 4.1V to 5.7V, 0V to 10V undamaged)</li><li><strong>USB</strong> input (operational range 4.1V to 5.7V, 0V to 6V undamaged)</li><li>Servo input: VDD_SERVO pin of <strong>FMU PWM OUT</strong> and <strong>I/O PWM OUT</strong> (0V to 42V undamaged)</li></ol><h2 id="assembly-setup" tabindex="-1">Assembly/Setup <a class="header-anchor" href="#assembly-setup" aria-label="Permalink to &quot;Assembly/Setup&quot;">​</a></h2><p>The <a href="./../assembly/quick_start_pixhawk4.html">Pixhawk 4 Wiring Quick Start</a> provides instructions on how to assemble required/important peripherals including GPS, Power Management Board etc.</p><h2 id="building-firmware" tabindex="-1">Building Firmware <a class="header-anchor" href="#building-firmware" aria-label="Permalink to &quot;Building Firmware&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make px4_fmu-v5_default</span></span></code></pre></div><p><a id="debug_port"></a></p><h2 id="debug-port" tabindex="-1">Debug Port <a class="header-anchor" href="#debug-port" aria-label="Permalink to &quot;Debug Port&quot;">​</a></h2><p>The <a href="./../debug/system_console.html">PX4 System Console</a> and <a href="./../debug/swd_debug.html">SWD interface</a> run on the <strong>FMU Debug</strong> port, while the I/O console and SWD interface can be accessed via <strong>I/O Debug</strong> port. In order to access these ports, the user must remove the <em>Pixhawk 4</em> casing.</p><p><img src="'+a+'" alt="Pixhawk 4 Debug Ports"></p><p>The pinout uses the standard <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk debug connector pinout</a>. For wiring information see:</p><ul><li><a href="./../debug/system_console.html#pixhawk_debug_port">System Console &gt; Pixhawk Debug Port</a></li></ul><h2 id="peripherals" tabindex="-1">Peripherals <a class="header-anchor" href="#peripherals" aria-label="Permalink to &quot;Peripherals&quot;">​</a></h2><ul><li><a href="https://store-drotek.com/793-digital-differential-airspeed-sensor-kit-.html" target="_blank" rel="noreferrer">Digital Airspeed Sensor</a></li><li><a href="./../telemetry/">Telemetry Radio Modules</a></li><li><a href="./../sensor/rangefinders.html">Rangefinders/Distance sensors</a></li></ul><h2 id="supported-platforms-airframes" tabindex="-1">Supported Platforms / Airframes <a class="header-anchor" href="#supported-platforms-airframes" aria-label="Permalink to &quot;Supported Platforms / Airframes&quot;">​</a></h2><p>Any multicopter / airplane / rover or boat that can be controlled with normal RC servos or Futaba S-Bus servos. The complete set of supported configurations can be seen in the <a href="./../airframes/airframe_reference.html">Airframes Reference</a>.</p><h2 id="further-info" tabindex="-1">Further info <a class="header-anchor" href="#further-info" aria-label="Permalink to &quot;Further info&quot;">​</a></h2><ul><li><a href="https://github.com/PX4/PX4-user_guide/raw/v1.15/assets/flight_controller/pixhawk4/pixhawk4_technical_data_sheet.pdf" target="_blank" rel="noreferrer">Pixhawk 4 Technical Data Sheet</a></li><li><a href="https://docs.google.com/spreadsheets/d/1-n0__BYDedQrc_2NHqBenG1DNepAgnHpSGglke-QQwY/edit#gid=912976165" target="_blank" rel="noreferrer">FMUv5 reference design pinout</a>.</li><li><a href="./../assembly/quick_start_pixhawk4.html">Pixhawk 4 Wiring QuickStart</a></li><li><a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Pixhawk4-Pinouts.pdf" target="_blank" rel="noreferrer">Pixhawk 4 Pinouts</a> (Holybro)</li><li><a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Pixhawk4-quickstartguide.pdf" target="_blank" rel="noreferrer">Pixhawk 4 Quick Start Guide</a> (Holybro)</li></ul>',48),p=[h];function u(c,m,g,f,b,P){return l(),s("div",null,p)}const S=i(d,[["render",u]]);export{x as __pageData,S as default};
