import{_ as e,a as t}from"./chunks/pixhawk_6c_mini_dimension.AWcw-Z5D.js";import{_ as r,c as a,o as i,ab as o}from"./chunks/framework.CUflZczI.js";const w=JSON.parse('{"title":"Holybro Pixhawk 6C Mini","description":"","frontmatter":{},"headers":[],"relativePath":"en/flight_controller/pixhawk6c_mini.md","filePath":"en/flight_controller/pixhawk6c_mini.md"}'),l={name:"en/flight_controller/pixhawk6c_mini.md"},n=o('<h1 id="holybro-pixhawk-6c-mini" tabindex="-1">Holybro Pixhawk 6C Mini <a class="header-anchor" href="#holybro-pixhawk-6c-mini" aria-label="Permalink to &quot;Holybro Pixhawk 6C Mini&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://holybro.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p><em>Pixhawk 6C Mini</em><sup>®</sup> is the latest update to the successful family of Pixhawk® flight controllers designed and made in collaboration with Holybro<sup>®</sup> and the PX4 team.</p><p>It is equipped with a high performance H7 Processor, and comes with IMU redundancy, temperature-controlled IMU board, and cost effective design, delivering incredible performance and reliability. It complies with the Pixhawk <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Connector Standard</a>.</p><p><img src="'+e+'" alt="Pixhawk6c mini Upright Image"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>This autopilot is <a href="./../flight_controller/autopilot_pixhawk_standard.html">supported</a> by the PX4 maintenance and test teams.</p></div><h2 id="introduction" tabindex="-1">Introduction <a class="header-anchor" href="#introduction" aria-label="Permalink to &quot;Introduction&quot;">​</a></h2><p>The Pixhawk® 6C Mini is the latest update to the successful family of Pixhawk® flight controllers.</p><p>Inside the Pixhawk® 6C Mini, you can find an STMicroelectronics® based STM32H743, paired with sensor technology from Bosch® &amp; InvenSense®, giving you flexibility and reliability for controlling any autonomous vehicle, suitable for both academic and commercial applications.</p><p>The Pixhawk® 6C Mini&#39;s H7 microcontroller contain the Arm® Cortex®-M7 core running up to 480 MHz, has 2MB flash memory and 1MB RAM. Thanks to the updated processing power, developers can be more productive and efficient with their development work, allowing for complex algorithms and models.</p><p>The Pixhawk 6C Mini includes high-performance, low-noise IMUs on board, designed to be cost effective while having IMU redundancy. A vibration isolation System to filter out high-frequency vibration and reduce noise to ensure accurate readings, allowing vehicles to reach better overall flight performances.</p><p>The Pixhawk® 6C Mini is perfect for developers at corporate research labs, startups, academics (research, professors, students), and commercial application.</p><p><strong>Key Design Points</strong></p><ul><li>High performance STM32H743 Processor with more computing power &amp; RAM</li><li>New cost-effective design with low-profile form factor</li><li>Newly designed integrated vibration isolation system to filter out high frequency vibration and reduce noise to ensure accurate readings</li><li>IMUs are temperature-controlled by onboard heating resistors, allowing optimum working temperature of IMUs </li></ul><h2 id="technical-specification" tabindex="-1">Technical Specification <a class="header-anchor" href="#technical-specification" aria-label="Permalink to &quot;Technical Specification&quot;">​</a></h2><h3 id="processors-sensors" tabindex="-1"><strong>Processors &amp; Sensors</strong> <a class="header-anchor" href="#processors-sensors" aria-label="Permalink to &quot;**Processors &amp; Sensors**&quot;">​</a></h3><ul><li>FMU Processor: STM32H743 <ul><li>32 Bit Arm® Cortex®-M7, 480MHz, 2MB memory, 1MB SRAM </li></ul></li><li>IO Processor: STM32F103 <ul><li> 32 Bit Arm® Cortex®-M3, 72MHz, 64KB SRAM </li></ul></li><li>On-board sensors <ul><li> Accel/Gyro: ICM-42688-P </li><li>Accel/Gyro: BMI055 </li><li>Mag: IST8310 </li><li>Barometer: MS5611</li></ul></li></ul><h3 id="electrical-data" tabindex="-1"><strong>Electrical data</strong> <a class="header-anchor" href="#electrical-data" aria-label="Permalink to &quot;**Electrical data**&quot;">​</a></h3><ul><li>Voltage Ratings: <ul><li>Max input voltage: 6V</li><li>USB Power Input: 4.75~5.25V</li><li>Servo Rail Input: 0~36V</li></ul></li><li>Current Ratings: <ul><li>`TELEM1`` Max output current limiter: 1A</li><li>All other port combined output current limiter: 1A</li></ul></li></ul><h3 id="mechanical-data" tabindex="-1"><strong>Mechanical data</strong> <a class="header-anchor" href="#mechanical-data" aria-label="Permalink to &quot;**Mechanical data**&quot;">​</a></h3><ul><li>Dimensions: 53.3 x 39 x 16.2 mm</li><li>Weight : 39.2g</li></ul><h3 id="interfaces" tabindex="-1"><strong>Interfaces</strong> <a class="header-anchor" href="#interfaces" aria-label="Permalink to &quot;**Interfaces**&quot;">​</a></h3><ul><li><p>16- PWM servo outputs (8 from IO, 6 from FMU)</p></li><li><p>3 general purpose serial ports</p><ul><li><code>TELEM1</code> - Full flow control, separate 1A current limit</li><li><code>TELEM2</code> - Full flow control</li></ul></li><li><p>2 GPS ports</p><ul><li>GPS1 - Full GPS port (GPS plus safety switch)</li><li>GPS2 - Basic GPS port</li></ul></li><li><p>1 I2C port</p><ul><li>Supports dedicated I2C calibration EEPROM located on sensor module</li></ul></li><li><p>2 CAN Buses</p><ul><li>CAN Bus has individual silent controls or ESC RX-MUX control</li></ul></li><li><p>1 Debug ports:</p><ul><li>FMU Debug Mini</li></ul></li><li><p>Dedicated R/C input for Spektrum/DSM and S.BUS, CPPM, analog / PWM RSSI</p></li><li><p>1 Power input ports (Analog)</p></li><li><p>Other Characteristics:</p><ul><li>Operating &amp; storage temperature: -40 ~ 85°c</li></ul></li></ul><h2 id="where-to-buy" tabindex="-1">Where to Buy <a class="header-anchor" href="#where-to-buy" aria-label="Permalink to &quot;Where to Buy&quot;">​</a></h2><p>Order from <a href="https://holybro.com/products/pixhawk-6c-mini" target="_blank" rel="noreferrer">Holybro</a>.</p><h2 id="assembly-setup" tabindex="-1">Assembly/Setup <a class="header-anchor" href="#assembly-setup" aria-label="Permalink to &quot;Assembly/Setup&quot;">​</a></h2><p>The Pixhawk 4 Mini&#39;s port is very similar to the Pixhawk 6C Mini&#39;s port. Please refer to the <a href="./../assembly/quick_start_pixhawk4_mini.html">Pixhawk 4 Mini Wiring Quick Start</a> as it provides instructions on how to assemble required/important peripherals including GPS, Power Module etc.</p><h2 id="pinouts" tabindex="-1">Pinouts <a class="header-anchor" href="#pinouts" aria-label="Permalink to &quot;Pinouts&quot;">​</a></h2><ul><li><a href="https://docs.holybro.com/autopilot/pixhawk-6c-mini/pixhawk-6c-mini-ports" target="_blank" rel="noreferrer">Holybro Pixhawk 6C Mini Port Pinout</a></li></ul><h2 id="serial-port-mapping" tabindex="-1">Serial Port Mapping <a class="header-anchor" href="#serial-port-mapping" aria-label="Permalink to &quot;Serial Port Mapping&quot;">​</a></h2><table><thead><tr><th>UART</th><th>Device</th><th>QGC Parameter Description</th><th>Port Label on FC</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>GPS1</td><td>GPS1</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM3</td><td>N/A</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>N/A</td><td>FMU Debug</td></tr><tr><td>UART5</td><td>/dev/ttyS3</td><td>TELEM2</td><td>TELEM2</td></tr><tr><td>USART6</td><td>/dev/ttyS4</td><td>PX4IO</td><td>I/O PWM Out</td></tr><tr><td>UART7</td><td>/dev/ttyS5</td><td>TELEM1</td><td>TELEM1</td></tr><tr><td>UART8</td><td>/dev/ttyS6</td><td>GPS2</td><td>GPS2</td></tr></tbody></table><h2 id="dimensions" tabindex="-1">Dimensions <a class="header-anchor" href="#dimensions" aria-label="Permalink to &quot;Dimensions&quot;">​</a></h2><p><img src="'+t+'" alt="Pixhawk6c Mini Dimensions"></p><h2 id="voltage-ratings" tabindex="-1">Voltage Ratings <a class="header-anchor" href="#voltage-ratings" aria-label="Permalink to &quot;Voltage Ratings&quot;">​</a></h2><p><em>Pixhawk 6C Mini</em> can be double-redundant on the power supply if two power sources are supplied. The two power rails are: <strong>POWER1</strong> and <strong>USB</strong>.</p><p><strong>Normal Operation Maximum Ratings</strong></p><p>Under these conditions all power sources will be used in this order to power the system:</p><ol><li><strong>POWER1</strong> inputs (4.9V to 5.5V)</li><li><strong>USB</strong> input (4.75V to 5.25V)</li></ol><p><strong>Absolute Maximum Ratings</strong></p><p>Under these conditions the system will not draw any power (will not be operational), but will remain intact.</p><ol><li><strong>POWER1</strong> inputs (operational range 4.1V to 5.7V, 0V to 10V undamaged)</li><li><strong>USB</strong> input (operational range 4.1V to 5.7V, 0V to 6V undamaged)</li><li>Servo input: VDD_SERVO pin of <strong>FMU PWM OUT</strong> and <strong>I/O PWM OUT</strong> (0V to 42V undamaged)</li></ol><p><strong>Voltage monitoring</strong></p><p>Pixhawk 6C Mini uses analog power modules.</p><p>Holybro makes various analog <a href="./../power_module/">power modules</a> for different needs:</p><ul><li><a href="./../power_module/holybro_pm02.html">PM02 Power Module</a></li><li><a href="./../power_module/holybro_pm06_pixhawk4mini_power_module.html">PM06 Power Module</a></li><li><a href="./../power_module/holybro_pm07_pixhawk4_power_module.html">PM07 Power Module</a></li><li><a href="https://holybro.com/products/pm08-power-module-14s-200a" target="_blank" rel="noreferrer">PM08 Power Module</a></li></ul><h2 id="building-firmware" tabindex="-1">Building Firmware <a class="header-anchor" href="#building-firmware" aria-label="Permalink to &quot;Building Firmware&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make px4_fmu-v6c_default</span></span></code></pre></div><p><a id="debug_port"></a></p><h2 id="debug-port" tabindex="-1">Debug Port <a class="header-anchor" href="#debug-port" aria-label="Permalink to &quot;Debug Port&quot;">​</a></h2><p>The <a href="./../debug/system_console.html">PX4 System Console</a> and <a href="./../debug/swd_debug.html">SWD interface</a> run on the <strong>FMU Debug</strong> port.</p><p>The pinouts and connector comply with the <a href="./../debug/swd_debug.html#pixhawk-debug-mini">Pixhawk Debug Mini</a> interface defined in the <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a> interface (JST SH connector).</p><table><thead><tr><th>Pin</th><th>Signal</th><th>Volt</th></tr></thead><tbody><tr><td>1 (red)</td><td><code>Vtref</code></td><td>+3.3V</td></tr><tr><td>2 (blk)</td><td>Console TX (OUT)</td><td>+3.3V</td></tr><tr><td>3 (blk)</td><td>Console RX (IN)</td><td>+3.3V</td></tr><tr><td>4 (blk)</td><td><code>SWDIO</code></td><td>+3.3V</td></tr><tr><td>5 (blk)</td><td><code>SWCLK</code></td><td>+3.3V</td></tr><tr><td>6 (blk)</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><p>For information about using this port see:</p><ul><li><a href="./../debug/swd_debug.html">SWD Debug Port</a></li><li><a href="./../debug/system_console.html">PX4 System Console</a> (Note, the FMU console maps to USART3).</li></ul><h2 id="peripherals" tabindex="-1">Peripherals <a class="header-anchor" href="#peripherals" aria-label="Permalink to &quot;Peripherals&quot;">​</a></h2><ul><li><a href="https://holybro.com/products/digital-air-speed-sensor" target="_blank" rel="noreferrer">Digital Airspeed Sensor</a></li><li><a href="./../telemetry/">Telemetry Radio Modules</a>: <ul><li><a href="./../telemetry/holybro_sik_radio.html">Holybro Telemetry Radio</a></li><li><a href="./../telemetry/holybro_microhard_p900_radio.html">Holybro Microhard P900 Radio</a></li><li><a href="./../telemetry/holybro_xbp9x_radio.html">Holybro XBP9X Telemetry Radio</a></li></ul></li><li><a href="./../sensor/rangefinders.html">Rangefinders/Distance sensors</a></li></ul><h2 id="supported-platforms-airframes" tabindex="-1">Supported Platforms / Airframes <a class="header-anchor" href="#supported-platforms-airframes" aria-label="Permalink to &quot;Supported Platforms / Airframes&quot;">​</a></h2><p>Any multicopter / airplane / rover or boat that can be controlled with normal RC servos or Futaba S-Bus servos. The complete set of supported configurations can be seen in the <a href="./../airframes/airframe_reference.html">Airframes Reference</a>.</p><h2 id="see-also" tabindex="-1">See Also <a class="header-anchor" href="#see-also" aria-label="Permalink to &quot;See Also&quot;">​</a></h2><ul><li><a href="https://docs.holybro.com/" target="_blank" rel="noreferrer">Holybro Docs</a> (Holybro)</li><li><a href="./../assembly/quick_start_pixhawk4_mini.html">Pixhawk 4 Mini Wiring Quick Start</a> (and <a href="./../assembly/quick_start_pixhawk6c.html">Pixhawk 6C Wiring QuickStart</a>)</li><li><a href="./../power_module/holybro_pm02.html">PM02 Power Module</a></li><li><a href="./../power_module/holybro_pm06_pixhawk4mini_power_module.html">PM06 Power Module</a></li><li><a href="./../power_module/holybro_pm07_pixhawk4_power_module.html">PM07 Power Module</a></li><li><a href="https://holybro.com/products/pm08-power-module-14s-200a" target="_blank" rel="noreferrer">PM08 Power Module</a></li><li><a href="https://docs.google.com/spreadsheets/d/1FcmWRKd6zjdz3-cnjEDYEmANKZOFzNSc/edit?usp=sharing&amp;ouid=113251442407318461574&amp;rtpof=true&amp;sd=true" target="_blank" rel="noreferrer">FMUv6C reference design pinout</a>.</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a>.</li></ul>',62),s=[n];function d(h,p,c,u,m,b){return i(),a("div",null,s)}const P=r(l,[["render",d]]);export{w as __pageData,P as default};
