import{_ as t}from"./chunks/pixhawk6x_hero_upright.BlU9ehkm.js";import{_ as e}from"./chunks/pixhawk6x_exploded_diagram.BKfTaWKp.js";import{_ as a}from"./chunks/pixhawk6x_wiring_diagram.Aap2Cb8p.js";import{_ as r,c as i,o,ab as l}from"./chunks/framework.CUflZczI.js";const x=JSON.parse('{"title":"Holybro Pixhawk 6X","description":"","frontmatter":{},"headers":[],"relativePath":"en/flight_controller/pixhawk6x.md","filePath":"en/flight_controller/pixhawk6x.md"}'),n={name:"en/flight_controller/pixhawk6x.md"},s=l('<h1 id="holybro-pixhawk-6x" tabindex="-1">Holybro Pixhawk 6X <a class="header-anchor" href="#holybro-pixhawk-6x" aria-label="Permalink to &quot;Holybro Pixhawk 6X&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://holybro.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p><em>Pixhawk 6X</em><sup>®</sup> is the latest update to the successful family of Pixhawk® flight controllers designed and made in collaboration with Holybro<sup>®</sup> and the PX4 team.</p><p>It is based on the <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-012%20Pixhawk%20Autopilot%20v6X%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk​​® Autopilot FMUv6X Standard</a>, <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-010%20Pixhawk%20Autopilot%20Bus%20Standard.pdf" target="_blank" rel="noreferrer">Autopilot Bus Standard</a>, and <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Connector Standard</a>.</p><p>Equipped with a high performance H7 Processor, modular design, triple redundancy, temperature-controlled IMU board, isolated sensor domains, delivering incredible performance, reliability, and flexibility.</p><p><img src="'+t+'" width="230px" title="Pixhawk6X Upright Image"> <img src="'+e+'" width="400px" title="Pixhawk6X Exploded Image"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>This autopilot is <a href="./../flight_controller/autopilot_pixhawk_standard.html">supported</a> by the PX4 maintenance and test teams.</p></div><h2 id="introduction" tabindex="-1">Introduction <a class="header-anchor" href="#introduction" aria-label="Permalink to &quot;Introduction&quot;">​</a></h2><p>Inside the Pixhawk®​ 6X, you can find an STMicroelectronics​® based STM32H753, paired with sensor technology from Bosch®​​, InvenSense®​,​ giving you flexibility and reliability for controlling any autonomous vehicle, suitable for both academic and commercial applications.</p><p>The Pixhawk® 6X&#39;s H7 microcontroller contain the Arm® Cortex®-M7 core running up to 480 MHz, has 2MB flash memory and 1MB RAM. The PX4 Autopilot takes advantage of the increased power and RAM. Thanks to the updated processing power, developers can be more productive and efficient with their development work, allowing for complex algorithms and models.</p><p>The FMUv6X open standard includes high-performance, low-noise IMUs on board, designed for better stabilization. Triple redundant IMU &amp; double redundant barometer on separate buses. When the PX4 Autopilot detects a sensor failure, the system seamlessly switches to another to maintain flight control reliability.</p><p>An independent LDO powers every sensor set with independent power control. A vibration isolation System to filter out high-frequency vibration and reduce noise to ensure accurate readings, allowing vehicles to reach better overall flight performances.</p><p>External sensor bus (SPI5) has two chip select lines and data-ready signals for additional sensors and payload with SPI-interface, and with an integrated Microchip Ethernet PHY, high-speed communication with mission computers via ethernet is now possible.</p><p>The Pixhawk®​ 6X is perfect for developers at corporate research labs, startups, academics (research, professors, students), and commercial application.</p><h2 id="key-design-points" tabindex="-1">Key Design Points <a class="header-anchor" href="#key-design-points" aria-label="Permalink to &quot;Key Design Points&quot;">​</a></h2><ul><li>High performance STM32H753 Processor</li><li>Modular flight controller: separated IMU, FMU, and Base system connected by a 100-pin &amp; a 50-pin Pixhawk®​ Autopilot Bus connector.</li><li>Redundancy: 3x IMU sensors &amp; 2x Barometer sensors on separate buses</li><li>Triple redundancy domains: Completely isolated sensor domains with separate buses and separate power control</li><li>Newly designed vibration isolation system to filter out high frequency vibration and reduce noise to ensure accurate readings</li><li>Ethernet interface for high-speed mission computer integration</li><li>IMUs are temperature-controlled by onboard heating resistors, allowing optimum working temperature of IMUs </li></ul><h3 id="processors-sensors" tabindex="-1">Processors &amp; Sensors <a class="header-anchor" href="#processors-sensors" aria-label="Permalink to &quot;Processors &amp; Sensors&quot;">​</a></h3><ul><li>FMU Processor: STM32H753 <ul><li>32 Bit Arm® Cortex®-M7, 480MHz, 2MB flash memory, 1MB RAM</li></ul></li><li>IO Processor: STM32F100 <ul><li>32 Bit Arm® Cortex®-M3, 24MHz, 8KB SRAM</li></ul></li><li>On-board sensors <ul><li>Accel/Gyro: ICM-20649 or BMI088</li><li>Accel/Gyro: ICM-42688-P</li><li>Accel/Gyro: ICM-42670-P</li><li>Mag: BMM150</li><li>Barometer: 2x BMP388</li></ul></li></ul><h3 id="electrical-data" tabindex="-1">Electrical data <a class="header-anchor" href="#electrical-data" aria-label="Permalink to &quot;Electrical data&quot;">​</a></h3><ul><li>Voltage Ratings: <ul><li>Max input voltage: 6V</li><li>USB Power Input: 4.75~5.25V</li><li>Servo Rail Input: 0~36V</li></ul></li><li>Current Ratings: <ul><li><code>TELEM1</code> output current limiter: 1.5A</li><li>All other port combined output current limiter: 1.5A</li></ul></li></ul><h3 id="mechanical-data" tabindex="-1">Mechanical data <a class="header-anchor" href="#mechanical-data" aria-label="Permalink to &quot;Mechanical data&quot;">​</a></h3><ul><li>Dimensions <ul><li>Flight Controller Module: 38.8 x 31.8 x 14.6mm</li><li>Standard Baseboard: 52.4 x 103.4 x 16.7mm</li><li>Mini Baseboard: 43.4 x 72.8 x 14.2 mm</li></ul></li><li>Weight <ul><li>Flight Controller Module: 23g</li><li>Standard Baseboard: 51g</li><li>Mini Baseboard: 26.5g</li></ul></li></ul><h3 id="interfaces" tabindex="-1">Interfaces <a class="header-anchor" href="#interfaces" aria-label="Permalink to &quot;Interfaces&quot;">​</a></h3><ul><li><p>16- PWM servo outputs</p></li><li><p>R/C input for Spektrum / DSM</p></li><li><p>Dedicated R/C input for PPM and S.Bus input</p></li><li><p>Dedicated analog / PWM RSSI input and S.Bus output</p></li><li><p>4 general purpose serial ports</p><ul><li>3 with full flow control</li><li>1 with separate 1.5A current limit (Telem1)</li><li>1 with I2C and additional GPIO line for external NFC reader</li></ul></li><li><p>2 GPS ports</p><ul><li>1 full GPS plus Safety Switch Port</li><li>1 basic GPS port</li></ul></li><li><p>1 I2C port</p></li><li><p>1 Ethernet port</p><ul><li>Transformerless Applications</li><li>100Mbps</li></ul></li><li><p>1 SPI bus</p><ul><li>2 chip select lines</li><li>2 data-ready lines</li><li>1 SPI SYNC line</li><li>1 SPI reset line</li></ul></li><li><p>2 CAN Buses for CAN peripheral</p><ul><li>CAN Bus has individual silent controls or ESC RX-MUX control</li></ul></li><li><p>2 Power input ports with SMBus</p><ul><li>1 AD &amp; IO port</li><li>2 additional analog input</li><li>1 PWM/Capture input</li><li>2 Dedicated debug and GPIO lines</li></ul></li><li><p>Other Characteristics:</p><ul><li>Operating &amp; storage temperature: -40 ~ 85°c</li></ul></li></ul><h2 id="where-to-buy" tabindex="-1">Where to Buy <a class="header-anchor" href="#where-to-buy" aria-label="Permalink to &quot;Where to Buy&quot;">​</a></h2><p>Order from <a href="https://holybro.com/products/pixhawk-6x" target="_blank" rel="noreferrer">Holybro</a>.</p><h2 id="assembly-setup" tabindex="-1">Assembly/Setup <a class="header-anchor" href="#assembly-setup" aria-label="Permalink to &quot;Assembly/Setup&quot;">​</a></h2><p>The <a href="./../assembly/quick_start_pixhawk6x.html">Pixhawk 6X Wiring Quick Start</a> provides instructions on how to assemble required/important peripherals including GPS, Power Module etc.</p><h2 id="connections" tabindex="-1">Connections <a class="header-anchor" href="#connections" aria-label="Permalink to &quot;Connections&quot;">​</a></h2><p>Sample Wiring Diagram <img src="'+a+'" alt="Pixhawk 6X Wiring Overview"></p><h2 id="pinouts" tabindex="-1">Pinouts <a class="header-anchor" href="#pinouts" aria-label="Permalink to &quot;Pinouts&quot;">​</a></h2><ul><li><a href="https://docs.holybro.com/autopilot/pixhawk-6x/pixhawk-baseboard-pinout" target="_blank" rel="noreferrer">Holybro Pixhawk Baseboard Pinout</a></li><li><a href="https://docs.holybro.com/autopilot/pixhawk-6x/pixhawk-mini-baseboard-pinout" target="_blank" rel="noreferrer">Holybro Pixhawk Mini-Baseboard Pinout</a></li></ul><p>Notes:</p><ul><li>The <a href="./../camera/fc_connected_camera.html#camera-capture-configuration">camera capture pin</a> (<code>PI0</code>) is pin 2 on the AD&amp;IO port, marked above as <code>FMU_CAP1</code>.</li></ul><h2 id="serial-port-mapping" tabindex="-1">Serial Port Mapping <a class="header-anchor" href="#serial-port-mapping" aria-label="Permalink to &quot;Serial Port Mapping&quot;">​</a></h2><table><thead><tr><th>UART</th><th>Device</th><th>Port</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>GPS</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM3</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>Debug Console</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>UART4 &amp; I2C</td></tr><tr><td>UART5</td><td>/dev/ttyS4</td><td>TELEM2</td></tr><tr><td>USART6</td><td>/dev/ttyS5</td><td>PX4IO/RC</td></tr><tr><td>UART7</td><td>/dev/ttyS6</td><td>TELEM1</td></tr><tr><td>UART8</td><td>/dev/ttyS7</td><td>GPS2</td></tr></tbody></table><h2 id="dimensions" tabindex="-1">Dimensions <a class="header-anchor" href="#dimensions" aria-label="Permalink to &quot;Dimensions&quot;">​</a></h2><p><a href="https://docs.holybro.com/autopilot/pixhawk-6x/dimensions" target="_blank" rel="noreferrer">Pixhawk 6X Dimensions</a></p><h2 id="voltage-ratings" tabindex="-1">Voltage Ratings <a class="header-anchor" href="#voltage-ratings" aria-label="Permalink to &quot;Voltage Ratings&quot;">​</a></h2><p><em>Pixhawk 6X</em> can be triple-redundant on the power supply if three power sources are supplied. The three power rails are: <strong>POWER1</strong>, <strong>POWER2</strong> and <strong>USB</strong>. The <strong>POWER1</strong> &amp; <strong>POWER2</strong> ports on the Pixhawk 6X uses the 6 circuit <a href="https://www.molex.com/molex/products/part-detail/pcb_receptacles/5024430670" target="_blank" rel="noreferrer">2.00mm Pitch CLIK-Mate Wire-to-Board PCB Receptacle</a>.</p><p><strong>Normal Operation Maximum Ratings</strong></p><p>Under these conditions all power sources will be used in this order to power the system:</p><ol><li><strong>POWER1</strong> and <strong>POWER2</strong> inputs (4.9V to 5.5V)</li><li><strong>USB</strong> input (4.75V to 5.25V)</li></ol><p><strong>Absolute Maximum Ratings</strong></p><p>Under these conditions the system will not draw any power (will not be operational), but will remain intact.</p><ol><li><strong>POWER1</strong> and <strong>POWER2</strong> inputs (operational range 4.1V to 5.7V, 0V to 10V undamaged)</li><li><strong>USB</strong> input (operational range 4.1V to 5.7V, 0V to 6V undamaged)</li><li>Servo input: VDD_SERVO pin of <strong>FMU PWM OUT</strong> and <strong>I/O PWM OUT</strong> (0V to 42V undamaged)</li></ol><p><strong>Voltage monitoring</strong></p><p>Digital I2C battery monitoring is enabled by default (see <a href="./../assembly/quick_start_pixhawk6x.html#power">Quickstart &gt; Power</a>).</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Analog battery monitoring via an ADC is not supported on this particular board, but may be supported in variations of this flight controller with a different baseboard.</p></div><h2 id="building-firmware" tabindex="-1">Building Firmware <a class="header-anchor" href="#building-firmware" aria-label="Permalink to &quot;Building Firmware&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make px4_fmu-v6x_default</span></span></code></pre></div><p><a id="debug_port"></a></p><h2 id="debug-port" tabindex="-1">Debug Port <a class="header-anchor" href="#debug-port" aria-label="Permalink to &quot;Debug Port&quot;">​</a></h2><p>The <a href="./../debug/system_console.html">PX4 System Console</a> and <a href="./../debug/swd_debug.html">SWD interface</a> run on the <strong>FMU Debug</strong> port.</p><p>The pinouts and connector comply with the <a href="./../debug/swd_debug.html#pixhawk-debug-full">Pixhawk Debug Full</a> interface defined in the <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a> interface (JST SM10B connector).</p><table><thead><tr><th>Pin</th><th>Signal</th><th>Volt</th></tr></thead><tbody><tr><td>1 (red)</td><td><code>Vtref</code></td><td>+3.3V</td></tr><tr><td>2 (blk)</td><td>Console TX (OUT)</td><td>+3.3V</td></tr><tr><td>3 (blk)</td><td>Console RX (IN)</td><td>+3.3V</td></tr><tr><td>4 (blk)</td><td><code>SWDIO</code></td><td>+3.3V</td></tr><tr><td>5 (blk)</td><td><code>SWCLK</code></td><td>+3.3V</td></tr><tr><td>6 (blk)</td><td><code>SWO</code></td><td>+3.3V</td></tr><tr><td>7 (blk)</td><td>NFC GPIO</td><td>+3.3V</td></tr><tr><td>8 (blk)</td><td>PH11</td><td>+3.3V</td></tr><tr><td>9 (blk)</td><td>nRST</td><td>+3.3V</td></tr><tr><td>10 (blk)</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><p>For information about using this port see:</p><ul><li><a href="./../debug/swd_debug.html">SWD Debug Port</a></li><li><a href="./../debug/system_console.html">PX4 System Console</a> (Note, the FMU console maps to USART3).</li></ul><h2 id="peripherals" tabindex="-1">Peripherals <a class="header-anchor" href="#peripherals" aria-label="Permalink to &quot;Peripherals&quot;">​</a></h2><ul><li><a href="https://holybro.com/products/digital-air-speed-sensor" target="_blank" rel="noreferrer">Digital Airspeed Sensor</a></li><li><a href="https://holybro.com/collections/telemetry-radios?orderby=date" target="_blank" rel="noreferrer">Telemetry Radio Modules</a></li><li><a href="./../sensor/rangefinders.html">Rangefinders/Distance sensors</a></li></ul><h2 id="supported-platforms-airframes" tabindex="-1">Supported Platforms / Airframes <a class="header-anchor" href="#supported-platforms-airframes" aria-label="Permalink to &quot;Supported Platforms / Airframes&quot;">​</a></h2><p>Any multicopter / airplane / rover or boat that can be controlled with normal RC servos or Futaba S-Bus servos. The complete set of supported configurations can be seen in the <a href="./../airframes/airframe_reference.html">Airframes Reference</a>.</p><h2 id="further-info" tabindex="-1">Further info <a class="header-anchor" href="#further-info" aria-label="Permalink to &quot;Further info&quot;">​</a></h2><ul><li><a href="https://docs.holybro.com/" target="_blank" rel="noreferrer">Holybro Docs</a> (Holybro)</li><li><a href="./../assembly/quick_start_pixhawk6x.html">Pixhawk 6X Wiring QuickStart</a></li><li><a href="./../power_module/holybro_pm02d.html">PM02D Power Module</a></li><li><a href="./../power_module/holybro_pm03d.html">PM03D Power Module</a></li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-012%20Pixhawk%20Autopilot%20v6X%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Autopilot FMUv6X Standard</a>.</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-010%20Pixhawk%20Autopilot%20Bus%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Autopilot Bus Standard</a>.</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a>.</li></ul>',66),d=[s];function p(h,c,u,m,b,g){return o(),i("div",null,d)}const S=r(n,[["render",p]]);export{x as __pageData,S as default};
