import{_ as e,a as i}from"./chunks/spracingh7extreme-bottom.BnLJSNNC.js";import{_ as t,c as r,o,ab as a}from"./chunks/framework.CUflZczI.js";const P=JSON.parse('{"title":"SPRacingH7EXTREME (PX4 Edition)","description":"","frontmatter":{},"headers":[],"relativePath":"en/flight_controller/spracingh7extreme.md","filePath":"en/flight_controller/spracingh7extreme.md"}'),l={name:"en/flight_controller/spracingh7extreme.md"},n=a('<h1 id="spracingh7extreme-px4-edition" tabindex="-1">SPRacingH7EXTREME (PX4 Edition) <a class="header-anchor" href="#spracingh7extreme-px4-edition" aria-label="Permalink to &quot;SPRacingH7EXTREME (PX4 Edition)&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://shop.seriouslypro.com" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The <a href="https://shop.seriouslypro.com/sp-racing-h7-extreme" target="_blank" rel="noreferrer">SPRacingH7EXTREME</a> is a feature packed FC/PDB with DUAL ICM20602 gyros, H7 400/480Mhz(+) CPU, high-precision BMP388 barometer, SD Card socket, current sensor, 8 easily accessible motor outputs, OSD, Microphone, Audio output, and more.</p><p>It can be used easily for small to large quads, planes, octocoptors and more advanced frames. It&#39;s best used with separate ESCs as it features a built-in power distribution board (PDB). Wiring up a 4in1 ESC is easy too.</p><p>There is also a 12 pin stacking connector which provides 4 more motor outputs, SPI and a UART connectivity.</p><p><img src="'+e+'" alt="SPRacingH7EXTREME PCB Top"></p><p><img src="'+i+'" alt="SPRacingH7EXTREME PCB Bottom"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="key-features" tabindex="-1">Key Features <a class="header-anchor" href="#key-features" aria-label="Permalink to &quot;Key Features&quot;">​</a></h2><ul><li>Main System-on-Chip: <a href="https://www.st.com/en/microcontrollers-microprocessors/stm32h750vb.html" target="_blank" rel="noreferrer">STM32H750VBT6 rev.y/v</a><ul><li>CPU: 400/480Mhz(+) ARM Cortex M7 with single-precision FPU. (+ 480Mhz with Rev V CPUs)</li><li>RAM: 1MB</li><li>16MB External Flash 4-bit QuadSPI in Memory Mapped mode for code <em>and</em> config.</li></ul></li><li>On-board sensors: <ul><li>Dual Gyros (1xSPI each, with separate interrupt signals, 32khz capable, fsync capable)</li><li>High-precision BMP388 Baro (I2C + interrupt)</li><li>110A Current Sensor</li></ul></li><li>GPS via External 8 pin IO port.</li><li>Audio/Visual <ul><li>On-screen display OSD (dedicated SPI, character based, MAX7456)</li><li>Microphone sensor</li><li>Audio output from CPU DAC.</li><li>Audio mixer for microphone/DAC outputs.</li></ul></li><li>Interfaces <ul><li>SD Card (4-bit SDIO not 1-bit SPI)</li><li>IR transponder (iLAP compatible)</li><li>Buzzer circuitry</li><li>RSSI (Analog/PWM)</li><li>12 motor outputs (4 by motor pads, 4 in the middle, and 4 on stacking connector).</li><li>1x SPI breakout onto stacking connector</li><li>6 Serial Ports (5x TX &amp; RX, 1x TX-only bi-directional for telemetry)</li><li>Boot Button (Side press)</li><li>Bind/User Button (Side press)</li><li>Receiver ports (all usual protocols, no inverter needed)</li><li>CAM OSD control and Video IN on CAM socket.</li><li>SWD debugging port.</li></ul></li><li>Video OUT + Audio OUT on VTX socket.</li><li>USB with OTG capability (ID and VBUS connected to CPU)</li><li>Power System <ul><li>Integrated PDB.</li><li>2-6S BEC</li><li>TVS Protection Diode</li><li>Dedicated 500ma VREG for Gyros, with gyro noise filter capacitors.</li><li>Second 500ma VREG for CPU, Baro, Microphone, etc.</li></ul></li><li>Other features <ul><li>Status Led</li><li>LED strip support (with well-placed connection pads).</li><li>Bootable from SD Card or External flash.</li><li>Flashable from SD Card.</li><li>Solder-from-top design.</li><li>PCB Cutouts for battery wires.</li><li>No Compass, use an external GPS with a magnometer/compass sensor connected to the GPS IO port.</li><li>Also runs Betaflight 4.x+, Cleanflight 4.x+.</li><li>Designed by Dominic Clifton, the guy that created Cleanflight</li></ul></li><li>Dimensions <ul><li>36x36mm with 30.5*30.5 mouting pattern, M4 holes.</li><li>Soft-mount M4 to M3 grommets supplied.</li></ul></li></ul><h2 id="where-to-buy" tabindex="-1">Where to Buy <a class="header-anchor" href="#where-to-buy" aria-label="Permalink to &quot;Where to Buy&quot;">​</a></h2><p>The SPRacingH7EXTREME is available from the <a href="https://shop.seriouslypro.com/sp-racing-h7-extreme" target="_blank" rel="noreferrer">Seriously Pro shop</a>.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Select the PX4 edition when purchasing!</p></div><h2 id="manual-pinouts-and-connection-diagrams" tabindex="-1">Manual, Pinouts and Connection Diagrams <a class="header-anchor" href="#manual-pinouts-and-connection-diagrams" aria-label="Permalink to &quot;Manual, Pinouts and Connection Diagrams&quot;">​</a></h2><p>The manual with pinouts can be downloaded from <a href="http://seriouslypro.com/files/SPRacingH7EXTREME-Manual-latest.pdf" target="_blank" rel="noreferrer">here</a>. See the <a href="http://seriouslypro.com/spracingh7extreme" target="_blank" rel="noreferrer">SPRacingH7EXTREME website</a> for other diagrams.</p><h2 id="credits" tabindex="-1">Credits <a class="header-anchor" href="#credits" aria-label="Permalink to &quot;Credits&quot;">​</a></h2><p>This design was created by <a href="https://github.com/hydra" target="_blank" rel="noreferrer">Dominic Clifton</a> Initial PX4 support by <a href="https://github.com/Igor-Misic" target="_blank" rel="noreferrer">Igor-Misic</a></p>',17),s=[n];function c(p,h,u,d,m,f){return o(),r("div",null,s)}const S=t(l,[["render",c]]);export{P as __pageData,S as default};
