import{_ as e}from"./chunks/difficulty_hard.N1nnc_xq.js";import{_ as t}from"./chunks/remote_control.ss1psCxn.js";import{_ as a}from"./chunks/acrobatic_fw.CXa-EOaK.js";import{_ as r,c as o,o as i,ab as c}from"./chunks/framework.CUflZczI.js";const W=JSON.parse('{"title":"Acro Mode (Fixed-Wing)","description":"","frontmatter":{},"headers":[],"relativePath":"en/flight_modes_fw/acro.md","filePath":"en/flight_modes_fw/acro.md"}'),d={name:"en/flight_modes_fw/acro.md"},n=c('<h1 id="acro-mode-fixed-wing" tabindex="-1">Acro Mode (Fixed-Wing) <a class="header-anchor" href="#acro-mode-fixed-wing" aria-label="Permalink to &quot;Acro Mode (Fixed-Wing)&quot;">​</a></h1><p><img src="'+e+'" title="Hard to fly" width="30px"> <img src="'+t+'" title="Manual/Remote control required" width="30px"> </p><p><em>Acro mode</em> is the manual mode for performing acrobatic maneuvers e.g. rolls, flips, stalls and acrobatic figures.</p><p>The roll, pitch and yaw sticks control the rate of angular rotation around the respective axes and throttle is passed directly to control allocation. When sticks are centered the vehicle will stop rotating, but remain in its current orientation (on its side, inverted, or whatever) and moving according to its current momentum.</p><p><img src="'+a+'" alt="FW Manual Acrobatic Flight"></p><h2 id="technical-description" tabindex="-1">Technical Description <a class="header-anchor" href="#technical-description" aria-label="Permalink to &quot;Technical Description&quot;">​</a></h2><p>Manual mode for performing acrobatic maneuvers e.g. rolls, flips, stalls and acrobatic figures.</p><p>Roll-pitch-yaw stick inputs are translated to angular rate commands that are stabilized by autopilot. Throttle is passed directly to control allocation.</p><h2 id="parameters" tabindex="-1">Parameters <a class="header-anchor" href="#parameters" aria-label="Permalink to &quot;Parameters&quot;">​</a></h2><table><thead><tr><th>Parameter</th><th>Description</th></tr></thead><tbody><tr><td><a id="FW_ACRO_X_MAX"></a><a href="./../advanced_config/parameter_reference.html#FW_ACRO_X_MAX">FW_ACRO_X_MAX</a></td><td>Acro body x max rate (the body x rate the controller is trying to achieve if the user applies full roll stick input in acro mode). Default: 90 degrees.</td></tr><tr><td><a id="FW_ACRO_Y_MAX"></a><a href="./../advanced_config/parameter_reference.html#FW_ACRO_Y_MAX">FW_ACRO_Y_MAX</a></td><td>Acro body y max rate (the body y rate the controller is trying to achieve if the user applies full pitch stick input in acro mode). Default: 90 degrees.</td></tr><tr><td><a id="FW_ACRO_Z_MAX"></a><a href="./../advanced_config/parameter_reference.html#FW_ACRO_Z_MAX">FW_ACRO_Z_MAX</a></td><td>Acro body z max rate (the body z rate the controller is trying to achieve if the user applies full yaw stick input in acro mode). Default: 45 degrees.</td></tr><tr><td><a id="FW_ACRO_YAW_EN"></a><a href="./../advanced_config/parameter_reference.html#FW_ACRO_YAW_EN">FW_ACRO_YAW_EN</a></td><td>Enable yaw rate controller (if disabled the pilot commands directly the yaw actuator). <code>0</code>: Disabled (Default), <code>1</code>: Enabled.</td></tr></tbody></table>',10),l=[n];function s(_,h,m,p,f,u){return i(),o("div",null,l)}const x=r(d,[["render",s]]);export{W as __pageData,x as default};
