import{_ as a}from"./chunks/final_side.DW-ghjQN.js";import{_ as t,a as i}from"./chunks/airframe_rover_aion.Cztchofw.js";import{_ as o,E as n,c as s,J as l,m as e,a as c,ab as m,o as f}from"./chunks/framework.CUflZczI.js";const R=JSON.parse('{"title":"Rovers (UGVs)","description":"","frontmatter":{},"headers":[],"relativePath":"en/frames_rover/index.md","filePath":"en/frames_rover/index.md"}'),h={name:"en/frames_rover/index.md"},d=e("h1",{id:"rovers-ugvs",tabindex:"-1"},[c("Rovers (UGVs) "),e("a",{class:"header-anchor",href:"#rovers-ugvs","aria-label":'Permalink to "Rovers (UGVs)"'},"​")],-1),p=m('<div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Support for rover is <a href="./../airframes/#experimental-vehicles">experimental</a>. Maintainer volunteers, <a href="./../contribute/">contribution</a> of new features, new frame configurations, or other improvements would all be very welcome!</p></div><p>PX4 supports rovers (Unmanned Ground Vehicles - UGVs) with <a href="#rover-types">ackermann and differential</a> steering.</p><p>This section contains build logs/instructions for assembling as well as configuring a number of UGV frames.</p><p><img src="'+a+'" alt="Traxxas Rover Picture"></p><h2 id="rover-types" tabindex="-1">Rover Types <a class="header-anchor" href="#rover-types" aria-label="Permalink to &quot;Rover Types&quot;">​</a></h2><p>PX4 supports rovers with:</p><ul><li><strong>Differential steering</strong>: direction is controlled by moving the left- and right-side wheels at different speeds. This kind of steering commonly used on bulldozers, tanks, and other tracked vehicles.</li><li><strong>Ackermann steering</strong>: direction is controlled by pointing wheels in the direction of travel (<a href="https://en.wikipedia.org/wiki/Ackermann_steering_geometry" target="_blank" rel="noreferrer">ackermann geometry</a> compensates for the fact that wheels on the inside and outside of the turn move at different rates). This kind of steering is used on most commercial vehicles, including cars, trucks etc.</li></ul><p>The supported frames can be seen in <a href="./../airframes/airframe_reference.html#rover">Airframes Reference &gt; Rover</a>.</p><h2 id="how-to-configure-a-rover" tabindex="-1">How to Configure a Rover <a class="header-anchor" href="#how-to-configure-a-rover" aria-label="Permalink to &quot;How to Configure a Rover&quot;">​</a></h2><h3 id="ackermann-steering-configuration" tabindex="-1">Ackermann Steering Configuration <a class="header-anchor" href="#ackermann-steering-configuration" aria-label="Permalink to &quot;Ackermann Steering Configuration&quot;">​</a></h3><p>Setting up a rover with Ackermann steering is straightforward:</p><ol><li><p>In the <a href="./../config/airframe.html">Airframe</a> configuration, select the <em>Generic Ground Vehicle</em>.</p><p><img src="'+t+'" alt="Select Ackermann steered airframe"></p><p>Select the <strong>Apply and Restart</strong> button.</p></li><li><p>Open the <a href="./../config/actuators.html">Actuators Configuration &amp; Testing</a> to map the steering and throttle functions to flight controller outputs.</p></li></ol><h3 id="differential-steering-configuration" tabindex="-1">Differential Steering Configuration <a class="header-anchor" href="#differential-steering-configuration" aria-label="Permalink to &quot;Differential Steering Configuration&quot;">​</a></h3><ol><li><p>In the <a href="./../config/airframe.html">Airframe</a> configuration, select either the <em>Aion Robotics R1 UGV</em> or <em>NXP Cup car: DF Robot GPX</em></p><p><img src="'+i+'" alt="Select Differential steered airframe"></p></li></ol><p>Select the <strong>Apply and Restart</strong> button.</p><ol><li>Open the <a href="./../config/actuators.html">Actuators Configuration &amp; Testing</a> and map the left and right motor functions to flight controller outputs.</li></ol><h2 id="simulation" tabindex="-1">Simulation <a class="header-anchor" href="#simulation" aria-label="Permalink to &quot;Simulation&quot;">​</a></h2><p><a href="./../sim_gazebo_classic/">Gazebo Classic</a> provides simulations for both types of steering:</p><ul><li>Ackermann: <a href="./../sim_gazebo_classic/vehicles.html#ackermann-ugv">ackermann rover</a></li><li>Differential: <a href="./../sim_gazebo_classic/vehicles.html#differential-ugv">r1 rover</a></li></ul><h2 id="videos" tabindex="-1">Videos <a class="header-anchor" href="#videos" aria-label="Permalink to &quot;Videos&quot;">​</a></h2><p>This video shows the <a href="./../frames_rover/traxxas_stampede.html">Traxxas Stampede Rover</a> (an Ackermann vehicle).</p>',21),u=e("p",null,[e("div",{class:"embed-responsive embed-responsive-16by9"},[e("iframe",{class:"embed-responsive-item youtube-player",type:"text/html",width:"640",height:"390",src:"https://www.youtube.com/embed/N3HvSKS3nCw",frameborder:"0",webkitallowfullscreen:"",mozallowfullscreen:"",allowfullscreen:""})])],-1);function g(v,_,b,k,w,x){const r=n("LinkedBadge");return f(),s("div",null,[d,l(r,{type:"warning",text:"Experimental",url:"../airframes/#experimental-vehicles"}),p,u])}const T=o(h,[["render",g]]);export{R as __pageData,T as default};
