import{_ as e,c as o,o as t,ab as a}from"./chunks/framework.CUflZczI.js";const f=JSON.parse('{"title":"ROS 1 with MAVROS","description":"","frontmatter":{},"headers":[],"relativePath":"en/ros/ros1.md","filePath":"en/ros/ros1.md"}'),r={name:"en/ros/ros1.md"},s=a('<h1 id="ros-1-with-mavros" tabindex="-1">ROS 1 with MAVROS <a class="header-anchor" href="#ros-1-with-mavros" aria-label="Permalink to &quot;ROS 1 with MAVROS&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>The PX4 development team recommend that users migrate to <a href="./../ros2/">ROS 2</a> (i.e. skip this section)!</p></div><p><a href="./../ros/">ROS</a> (Robot Operating System) is a general-purpose robotics library that can be used to create powerful drone applications for the PX4 Autopilot.</p><p>This section contains topics related to using the &quot;original version of ROS&quot; and the <a href="./../ros/mavros_installation.html">MAVROS</a> package to communicate with PX4 over <a href="./../middleware/mavlink.html">MAVLink</a> (MAVROS bridges ROS topics to MAVLink and PX4 conventions).</p><p>The main topics covered are:</p><ul><li><a href="./../ros/mavros_installation.html">ROS/MAVROS Installation Guide</a>: Setup a PX4 development environment with ROS 1 and MAVROS.</li><li><a href="./../ros/mavros_offboard_cpp.html">ROS/MAVROS Offboard Example (C++)</a>: Tutorial showing the main concepts related to writing a C++ MAVROS/ROS node.</li><li><a href="./../ros/mavros_custom_messages.html">ROS MAVROS Sending Custom Messages</a></li><li><a href="./../simulation/ros_interface.html">ROS with Gazebo Classic Simulation</a></li><li><a href="./../sim_gazebo_classic/octomap.html">Gazebo Classic OctoMap Models with ROS</a></li><li><a href="./../ros/raspberrypi_installation.html">ROS Installation on RPi</a></li><li><a href="./../ros/external_position_estimation.html">External Position Estimation (Vision/Motion based)</a></li></ul><h2 id="other-resources" tabindex="-1">Other Resources <a class="header-anchor" href="#other-resources" aria-label="Permalink to &quot;Other Resources&quot;">​</a></h2><ul><li><a href="./../ros/#ros-setups">PX4 ROS Setups</a>.</li><li><a href="https://github.com/robin-shaun/XTDrone/blob/master/README.en.md" target="_blank" rel="noreferrer">XTDrone</a> - ROS + PX4 simulation environment for computer vision. The <a href="https://www.yuque.com/xtdrone/manual_en" target="_blank" rel="noreferrer">XTDrone Manual</a> has everything you need to get started!</li><li><a href="https://github.com/amov-lab/Prometheus/blob/master/README_EN.md" target="_blank" rel="noreferrer">Prometheus Autonomous Drone Project</a> - Prometheus is a ROS 1 based, BSD-3 licensed collection of autonomous drone software packages from <a href="https://github.com/amov-lab" target="_blank" rel="noreferrer">AMOVLab</a>, which provides a full set of solutions for the intelligent and autonomous flight of drones, such as mapping, localization, planning, control, and target detection, fully integrated with the <a href="./../sim_gazebo_classic/">Gazebo Classic</a> Simulator.</li></ul>',8),i=[s];function n(l,h,c,m,u,p){return t(),o("div",null,i)}const _=e(r,[["render",n]]);export{f as __pageData,_ as default};
