import{_ as t,c as e,o,ab as a}from"./chunks/framework.CUflZczI.js";const _=JSON.parse('{"title":"Community Supported Simulators","description":"","frontmatter":{},"headers":[],"relativePath":"en/simulation/community_supported_simulators.md","filePath":"en/simulation/community_supported_simulators.md"}'),i={name:"en/simulation/community_supported_simulators.md"},r=a('<h1 id="community-supported-simulators" tabindex="-1">Community Supported Simulators <a class="header-anchor" href="#community-supported-simulators" aria-label="Permalink to &quot;Community Supported Simulators&quot;">â€‹</a></h1><p>This section contains information about <em>community-supported</em> simulations</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>These simulators are not maintained, tested, or supported, by the core development team. They may or may not work with current versions of PX4.</p><p>See <a href="./../dev_setup/dev_env.html">Toolchain Installation</a> for information about the environments and tools supported by the core development team.</p></div><p>The tools have variable levels of support from their communities (some are well supported and others are not). Questions about these tools should be raised on the <a href="./../contribute/support.html#forums-and-chat">discussion forums</a></p><table><thead><tr><th>Simulator</th><th>Description</th></tr></thead><tbody><tr><td><a href="./../sim_flightgear/">FlightGear</a></td><td><p>A simulator that provides physically and visually realistic simulations. In particular it can simulate many weather conditions, including thunderstorms, snow, rain and hail, and can also simulate thermals and different types of atmospheric flows. <a href="./../sim_flightgear/multi_vehicle.html">Multi-vehicle simulation</a> is also supported.</p> <p><strong>Supported Vehicles:</strong> Plane, Autogyro, Rover</p></td></tr><tr><td><a href="./../sim_jmavsim/">JMAVSim</a></td><td><p>A simple multirotor/quad simulator. This was previously part of the PX4 development toolchain but was removed in favour of <a href="./../sim_gazebo_gz/">Gazebo</a>.</p> <p><strong>Supported Vehicles:</strong> Quad</p></td></tr><tr><td><a href="./../sim_jsbsim/">JSBSim</a></td><td><p>A simulator that provides advanced flight dynamics models. This can be used to model realistic flight dynamics based on wind tunnel data.</p> <p><strong>Supported Vehicles:</strong> Plane, Quad, Hex</p></td></tr><tr><td><a href="./../sim_airsim/">AirSim</a></td><td><p>A cross platform simulator that provides physically and visually realistic simulations. This simulator is resource intensive, and requires a very significantly more powerful computer than the other simulators described here.</p><p><strong>Supported Vehicles:</strong> Iris (MultiRotor model and a configuration for PX4 QuadRotor in the X configuration).</p></td></tr><tr><td><a href="./../sim_sih/">Simulation-In-Hardware</a> (SIH)</td><td><p>An alternative to HITL that offers a hard real-time simulation directly on the hardware autopilot. This simulator is implemented in C++ as a PX4 module directly in the Firmware <a href="https://github.com/PX4/PX4-Autopilot/tree/release/1.15/src/modules/simulation/simulator_sih" target="_blank" rel="noreferrer">code</a>. </p><p><strong>Supported Vehicles:</strong> Plane, Quad, Tailsitter</p></td></tr></tbody></table>',5),s=[r];function n(l,m,d,u,p,h){return o(),e("div",null,s)}const f=t(i,[["render",n]]);export{_ as __pageData,f as default};
