import{_ as e}from"./chunks/micronav.BndX9JiN.js";import{_ as t,c as o,o as r,ab as a}from"./chunks/framework.CUflZczI.js";const g=JSON.parse('{"title":"조이스틱 설정","description":"","frontmatter":{},"headers":[],"relativePath":"ko/config/joystick.md","filePath":"ko/config/joystick.md"}'),n={name:"ko/config/joystick.md"},i=a('<h1 id="조이스틱-설정" tabindex="-1">조이스틱 설정 <a class="header-anchor" href="#조이스틱-설정" aria-label="Permalink to &quot;조이스틱 설정&quot;">​</a></h1><p>A <a href="https://en.wikipedia.org/wiki/Joystick" target="_blank" rel="noreferrer">computer joystick</a> or gamepad connected through <em>QGroundControl</em> can be used to manually control the vehicle (<em>instead</em> of using an <a href="./../config/radio.html">RC Transmitter</a>).</p><p>This approach may be used by manual control units that have an integrated ground control station (like the <em>UAVComponents</em> <a href="https://uxvtechnologies.com/ground-control-stations/micronav/" target="_blank" rel="noreferrer">MicroNav</a> shown below). 조이스틱은 일반적으로 비행 시뮬레이션용으로 많이 사용합니다.</p><p><img src="'+e+'" alt="Joystick MicroNav"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p><a href="./../config/radio.html">Radio Setup</a> is not required if using only a joystick (because a joystick is not an RC controller)!</p></div><div class="info custom-block"><p class="custom-block-title"><em>QGroundControl</em> uses the cross-platform <a href="http://www.libsdl.org/index.php" target="_blank" rel="noreferrer">SDL2</a> library to convert joystick movements to MAVLink <a href="https://mavlink.io/en/messages/common.html#MANUAL_CONTROL" target="_blank" rel="noreferrer">MANUAL_CONTROL</a> messages, which are then sent to PX4 over the telemetry channel. 결과적으로 조이스틱 기반 제어 시스템은 차량이 조이스틱 움직임에 반응하기 위해 안정적인 고대역폭 원격 채널이 필요합니다.</p></div><h2 id="px4-조이스틱-지원-활성화" tabindex="-1">PX4 조이스틱 지원 활성화 <a class="header-anchor" href="#px4-조이스틱-지원-활성화" aria-label="Permalink to &quot;PX4 조이스틱 지원 활성화&quot;">​</a></h2><p>Information about how to set up a joystick is covered in: <a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/setup_view/joystick.html" target="_blank" rel="noreferrer">QGroundControl &gt; Joystick Setup</a>.</p><p>요약:</p><ul><li>Open <em>QGroundControl</em></li><li>Set the parameter <a href="./../advanced_config/parameter_reference.html#COM_RC_IN_MODE">COM_RC_IN_MODE=1</a> - <code>Joystick</code><ul><li>See <a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/setup_view/parameters.html" target="_blank" rel="noreferrer">Parameters</a> for information about setting parameters</li><li>Setting the parameter to <code>2</code> or <code>3</code> also enables Joystick under some circumstances.</li></ul></li><li>조이스틱을 연결합니다.</li><li>연결된 조이스틱(<strong>기체 설정 &gt; 조이스틱</strong>)을 설정하세요.</li></ul>',10),s=[i];function c(l,m,d,h,p,u){return r(),o("div",null,s)}const k=t(n,[["render",c]]);export{g as __pageData,k as default};
