import{_ as e}from"./chunks/avionics_anon_altimeter_uavcan_interface.BgsmXSK3.js";import{_ as t,c as a,o as r,ab as o}from"./chunks/framework.CUflZczI.js";const b=JSON.parse('{"title":"Avionics Anonymous Laser Altimeter DroneCan Interface","description":"","frontmatter":{},"headers":[],"relativePath":"ko/dronecan/avanon_laser_interface.md","filePath":"ko/dronecan/avanon_laser_interface.md"}'),n={name:"ko/dronecan/avanon_laser_interface.md"},i=o('<h1 id="avionics-anonymous-laser-altimeter-dronecan-interface" tabindex="-1">Avionics Anonymous Laser Altimeter DroneCan Interface <a class="header-anchor" href="#avionics-anonymous-laser-altimeter-dronecan-interface" aria-label="Permalink to &quot;Avionics Anonymous Laser Altimeter DroneCan Interface&quot;">​</a></h1><div class="info custom-block"><p class="custom-block-title">In 2022, UAVCAN (v0) was forked and is maintained as <code>DroneCAN</code>. While this product still mentions &quot;UAVCAN&quot;, it is fully compatible with PX4&#39;s DroneCAN support.</p></div><p>The <a href="https://www.tindie.com/products/avionicsanonymous/uavcan-laser-altimeter-interface/" target="_blank" rel="noreferrer">Avionics Anonymous Laser Altimeter Interface</a> allows a <a href="#supported_rangefinders">number of common rangefinders</a> to be connected via the CAN bus (this is a more robust interface than I2C).</p><p><img src="'+e+'" alt="Avionics Anonymous Laser Altimeter DroneCAN Interface"></p><h2 id="where-to-buy" tabindex="-1">Where to Buy <a class="header-anchor" href="#where-to-buy" aria-label="Permalink to &quot;Where to Buy&quot;">​</a></h2><ul><li><a href="https://www.tindie.com/products/avionicsanonymous/uavcan-laser-altimeter-interface/" target="_blank" rel="noreferrer">AvAnon Laser Interface</a></li></ul><p><a id="supported_rangefinders"></a></p><h2 id="supported-rangefinders" tabindex="-1">Supported Rangefinders <a class="header-anchor" href="#supported-rangefinders" aria-label="Permalink to &quot;Supported Rangefinders&quot;">​</a></h2><p>A full list of supported rangefinders can be found on the link above.</p><p>At time of writing the following rangefinders are supported:</p><ul><li>Lightware SF30/D</li><li>Lightware SF10/a</li><li>Lightware SF10/b</li><li>Lightware SF10/c</li><li>Lightware SF11/c</li><li>Lightware SF/LW20/b</li><li>Lightware SF/LW20/c</li></ul><h2 id="hardware-setup" tabindex="-1">Hardware Setup <a class="header-anchor" href="#hardware-setup" aria-label="Permalink to &quot;Hardware Setup&quot;">​</a></h2><h3 id="wiring" tabindex="-1">Wiring <a class="header-anchor" href="#wiring" aria-label="Permalink to &quot;Wiring&quot;">​</a></h3><p>The rangefinder (laser) is connected to the AvAnon interface board, which is connected to one of the CAN ports on your autopilot. The wiring is as per the pinout above, or the necessary cables can be purchased to connect to your system right out of the box. These are available at the links <a href="https://www.tindie.com/products/avionicsanonymous/uavcan-laser-altimeter-interface/" target="_blank" rel="noreferrer">here</a>.</p><p>The interface board provides a filtered power output for the laser, but does not provide its own regulation. Therefore the laser must be compatible with whatever voltage is supplied to the board.</p><h3 id="pinouts" tabindex="-1">Pinouts <a class="header-anchor" href="#pinouts" aria-label="Permalink to &quot;Pinouts&quot;">​</a></h3><h3 id="can-connector" tabindex="-1">CAN Connector <a class="header-anchor" href="#can-connector" aria-label="Permalink to &quot;CAN Connector&quot;">​</a></h3><table><thead><tr><th>Pin</th><th>Name</th><th>Description</th></tr></thead><tbody><tr><td>1</td><td>POWER_IN</td><td>Power Supply. 4.0-5.5V supported, but must also be compatible with connected laser.</td></tr><tr><td>2</td><td>TX/SCL</td><td>TX for serial mode, Clock for I2C mode.</td></tr><tr><td>3</td><td>RX/SDA</td><td>RX for serial mode, Data for I2C mode.</td></tr><tr><td>4</td><td>GND</td><td>Signal/power ground.</td></tr></tbody></table><h3 id="laser-connector" tabindex="-1">Laser Connector <a class="header-anchor" href="#laser-connector" aria-label="Permalink to &quot;Laser Connector&quot;">​</a></h3><table><thead><tr><th>Pin</th><th>Name</th><th>Description</th></tr></thead><tbody><tr><td>1</td><td>POWER_OUT</td><td>Filtered power at the supply voltage.</td></tr><tr><td>2</td><td>CAN+</td><td>TX for serial mode, Clock for I2C mode.</td></tr><tr><td>3</td><td>RX/SDA</td><td>RX for serial mode, Data for I2C mode.</td></tr><tr><td>4</td><td>GND</td><td>Signal/power ground.</td></tr></tbody></table><h2 id="px4-configuration" tabindex="-1">PX4 Configuration <a class="header-anchor" href="#px4-configuration" aria-label="Permalink to &quot;PX4 Configuration&quot;">​</a></h2><p>To enable the laser altimeter you will need to <a href="./../advanced_config/parameters.html">set the following parameters</a> (in QGroundControl):</p><ul><li>Enable DroneCAN by setting <a href="./../advanced_config/parameter_reference.html#UAVCAN_ENABLE">UAVCAN_ENABLE</a> non zero.</li><li>Enable DroneCAN rangefinder subscription by setting <a href="./../advanced_config/parameter_reference.html#UAVCAN_SUB_RNG">UAVCAN_SUB_RNG</a></li><li>Set the minimum and maximum range of the rangefinder using <a href="./../advanced_config/parameter_reference.html#UAVCAN_RNG_MIN">UAVCAN_RNG_MIN</a> and <a href="./../advanced_config/parameter_reference.html#UAVCAN_RNG_MAX">UAVCAN_RNG_MAX</a>.</li></ul>',23),d=[i];function s(l,c,h,u,f,p){return r(),a("div",null,d)}const A=t(n,[["render",s]]);export{b as __pageData,A as default};
