import{_ as t}from"./chunks/ark_v6x_front.Fch3dwME.js";import{_ as e,c as r,o as a,ab as o}from"./chunks/framework.CUflZczI.js";const k=JSON.parse('{"title":"ARK Electronics ARKV6X","description":"","frontmatter":{},"headers":[],"relativePath":"ko/flight_controller/arkv6x.md","filePath":"ko/flight_controller/arkv6x.md"}'),i={name:"ko/flight_controller/arkv6x.md"},l=o('<h1 id="ark-electronics-arkv6x" tabindex="-1">ARK Electronics ARKV6X <a class="header-anchor" href="#ark-electronics-arkv6x" aria-label="Permalink to &quot;ARK Electronics ARKV6X&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://arkelectron.com/contact-us/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The USA-built ARKV6X flight controller is based on the <a href="https://github.com/pixhawk/Pixhawk-Standards" target="_blank" rel="noreferrer">FMUV6X and Pixhawk Autopilot Bus open source standards</a>.</p><p>With triple synced IMUs, data averaging, voting, and filtering is possible. The Pixhawk Autopilot Bus (PAB) form factor enables the ARKV6X to be used on any <a href="./../flight_controller/pixhawk_autopilot_bus.html">PAB-compatible carrier board</a>, such as the <a href="./../flight_controller/arkpab.html">ARK Pixhawk Autopilot Bus Carrier</a>.</p><p><img src="'+t+'" alt="ARKV6X Main Photo"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="where-to-buy" tabindex="-1">Where To Buy <a class="header-anchor" href="#where-to-buy" aria-label="Permalink to &quot;Where To Buy&quot;">​</a></h2><p>Order From <a href="https://arkelectron.com/product/arkv6x/" target="_blank" rel="noreferrer">Ark Electronics</a> (US)</p><h2 id="sensors" tabindex="-1">Sensors <a class="header-anchor" href="#sensors" aria-label="Permalink to &quot;Sensors&quot;">​</a></h2><ul><li><a href="https://invensense.tdk.com/products/motion-tracking/6-axis/icm-42688-p/" target="_blank" rel="noreferrer">Dual Invensense ICM-42688-P IMUs</a></li><li><a href="https://invensense.tdk.com/products/smartindustrial/iim-42652/" target="_blank" rel="noreferrer">Invensense IIM-42652 Industrial IMU</a></li><li><a href="https://www.bosch-sensortec.com/products/environmental-sensors/pressure-sensors/bmp390/" target="_blank" rel="noreferrer">Bosch BMP390 Barometer</a></li><li><a href="https://www.bosch-sensortec.com/products/motion-sensors/magnetometers/bmm150/" target="_blank" rel="noreferrer">Bosch BMM150 Magnetometer</a></li></ul><h2 id="microprocessor" tabindex="-1">Microprocessor <a class="header-anchor" href="#microprocessor" aria-label="Permalink to &quot;Microprocessor&quot;">​</a></h2><ul><li><a href="https://www.st.com/en/microcontrollers-microprocessors/stm32h743ii.html" target="_blank" rel="noreferrer">STM32H743IIK6 MCU</a><ul><li>480MHz</li><li>2MB Flash</li><li>1MB Flash</li></ul></li></ul><h2 id="other-features" tabindex="-1">Other Features <a class="header-anchor" href="#other-features" aria-label="Permalink to &quot;Other Features&quot;">​</a></h2><ul><li>FRAM</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-010%20Pixhawk%20Autopilot%20Bus%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Autopilot Bus (PAB) Form Factor</a></li><li>LED Indicators</li><li>MicroSD Slot</li><li>USA Built</li><li>Designed with a 1W heater. Keeps sensors warm in extreme conditions</li></ul><h2 id="power-requirements" tabindex="-1">Power Requirements <a class="header-anchor" href="#power-requirements" aria-label="Permalink to &quot;Power Requirements&quot;">​</a></h2><ul><li>5V</li><li>500mA <ul><li>300ma for main system</li><li>200ma for heater</li></ul></li></ul><h2 id="additional-information" tabindex="-1">Additional Information <a class="header-anchor" href="#additional-information" aria-label="Permalink to &quot;Additional Information&quot;">​</a></h2><ul><li>Weight: 5.0 g</li><li>Dimensions: 3.6 x 2.9 x 0.5 cm</li></ul><h2 id="pinout" tabindex="-1">Pinout <a class="header-anchor" href="#pinout" aria-label="Permalink to &quot;Pinout&quot;">​</a></h2><p>For pinout of the ARKV6X see the <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-010%20Pixhawk%20Autopilot%20Bus%20Standard.pdf" target="_blank" rel="noreferrer">DS-10 Pixhawk Autopilot Bus Standard</a></p><h2 id="serial-port-mapping" tabindex="-1">Serial Port Mapping <a class="header-anchor" href="#serial-port-mapping" aria-label="Permalink to &quot;Serial Port Mapping&quot;">​</a></h2><table><thead><tr><th>UART</th><th>Device</th><th>Port</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>GPS</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM3</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>Debug Console</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>UART4 &amp; I2C</td></tr><tr><td>UART5</td><td>/dev/ttyS4</td><td>TELEM2</td></tr><tr><td>USART6</td><td>/dev/ttyS5</td><td>PX4IO/RC</td></tr><tr><td>UART7</td><td>/dev/ttyS6</td><td>TELEM1</td></tr><tr><td>UART8</td><td>/dev/ttyS7</td><td>GPS2</td></tr></tbody></table><h2 id="building-firmware" tabindex="-1">Building Firmware <a class="header-anchor" href="#building-firmware" aria-label="Permalink to &quot;Building Firmware&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make ark_fmu-v6x_default</span></span></code></pre></div>',24),s=[l];function n(d,h,c,u,p,m){return a(),r("div",null,s)}const _=e(i,[["render",n]]);export{k as __pageData,_ as default};
