import{_ as a,a as r,b as l,c as d,d as o}from"./chunks/bottom.dvipQsek.js";import{_ as i,E as n,c as s,J as h,m as t,a as c,ab as f,o as m}from"./chunks/framework.CUflZczI.js";const T=JSON.parse('{"title":"ModalAI 플라이트 코어 v1","description":"","frontmatter":{},"headers":[],"relativePath":"ko/flight_controller/modalai_fc_v1.md","filePath":"ko/flight_controller/modalai_fc_v1.md"}'),g={name:"ko/flight_controller/modalai_fc_v1.md"},p=t("h1",{id:"modalai-플라이트-코어-v1",tabindex:"-1"},[c("ModalAI 플라이트 코어 v1 "),t("a",{class:"header-anchor",href:"#modalai-플라이트-코어-v1","aria-label":'Permalink to "ModalAI 플라이트 코어 v1"'},"​")],-1),b=f('<p>ModalAI <a href="https://modalai.com/flight-core" target="_blank" rel="noreferrer">Flight Core v1</a> (<a href="https://docs.modalai.com/flight-core-datasheet" target="_blank" rel="noreferrer">데이터시트</a>)은 미국에서 제작된 PX4 용 비행 콘트롤러입니다. Flight Core는 장애물 회피와 GPS 거부 내비게이션을 위해 ModalAI <a href="https://modalai.com/voxl" target="_blank" rel="noreferrer">VOXL</a>(<a href="https://docs.modalai.com/voxl-datasheet/" target="_blank" rel="noreferrer">데이터시트</a>)과 페어링하거나 독립형 비행 콘트롤러로 독립적으로 사용할 수 있습니다. :::</p><p>The ModalAI <a href="https://modalai.com/flight-core" target="_blank" rel="noreferrer">Flight Core v1</a> (<a href="https://docs.modalai.com/flight-core-datasheet" target="_blank" rel="noreferrer">Datasheet</a>) is a flight controller for PX4, made in the USA. The Flight Core can be paired with ModalAI <a href="https://modalai.com/voxl" target="_blank" rel="noreferrer">VOXL</a> (<a href="https://docs.modalai.com/voxl-datasheet/" target="_blank" rel="noreferrer">Datasheet</a>) for obstacle avoidance and GPS-denied navigation, or used independently as a standalone flight controller.</p><p><img src="'+a+'" alt="FlightCoreV1"></p><p>Flight Core is identical to the PX4 Flight Controller portion of <a href="https://www.modalai.com/voxl-flight" target="_blank" rel="noreferrer">VOXL Flight</a> (<a href="https://docs.modalai.com/voxl-flight-datasheet/" target="_blank" rel="noreferrer">Datasheet</a>) which integrates the VOXL Companion Computer and Flight Core into a single PCB.</p><p>:::note 자세한 하드웨어 문서는 <a href="https://docs.modalai.com/flight-core-datasheet/" target="_blank" rel="noreferrer">여기</a>를 참고하십시오. :::</p><h2 id="사양" tabindex="-1">사양 <a class="header-anchor" href="#사양" aria-label="Permalink to &quot;사양&quot;">​</a></h2><table><thead><tr><th style="text-align:left;">기능</th><th style="text-align:left;">세부 정보</th></tr></thead><tbody><tr><td style="text-align:left;">중량</td><td style="text-align:left;">6 g</td></tr><tr><td style="text-align:left;">MCU</td><td style="text-align:left;">216MHz, 32-bit ARM M7 <a href="https://www.st.com/en/microcontrollers-microprocessors/stm32f765ii.html" target="_blank" rel="noreferrer">STM32F765II</a></td></tr><tr><td style="text-align:left;">메모리</td><td style="text-align:left;">256Kb FRAM</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">2Mbit Flash</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">512Kbit SRAM</td></tr><tr><td style="text-align:left;">펌웨어</td><td style="text-align:left;"><a href="https://github.com/PX4/PX4-Autopilot/tree/release/1.15/boards/modalai/fc-v1" target="_blank" rel="noreferrer">PX4</a></td></tr><tr><td style="text-align:left;">관성계</td><td style="text-align:left;"><a href="https://www.invensense.com/products/motion-tracking/6-axis/icm-20602/" target="_blank" rel="noreferrer">ICM-20602</a> (SPI1)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">ICM-42688 (SPI2)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;"><a href="https://www.bosch-sensortec.com/bst/products/all_products/bmi088_1" target="_blank" rel="noreferrer">BMI088</a> (SPI6)</td></tr><tr><td style="text-align:left;">기압계</td><td style="text-align:left;"><a href="https://www.bosch-sensortec.com/products/environmental-sensors/pressure-sensors/bmp388/" target="_blank" rel="noreferrer">BMP388</a> (I2C4)</td></tr><tr><td style="text-align:left;">보안 요소</td><td style="text-align:left;"><a href="https://www.nxp.com/products/security-and-authentication/authentication/plug-and-trust-the-fast-easy-way-to-deploy-secure-iot-connections:A71CH" target="_blank" rel="noreferrer">A71CH</a> (I2C4)</td></tr><tr><td style="text-align:left;">microSD 카드</td><td style="text-align:left;"><a href="./../dev_log/logging.html#sd-cards">지원되는 카드 정보</a></td></tr><tr><td style="text-align:left;">입력</td><td style="text-align:left;">GPS/자력계</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Spektrum</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">텔레메트리</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">CAN 버스</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">PPM</td></tr><tr><td style="text-align:left;">출력</td><td style="text-align:left;">LED 6 개 (2xRGB)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">PWM 채널 8개</td></tr><tr><td style="text-align:left;">추가 인터페이스</td><td style="text-align:left;">시리얼포트 3개</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">I2C</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">GPIO</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">More detailed hardware documentation can be found <a href="https://docs.modalai.com/flight-core-datasheet/" target="_blank" rel="noreferrer">here</a>.</p></div><h2 id="크기" tabindex="-1">크기 <a class="header-anchor" href="#크기" aria-label="Permalink to &quot;크기&quot;">​</a></h2><p><img src="'+r+'" alt="FlightCoreV1Dimensions"></p><h2 id="px4-firmware-compatibility" tabindex="-1">PX4 Firmware Compatibility <a class="header-anchor" href="#px4-firmware-compatibility" aria-label="Permalink to &quot;PX4 Firmware Compatibility&quot;">​</a></h2><p><em>Flight Core v1</em> is fully compatible with the official PX4 Firmware from PX4 v1.11.</p><p>ModalAI maintains a <a href="https://github.com/modalai/px4-firmware/tree/modalai-1.11" target="_blank" rel="noreferrer">branched PX4 version</a> for PX4 v1.11. This includes UART ESC support and improvements in VIO and VOA that are planned to be upstreamed.</p><p>이 보드는 QGroundControl 4.0 이상에서 지원됩니다.</p><h2 id="qgroundcontrol-지원" tabindex="-1">QGroundControl 지원 <a class="header-anchor" href="#qgroundcontrol-지원" aria-label="Permalink to &quot;QGroundControl 지원&quot;">​</a></h2><p>아래 다이어그램은 PX4 v1.11(및 <a href="https://github.com/modalai/px4-firmware/tree/modalai-1.10" target="_blank" rel="noreferrer">ModalAI가 유지하는 PX4 v1.10 브랜치</a>)부터 <code>ROTATION_NONE</code> 권장 방향을 나타냅니다.</p><h2 id="구매처" tabindex="-1">구매처 <a class="header-anchor" href="#구매처" aria-label="Permalink to &quot;구매처&quot;">​</a></h2><ul><li><a href="https://modalai.com/flight-core" target="_blank" rel="noreferrer">Flight Core Complete Kit</a></li><li><a href="https://modalai.com/flight-core" target="_blank" rel="noreferrer">단일 PCB에서 VOXL Companion Computer와 통합 Flight Core</a></li><li><a href="https://modalai.com/flight-deck" target="_blank" rel="noreferrer">VOXL 보조 컴퓨터 및 장애물 회피 카메라(VOXL Flight Deck)와 통합 플라이트 코어</a> (<a href="https://docs.modalai.com/voxl-flight-deck-platform-datasheet/" target="_blank" rel="noreferrer">데이터시트</a>)</li><li><a href="https://shop.modalai.com/products/voxl-flight-deck-r1" target="_blank" rel="noreferrer">VOXL와 카메라로 조립된 Flight Core</a></li></ul><h2 id="빠른-시작" tabindex="-1">빠른 시작 <a class="header-anchor" href="#빠른-시작" aria-label="Permalink to &quot;빠른 시작&quot;">​</a></h2><h3 id="방향" tabindex="-1">방향 <a class="header-anchor" href="#방향" aria-label="Permalink to &quot;방향&quot;">​</a></h3><p>The diagram below shows the recommended orientation, which corresponds to <code>ROTATION_NONE</code> starting with PX4 v1.11.</p><p><img src="'+l+'" alt="FlightCoreV1Orientation"></p><h3 id="커넥터" tabindex="-1">커넥터 <a class="header-anchor" href="#커넥터" aria-label="Permalink to &quot;커넥터&quot;">​</a></h3><p>핀배열에 관련된 더 많은 정보는 <a href="https://docs.modalai.com/flight-core-datasheet-connectors" target="_blank" rel="noreferrer">여기</a>을 참고하십시오.</p><p><img src="'+d+'" alt="FlightCoreV1 상단"></p><table><thead><tr><th>커넥터</th><th>요약</th></tr></thead><tbody><tr><td>J1</td><td>VOXL 통신 인터페이스 커넥터 (TELEM2)</td></tr><tr><td>J2</td><td>프로그래밍 및 디버그 커넥터</td></tr><tr><td>J3</td><td>USB 커넥터</td></tr><tr><td>J4</td><td>UART2, UART ESC (TELEM3)</td></tr><tr><td>J5</td><td>텔레메트리 커넥터 (TELEM1)</td></tr><tr><td>J6</td><td>VOXL - 전원 관리 입력/확장</td></tr><tr><td>J7</td><td>8 채널 PWM 출력 커넥터</td></tr><tr><td>J8</td><td>CAN 버스 커넥터</td></tr><tr><td>J9</td><td>PPM RC 입력</td></tr><tr><td>J10</td><td>외부 GPS 및 자력계 커넥터</td></tr><tr><td>J12</td><td>RC 입력, Spektrum/SBus/UART 커넥터</td></tr><tr><td>J13</td><td>I2C 디스플레이(예비 센서 커넥터)/안전 버튼 입력</td></tr></tbody></table><p><img src="'+o+'" alt="FlightCoreV1 하단"></p><h3 id="사용자-가이드" tabindex="-1">사용자 가이드 <a class="header-anchor" href="#사용자-가이드" aria-label="Permalink to &quot;사용자 가이드&quot;">​</a></h3><p>전체 사용자 가이드는 <a href="https://docs.modalai.com/flight-core-manual/" target="_blank" rel="noreferrer">여기</a>를 참고하십시오.</p><h3 id="빌드-방법" tabindex="-1">빌드 방법 <a class="header-anchor" href="#빌드-방법" aria-label="Permalink to &quot;빌드 방법&quot;">​</a></h3><p>이 대상에 대한 <a href="./../dev_setup/building_px4.html">PX4 빌드</a> 방법:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make modalai_fc-v1</span></span></code></pre></div><h2 id="시리얼-포트-매핑" tabindex="-1">시리얼 포트 매핑 <a class="header-anchor" href="#시리얼-포트-매핑" aria-label="Permalink to &quot;시리얼 포트 매핑&quot;">​</a></h2><table><thead><tr><th>UART</th><th>장치</th><th>포트</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>GPS1 (J10)</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM3 (J4)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>디버깅 콘솔(J2)</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>확장 UART (J6)</td></tr><tr><td>UART5</td><td>/dev/ttyS4</td><td>TELEM2, 기본 VOXL 통신 (J1)</td></tr><tr><td>USART6</td><td>/dev/ttyS5</td><td>RC (J12)</td></tr><tr><td>UART7</td><td>/dev/ttyS6</td><td>TELEM1 (J5)</td></tr><tr><td>UART8</td><td>/dev/ttyS7</td><td>해당없음</td></tr></tbody></table><h2 id="지원" tabindex="-1">지원 <a class="header-anchor" href="#지원" aria-label="Permalink to &quot;지원&quot;">​</a></h2><p>자세한 내용은 <a href="https://forum.modalai.com/category/10/flight-core" target="_blank" rel="noreferrer">ModalAI 포럼</a>을 참고하십시오.</p>',36);function _(x,u,y,k,v,P){const e=n("Badge");return m(),s("div",null,[p,h(e,{type:"tip",text:"PX4 v1.11"}),b])}const S=i(g,[["render",_]]);export{T as __pageData,S as default};
