import{_ as a,c as t,o,ab as e}from"./chunks/framework.CUflZczI.js";const m=JSON.parse('{"title":"Pixhawk Autopilot Bus & Carriers","description":"","frontmatter":{},"headers":[],"relativePath":"ko/flight_controller/pixhawk_autopilot_bus.md","filePath":"ko/flight_controller/pixhawk_autopilot_bus.md"}'),r={name:"ko/flight_controller/pixhawk_autopilot_bus.md"},i=e('<h1 id="pixhawk-autopilot-bus-carriers" tabindex="-1">Pixhawk Autopilot Bus &amp; Carriers <a class="header-anchor" href="#pixhawk-autopilot-bus-carriers" aria-label="Permalink to &quot;Pixhawk Autopilot Bus &amp; Carriers&quot;">​</a></h1><p>The <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-010%20Pixhawk%20Autopilot%20Bus%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Autopilot Bus (PAB) Standard</a> provides a standard interface design that allows any compliant Pixhawk flight controller to be used &quot;plug-and-play&quot; with any compliant base board.</p><p>This modularity makes it easier to integrate flight controllers into different system-on-module designs. For example, the PAB means that you can use the same flight controller hardware on a more compact board with fewer outputs, or on a board that integrates with a companion computer, and so on.</p><p>The following carriers and flight controllers are PAB-compliant, and can therefore be used interchangeably.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The &quot;Mechanical Design&quot; section of the standard provides specific recommendations for mechanical compatibility between vendors. The flight controllers and baseports listed here are expected to be compliant with all recommendations.</p></div><h2 id="pab-compatible-carriers" tabindex="-1">PAB Compatible Carriers <a class="header-anchor" href="#pab-compatible-carriers" aria-label="Permalink to &quot;PAB Compatible Carriers&quot;">​</a></h2><ul><li><a href="./../flight_controller/arkpab.html">ARK Electronics Pixhawk Autopilot Bus Carrier</a></li><li><a href="https://holybro.com/products/pixhawk-baseboards" target="_blank" rel="noreferrer">Holybro Pixhawk Standard Baseboard</a></li><li><a href="https://holybro.com/products/pixhawk-baseboards" target="_blank" rel="noreferrer">Holybro Pixhawk Mini Baseboard</a></li><li><a href="./../companion_computer/holybro_pixhawk_rpi_cm4_baseboard.html">Holybro Pixhawk RPi CM4 Baseboard</a> (Integrated Companion/Flight Controller Board)</li></ul><h2 id="pab-compatible-flight-controllers" tabindex="-1">PAB Compatible Flight Controllers <a class="header-anchor" href="#pab-compatible-flight-controllers" aria-label="Permalink to &quot;PAB Compatible Flight Controllers&quot;">​</a></h2><ul><li><a href="./../flight_controller/arkv6x.html">ARK Electronics ARKV6X</a></li><li><a href="./../flight_controller/pixhawk5x.html">Holybro Pixhawk 5X (FMUv5X)</a></li><li><a href="./../flight_controller/pixhawk6x.html">Holybro Pixhawk 6X (FMUv6X)</a></li><li><a href="./../flight_controller/cuav_pixhawk_v6x.html">CUAV Pixhawk V6X (FMUv6X)</a></li></ul>',9),l=[i];function s(h,n,c,p,d,b){return o(),t("div",null,l)}const _=a(r,[["render",s]]);export{m as __pageData,_ as default};
