import{_ as t,a as l}from"./chunks/pinmap.pN8VcZTU.js";import{_ as a,c as e,o as i,ab as r}from"./chunks/framework.CUflZczI.js";const m=JSON.parse('{"title":"ThePeach FCC-R1","description":"","frontmatter":{},"headers":[],"relativePath":"ko/flight_controller/thepeach_r1.md","filePath":"ko/flight_controller/thepeach_r1.md"}'),o={name:"ko/flight_controller/thepeach_r1.md"},s=r('<h1 id="thepeach-fcc-r1" tabindex="-1">ThePeach FCC-R1 <a class="header-anchor" href="#thepeach-fcc-r1" aria-label="Permalink to &quot;ThePeach FCC-R1&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">PX4에서 이 제품을 제조하지 않습니다. 하드웨어 지원이나 호환 문제는 <a href="https://thepeach.kr/" target="_blank" rel="noreferrer">제조사</a>에 문의하십시오.</p></div><p><strong>ThePeach FCC-R1</strong>은 <strong>(주)더피치</strong>에서 설계 및 제조한 고급 비행 컨트롤러입니다.</p><p>이 비행 컨트롤러는 오픈 하드웨어 설계인 <strong>픽스호크-프로젝트 FMUv3</strong>를 기반으로하며, <strong>Nuttx OS</strong>에서 <strong>PX4</strong>를 실행합니다.</p><p><img src="'+t+'" alt="ThePeach_R1"></p><h2 id="주요-사양" tabindex="-1">주요 사양 <a class="header-anchor" href="#주요-사양" aria-label="Permalink to &quot;주요 사양&quot;">​</a></h2><ul><li><p>Main 프로세서: STM32F427VIT6</p><ul><li>32bit ARM Cortex-M4, 168 MHz 256 KB RAM 2 MB Flash memory</li></ul></li><li><p>IO 프로세서: STM32F100C8T6</p><ul><li>ARM Cortex-M3, 32bit ARM Cortex-M3, 24 MHz, 8KB SRAM</li></ul></li><li><p>센서</p><ul><li>가속도/자이로스코프: ICM-20602</li><li>가속도/자이로스코프/지자기: MPU-9250</li><li>기압계: MS5611</li></ul></li><li><p>인터페이스</p><ul><li>8+6개의 PWM 출력 (IO 8개, FMU 6개)</li><li>Spektrum DSM / DSM2 / DSM-X Satellite 입력 호환</li><li>Futaba S.BUS 입출력 호환</li><li>PPM sum 신호 입력</li><li>아날로그/PWM RSSI 입력</li><li>S.bus 서보 출력</li><li>안전 스위치/LED</li><li>UART 포트 4개: TELEM1, TELEM2(라즈베리파이 CM3+), GPS, SERIAL4</li><li>I2C 포트 2개</li><li>CAN 버스 1개</li><li>배터리 하나의 전압 / 전류에 대한 아날로그 입력</li></ul></li><li><p>라즈베리파이 CM3+의 인터페이스</p><ul><li>VBUS</li><li>DDR2 커넥터: 라즈베리파이 CM3+</li><li>UART 포트 1개</li><li>USB 포트 2개</li><li>라즈베리파이 카메라</li></ul></li><li><p>기계적 특성</p><ul><li>치수: 49.2 x 101 x 18.2mm</li><li>중량: 100g</li></ul></li></ul><h2 id="커넥터" tabindex="-1">커넥터 <a class="header-anchor" href="#커넥터" aria-label="Permalink to &quot;커넥터&quot;">​</a></h2><p><img src="'+l+'" alt="pinmap_top"></p><h2 id="시리얼-포트-매핑" tabindex="-1">시리얼 포트 매핑 <a class="header-anchor" href="#시리얼-포트-매핑" aria-label="Permalink to &quot;시리얼 포트 매핑&quot;">​</a></h2><table><thead><tr><th>UART</th><th>장치</th><th>포트</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>IO 프로세서 디버그</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (흐름 제어)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2 (라즈베리파이 cm3+)</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>GPS1</td></tr><tr><td>USART6</td><td>/dev/ttyS4</td><td>PX4IO</td></tr><tr><td>UART7</td><td>/dev/ttyS5</td><td>디버그 콘솔</td></tr><tr><td>UART8</td><td>/dev/ttyS6</td><td>TELEM4</td></tr></tbody></table><h2 id="정격-전압" tabindex="-1">정격 전압 <a class="header-anchor" href="#정격-전압" aria-label="Permalink to &quot;정격 전압&quot;">​</a></h2><p><strong>ThePeach FCC-R1</strong>은 두 개의 전원이 공급되는 경우, 전원 공급 장치의 이중 중복이 가능합니다. 이 두 개의 전원 레일은: <strong>POWER</strong>와 <strong>USB</strong>입니다.</p><p>참고:</p><ol><li>출력 전원 레일인 <strong>FMU PWM OUT</strong> 과 <strong>I/O PWM OUT</strong>은 비행 컨트롤러 보드에 전원을 공급하지 않습니다.(공급받지도 않습니다). <strong>POWER</strong> 또는 <strong>USB</strong>중 하나에 전원을 공급하여야 합니다. 그렇지 않으면 보드에 전원이 공급되지 않습니다.</li><li>USB는 **라즈베리파이 CM3+**에 전원을 공급하지 않습니다. <strong>POWER</strong>에 전원을 공급해야 합니다. 그렇지 않으면 라즈베리파이 CM3+에 전원이 공급되지 않습니다.</li></ol><p><strong>정상 작동 최대 정격 전압</strong></p><p>이러한 조건에서 전원은 아래의 순서대로 시스템에 전원을 공급하여야 합니다.</p><ol><li>POWER 입력 (5V ~ 5.5V)</li><li>USB 입력(4.75V ~ 5.25V)</li></ol><p><strong>절대 최대 정격 전압</strong></p><p>이러한 조건에서 모든 전원은 비행 컨트롤러에 영구적인 손상을 입힙니다.</p><ol><li><p>POWER 입력 (5.5V 초과)</p></li><li><p>USB 입력 (5.5V 초과)</p></li></ol><h2 id="펌웨어-빌드" tabindex="-1">펌웨어 빌드 <a class="header-anchor" href="#펌웨어-빌드" aria-label="Permalink to &quot;펌웨어 빌드&quot;">​</a></h2><p>이 비행 컨트롤러용 PX4를 빌드하려면:</p><div class="language-jsx vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">jsx</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">make thepeach_r1_default</span></span></code></pre></div><h2 id="구매처" tabindex="-1">구매처 <a class="header-anchor" href="#구매처" aria-label="Permalink to &quot;구매처&quot;">​</a></h2><p><a href="http://thepeach.shop/" target="_blank" rel="noreferrer">(주)더피치</a>에서 주문하십시오.</p>',26),d=[s];function h(n,p,c,g,u,_){return i(),e("div",null,d)}const P=a(o,[["render",h]]);export{m as __pageData,P as default};
