import{_ as t,a as e,b as a}from"./chunks/jst_cable.CR8jdDHX.js";import{_ as n,a as i}from"./chunks/septentrio_mosaic_a5_h_t_clas_gnss_module_receiverwebui.BxhYoNx0.js";import{_ as o,c as r,o as l,ab as d}from"./chunks/framework.CUflZczI.js";const w=JSON.parse('{"title":"Septentrio mosaic-go","description":"","frontmatter":{},"headers":[],"relativePath":"ko/gps_compass/septentrio_mosaic-go.md","filePath":"ko/gps_compass/septentrio_mosaic-go.md"}'),s={name:"ko/gps_compass/septentrio_mosaic-go.md"},c=d('<h1 id="septentrio-mosaic-go" tabindex="-1">Septentrio mosaic-go <a class="header-anchor" href="#septentrio-mosaic-go" aria-label="Permalink to &quot;Septentrio mosaic-go&quot;">​</a></h1><p>The Septentrio mosaic-go receivers are evaluation kits for their mosaic-X5 and mosaic-H receiver modules. Because of their small size and low weight, they are ideal for autopilot applications. The available variants are the <a href="https://www.septentrio.com/en/products/gps/gnss-receiver-modules/mosaic-go-evaluation-kit" target="_blank" rel="noreferrer">mosaic-go</a> and <a href="https://www.septentrio.com/en/products/gps/gnss-receiver-modules/mosaic-h-evaluation-kit" target="_blank" rel="noreferrer">mosaic-go heading</a>.</p><p><img src="'+t+'" alt="Mosaic go Highly Accurate GNSS Receiver Module"></p><p>Their features include the following:</p><ul><li>High update rate (&gt;100 Hz) and low latency, both crucial for control systems of autonomous applications</li><li>Reliable centimetre-level positioning</li><li>Full L2 support via P(Y) code</li><li>Size: 71 x 59 x 12 mm ± 1mm</li><li>Weight: 58g ± 1g</li></ul><h2 id="purchase" tabindex="-1">Purchase <a class="header-anchor" href="#purchase" aria-label="Permalink to &quot;Purchase&quot;">​</a></h2><p>The mosaic-go kit is available on Septentrio&#39;s <a href="https://web.septentrio.com/l/858493/2022-04-19/xgrnz" target="_blank" rel="noreferrer">official store</a>:</p><ul><li><a href="https://web.septentrio.com/l/858493/2022-04-19/xgrp9" target="_blank" rel="noreferrer">mosaic-go heading GNSS module evaluation kit</a></li><li><a href="https://web.septentrio.com/l/858493/2022-04-19/xgrpd" target="_blank" rel="noreferrer">mosaic-go GNSS module receiver evaluation kit</a></li></ul><p>Other PX4 supported devices from Septentrio:</p><ul><li><a href="./../gps_compass/septentrio_asterx-rib.html">AsteRx OEM with Robotics Interface Board</a></li></ul><h2 id="mosaic-go-evaluation-kit-contents" tabindex="-1">mosaic-go Evaluation Kit Contents <a class="header-anchor" href="#mosaic-go-evaluation-kit-contents" aria-label="Permalink to &quot;mosaic-go Evaluation Kit Contents&quot;">​</a></h2><ul><li>1 mosaic-H or mosaic-X5 module soldered on an interface board inside a metallic housing.</li><li>1 USB cable</li><li>6-pin COM1 open-ended cable</li><li>4-pin COM2 open-ended cable</li><li>Help user guide card</li></ul><h2 id="physical-interfaces" tabindex="-1">Physical Interfaces <a class="header-anchor" href="#physical-interfaces" aria-label="Permalink to &quot;Physical Interfaces&quot;">​</a></h2><table><thead><tr><th>Type</th><th>Label</th><th>Use</th></tr></thead><tbody><tr><td>USB Micro-B</td><td>USB</td><td>USB communication and power</td></tr><tr><td>RSV USB Micro-B</td><td>RSV</td><td>Reserved and should not be used</td></tr><tr><td>SMA</td><td>RF-IN{1,2}</td><td>Main (and auxiliary) antenna connections</td></tr><tr><td>6-pin JST</td><td>Serial</td><td>Serial communication and power</td></tr><tr><td>4-pin JST</td><td>Serial</td><td>Serial communication</td></tr><tr><td>microSD</td><td>TF CARD</td><td>Serial communication</td></tr></tbody></table><blockquote><p>Dual-antenna is only available on mosaic-H-based receivers.</p></blockquote><h3 id="_6-pin-connector" tabindex="-1">6-pin Connector <a class="header-anchor" href="#_6-pin-connector" aria-label="Permalink to &quot;6-pin Connector&quot;">​</a></h3><p><em>Connector type: GH connector, 1.25mm pitch, 6 way. Mating connector housing: GHR-06V-S.</em></p><table><thead><tr><th>Pin Name</th><th>Direction</th><th>Level</th><th>Description</th><th>Comment</th></tr></thead><tbody><tr><td>VCC</td><td>PWR</td><td>4.75V-5.5V</td><td>Main power supply</td><td></td></tr><tr><td>GND</td><td></td><td>0</td><td>Ground</td><td></td></tr><tr><td>TXD1</td><td>Out</td><td>3V3_LVTTL</td><td>Serial COM1 transmit line</td><td>Directly connects to TXD1 of internal mosaic</td></tr><tr><td>RXD1</td><td>In</td><td>3V3_LVTTL</td><td>Serial COM1 receive line</td><td>Directly connects to RXD1 of internal mosaic</td></tr><tr><td>PPS</td><td>Out</td><td>3V3_LVTTL</td><td>PPSoutput</td><td>PPSO from mosaic converted to 3.3V</td></tr><tr><td>EVENT</td><td>In</td><td>3V3_LVTTL</td><td>Event timer input</td><td>Connects to EVENTA of mosaic through a 3V3to 1V8 level translator</td></tr></tbody></table><h3 id="_4-pin-connector" tabindex="-1">4-pin Connector <a class="header-anchor" href="#_4-pin-connector" aria-label="Permalink to &quot;4-pin Connector&quot;">​</a></h3><p><em>Connector type: GH connector, 1.25mm pitch, 4way. Mating connector housing: GHR-04V-S.</em></p><table><thead><tr><th>Pin Name</th><th>Direction</th><th>Level</th><th>Description</th><th>Comment</th></tr></thead><tbody><tr><td>NRST</td><td>In</td><td>3V3_LVTTL</td><td>Reset input</td><td>Directly connects to nRST_IN of internal mosaic</td></tr><tr><td>TXD2</td><td>Out</td><td>3V3_LVTTL</td><td>Serial COM2 transmit line</td><td>Directly connects to TXD2 of internal mosaic</td></tr><tr><td>RXD2</td><td>In</td><td>3V3_LVTTL</td><td>Serial COM2 receive line</td><td>Directly connects to RXD2 of internal mosaic</td></tr><tr><td>GND</td><td></td><td>0</td><td>Ground</td><td></td></tr></tbody></table><h2 id="px4-configuration" tabindex="-1">PX4 Configuration <a class="header-anchor" href="#px4-configuration" aria-label="Permalink to &quot;PX4 Configuration&quot;">​</a></h2><p>PX4 configuration is covered in <a href="./../gps_compass/septentrio.html">Septentrio GNSS Receivers</a>.</p><h2 id="hardware-connection-example-pixhawk-4" tabindex="-1">Hardware Connection Example Pixhawk 4 <a class="header-anchor" href="#hardware-connection-example-pixhawk-4" aria-label="Permalink to &quot;Hardware Connection Example Pixhawk 4&quot;">​</a></h2><ol><li>Make sure the receiver is powered with at least 3.3V. You can use the USB Micro-B connector or the 6-pin JST connector.</li><li>Connect one or two GNSS antennas to the RF-IN ports on the mosaic-go.</li><li>Connect the 6-pin connector (COM1) to the Pixhawk&#39;s <em>GPS MODULE</em> port. This will provide power to the mosaic-go and with this single connection it will be able to send single and dual-antenna information to the Pixhawk 4.</li></ol><p><img src="'+e+'" alt="Wiring diagram, Pixhawk 4 - mosaic-go" title="Wiring diagram, Pixhawk 4 - mosaic-go"></p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Make sure the JST cable is wired correctly since this is not a standard cable:</p><p><img src="'+a+'" alt="Wiring of JST cable"></p></div><h3 id="dual-antenna" tabindex="-1">Dual-Antenna <a class="header-anchor" href="#dual-antenna" aria-label="Permalink to &quot;Dual-Antenna&quot;">​</a></h3><p>The attitude (heading/pitch) can be computed from the orientation of the baseline between the main and the aux1 GNSS antennas.</p><p><img src="'+n+'" alt="Multi-antenna attitude determination setup"></p><p>To enable multi-antenna attitude determination, follow the following procedure:</p><ol><li>Attach two antennas to your vehicle, using cables of approximately the same length. The default antenna configuration is as depicted in the figure. It consists in placing the antennas aligned with the longitudinal axis of the vehicle, main antenna behind AUX1. For best accuracy, try to maximize the distance between the antennas, and avoid significant height difference between the antenna ARPs.</li><li>In practice, the two antenna ARPs may not be exactly at the same height in the vehicle frame, or the main-aux1 baseline may not be exactly parallel or perpendicular to the longitudinal axis of the vehicle. This leads to offsets in the computed attitude angles. These can be compensated for with the heading parameters provided by the Septentrio driver in PX4.</li></ol><div class="info custom-block"><p class="custom-block-title">INFO</p><p>For optimal heading results, the two antennas should be seperated by at least 30cm / 11.8 in (ideally 50cm / 19.7in or more).</p><p>For additional configuration of the dual antenna setup, please refer to our <a href="https://support.septentrio.com/l/858493/2022-04-19/xgrqd" target="_blank" rel="noreferrer">Knowledge Base</a> or the <a href="https://web.septentrio.com/l/858493/2022-04-19/xgrql" target="_blank" rel="noreferrer">hardware manual</a>.</p></div><h3 id="web-app" tabindex="-1">Web App <a class="header-anchor" href="#web-app" aria-label="Permalink to &quot;Web App&quot;">​</a></h3><p>mosaic-H GPS/GNSS receiver module with heading comes with fully documented interfaces, commands and data messages. The included GNSS receiver control and analysis software <a href="https://web.septentrio.com/l/858493/2022-04-19/xgrqp" target="_blank" rel="noreferrer">RxTools</a> allows receiver configuration, monitoring as well as data logging and analysis.</p><p>The receiver includes an intuitive web user interface for easy operation and monitoring allowing you to control the receiver from any mobile device or computer. The web interface also uses easy-to-read quality indicators ideal to monitor the receiver operation during the job at hand.</p><p><img src="'+i+'" alt="Septentrio web user interface"></p><h2 id="led-status" tabindex="-1">LED Status <a class="header-anchor" href="#led-status" aria-label="Permalink to &quot;LED Status&quot;">​</a></h2><table><thead><tr><th>LED Color</th><th style="text-align:center;">Powered</th><th style="text-align:center;">SD card mounted</th><th style="text-align:center;">PVT Solution</th><th style="text-align:center;">Logging enabled</th></tr></thead><tbody><tr><td>Red</td><td style="text-align:center;">✓️</td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td>Green</td><td style="text-align:center;">✓️</td><td style="text-align:center;">✓️</td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td>Blue</td><td style="text-align:center;">✓️</td><td style="text-align:center;">✓️</td><td style="text-align:center;">✓️</td><td style="text-align:center;"></td></tr><tr><td>Purple</td><td style="text-align:center;">✓️</td><td style="text-align:center;"></td><td style="text-align:center;">✓️</td><td style="text-align:center;"></td></tr><tr><td>Purple + Blue</td><td style="text-align:center;">✓️</td><td style="text-align:center;">✓️</td><td style="text-align:center;">✓️</td><td style="text-align:center;">✓️</td></tr><tr><td>Red + Green</td><td style="text-align:center;">✓️</td><td style="text-align:center;">✓️</td><td style="text-align:center;"></td><td style="text-align:center;">✓️</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>For more detailed information about the mosaic-go and its module, please refer to the <a href="https://web.septentrio.com/l/858493/2022-04-19/xgrrd" target="_blank" rel="noreferrer">hardware manual</a> or the <a href="https://support.septentrio.com/l/858493/2022-04-19/xgrrl" target="_blank" rel="noreferrer">Septentrio Support</a> page.</p></div>',40),h=[c];function p(m,u,g,b,f,x){return l(),r("div",null,h)}const v=o(s,[["render",p]]);export{w as __pageData,v as default};
