import{_ as o,a as e}from"./chunks/pm03d_contents.BF2cHk11.js";import{_ as r}from"./chunks/pm02d_pinout.7bTPG-dY.js";import{_ as i,c as t,o as a,ab as l}from"./chunks/framework.CUflZczI.js";const x=JSON.parse('{"title":"Holybro PM03D Power Module","description":"","frontmatter":{},"headers":[],"relativePath":"ko/power_module/holybro_pm03d.md","filePath":"ko/power_module/holybro_pm03d.md"}'),n={name:"ko/power_module/holybro_pm03d.md"},s=l('<h1 id="holybro-pm03d-power-module" tabindex="-1">Holybro PM03D Power Module <a class="header-anchor" href="#holybro-pm03d-power-module" aria-label="Permalink to &quot;Holybro PM03D Power Module&quot;">​</a></h1><p>The PM03D Power Module acts as both a Power Module (PM) and a Power Distribution Board (PDB). In addition to providing regulated power to Pixhawk v5X and the ESCs, it sends information to the autopilot about battery voltage and current supplied to the flight controller and the motors.</p><p>The power module is connected using the I2C protocol. It is designed for flight controllers based on the Pixhawk FMUv5X and FMUv6X open standard, including the <a href="./../flight_controller/pixhawk5x.html">Pixhawk 5X</a>.</p><div class="info custom-block"><p class="custom-block-title">The PM is <strong>NOT</strong> compatible with flight controllers that require an analog power module, including: <a href="./../flight_controller/pixhawk4.html">Pixhawk 4</a>, <a href="./../flight_controller/durandal.html">Durandal</a>, <a href="./../flight_controller/holybro_pix32_v5.html">Pix32 v5</a>, etc.</p></div><p><img src="'+o+'" alt="Pixhawk5x Upright Image"></p><h2 id="features" tabindex="-1">Features <a class="header-anchor" href="#features" aria-label="Permalink to &quot;Features&quot;">​</a></h2><ul><li>Plug &amp; play, no additional setup required for QGroundcontrol &amp; Mission Planner</li><li>XT-30 connectors and solder pads for easy motor ESC connection</li><li>Onboard voltage regulators: two 5.2V &amp; one selectable 8V/12V</li><li>5V/A ports for powering companion computer or peripheral device</li><li>Selectable 8V or 12V triple row pin header for powering peripheral device</li></ul><h2 id="specifications" tabindex="-1">Specifications <a class="header-anchor" href="#specifications" aria-label="Permalink to &quot;Specifications&quot;">​</a></h2><ul><li><strong>Maximum input voltage</strong>: 6S battery</li><li><strong>Rated current</strong>: 60A</li><li><strong>Max current</strong>: 120A (&lt;60 Sec)</li><li><strong>Max current sensing</strong>: 164A</li><li><strong>Communication protocol</strong>: I2C</li><li><strong>Dimension</strong>: 84 x 78 x 12mm (excluding wires)</li><li><strong>Mounting</strong>: 45 x 45mm</li><li><strong>Weight</strong>: 59g</li><li><strong>Connections</strong>:</li><li>XT-60 for battery</li><li>XT-30 for motor &amp; peripheral device (battery voltage)</li><li>Solder pads in each corner (battery voltage)</li><li>CLIK-Mate 2.0mm for flight controller (5.2V/3A standalone BEC)</li><li>JST GH 4pin (5.2V/3A, BEC shared with 5.2V triple row pin header)</li><li>2x Triple row pin header (5.2V/3A, BEC shared with JST GH 4pin)</li><li>2x Triple row pin header (8V or 12V selectable by moving header jumper, 3A)</li></ul><h2 id="package-contents" tabindex="-1">Package Contents <a class="header-anchor" href="#package-contents" aria-label="Permalink to &quot;Package Contents&quot;">​</a></h2><ul><li>1x PM06 board</li><li>1x 80mm XT60 connector wire (pre-soldered)</li><li>1x Electrolytic capacitor: 220uF 63V (pre-soldered)</li><li>1x 2.0mm pitch CLIK-Mate 6pin cable (To power flight controller)</li><li>4pin JST GH to USB Type C</li><li>4pin JST GH to barrel plug (2.1*5.5mm)</li><li>4pin JST GH to barrel plug (2.5*5.5mm)</li><li>4pin Pin Dupont Cable (2pc)</li><li>Nylon standoffs &amp; nuts</li></ul><img src="'+e+'" width="650px" title="Pixhawk5x Upright Image"><h2 id="where-to-buy" tabindex="-1">Where to Buy <a class="header-anchor" href="#where-to-buy" aria-label="Permalink to &quot;Where to Buy&quot;">​</a></h2><p><a href="https://holybro.com/products/pm03d-power-module" target="_blank" rel="noreferrer">Order from Holybro Store</a></p><h2 id="wiring-connections" tabindex="-1">Wiring/Connections <a class="header-anchor" href="#wiring-connections" aria-label="Permalink to &quot;Wiring/Connections&quot;">​</a></h2><p><img src="'+r+'" alt="pinout"></p><p>Additional wiring and connection information can be found in: <a href="./../assembly/quick_start_pixhawk5x.html">Holybro Pixhawk 5x Wiring Quick Start</a>.</p>',17),c=[s];function p(d,h,m,u,g,b){return a(),t("div",null,c)}const P=i(n,[["render",p]]);export{x as __pageData,P as default};
