import{_ as a}from"./chunks/fc_heading_mark_1.BpJEJryq.js";import{_ as e}from"./chunks/fc_orientation_1.D8ee5fZQ.js";import{_ as t}from"./chunks/3dr_anti_vibration_mounting_foam.D_h4u7yq.js";import{_ as o,c as r,o as _,ab as i}from"./chunks/framework.CUflZczI.js";const v=JSON.parse('{"title":"Монтаж Політного Контролера","description":"","frontmatter":{},"headers":[],"relativePath":"uk/assembly/mount_and_orient_controller.md","filePath":"uk/assembly/mount_and_orient_controller.md"}'),l={name:"uk/assembly/mount_and_orient_controller.md"},s=i('<h1 id="монтаж-політного-контролера" tabindex="-1">Монтаж Політного Контролера <a class="header-anchor" href="#монтаж-політного-контролера" aria-label="Permalink to &quot;Монтаж Політного Контролера&quot;">​</a></h1><p>Політний контролер слід розташувати на рамі якомога ближче до центру маси (CoG), верхньою стороною вгору, та орієнтувати так, щоб <em>стрілка позначки курсу</em> вказувала на передню частину апарату. Часто необхідна <a href="#vibration-isolation">віброізоляція</a>, тому слід дотримуватися рекомендацій виробника. Якщо змонтовано таким чином, подальша конфігурація PX4 не потрібна.</p><h2 id="орієнтація" tabindex="-1">Орієнтація <a class="header-anchor" href="#орієнтація" aria-label="Permalink to &quot;Орієнтація&quot;">​</a></h2><p>Майже у всіх контролерів польоту є <em>стрілка маркування напрямку</em> (показана нижче). Контролер повинен бути розміщений на верхній стороні рами, орієнтований таким чином, щоб стрілка вказувала вперед транспортного засобу (на всіх рамах повітряних суден - літак, багатокоптер, VTOL, наземні транспортні засоби тощо).</p><p><img src="'+a+'" alt="Позначка заголовка FC"></p><p><img src="'+e+'" alt="Орієнтація FC"></p><div class="info custom-block"><p class="custom-block-title">Якщо контролер не може бути встановлений у рекомендованому/стандартному положенні через фізичні обмеження, вам потрібно налаштувати програмне забезпечення автопілота з орієнтацією, яку ви дійсно використали: <a href="./../config/flight_controller_orientation.html">Орієнтація Польотного Контролера</a>.</p></div><h2 id="положення" tabindex="-1">Положення <a class="header-anchor" href="#положення" aria-label="Permalink to &quot;Положення&quot;">​</a></h2><p>Контролер польоту повинен бути розміщений на рамці якнайближче до центру ваги.</p><p>Якщо ви не можете встановити контролер у цьому положенні, то вам слід <a href="./../advanced_config/parameters.html">налаштувати</a> наступні параметри для встановлення зміщення відносно ЦТТ: <a href="./../advanced_config/parameter_reference.html#EKF2_IMU_POS_X">EKF2_IMU_POS_X</a>, <a href="./../advanced_config/parameter_reference.html#EKF2_IMU_POS_Y">EKF2_IMU_POS_Y</a>, <a href="./../advanced_config/parameter_reference.html#EKF2_IMU_POS_Z">EKF2_IMU_POS_Z</a> (для типового оцінювача EKF2).</p><p>Зверніть увагу, що якщо ви не встановите ці зміщення, то оцінки положення / швидкості EKF2 будуть відображатися на місці його розташування, а не в ЦМ. Це може призвести до небажаних коливань, залежно від того, наскільки далеко знаходиться IMU від CoG.</p><details class="details custom-block"><summary>Details</summary><p>Пояснення Щоб зрозуміти вплив відсутності встановлення цих зміщень, розгляньте випадок, коли контролер польоту (IMU) знаходиться спереду CoG, ви летите в режимі позиції, і відбувається впередній кидковий рух навколо CoG. Оцінка висоти зменшиться, оскільки IMU фактично рухалася вниз. Як реакцію, контролер висоти надасть більше тяги для компенсації. Амплітуда залежить від того, наскільки далеко знаходиться IMU від CoG. Можливо, це може бути незначним, але це все ще деякий непотрібний зусилля по контролю, яке постійно застосовується. Якщо вказані зміщення, чистий рух висоти не створить жодних змін у приблизній оцінці, тому буде менше паразитних коригувань.</p></details><h2 id="віброізоляція" tabindex="-1">Віброізоляція <a class="header-anchor" href="#віброізоляція" aria-label="Permalink to &quot;Віброізоляція&quot;">​</a></h2><p>Плати управління польотом з вбудованими акселерометрами або гіроскопами чутливі до вібрацій. Деякі плати включають вбудовану віброізоляцію, тоді як інші постачаються з <em>монтажною піною</em>, яку ви можете використовувати для ізоляції контролера від транспортного засобу.</p><p><img src="'+t+'" alt="Монтажна піна для Pixhawk"> <em>Піна для поглинання вібрації</em></p><p>Ви повинні використовувати стратегію монтажу, рекомендовану у документації вашого контролера польоту.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p><a href="./../log/flight_review.html#vibration">Аналіз Логів за допомогою Flight Review &gt; Вібрація</a> пояснює, як використовувати логи для підтвердження того, чи є вібрація ймовірною причиною проблем з польотом.</p></div>',17),n=[s];function c(m,p,d,h,f,u){return _(),r("div",null,n)}const I=o(l,[["render",c]]);export{v as __pageData,I as default};
