import{_ as a}from"./chunks/pixhawk4_compass_gps.BgeOJnqO.js";import{_ as e,c as t,o,ab as r}from"./chunks/framework.CUflZczI.js";const G=JSON.parse('{"title":"Монтаж компаса (або GNSS/компаса)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/assembly/mount_gps_compass.md","filePath":"uk/assembly/mount_gps_compass.md"}'),s={name:"uk/assembly/mount_gps_compass.md"},_=r('<h1 id="монтаж-компаса-або-gnss-компаса" tabindex="-1">Монтаж компаса (або GNSS/компаса) <a class="header-anchor" href="#монтаж-компаса-або-gnss-компаса" aria-label="Permalink to &quot;Монтаж компаса (або GNSS/компаса)&quot;">​</a></h1><p>Модулі компаса та GNSS/компаса слід монтувати на рамі якомога подалі від ліній живлення мотора/ЕЛЕКТРОННИХ ШС і інших джерел електромагнітних перешкод, і <a href="#compass-orientation">орієнтувати</a> вертикально з напрямним маркером, спрямованим вперед транспортного засобу. Ви також повинні налаштувати PX4, щоб <a href="#position">встановити позицію</a> приймача відносно центру ваги CoG(ЦМ).</p><p>Для багатороторних літаків зазвичай компас монтується на підставці, тоді як для фіксованих крил та VTOL компас зазвичай монтується на крилі.</p><h2 id="орієнтація-компаса" tabindex="-1">Орієнтація компаса <a class="header-anchor" href="#орієнтація-компаса" aria-label="Permalink to &quot;Орієнтація компаса&quot;">​</a></h2><p>Компас повинен ідеально бути орієнтованим так, щоб він був вертикальним, а напрямний маркер вказував вперед транспортного засобу (типова орієнтація), але за необхідності може бути орієнтований під кутами, кратними 45° від цієї орієнтації (у будь-якій вісі), як це визначено у <a href="https://mavlink.io/en/messages/common.html#MAV_SENSOR_ORIENTATION" target="_blank" rel="noreferrer">стандартних орієнтаціях MAVLink</a> (вони слідують тій самій конвенції рамки, що і при <a href="./../config/flight_controller_orientation.html#calculating-orientation">орієнтації контролера польоту</a>).</p><p>На діаграмі нижче показано напрямний маркер на контролері польоту Pixhawk 4 та компасі.</p><p><img src="'+a+'" alt="Connect compass/GPS to Pixhawk 4"></p><p>PX4 автоматично визначить орієнтацію для будь-яких з цих стандартних орієнтацій під час <a href="./../config/compass.html">калібрування компаса</a> (<a href="./../advanced_config/parameter_reference.html#SENS_MAG_AUTOROT">за замовчуванням</a>).</p><p>Компас також може бути монтуваний в будь-яких інших &quot;власних кутах Ейлера&quot;, але в цьому випадку вам доведеться вручну налаштувати орієнтації. Для отримання додаткової інформації див. <a href="./../config/flight_controller_orientation.html#setting-the-compass-orientation">Налаштування орієнтації компаса</a> в <em>орієнтації контролера польоту/датчика</em>.</p><h2 id="положення" tabindex="-1">Положення <a class="header-anchor" href="#положення" aria-label="Permalink to &quot;Положення&quot;">​</a></h2><p>Для компенсації відносного руху між приймачем і ЦМ(CoG), ви повинні <a href="./../advanced_config/parameters.html">налаштувати</a> наступні параметри для встановлення зсувів: <a href="./../advanced_config/parameter_reference.html#EKF2_GPS_POS_X">EKF2_GPS_POS_X</a>, <a href="./../advanced_config/parameter_reference.html#EKF2_GPS_POS_Y">EKF2_GPS_POS_Y</a> та <a href="./../advanced_config/parameter_reference.html#EKF2_GPS_POS_Z">EKF2_GPS_POS_Z</a>.</p><p>Це важливо, оскільки кадр тіла, що оцінюється EKF, буде збігатися з місцезнаходженням модуля GNSS та вважатиме його розміщеним на CoG(ЦМ). Якщо модуль GNSS значно відокремлений від CoG(ЦМ), то обертання навколо CoG(ЦМ) буде інтерпретовано як зміна висоти, що у деяких режимах польоту (наприклад, режим позиції) може призвести до непотрібних корекцій.</p><p>Це особливо важливо при використанні <a href="./../advanced/rtk_gps.html">RTK GNSS</a>, який має точність на рівні сантиметрів, оскільки у разі неналаштування зсувів вимірювання GNSS часто будуть відхилені як несумісні з поточною оцінкою EKF.</p><details class="details custom-block"><summary>Details</summary><p>Пояснення Наприклад, якщо модуль GNSS знаходиться на відстані 10 см вище від CoG(ЦМ), а ІМП знаходиться в CoG(ЦМ), рух по тангажу зі швидкістю 1 рад/с створить вимірювання швидкості GNSS в 10 см/с, <em>навіть якщо ЦМ не рухається</em>. Якщо точність швидкості приймача GNSS становить 1 см/с, то EKF може перестати довіряти вимірюванням, оскільки вони виглядають невірними (помилкові на 10 разів точніше). Якщо зсуви визначені, EKF скоригує вимірювання, використовуючи дані гіроскопа.</p></details>',14),n=[_];function c(i,m,p,l,h,S){return o(),t("div",null,n)}const u=e(s,[["render",c]]);export{G as __pageData,u as default};
