import{_ as t,a,b as e,c as r,d as l,e as s,f as i,g as o}from"./chunks/quickstart_08.BBEahfzn.js";import{_ as n}from"./chunks/pixhawk_v6x_pinouts.DBmpEJci.js";import{_ as d,c as h,o as p,ab as c}from"./chunks/framework.CUflZczI.js";const C=JSON.parse('{"title":"Швидке підключення CUAV Pixhawk V6X","description":"","frontmatter":{},"headers":[],"relativePath":"uk/assembly/quick_start_cuav_pixhawk_v6x.md","filePath":"uk/assembly/quick_start_cuav_pixhawk_v6x.md"}'),g={name:"uk/assembly/quick_start_cuav_pixhawk_v6x.md"},f=c('<h1 id="швидке-підключення-cuav-pixhawk-v6x" tabindex="-1">Швидке підключення CUAV Pixhawk V6X <a class="header-anchor" href="#швидке-підключення-cuav-pixhawk-v6x" aria-label="Permalink to &quot;Швидке підключення CUAV Pixhawk V6X&quot;">​</a></h1><p>У цьому короткому посібнику показано, як увімкнути живлення польотного контролера <a href="./../flight_controller/cuav_pixhawk_v6x.html">Pixhawk V6X<sup>®</sup></a> та підключити до нього найважливіші периферійні пристрої.</p><h2 id="огляд-схеми-підключення" tabindex="-1">Огляд схеми підключення <a class="header-anchor" href="#огляд-схеми-підключення" aria-label="Permalink to &quot;Огляд схеми підключення&quot;">​</a></h2><p>На зображенні нижче показано, як підключити найважливіші датчики та периферійні пристрої (за винятком виходів мотора та сервоприводів). Ми розглянемо кожен з них детально в наступних розділах.</p><p><img src="'+t+'" alt="wiring"></p><table><thead><tr><th style="text-align:left;">Інтерфейс</th><th style="text-align:left;"><strong>Функція</strong></th></tr></thead><tbody><tr><td style="text-align:left;">POWER C1</td><td style="text-align:left;">Підключіть CAN PMU SE до цього інтерфейсу; цей інтерфейс підключений до модуля живлення UAVCAN</td></tr><tr><td style="text-align:left;">POWER C2</td><td style="text-align:left;">Підключіть CAN PMU SE до цього інтерфейсу; цей інтерфейс підключений до модуля живлення UAVCAN</td></tr><tr><td style="text-align:left;">POWER 1</td><td style="text-align:left;">Підключіть модуль живлення SMbus (I2C)</td></tr><tr><td style="text-align:left;">POWER 2</td><td style="text-align:left;">Підключіть модуль живлення SMbus (I2C)</td></tr><tr><td style="text-align:left;">GPS&amp;SAFETY</td><td style="text-align:left;">Підключіть Neo серії GPS/C-RTK 9PS, включаючи GPS, запобіжний вимикач, інтерфейс зумера.</td></tr><tr><td style="text-align:left;">GPS2</td><td style="text-align:left;">Підключіть модуль GPS/RTK</td></tr><tr><td style="text-align:left;">UART 4</td><td style="text-align:left;">Користувацькі налаштування</td></tr><tr><td style="text-align:left;">TELEM (1,2,3)</td><td style="text-align:left;">Підключіть телеметрію або пристрої MAVLink</td></tr><tr><td style="text-align:left;">TF CARD</td><td style="text-align:left;">Карта SD для зберігання журналів (карта попередньо вставлена на заводі).</td></tr><tr><td style="text-align:left;">M1~M8</td><td style="text-align:left;">Вихідний IO PWM (для підключення до ESC і Servo)</td></tr><tr><td style="text-align:left;">A1~A8</td><td style="text-align:left;">FMU PWM вивід. Може бути визначено як PWM/GPIO; підтримує dshot; використовується для підключення затвора камери/гарячого черевика, сервоприводу тощо.</td></tr><tr><td style="text-align:left;">USB</td><td style="text-align:left;">Під&#39;єднатися до комп&#39;ютера для зв&#39;язку між контролером польоту та комп&#39;ютером, наприклад, як завантаження прошивки.</td></tr><tr><td style="text-align:left;">CAN1/CAN2</td><td style="text-align:left;">Підключає пристрої Dronecan/UAVCAN, такі як NEO3 Pro.</td></tr><tr><td style="text-align:left;">DSM/SUB/RSSI</td><td style="text-align:left;">Включає інтерфейси введення сигналів DSM, SBUS, RSSI, інтерфейс DSM може бути підключений до супутникового приймача DSM, інтерфейс SBUS - до приймача дистанційного керування SBUS, RSSI - для модуля зворотного повернення сили сигналу</td></tr><tr><td style="text-align:left;">PPM</td><td style="text-align:left;">Підключення PPM RC Receiver</td></tr><tr><td style="text-align:left;">ETH</td><td style="text-align:left;">Інтерфейс Ethernet. Підключіть пристрої Ethernet, такі як комп&#39;ютери завдань</td></tr><tr><td style="text-align:left;">AD&amp;IO</td><td style="text-align:left;">Є два аналогових входи (ADC3.3/ADC6.6); зазвичай не використовуються</td></tr></tbody></table><h2 id="передніи-бампер" tabindex="-1">Передній бампер <a class="header-anchor" href="#передніи-бампер" aria-label="Permalink to &quot;Передній бампер&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Якщо контролер не може бути змонтований у рекомендованому/стандартному положенні (наприклад, через обмеження місця), вам потрібно буде налаштувати програмне забезпечення автопілота з орієнтацією, яку ви фактично використовували: <a href="./../config/flight_controller_orientation.html">Орієнтація контролера польоту</a>.</p></div><p><img src="'+a+'" alt="front"></p><h2 id="gps-компас-зумер-захиснии-вимикач-світлодіод" tabindex="-1">GPS + компас + зумер + захисний вимикач + світлодіод <a class="header-anchor" href="#gps-компас-зумер-захиснии-вимикач-світлодіод" aria-label="Permalink to &quot;GPS + компас + зумер + захисний вимикач + світлодіод&quot;">​</a></h2><p>Pixhawk<sup>®</sup> V6X можна придбати з GPS <a href="https://store.cuav.net/shop/neo-3/" target="_blank" rel="noreferrer">NEO3</a> (10-контактний роз&#39;єм) та слід підключити до порту <strong>GPS1</strong>. Ці модулі GNSS мають вбудований компас, безпечний перемикач, дзвіночок та світлодіод.</p><p>Якщо вам потрібно використовувати допоміжний GPS, підключіться до порту <strong>GPS2</strong>.</p><p>GPS/компас слід <a href="./../assembly/mount_gps_compass.html">монтувати на раму</a> якомога подалі від інших електронних пристроїв, з напрямком вперед транспортного засобу (відокремлення компаса від інших електронних пристроїв зменшить втручання).</p><p><img src="'+e+'" alt="GPS"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Pixhawk V6X<sup>®</sup> несумісний з NEO V2 GPS вбудованим дзвіночком: вам слід використовувати <a href="https://store.cuav.net/shop/neo-3/" target="_blank" rel="noreferrer">NEO3/NEO 3Pro</a> замість нього. Вбудований безпечний вимикач в GPS-модулі увімкнений <em>за замовчуванням</em> (коли включений, PX4 не дозволить вам готувати до польоту). Щоб вимкнути безпеку, натисніть і утримуйте безпечний вимикач протягом 1 секунди. Ви можете натиснути безпечний вимикач знову, щоб увімкнути безпеку та відключити транспортний засіб (це може бути корисно, якщо, з якихось причин, ви не можете вимкнути транспортний засіб за допомогою вашого пульта дистанційного керування або наземної станції).</p></div><h2 id="радіоуправління" tabindex="-1">Радіоуправління <a class="header-anchor" href="#радіоуправління" aria-label="Permalink to &quot;Радіоуправління&quot;">​</a></h2><p>Для того щоб керувати транспортним засобом <em>вручну</em>, потрібна система радіоуправління (RC) (PX4 не потребує системи радіоуправління для автономних режимів польоту).</p><p>Вам потрібно <a href="./../getting_started/rc_transmitter_receiver.html">вибрати сумісний передавач/приймач</a> і <em>зв&#39;язати</em> їх таким чином, щоб вони взаємодіяли (ознайомтеся з інструкціями, що додаються до вашого конкретного передавача/приймача).</p><ul><li>Приймачі Spektrum/DSM підключаються до входу <strong>DSM/SBUS</strong>.</li><li>Приймачі PPM підключаються до вхідного порту <strong>PPM</strong>.</li></ul><p>Для отримання додаткової інформації про вибір радіосистеми, сумісність приймача та зв&#39;язок вашої передавача/приймача, див. статтю: <a href="./../getting_started/rc_transmitter_receiver.html">Пульт керування передавачів &amp; приймачів</a>.</p><p><img src="'+r+'" alt="RC control"></p><h2 id="живлення" tabindex="-1">Живлення <a class="header-anchor" href="#живлення" aria-label="Permalink to &quot;Живлення&quot;">​</a></h2><p>Pixhawk V6X<sup>®</sup> обладнаний модулем CAN PMU lite, який підтримує літійну батарею від 3 до 14s. Підключіть 6-контактний роз&#39;єм модуля до інтерфейсу керування польотом <strong>Power C1</strong> або <strong>Power C2</strong>.</p><p><img src="'+l+'" alt="Power"></p><p>Порт живлення <em>Pixhawk V6X</em> отримує цифровий сигнал Dronecan від модуля живлення CAN PMU lite для напруги, поточного струму та даних про залишкову ємність батареї, лінія VCC повинна забезпечувати принаймні 3А постійного струму та за замовчуванням повинна бути 5,2 В. Нижчий напруга 5V все ще прийнятний, але не рекомендується.</p><h2 id="телеметріина-радіо-система" tabindex="-1">Телеметрійна (радіо) система <a class="header-anchor" href="#телеметріина-радіо-система" aria-label="Permalink to &quot;Телеметрійна (радіо) система&quot;">​</a></h2><p><a href="./../telemetry/">Телеметричні радіостанції</a> можуть використовуватися для зв&#39;язку та управління транспортним засобом у польоті з наземної станції (наприклад, ви можете направляти БПЛА до певної позиції або завантажувати нове завдання).</p><p>Бортову радіостанцію слід підключити до порту <strong>TELEM1</strong>/<strong>TELEM2</strong>/<strong>TELEM3</strong>, як показано нижче (у разі підключення до <strong>TELEM1</strong>, подальша конфігурація не потрібна). Інша радіостанція підключається до вашого комп&#39;ютера або мобільного пристрою наземної станції (зазвичай за допомогою USB).</p><p>Ви також можете придбати телеметричні радіо з <a href="https://store.cuav.net/uav-telemetry-module/" target="_blank" rel="noreferrer">магазину CUAV</a>.</p><p><img src="'+s+'" alt="Telemetry radios"></p><h2 id="sd-карта" tabindex="-1">SD-карта <a class="header-anchor" href="#sd-карта" aria-label="Permalink to &quot;SD-карта&quot;">​</a></h2><p>Картки SD настійно рекомендується, оскільки вони потрібні для <a href="./../getting_started/flight_reporting.html">запису та аналізу даних польоту</a>, для виконання завдань та використання апаратного засобу UAVCAN bus. Карта пам&#39;яті SD вже встановлена на Pixhawk V6X<sup>®</sup>, коли вона залишає фабрику.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Для отримання додаткової інформації див. <a href="./../getting_started/px4_basic_concepts.html#sd-cards-removable-memory">Основні концепції &gt; SD-карти (знімна пам&#39;ять)</a>.</p></div><h2 id="мотори-сервоприводи" tabindex="-1">Мотори/Сервоприводи <a class="header-anchor" href="#мотори-сервоприводи" aria-label="Permalink to &quot;Мотори/Сервоприводи&quot;">​</a></h2><p>Двигуни/сервоприводи підключаються до портів <strong>M1~M8</strong> (<strong>MAIN</strong>) та <strong>A1~A8</strong> (<strong>AUX</strong>) в порядку, вказаному для вашого транспортного засобу в <a href="./../airframes/airframe_reference.html">Airframe Reference</a>.</p><p><img src="'+i+'" alt="Motors"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p><strong>Основні</strong> виходи в ПЗ PX4 відображені на портах M1~M8 Pixhawk V6X (від IO), тоді як виходи <strong>AUX</strong> допоміжних функцій відображені на портах A1~A8 (від FMU). Наприклад, <strong>MAIN1</strong> відображається на вивід M1, а <strong>AUX1</strong> відображається на вивід A1. Цей довідник містить зіставлення портів виводу до моторів/сервоприводів для всіх підтримуваних повітряних та наземних шасі (якщо ваше шасі не вказане в довіднику, то використовуйте &quot;загальний&quot; планер відповідного типу).</p></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Відображення не є однорідним для всіх конструкцій (наприклад, ви не можете покладатися на те, що ручка газу буде на тому ж вихідному порту для всіх повітряних конструкцій). Переконайтеся, що ви використовуєте правильне відображення для вашого транспортного засобу.</p></div><h2 id="джерело-живлення-для-сервоприводу" tabindex="-1">Джерело живлення для сервоприводу <a class="header-anchor" href="#джерело-живлення-для-сервоприводу" aria-label="Permalink to &quot;Джерело живлення для сервоприводу&quot;">​</a></h2><p>Pixhawk V6X<sup>®</sup> не подає живлення на сервоприводи. Якщо використовується літак або ровер, до будь-якої з контактів живлення (+) в M1~M8/A1~A8 потрібно підключити зовнішній BEC (наприклад, ESC з BEC або окремий BEC на 5 В або акумулятор LiPo на 2S) для керування сервоприводами.</p><p><img src="'+o+'" alt="servo power supply"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Напруга шини живлення повинна бути відповідною для використаного сервоприводу!</p></div><h2 id="інші-периферіині-пристроі" tabindex="-1">Інші периферійні пристрої <a class="header-anchor" href="#інші-периферіині-пристроі" aria-label="Permalink to &quot;Інші периферійні пристрої&quot;">​</a></h2><p>Підключення та конфігурація додаткових/менш поширених компонентів описано в темах для окремих <a href="./../peripherals/">периферійних пристроїв</a>.</p><h2 id="розводка" tabindex="-1">Розводка <a class="header-anchor" href="#розводка" aria-label="Permalink to &quot;Розводка&quot;">​</a></h2><p><img src="'+n+'" alt="Pixhawk V6x Pinout"></p><h2 id="конфігурація" tabindex="-1">Конфігурація <a class="header-anchor" href="#конфігурація" aria-label="Permalink to &quot;Конфігурація&quot;">​</a></h2><p>Загальну інформацію про конфігурацію описано в: <a href="./../config/">Конфігурація автопілота</a>.</p><p>Конкретні конфігурації QuadPlane тут: <a href="./../config_vtol/vtol_quad_configuration.html">QuadPlane VTOL налаштування</a></p><h2 id="детальна-інформація" tabindex="-1">Детальна інформація <a class="header-anchor" href="#детальна-інформація" aria-label="Permalink to &quot;Детальна інформація&quot;">​</a></h2><ul><li><a href="https://doc.cuav.net/" target="_blank" rel="noreferrer">Документація CUAV</a> (CUAV)</li><li><a href="./../flight_controller/cuav_pixhawk_v6x.html">Pixhawk V6X</a> (Сторінка огляду документа PX4)</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-012%20Pixhawk%20Autopilot%20v6X%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Автопілот FMUv6X Стандартний</a></li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-010%20Pixhawk%20Autopilot%20Bus%20Standard.pdf" target="_blank" rel="noreferrer">Стандарт шини автопілота Pixhawk</a></li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Стандарт Pixhawk Connector</a></li></ul>',51),m=[f];function u(x,_,P,k,b,S){return p(),h("div",null,m)}const M=d(g,[["render",u]]);export{C as __pageData,M as default};
