import{_ as l,a}from"./chunks/durandal_pinouts_back.GY1z1TAI.js";import{_ as t,a as o,b as i,c as s,d as p,e as n,f as c,g as d}from"./chunks/durandal_sd_slot.BySQM1NL.js";import{_ as h,E as m,c as u,J as _,m as e,a as f,ab as P,o as b}from"./chunks/framework.CUflZczI.js";const C=JSON.parse('{"title":"Швидке підключення Durandal Wiring","description":"","frontmatter":{},"headers":[],"relativePath":"uk/assembly/quick_start_durandal.md","filePath":"uk/assembly/quick_start_durandal.md"}'),g={name:"uk/assembly/quick_start_durandal.md"},k=e("h1",{id:"швидке-підключення-durandal-wiring",tabindex:"-1"},[f("Швидке підключення Durandal Wiring "),e("a",{class:"header-anchor",href:"#швидке-підключення-durandal-wiring","aria-label":'Permalink to "Швидке підключення Durandal Wiring"'},"​")],-1),D=P('<div class="warning custom-block"><p class="custom-block-title">PX4 не виробляє цей (чи будь-який інший) автопілот. Зверніться до <a href="https://holybro.com/" target="_blank" rel="noreferrer">виробника</a> щодо підтримки апаратного забезпечення чи відповідності вимогам.</p></div><p>У цьому короткому посібнику показано, як увімкнути живлення польотного контролера Holybro <a href="./../flight_controller/durandal.html">Durandal</a><sup>®</sup> та підʼєднати до нього найважливіші периферійні пристрої.</p><p><img src="'+l+'" alt="Durandal"></p><h2 id="розпаковка" tabindex="-1">Розпаковка <a class="header-anchor" href="#розпаковка" aria-label="Permalink to &quot;Розпаковка&quot;">​</a></h2><p>Durandal продається в комплекті з різноманітними комбінаціями аксесуарів, включаючи модулі живлення: <em>PM02 V3</em> та <em>PM07</em>, та <em>GPS/Компас Pixhawk 4</em> (u-blox NEO-M8N).</p><p>Вміст коробки з модулем живлення <em>PM02 V3</em> показано нижче (у коробці також є посібник з роз&#39;ємами та інструкція з модулю живлення).</p><p><img src="'+t+'" alt="Durandal Box"></p><h2 id="огляд-схеми-підключення" tabindex="-1">Огляд схеми підключення <a class="header-anchor" href="#огляд-схеми-підключення" aria-label="Permalink to &quot;Огляд схеми підключення&quot;">​</a></h2><p>На зображенні нижче показано, як під&#39;єднати найважливіші датчики та периферійні пристрої (за винятком виходів мотора та сервоприводів). Ми розглянемо кожен з них детально в наступних розділах.</p><p><img src="'+o+'" alt="Durandal Wiring Overview"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Додаткову інформацію про доступні порти можна знайти тут: <a href="./../flight_controller/durandal.html#pinouts">Durandal &gt; Pinouts</a>.</p></div><h2 id="монтаж-та-орієнтація-контролера" tabindex="-1">Монтаж та орієнтація контролера <a class="header-anchor" href="#монтаж-та-орієнтація-контролера" aria-label="Permalink to &quot;Монтаж та орієнтація контролера&quot;">​</a></h2><p><em>Дюрандал</em> повинен бути встановлений на раму, розташований якомога ближче до центру ваги вашого апарату, орієнтований верхньою стороною вгору зі стрілкою, що вказує вперед апарату.</p><p><img src="'+i+'" alt="Mounting/Orientation"></p><p>Якщо контролер не може бути змонтований у рекомендованому/стандартному положенні (наприклад, через обмеження місця), вам потрібно буде налаштувати програмне забезпечення автопілота з орієнтацією, яку ви фактично використовували: <a href="./../config/flight_controller_orientation.html">Орієнтація контролера польоту</a>.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Плата має внутрішню вібраційну ізоляцію. Не використовуйте віброізоляційну пінку для монтажу контролера (подвійна стрічка на клейовій основі зазвичай достатня).</p></div><h2 id="gps-compass-buzzer-safety-switch-led" tabindex="-1">GPS + Compass + Buzzer + Safety Switch + LED <a class="header-anchor" href="#gps-compass-buzzer-safety-switch-led" aria-label="Permalink to &quot;GPS + Compass + Buzzer + Safety Switch + LED&quot;">​</a></h2><p>Durandal розроблений для успішної роботи з <em>модулем GPS Pixhawk 4</em>, який має вбудований компас, безпечний вимикач, сигналізацію, світлодіоди. Він підключається безпосередньо до <a href="./../flight_controller/durandal.html#gps">порту GPS</a> за допомогою 10-жильного кабелю.</p><p>GPS/компас слід монтувати на раму якомога подалі від інших електронних пристроїв, з напрямком вперед транспортного засобу (відокремлення компаса від інших електронних пристроїв зменшить втручання).</p><p><img src="'+s+'" alt="Connect compass/GPS to Durandal"></p><div class="info custom-block"><p class="custom-block-title">Вбудований безпечний вимикач в GPS-модулі увімкнений <em>за замовчуванням</em> (коли включений, PX4 не дозволить вам готувати до польоту). Щоб вимкнути безпеку, натисніть і утримуйте безпечний вимикач протягом 1 секунди. Ви можете натиснути безпечний вимикач знову, щоб увімкнути безпеку та відключити транспортний засіб (це може бути корисно, якщо, з якихось причин, ви не можете вимкнути транспортний засіб за допомогою вашого пульта дистанційного керування або наземної станції).</p></div><h2 id="потужність" tabindex="-1">Потужність <a class="header-anchor" href="#потужність" aria-label="Permalink to &quot;Потужність&quot;">​</a></h2><p>Ви можете використовувати модуль живлення або розподільник живлення для живлення двигунів/сервоприводів та виміру споживаної потужності. Рекомендовані модулі живлення показані нижче.</p><p><a id="pm02_v3"></a></p><h3 id="pm02d-power-module" tabindex="-1">PM02D Power Module <a class="header-anchor" href="#pm02d-power-module" aria-label="Permalink to &quot;PM02D Power Module&quot;">​</a></h3><p><a href="./../power_module/holybro_pm02.html">Модуль живлення (PM02 v3)</a> може бути поставлений разом з <em>Durandal</em>. Він надає регульоване живлення контролеру польоту та надсилає напругу/силу струму акумулятора контролеру польоту.</p><p>Підключіть вихід <em>Модуля живлення</em>, як показано.</p><p><img src="'+p+'" alt="Durandal PM02v3 Power connections"></p><ul><li>Порт напруги/струму PM: підключіться до <a href="./../flight_controller/durandal.html#power">POWER1</a> порту (або <code>POWER2</code>) за допомогою 6-жильного кабелю GH, що входить у комплект.</li><li>Вхід PM (роз&#39;єм XT60): підключіть до ліпо-акумулятора (2~12S).</li><li>Вихід живлення PM (роз&#39;єм XT60): підведіть до будь-якого контролера регулятора обертів мотора.</li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Оскільки цей модуль живлення не містить проводки розподілу живлення, ви зазвичай просто підключаєте всі контролери регуляторів обертів мотора паралельно до виходу модуля живлення (контролер регулятора обертів мотора повинен бути відповідним для постачаного рівня напруги).</p></div><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Контактна шина живлення (+) 8-контактного рейлу <strong>MAIN/AUX</strong> не живиться блоком живлення модуля живлення контролера польоту. Якщо вона повинна бути окремо живленою для керування сервоприводами для рульових поверхонь, елеронами тощо, лінію живлення потрібно підключити до ESC зі вбудованим BEC або окремого BEC напругою 5V або акумулятора LiPo 2S. Переконайтеся, що напруга сервопривода, який ви збираєтеся використовувати, є відповідною.</p></div><p>Модуль живлення має наступні характеристики/обмеження:</p><ul><li>Максимальна вхідна напруга: 60V</li><li>Максимальне вимірювання струму: 120A Voltage</li><li>Вимірювання струму, налаштоване для SV АЦП Перемикання виходів регулятора 5,2 В і 3А макс</li><li>Вага: 20г</li><li>Пакет включає: <ul><li>Плата PM02</li><li>6-контактний кабель MLX (1)</li><li>6pin GH cable (1)</li></ul></li></ul><p><a id="pm07"></a></p><h3 id="модуль-живлення-pixhawk-4-pm07" tabindex="-1">Модуль живлення Pixhawk 4 (PM07) <a class="header-anchor" href="#модуль-живлення-pixhawk-4-pm07" aria-label="Permalink to &quot;Модуль живлення Pixhawk 4 (PM07)&quot;">​</a></h3><p>Модуль живлення <a href="https://holybro.com/collections/power-modules-pdbs/products/pixhawk-4-power-module-pm07" target="_blank" rel="noreferrer">Pixhawk 4 (PM07)</a> може бути поставлений/використаний разом з <em>Durandal</em>. Це виступає як модуль живлення та розподільник живлення, який забезпечує регульоване живлення контролеру польоту та регуляторам швидкості, а також надсилає напругу/сили до контролера польоту.</p><p>Це підключено так само, як описано в документації <a href="./../assembly/quick_start_pixhawk4.html#power">Швидкий старт Pixhawk 4 &gt; Живлення</a>.</p><p>Має наступні характеристики/обмеження:</p><ul><li>Поточний PCB: загальна потужність виходів 120A (MAX)</li><li>UBEC вихідний струм 5В: 3А</li><li>Напруга входу UBEC: 7~51v (2~12s LiPo)</li><li>Розміри: 68<em>50</em>8 мм</li><li>Отвір для монтажу: 45*45мм</li><li>Вага: 36g</li><li>Пакет включає: <ul><li>Плата PM07 (1)</li><li>80мм коннекторія XT60 (1)</li></ul></li></ul><div class="info custom-block"><p class="custom-block-title">Див. також <a href="https://docs.holybro.com/power-module-and-pdb/power-module/pm07-quick-start-guide" target="_blank" rel="noreferrer">Керівництво зі швидкого запуску PM07</a> (Holybro).</p></div><h3 id="конфігурація-батареі" tabindex="-1">Конфігурація батареї <a class="header-anchor" href="#конфігурація-батареі" aria-label="Permalink to &quot;Конфігурація батареї&quot;">​</a></h3><p>Налаштування батареї/живлення потрібно налаштувати в <a href="./../config/battery.html">Налаштування оцінки батареї</a>. Для будь-якого модуля живлення вам потрібно буде налаштувати <em>кількість комірок</em>.</p><p>Вам не потрібно буде оновлювати <em>дільник напруги</em>, якщо ви використовуєте який-небудь інший модуль живлення (наприклад, той, що від Pixracer).</p><h2 id="радіоуправління" tabindex="-1">Радіоуправління <a class="header-anchor" href="#радіоуправління" aria-label="Permalink to &quot;Радіоуправління&quot;">​</a></h2><p>Для того щоб керувати транспортним засобом <em>вручну</em>, потрібна система радіоуправління (RC) (PX4 не потребує системи радіоуправління для автономних режимів польоту).</p><p>Вам потрібно <a href="./../getting_started/rc_transmitter_receiver.html">вибрати сумісний передавач/приймач</a> і <em>зв&#39;язати</em> їх таким чином, щоб вони взаємодіяли (ознайомтеся з інструкціями, що додаються до вашого конкретного передавача/приймача).</p><p>Нижче наведено інструкції, як підключити різні типи приймачів до <em>Durandal</em>:</p><ul><li><p>Приймачі Spektrum/DSM підключаються до входу <a href="./../flight_controller/durandal.html#dsm-rc-port">DSM RC</a>.</p><p><img src="'+n+'" alt="Durandal - DSM"></p></li><li><p>Приймачі PPM і S.Bus підключаються до входу <a href="./../flight_controller/durandal.html#rc-in">SBUS_IN/PPM_IN</a> (позначений як RC IN, поруч з головними/допоміжними входами).</p><p><img src="'+a+'" alt="Durandal - Back Pinouts (Schematic)"></p></li><li><p>Приймачі PPM та PWM, які мають <em>окремий провід для кожного каналу</em>, повинні підключатися до порту <strong>PPM RC</strong> <em>через PPM кодер</em> <a href="http://www.getfpv.com/radios/radio-accessories/holybro-ppm-encoder-module.html" target="_blank" rel="noreferrer">як цей</a> (приймачі PPM-Sum використовують один сигнальний провід для всіх каналів).</p></li></ul><p>Для отримання додаткової інформації про вибір радіосистеми, сумісність приймача та зв&#39;язок вашої передавача/приймача, див. статтю: <a href="./../getting_started/rc_transmitter_receiver.html">Пульт керування передавачів &amp; приймачів</a>.</p><h2 id="телеметричні-радіостанціі-опціонально" tabindex="-1">Телеметричні радіостанції (Опціонально) <a class="header-anchor" href="#телеметричні-радіостанціі-опціонально" aria-label="Permalink to &quot;Телеметричні радіостанції (Опціонально)&quot;">​</a></h2><p>Телеметрійні радіомодулі можуть використовуватися для зв&#39;язку та управління транспортним засобом в польоті з наземної станції (наприклад, ви можете направляти БПЛА в певне положення або завантажувати нове завдання).</p><p>Радіостанцію, розташовану на транспортному засобі, слід підключити до порту <a href="./../flight_controller/durandal.html#telem1_2_3">TELEM1</a> так, як показано нижче за допомогою одного з 6-позиційних роз&#39;ємів (якщо підключено до цього порту, додаткова конфігурація не потрібна). Інша радіостанція підключається до вашого комп&#39;ютера або мобільного пристрою наземної станції (зазвичай за допомогою USB).</p><p><img src="'+c+'" alt="Durandal/Telemetry Radio"></p><h2 id="sd-карта-опціонально" tabindex="-1">SD-карта (Опціонально) <a class="header-anchor" href="#sd-карта-опціонально" aria-label="Permalink to &quot;SD-карта (Опціонально)&quot;">​</a></h2><p>Картки SD настійно рекомендується, оскільки вони потрібні для <a href="./../getting_started/flight_reporting.html">запису та аналізу даних польоту</a>, для виконання місій та для використання апаратного засобу UAVCAN-bus. Вставте SD-карту в <em>Durandal</em> туди, де показано нижче.</p><p><img src="'+d+'" alt="Durandal SD Card"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Для додаткової інформації дивіться <a href="./../getting_started/px4_basic_concepts.html#sd-cards-removable-memory">Основні поради &gt; SD-карти (можливістю Видалення пам&#39;яті)</a>.</p></div><h2 id="двигуни" tabindex="-1">Двигуни <a class="header-anchor" href="#двигуни" aria-label="Permalink to &quot;Двигуни&quot;">​</a></h2><p>Сигнали керування двигунами/сервоприводами підключаються до портів <strong>I/O PWM OUT</strong> (<strong>MAIN OUT</strong>) та <strong>FMU PWM OUT</strong> (<strong>AUX</strong>) у порядку, вказаному для вашого апарату в <a href="./../airframes/airframe_reference.html">Airframe Reference</a>.</p><p><img src="'+a+'" alt="Durandal - Back Pinouts (Schematic)"></p><p>Двигуни повинні мати окреме <a href="#power">живлення</a>.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Якщо ваша конструкція не зазначена у довіднику конструкцій, скористайтеся &quot;загальною&quot; конструкцією потрібного типу.</p></div><div class="tip custom-block"><p class="custom-block-title">TIP</p><p><em>Durandal</em> має 5 допоміжних портів, тому не може бути використаний з повітряними рамами, які відображають AUX6, AUX7, AUX8 на двигуни або інші критичні керування польотом.</p></div><h2 id="інші-периферіині-пристроі" tabindex="-1">Інші периферійні пристрої <a class="header-anchor" href="#інші-периферіині-пристроі" aria-label="Permalink to &quot;Інші периферійні пристрої&quot;">​</a></h2><p>Підключення та конфігурація додаткових / менш поширених компонентів описано в темах для окремих <a href="./../peripherals/">периферійних пристроїв</a>.</p><h2 id="розпіновка" tabindex="-1">Розпіновка <a class="header-anchor" href="#розпіновка" aria-label="Permalink to &quot;Розпіновка&quot;">​</a></h2><p><a href="./../flight_controller/durandal.html#pinouts">Durandal &gt; Виводи</a></p><p><a id="configuration"></a></p><h2 id="конфігурація-px4" tabindex="-1">Конфігурація PX4 <a class="header-anchor" href="#конфігурація-px4" aria-label="Permalink to &quot;Конфігурація PX4&quot;">​</a></h2><p>Спочатку вам потрібно встановити <a href="./../config/firmware.html#custom">Прошивку PX4 &quot;Master&quot;</a> на контролер, використовуючи <em>QGroundControl</em>.</p><div class="info custom-block"><p class="custom-block-title">Підтримка Durandal буде в релізі PX4, який слідує за PX4 v1.10, який є <em>стабільним</em>.</p></div><p>Додаткову загальну інформацію щодо конфігурації описано в: <a href="./../config/">Конфігурація автопілота</a>.</p><p>Конкретні конфігурації QuadPlane тут: <a href="./../config_vtol/vtol_quad_configuration.html">QuadPlane VTOL налаштування</a></p><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="./../flight_controller/durandal.html">Огляд Durandal</a></li><li><a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Durandal_technical_data_sheet_90f8875d-8035-4632-a936-a0d178062077.pdf" target="_blank" rel="noreferrer">Технічний опис Durandal</a> (Holybro)</li><li><a href="https://holybro.com/collections/autopilot-flight-controllers/products/Durandal-Pinouts" target="_blank" rel="noreferrer">Durandal розпіновка</a> (Holybro)</li><li><a href="https://github.com/PX4/PX4-user_guide/raw/v1.15/assets/flight_controller/durandal/Durandal_MB_H743sch.pdf" target="_blank" rel="noreferrer">Durandal_MB_H743sch.pdf</a> (Схеми Durandal)</li><li><a href="https://github.com/PX4/PX4-user_guide/raw/v1.15/assets/flight_controller/durandal/STM32H743IIK_pinout.pdf" target="_blank" rel="noreferrer">STM32H743IIK_pinout.pdf</a> (Схема виводів Durandal)</li></ul>',75);function q(M,v,S,x,w,T){const r=m("Badge");return b(),u("div",null,[k,_(r,{type:"tip",text:"PX4 v1.11"}),D])}const I=h(g,[["render",q]]);export{C as __pageData,I as default};
