import{_ as t}from"./chunks/pixhawk4_logo_view.C3CsFYwh.js";import{_ as r,a,b as e,c as o,d as s,e as i}from"./chunks/pixhawk4_sd_card.D-bLSIq8.js";import{_ as d}from"./chunks/pixhawk4_compass_gps.BgeOJnqO.js";import{_ as l}from"./chunks/pixhawk4_power_management_board.BUEvWaxn.js";import{_ as h,c as n,o as m,ab as c}from"./chunks/framework.CUflZczI.js";const W=JSON.parse('{"title":"Швидкий старт підключення Pixhawk 4","description":"","frontmatter":{},"headers":[],"relativePath":"uk/assembly/quick_start_pixhawk4.md","filePath":"uk/assembly/quick_start_pixhawk4.md"}'),p={name:"uk/assembly/quick_start_pixhawk4.md"},P=c('<h1 id="швидкии-старт-підключення-pixhawk-4" tabindex="-1">Швидкий старт підключення Pixhawk 4 <a class="header-anchor" href="#швидкии-старт-підключення-pixhawk-4" aria-label="Permalink to &quot;Швидкий старт підключення Pixhawk 4&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">PX4 не займається виготовленням цього (чи якогось іншого) автопілоту. Зверніться до <a href="https://holybro.com/" target="_blank" rel="noreferrer">виробника</a> щодо підтримки апаратного забезпечення чи питань відповідності вимогам.</p></div><p>У цьому короткому посібнику показано, як увімкнути живлення польотного контролера <a href="./../flight_controller/pixhawk4.html">Pixhawk 6C</a>®<sup></sup> та підʼєднати до нього найважливіші периферійні пристрої.</p><img src="'+t+'" width="420px" title="Зображення Pixhawk4"><h2 id="огляд-схеми-підключення" tabindex="-1">Огляд схеми підключення <a class="header-anchor" href="#огляд-схеми-підключення" aria-label="Permalink to &quot;Огляд схеми підключення&quot;">​</a></h2><p>На зображенні нижче показано, як під&#39;єднати найважливіші датчики та периферійні пристрої (за винятком виходів мотора та сервоприводів). Ми розглянемо кожен з них докладно в наступних розділах.</p><p><img src="'+r+'" alt="Pixhawk 4 Wiring Overview"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Додаткову інформацію про доступні порти можна знайти тут: <a href="./../flight_controller/pixhawk4.html#connectors">Pixhawk 4 &gt; Connections</a>.</p></div><h2 id="монтаж-та-орієнтація-контролера" tabindex="-1">Монтаж та орієнтація контролера <a class="header-anchor" href="#монтаж-та-орієнтація-контролера" aria-label="Permalink to &quot;Монтаж та орієнтація контролера&quot;">​</a></h2><p><em>Pixhawk</em> необхідно змонтувати на раму разом з амортизаційними подушками проти вібрації (включені в комплект). Вона повинна розташовуватися якомога ближче до центру тяжіння вашого апарату верхньою стороною вгору зі стрілкою, що вказує в напрямку передньої частини апарату.</p><img src="'+a+'" align="center"><div class="info custom-block"><p class="custom-block-title">Якщо контролер не може бути змонтований у рекомендованому/стандартному положенні (наприклад, через обмеження місця), вам потрібно буде налаштувати програмне забезпечення автопілота з орієнтацією, яку ви фактично використовували: <a href="./../config/flight_controller_orientation.html">Орієнтація контролера польоту</a>.</p></div><h2 id="gps-компас-зумер-перемикач-безпеки-led" tabindex="-1">GPS + компас + зумер + перемикач безпеки + LED <a class="header-anchor" href="#gps-компас-зумер-перемикач-безпеки-led" aria-label="Permalink to &quot;GPS + компас + зумер + перемикач безпеки + LED&quot;">​</a></h2><p>Підключіть наданий GPS з інтегрованим компасом, безпечним перемикачем, піщальцем та світлодіодом до порту <strong>МОДУЛЬ GPS</strong>.</p><p>GPS/компас повинен бути <a href="./../assembly/mount_gps_compass.html">закріплений на рамі</a> якомога далі від іншої електроніки, так, щоб маркер напрямку був спрямований до передньої частини апарату (відокремлення компаса від іншої електроніки зменшить радіоперешкоди).</p><p><img src="'+d+'" alt="Connect compass/GPS to Pixhawk 4"></p><div class="info custom-block"><p class="custom-block-title">Вбудований перемикач безпеки GPS-модуля за <em>замовчуванням</em> увімкнений (коли він увімкнений, PX4 не дозволить вам привести апарат в працездатний стан). Щоб вимкнути безпеку, натисніть і утримуйте безпечний вимикач протягом 1 секунди. Ви можете натиснути безпечний вимикач знову, щоб увімкнути безпеку та відключити транспортний засіб (це може бути корисно, якщо, з якихось причин, ви не можете вимкнути транспортний засіб за допомогою вашого пульта дистанційного керування або наземної станції).</p></div><h2 id="живлення" tabindex="-1">Живлення <a class="header-anchor" href="#живлення" aria-label="Permalink to &quot;Живлення&quot;">​</a></h2><p>Підключіть вихід <em>плати управління живленням</em> (PM board), яка постачається в комплекті, до одного з кирпичів <strong>POWER</strong> <em>Pixhawk 4</em> за допомогою 6-провідного кабелю. Вхід PM <strong>2~12S</strong> буде підключений до вашої LiPo батареї. Підключення плати управління живленням, включаючи живлення та сигнальні з&#39;єднання з ESC та сервоприводами, пояснені в таблиці нижче. Зверніть увагу, що плата PM не постачає живлення до сервоприводів через контакти + та - <strong>FMU PWM-OUT</strong>.</p><p>На зображенні нижче показана плата управління живленням, яка постачається разом з <em>Pixhawk 4</em>.</p><p><img src="'+l+'" alt="Pixhawk 4 - Power Management Board"></p><div class="info custom-block"><p class="custom-block-title">Якщо використовується літак або ровер, 8-контактна шина живлення (+) <strong>FMU PWM-OUT</strong> повинна бути окремо живлена для керування сервоприводами для рульових пристроїв, елеронами тощо. Щоб це зробити, живильну рейку потрібно підключити до ESC з BEC або автономного BEC на 5V або 2S LiPo акумулятора. Будьте обережні з напругою сервопривода, який ви збираєтеся використовувати тут.</p></div><table><thead><tr><th>PIN&amp;Connector</th><th>Функція</th></tr></thead><tbody><tr><td>I/O PWM-IN</td><td>Дивіться примітку нижче для підключення до <em>Pixhawk 4</em></td></tr><tr><td>M1</td><td>I/O PWM OUT 1: підключіть дрот сигналу до ESC мотору 1 тут</td></tr><tr><td>M2</td><td>I/O PWM OUT 2: підключіть сигнальний провід до ESC двигуна 2 тут</td></tr><tr><td>M3</td><td>I/O PWM OUT 3: підключіть дрот сигналу до ESC мотору 3 тут</td></tr><tr><td>M4</td><td>I/O PWM OUT 4: підключіть дрот сигналу до ESC мотору 4 тут</td></tr><tr><td>M5</td><td>I/O PWM OUT 5: підключіть дрот сигналу до ESC мотору 5 тут</td></tr><tr><td>M6</td><td>I/O PWM OUT 6: підключіть дрот сигналу до ESC мотору 6 тут</td></tr><tr><td>M7</td><td>I/O PWM OUT 7: підключіть дрот сигналу до ESC мотору 7 тут</td></tr><tr><td>M8</td><td>I/O PWM OUT 8: підключіть дрот сигналу до ESC мотору 8 тут</td></tr><tr><td>FMU PWM-IN</td><td>Дивіться примітку нижче для підключення до <em>Pixhawk 4</em></td></tr><tr><td>FMU PWM-OUT</td><td>Якщо FMU PWM-IN підключено до <em>Pixhawk 4</em>, підключіть сигнальні проводи до ESC або сигналу, +, - проводи до сервоприводів тут</td></tr><tr><td>CAP&amp;ADC-OUT</td><td>підключіться до порту CAP &amp; ADC IN <em>Pixhawk 4</em></td></tr><tr><td>CAP&amp;ADC-IN</td><td>Вхід CAP &amp; ADC: Pinouts надруковані ззаду дошки</td></tr><tr><td>B+</td><td>підключіться до ESC B+, щоб живити ESC</td></tr><tr><td>GND</td><td>підключіться до землі ESC</td></tr><tr><td>PWR1</td><td>5v вихід 3A, підключення до <em>Pixhawk 4</em> POWER 1</td></tr><tr><td>PWR2</td><td>5в вихід 3A, підключити до <em>Pixhawk 4</em> POWER 2</td></tr><tr><td>2~12S</td><td>Живлення, підключіть до акумулятора LiPo ~12s</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">Залежно від типу вашої авіаструктури, зверніться до <a href="./../airframes/airframe_reference.html">Довідника з авіаструктур</a>, щоб підключити порти <strong>I/O PWM OUT</strong> та <strong>FMU PWM OUT</strong> <em>Pixhawk 4</em> до плати PM. <strong>MAIN</strong> виходи у прошивці PX4 призначені для порту <strong>I/O PWM OUT</strong> на <em>Pixhawk 4</em>, тоді як виходи <strong>AUX</strong> призначені для порту <strong>FMU PWM OUT</strong> на <em>Pixhawk 4</em>. Наприклад, <strong>MAIN1</strong> відповідає виводу IO_CH1 <strong>I/O PWM OUT</strong>, а <strong>AUX1</strong> відповідає виводу FMU_CH1 <strong>FMU PWM OUT</strong>. <strong>FMU PWM-IN</strong> плати PM внутрішньо підключений до <strong>FMU PWM-OUT</strong>. <strong>Вхід I/O PWM</strong> плати PM внутрішньо підключений до <strong>M1-8</strong>.</p></div><p>Наступна таблиця узагальнює, як підключити PWM OUT порти <em>Pixhawk 4</em> до PWM-IN портів PM плати, залежно від посилання на конструкцію повітряного судна.</p><table><thead><tr><th>Довідник зі структури літальних апаратів</th><th>З&#39;єднання між <em>Pixhawk 4</em> -- &gt; PM плата</th></tr></thead><tbody><tr><td><strong>MAIN</strong>: motor</td><td>I/O PWM OUT --&gt; I/O PWM IN</td></tr><tr><td><strong>MAIN</strong>: servo</td><td>I/O PWM OUT --&gt; FMU PWM IN</td></tr><tr><td><strong>AUX</strong>: motor</td><td>FMU PWM OUT --&gt; I/O PWM IN</td></tr><tr><td><strong>AUX</strong>: servo</td><td>FMU PWM OUT --&gt; FMU PWM IN</td></tr></tbody></table><p>Схема роз&#39;ємів <em>Pixhawk 4</em> для підключення живлення показана нижче. Сигнал CURRENT повинен переносити аналогове напругу від 0-3.3V для 0-120A за замовчуванням. Сигнал VOLTAGE повинен переносити аналогове напругу від 0-3.3V для 0-60V за замовчуванням. Лінії VCC повинні пропонувати принаймні 3A безперервного струму і за замовчуванням повинні мати напругу 5,1 В. Нижчий напруга 5V все ще прийнятний, але не рекомендується.</p><table><thead><tr><th>Pin</th><th>Сигнал</th><th>Volt</th></tr></thead><tbody><tr><td>1(red)</td><td>VCC</td><td>+5V</td></tr><tr><td>2(black)</td><td>VCC</td><td>+5V</td></tr><tr><td>3(black)</td><td>CURRENT</td><td>+3.3V</td></tr><tr><td>4(black)</td><td>VOLTAGE</td><td>+3.3V</td></tr><tr><td>5(black)</td><td>GND</td><td>GND</td></tr><tr><td>6(black)</td><td>GND</td><td>GND</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">При використанні модуля живлення, який постачається з комплектом, вам потрібно налаштувати <em>Кількість елементів</em> у <a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/setup_view/power.html" target="_blank" rel="noreferrer">Налаштуваннях живлення</a>, але вам не потрібно калібрувати <em>подільник напруги</em>. Вам доведеться оновити <em>дільник напруги</em>, якщо ви використовуєте будь-який інший модуль живлення (наприклад, той з Pixracer).</p></div><h2 id="радіоуправління" tabindex="-1">Радіоуправління <a class="header-anchor" href="#радіоуправління" aria-label="Permalink to &quot;Радіоуправління&quot;">​</a></h2><p>Для того щоб керувати транспортним засобом <em>вручну</em>, потрібна система радіоуправління (RC) (PX4 не потребує системи радіоуправління для автономних режимів польоту).</p><p>Вам потрібно <a href="./../getting_started/rc_transmitter_receiver.html">вибрати сумісний передавач/приймач</a> і <em>зв&#39;язати</em> їх таким чином, щоб вони взаємодіяли (ознайомтеся з інструкціями, що додаються до вашого конкретного передавача/приймача).</p><p>Нижче наведено інструкції, як підключити різні типи приймачів до <em>Pixhawk 4</em>:</p><ul><li><p>Приймачі Spektrum/DSM або S.BUS підключаються до входу <strong>DSM/SBUS RC</strong>.</p><p><img src="'+e+'" alt="Pixhawk 4 - Radio port for Spektrum receivers"></p></li><li><p>Приймачі PPM підключаються до вхідного порту <strong>PPM RC</strong>.</p><p><img src="'+o+'" alt="Pixhawk 4 - Radio port for PPM receivers"></p></li><li><p>Приймачі PPM та PWM, які мають <em>окремий провід для кожного каналу</em>, повинні підключатися до порту <strong>PPM RC</strong> <em>через PPM кодер</em> <a href="http://www.getfpv.com/radios/radio-accessories/holybro-ppm-encoder-module.html" target="_blank" rel="noreferrer">як цей</a> (приймачі PPM-Sum використовують один сигнальний провід для всіх каналів).</p></li></ul><p>Для отримання додаткової інформації про вибір радіосистеми, сумісність приймача та налаштування пари передавач/приймач див.: <a href="./../getting_started/rc_transmitter_receiver.html">Пульт керування передавачів&amp; приймачів</a>.</p><h2 id="телеметричні-радіостанціі-опціонально" tabindex="-1">Телеметричні радіостанції (Опціонально) <a class="header-anchor" href="#телеметричні-радіостанціі-опціонально" aria-label="Permalink to &quot;Телеметричні радіостанції (Опціонально)&quot;">​</a></h2><p>Телеметричні радіостанції можуть використовуватися для зв&#39;язку та управління транспортним засобом у польоті з наземної станції (наприклад, ви можете направляти БПЛА до певної позиції або завантажувати нове завдання).</p><p>Радіостанцію, розташовану на транспортному засобі, слід підключити до порту <strong>TELEM1</strong> так, як показано нижче (якщо підключено до цього порту, додаткова конфігурація не потрібна). Інша радіостанція підключається до комп&#39;ютера або мобільного пристрою наземної станції (зазвичай через USB).</p><p><img src="'+s+'" alt="Pixhawk 4/Telemetry Radio"></p><p><a id="sd_card"></a></p><h2 id="sd-card-optional" tabindex="-1">SD Card (Optional) <a class="header-anchor" href="#sd-card-optional" aria-label="Permalink to &quot;SD Card (Optional)&quot;">​</a></h2><p>Картки SD настійно рекомендується, оскільки вони потрібні для <a href="./../getting_started/flight_reporting.html">запису та аналізу даних польоту</a>, для виконання місій та для використання апаратного засобу UAVCAN-bus. Вставте картку (з комплекту Pixhawk 4) в <em>Pixhawk 4</em>, як показано нижче.</p><p><img src="'+i+'" alt="Pixhawk 4/SD Card"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Для отримання додаткової інформації див. <a href="./../getting_started/px4_basic_concepts.html#sd-cards-removable-memory">Основні концепції &gt; SD-карти (знімна пам&#39;ять)</a>.</p></div><h2 id="мотори" tabindex="-1">Мотори <a class="header-anchor" href="#мотори" aria-label="Permalink to &quot;Мотори&quot;">​</a></h2><p>Мотори/сервоприводи підключені до портів <strong>I/O PWM OUT</strong> (<strong>MAIN</strong>) та <strong>FMU PWM OUT</strong> (<strong>AUX</strong>) в порядку, вказаному для вашого апарату в <a href="./../airframes/airframe_reference.html">Довіднику планерів</a>.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Цей довідник перелічує протокол виводу порту до автора/servo зіставлення всіх підтримуваних повітря і наземних кадрів (якщо кадр відсутній, використовуйте блок &quot;типової&quot; рамки правильного типу).</p></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Відображення не є однорідним для всіх конструкцій (наприклад, ви не можете покладатися на те, що ручка газу буде на тому ж вихідному порту для всіх повітряних конструкцій). Переконайтеся, що ви використовуєте правильне відображення для вашого транспортного засобу.</p></div><h2 id="інші-периферіині-пристроі" tabindex="-1">Інші периферійні пристрої <a class="header-anchor" href="#інші-периферіині-пристроі" aria-label="Permalink to &quot;Інші периферійні пристрої&quot;">​</a></h2><p>Підключення та конфігурація додаткових/менш поширених компонентів описано в темах для окремих <a href="./../peripherals/">периферійних пристроїв</a>.</p><h2 id="розпіновка" tabindex="-1">Розпіновка <a class="header-anchor" href="#розпіновка" aria-label="Permalink to &quot;Розпіновка&quot;">​</a></h2><p><a href="https://holybro.com/manual/Pixhawk4-Pinouts.pdf" target="_blank" rel="noreferrer">Розводка Pixhawk 4</a> (Holybro)</p><h2 id="конфігурація" tabindex="-1">Конфігурація <a class="header-anchor" href="#конфігурація" aria-label="Permalink to &quot;Конфігурація&quot;">​</a></h2><p>Загальну інформацію про конфігурацію описано в: <a href="./../config/">Конфігурація автопілота</a>.</p><p>Конфігурація для QuadPlane описана тут: <a href="./../config_vtol/vtol_quad_configuration.html">Конфігурація QuadPlane VTOL</a></p><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="./../flight_controller/pixhawk4.html">Pixhawk 4</a> (сторінка огляду)</li><li><a href="https://github.com/PX4/PX4-user_guide/raw/v1.15/assets/flight_controller/pixhawk4/pixhawk4_technical_data_sheet.pdf" target="_blank" rel="noreferrer">Технічна інформація по Pixhawk 4</a></li><li><a href="https://holybro.com/manual/Pixhawk4-Pinouts.pdf" target="_blank" rel="noreferrer">Розводка Pixhawk 4</a> (Holybro)</li><li><a href="https://holybro.com/manual/Pixhawk4-quickstartguide.pdf" target="_blank" rel="noreferrer">Посібник з швидкого запуску Pixhawk 4 (Holybro)</a></li></ul>',57),g=[P];function k(_,u,b,M,f,x){return m(),n("div",null,g)}const I=h(p,[["render",k]]);export{W as __pageData,I as default};
