import{_ as t}from"./chunks/pixhawk4mini_iso_1.BZjVrNct.js";import{_ as a,a as i,b as e,c as r,d as o,e as s,f as l,g as c}from"./chunks/pixhawk4mini_sdcard.pyPEgePJ.js";import{_ as m,c as d,o as h,ab as n}from"./chunks/framework.CUflZczI.js";const v=JSON.parse('{"title":"Pixhawk 4 Mini Швидке підключення","description":"","frontmatter":{},"headers":[],"relativePath":"uk/assembly/quick_start_pixhawk4_mini.md","filePath":"uk/assembly/quick_start_pixhawk4_mini.md"}'),p={name:"uk/assembly/quick_start_pixhawk4_mini.md"},_=n('<h1 id="pixhawk-4-mini-швидке-підключення" tabindex="-1"><em>Pixhawk 4 Mini</em> Швидке підключення <a class="header-anchor" href="#pixhawk-4-mini-швидке-підключення" aria-label="Permalink to &quot;_Pixhawk 4 Mini_ Швидке підключення&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">PX4 не виробляє цей (чи будь-який) автопілот. Зверніться до <a href="https://holybro.com/" target="_blank" rel="noreferrer">виробника</a> щодо підтримки апаратного забезпечення чи відповідності вимогам.</p></div><p>Цей швидкий старт показує, як живити автопілот <a href="./../flight_controller/pixhawk4_mini.html"><em>Pixhawk<sup>®</sup> 4 Mini</em></a> та підключити його найважливіші периферійні пристрої.</p><p><img src="'+t+'" alt="Зображення Pixhawk4 mini"></p><h2 id="загальнии-огляд-проводки" tabindex="-1">Загальний огляд проводки <a class="header-anchor" href="#загальнии-огляд-проводки" aria-label="Permalink to &quot;Загальний огляд проводки&quot;">​</a></h2><p>На зображенні нижче показано, куди підключити найважливіші датчики та периферійні пристрої (за винятком моторів та сервоприводів).</p><p><img src="'+a+'" alt="Огляд підключення Pixhawk 4 Mini"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Додаткову інформацію про доступні порти можна знайти тут: <a href="./../flight_controller/pixhawk4_mini.html#interfaces"><em>Pixhawk 4 Mini</em> &gt; Interfaces</a>.</p></div><h2 id="монтаж-та-орієнтація-контролера" tabindex="-1">Монтаж та орієнтація контролера <a class="header-anchor" href="#монтаж-та-орієнтація-контролера" aria-label="Permalink to &quot;Монтаж та орієнтація контролера&quot;">​</a></h2><p><em>Pixhawk 4 Mini</em> повинен бути змонтований на вашу раму за допомогою амортизаційних підушок проти вібрації (включені в комплект). Вона повинна бути розташувана якомога ближче до центру тяжіння вашого автомобіля верхньою стороною вгору зі стрілкою, що вказує в напрямку передньої частини автомобіля.</p><p><img src="'+i+'" alt="Орієнтація Pixhawk 4 Mini"></p><div class="info custom-block"><p class="custom-block-title">Якщо контролер не може бути змонтований у рекомендованому/стандартному положенні (наприклад, через обмеження місця), вам потрібно буде налаштувати програмне забезпечення автопілота з орієнтацією, яку ви фактично використовували: <a href="./../config/flight_controller_orientation.html">Орієнтація контролера польоту</a>.</p></div><h2 id="gps-компас-зумер-захиснии-вимикач-світлодіод" tabindex="-1">GPS + компас + зумер + захисний вимикач + світлодіод <a class="header-anchor" href="#gps-компас-зумер-захиснии-вимикач-світлодіод" aria-label="Permalink to &quot;GPS + компас + зумер + захисний вимикач + світлодіод&quot;">​</a></h2><p>Підключіть наданий GPS з інтегрованим компасом, безпечним перемикачем, піщальцем та світлодіодом до порту <strong>МОДУЛЬ GPS</strong>. GPS/компас слід <a href="./../assembly/mount_gps_compass.html">монтувати на раму</a> якомога подалі від інших електронних пристроїв, з напрямком вперед транспортного засобу (відокремлення компаса від інших електронних пристроїв зменшить втручання).</p><p><img src="'+e+'" alt="Підключіть компас / GPS до Pixhawk 4"></p><div class="info custom-block"><p class="custom-block-title">Вбудований безпечний вимикач в GPS-модулі увімкнений <em>за замовчуванням</em> (коли включений, PX4 не дозволить вам готувати до польоту). Щоб вимкнути безпеку, натисніть і утримуйте безпечний вимикач протягом 1 секунди. Ви можете натиснути безпечний вимикач знову, щоб увімкнути безпеку та відключити транспортний засіб (це може бути корисно, якщо, з якихось причин, ви не можете вимкнути транспортний засіб за допомогою вашого пульта дистанційного керування або наземної станції).</p></div><h2 id="потужність" tabindex="-1">Потужність <a class="header-anchor" href="#потужність" aria-label="Permalink to &quot;Потужність&quot;">​</a></h2><p>Плата управління живленням (PMB) виконує функції блоку живлення та розподільчої плати живлення. Крім надання регульованого живлення <em>Pixhawk 4 Mini</em> та ESC, вона надсилає інформацію автопілоту про напругу батареї та поточний струм, який подається на керуючий пристрій польоту та двигуни.</p><p>Підключіть вихід PMB, що постачається в комплекті, до порту <strong>POWER</strong> <em>Pixhawk 4 Mini</em> за допомогою 6-жильного кабелю. Підключення PMB, включаючи живлення та сигнальні з&#39;єднання з ESC та сервоприводами, пояснені на зображенні нижче.</p><p><img src="'+r+'" alt="Pixhawk 4 - Плата управління живленням"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Вище показано лише з&#39;єднання одного ESC і одного сервопривода. Підключіть інші ESC та сервоприводи аналогічно.</p></div><table><thead><tr><th>Pin(s) або роз&#39;єм</th><th>Функція</th></tr></thead><tbody><tr><td>B+</td><td>Підключіться до ESC B+, щоб живити ESC</td></tr><tr><td>GND</td><td>Підключіться до землі ESC</td></tr><tr><td>PWR</td><td>Роз&#39;єм JST-GH 6-pin, вихід 5V 3A<br> підключити до живлення <em>Pixhawk 4 Mini</em></td></tr><tr><td>BAT</td><td>Живлення, підключіть до акумулятора LiPo 2~12s</td></tr></tbody></table><p>Схема роз&#39;ємів <em>Pixhawk 4 Mini</em> для підключення <strong>живлення</strong> показана нижче. Сигнал <code>CURRENT</code> повинен переносити аналогове напругу від 0 до 3,3 В для значення від 0 до 120 А за замовчуванням. Сигнал <code>VOLTAGE</code> повинен переносити аналогове напругу від 0-3.3V для 0-60V за замовчуванням. Лінії VCC повинні пропонувати принаймні 3A безперервного струму і за замовчуванням повинні мати напругу 5,1 В. Нижчий напруга 5V все ще прийнятний, але не рекомендується.</p><table><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1(red)</td><td>VCC</td><td>+5V</td></tr><tr><td>2(black)</td><td>VCC</td><td>+5V</td></tr><tr><td>3(black)</td><td>CURRENT</td><td>+3.3V</td></tr><tr><td>4(black)</td><td>VOLTAGE</td><td>+3.3V</td></tr><tr><td>5(black)</td><td>GND</td><td>GND</td></tr><tr><td>6(black)</td><td>GND</td><td>GND</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">Якщо використовується літак або ровер, рейка живлення (+) з 8 контактами <strong>MAIN OUT</strong> повинна бути окремо живлена для керування сервоприводами для рульових пристроїв, елеронами тощо. Щоб це зробити, живильну рейку потрібно підключити до ESC з BEC, автономного BEC на 5V або 2S LiPo акумулятора. Будьте обережні з напругою сервопривода, який ви збираєтеся використовувати тут.</p></div><div class="info custom-block"><p class="custom-block-title">При використанні модуля живлення, який постачається з комплектом, вам потрібно налаштувати <em>Кількість елементів</em> у <a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/setup_view/power.html" target="_blank" rel="noreferrer">Налаштуваннях живлення</a>, але вам не потрібно калібрувати <em>подільник напруги</em>. Вам доведеться оновити <em>дільник напруги</em>, якщо ви використовуєте будь-який інший модуль живлення (наприклад, той, що від Pixracer).</p></div><h2 id="радіокерування-rc" tabindex="-1">Радіокерування (RC) <a class="header-anchor" href="#радіокерування-rc" aria-label="Permalink to &quot;Радіокерування (RC)&quot;">​</a></h2><p>Для того щоб керувати транспортним засобом <em>вручну</em>, потрібна система радіоуправління (RC) (PX4 не потребує системи радіоуправління для автономних режимів польоту).</p><p>Вам потрібно <a href="./../getting_started/rc_transmitter_receiver.html">вибрати сумісний передавач/приймач</a> і <em>зв&#39;язати</em> їх таким чином, щоб вони взаємодіяли (ознайомтеся з інструкціями, що додаються до вашого конкретного передавача/приймача).</p><p>Нижче наведено інструкції з підключення різних типів приймачів до <em>Pixhawk 4 Mini</em>:</p><ul><li><p>Приймачі Spektrum/DSM або S.BUS підключаються до входу <strong>DSM/SBUS RC</strong>.</p><p><img src="'+o+'" alt="Pixhawk 4 Mini - Радіопорт для приймачів Spektrum"></p></li><li><p>PPM приймачі підключаються до порту входу <strong>PPM RC</strong>.</p><p><img src="'+s+'" alt="Pixhawk 4 Mini - Радіопорт для PPM-приймачів"></p></li><li><p>Приймачі PPM та PWM, які мають <em>окремий провід для кожного каналу</em>, повинні підключатися до порту <strong>PPM RC</strong> <em>через PPM кодер</em> <a href="http://www.getfpv.com/radios/radio-accessories/holybro-ppm-encoder-module.html" target="_blank" rel="noreferrer">як цей</a> (приймачі PPM-Sum використовують один сигнальний провід для всіх каналів).</p></li></ul><p>Для отримання додаткової інформації про вибір системи радіо, сумісність приймачів та зв&#39;язування вашої пари передавача/приймача дивіться: <a href="./../getting_started/rc_transmitter_receiver.html">Remote Control Transmitters &amp; Receivers</a>.</p><h2 id="телеметріине-радіо-опціонально" tabindex="-1">Телеметрійне радіо (Опціонально) <a class="header-anchor" href="#телеметріине-радіо-опціонально" aria-label="Permalink to &quot;Телеметрійне радіо (Опціонально)&quot;">​</a></h2><p>Телеметрійні радіомодулі можуть використовуватися для зв&#39;язку та управління транспортним засобом в польоті з наземної станції (наприклад, ви можете направляти БПЛА в певне положення або завантажувати нове завдання).</p><p>Радіостанцію, розташовану на транспортному засобі, слід підключити до порту <strong>TELEM1</strong> так, як показано нижче (якщо підключено до цього порту, додаткова конфігурація не потрібна). Інша радіостанція підключається до вашого комп&#39;ютера або мобільного пристрою наземної станції (зазвичай за допомогою USB).</p><p><img src="'+l+'" alt="Телеметрія Pixhawk 4 Mini"></p><h2 id="microsd-карта-опціонально" tabindex="-1">microSD-карта (Опціонально) <a class="header-anchor" href="#microsd-карта-опціонально" aria-label="Permalink to &quot;microSD-карта (Опціонально)&quot;">​</a></h2><p>Картки SD настійно рекомендується, оскільки вони потрібні для <a href="./../getting_started/flight_reporting.html">запису та аналізу даних польоту</a>, для виконання місій та для використання апаратного засобу UAVCAN-bus. Вставте картку (включену в комплект) в <em>Pixhawk 4 Mini</em>, як показано нижче.</p><p><img src="'+c+'" alt="Підключення Pixhawk 4 Mini SD Card"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Для отримання додаткової інформації див. <a href="./../getting_started/px4_basic_concepts.html#sd-cards-removable-memory">Основні концепції &gt; SD-карти (знімна пам&#39;ять)</a>.</p></div><h2 id="двигуни" tabindex="-1">Двигуни <a class="header-anchor" href="#двигуни" aria-label="Permalink to &quot;Двигуни&quot;">​</a></h2><p>Мотори/сервоприводи підключені до портів <strong>MAIN OUT</strong> в порядку, вказаному для вашого апарату в <a href="./../airframes/airframe_reference.html">Довіднику планерів</a>. Дивіться <a href="./../flight_controller/pixhawk4_mini.html#supported-platforms"><em>Pixhawk 4 Mini</em> &gt; Supported Platforms</a> для отримання додаткової інформації.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Цей довідник містить зіставлення портів виводу до моторів/сервоприводів для всіх підтримуваних повітряних та наземних шасі (якщо ваше шасі не вказане в довіднику, то використовуйте &quot;загальний&quot; планер відповідного типу).</p></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Відображення не є однорідним для всіх конструкцій (наприклад, ви не можете покладатися на те, що ручка газу буде на тому ж вихідному порту для всіх повітряних конструкцій). Переконайтеся, що ви використовуєте правильне відображення для вашого транспортного засобу.</p></div><h2 id="інші-периферіині-пристроі" tabindex="-1">Інші периферійні пристрої <a class="header-anchor" href="#інші-периферіині-пристроі" aria-label="Permalink to &quot;Інші периферійні пристрої&quot;">​</a></h2><p>Підключення та конфігурація додаткових/менш поширених компонентів описано в темах для окремих <a href="./../peripherals/">периферійних пристроїв</a>.</p><h2 id="конфігурація" tabindex="-1">Конфігурація <a class="header-anchor" href="#конфігурація" aria-label="Permalink to &quot;Конфігурація&quot;">​</a></h2><p>Загальна інформація про конфігурацію описана в: <a href="./../config/">Конфігурація автопілота</a>.</p><p>Особливу конфігурацію QuadPlane описано тут: <a href="./../config_vtol/vtol_quad_configuration.html">Конфігурація QuadPlane VTOL</a></p><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="./../flight_controller/pixhawk4_mini.html"><em>Підключення Pixhawk 4 Mini</em></a></li></ul>',51),k=[_];function u(P,b,f,g,x,w){return h(),d("div",null,k)}const C=m(p,[["render",u]]);export{v as __pageData,C as default};
