import{_ as t,a}from"./chunks/pixhawk5x_vehicle_front1.EICglI2P.js";import{_ as r,a as e}from"./chunks/pixhawk5x_pinout.DDhxjRJP.js";import{_ as o}from"./chunks/pixhawk5x_gps_front.BJK3Dtiq.js";import{_ as i}from"./chunks/pixhawk5x_sd_slot.Cr4YwYis.js";import{_ as s,c as l,o as h,ab as n}from"./chunks/framework.CUflZczI.js";const q=JSON.parse('{"title":"Швидке підключення Holybro Pixhawk 5x","description":"","frontmatter":{},"headers":[],"relativePath":"uk/assembly/quick_start_pixhawk5x.md","filePath":"uk/assembly/quick_start_pixhawk5x.md"}'),p={name:"uk/assembly/quick_start_pixhawk5x.md"},d=n('<h1 id="швидке-підключення-holybro-pixhawk-5x" tabindex="-1">Швидке підключення Holybro Pixhawk 5x <a class="header-anchor" href="#швидке-підключення-holybro-pixhawk-5x" aria-label="Permalink to &quot;Швидке підключення Holybro Pixhawk 5x&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">PX4 не виробляє цей (або будь-який інший) автопілот. Зверніться до <a href="https://holybro.com/" target="_blank" rel="noreferrer">виробника</a> щодо підтримки апаратного забезпечення чи відповідності вимогам.</p></div><p>Цей швидкий старт показує, як живити польовий контролер <a href="./../flight_controller/pixhawk5x.html">Pixhawk<sup>®</sup> 5X</a> та підключити його найважливіші периферійні пристрої.</p><img src="'+t+'" width="520px" title="Набір стандартів Pixhawk5x"><p>Набір Pixhawk 5 стандартний</p><h2 id="огляд-схеми-підключення" tabindex="-1">Огляд схеми підключення <a class="header-anchor" href="#огляд-схеми-підключення" aria-label="Permalink to &quot;Огляд схеми підключення&quot;">​</a></h2><p>На зображенні нижче показано, як підключити найважливіші датчики та периферійні пристрої.</p><p><img src="'+r+'" alt="Огляд підключення Pixhawk 5x"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Додаткову інформацію про доступні порти можна знайти тут: <a href="./../flight_controller/pixhawk5x.html#connections">Pixhawk 5X &gt; Connections</a>.</p></div><h2 id="монтаж-та-орієнтація-контролера" tabindex="-1">Монтаж та орієнтація контролера <a class="header-anchor" href="#монтаж-та-орієнтація-контролера" aria-label="Permalink to &quot;Монтаж та орієнтація контролера&quot;">​</a></h2><p><em>Pixhawk 5X</em> можна змонтувати на раму за допомогою двосторонньої стрічки, включеної в комплект. Він повинен бути розташований якомога ближче до центру ваги вашого транспортного засобу, орієнтований верхньою стороною вгору зі стрілкою, що вказує вперед транспортного засобу.</p><img src="'+a+'" width="400px" title="Набір Pixhawk5x стандартний"><div class="info custom-block"><p class="custom-block-title">Якщо контролер не може бути змонтований у рекомендованому/стандартному положенні (наприклад, через обмеження місця), вам потрібно буде налаштувати програмне забезпечення автопілота з орієнтацією, яку ви фактично використовували: <a href="./../config/flight_controller_orientation.html">Орієнтація контролера польоту</a>.</p></div><h2 id="gps-компас-зумер-захиснии-вимикач-світлодіод" tabindex="-1">GPS + компас + зумер + захисний вимикач + світлодіод <a class="header-anchor" href="#gps-компас-зумер-захиснии-вимикач-світлодіод" aria-label="Permalink to &quot;GPS + компас + зумер + захисний вимикач + світлодіод&quot;">​</a></h2><p>Набір <em>Pixhawk5X Standard Set</em> можна придбати з GPS M8N або M9N (10-контактний роз&#39;єм), який слід підключити до порту <strong>GPS1</strong>. Ці модулі GNSS мають вбудований компас, безпечний перемикач, дзвіночок та світлодіод.</p><p>Додатковий <a href="https://holybro.com/collections/gps" target="_blank" rel="noreferrer">GPS M8N або M9N</a> (6-контактний роз&#39;єм) можна придбати окремо і підключити до порту <strong>GPS2</strong>.</p><p>GPS/Компас слід <a href="./../assembly/mount_gps_compass.html">монтувати на раму</a> якомога подалі від інших електронних пристроїв, з напрямком вперед транспортного засобу (відокремлення компаса від інших електронних пристроїв зменшить втручання).</p><img src="'+o+'" width="200px" title="Набір Pixhawk 5 стандартний"><div class="info custom-block"><p class="custom-block-title">Вбудований безпечний вимикач в GPS-модулі увімкнений <em>за замовчуванням</em> (коли включений, PX4 не дозволить вам готувати до польоту). Щоб вимкнути безпеку, натисніть і утримуйте безпечний вимикач протягом 1 секунди. Ви можете натиснути безпечний вимикач знову, щоб увімкнути безпеку та відключити транспортний засіб (це може бути корисно, якщо, з якихось причин, ви не можете вимкнути транспортний засіб за допомогою вашого пульта дистанційного керування або наземної станції).</p></div><h2 id="power" tabindex="-1">Power <a class="header-anchor" href="#power" aria-label="Permalink to &quot;Power&quot;">​</a></h2><p>Підключіть вихід модуля живлення <em>PM02D Power Module</em> (PM-плата), який поставляється зі стандартним набором, до одного з роз&#39;ємів <strong>POWER</strong> <em>Pixhawk 5X</em> за допомогою 6-жильного кабелю. Порти PM02D та Power на Pixhawk 5X використовують 6 circuit <a href="https://www.molex.com/molex/products/part-detail/pcb_receptacles/5024430670" target="_blank" rel="noreferrer">2.00mm Pitch CLIK-Mate Wire-to-Board PCB Receptacle</a> &amp; <a href="https://www.molex.com/molex/products/part-detail/crimp_housings/5024390600" target="_blank" rel="noreferrer">Housing</a>.</p><p>Модуль живлення PM02D підтримує акумулятор <strong>2~6S</strong>, вхід до плати повинен бути підключений до вашого LiPo акумулятора. Зверніть увагу, що плата PM не постачає живлення на контакти + та - <strong>FMU PWM OUT</strong> та <strong>I/O PWM OUT</strong>.</p><p>Якщо використовується літак або рухомий об&#39;єкт, вихідна рейка <strong>FMU PWM-OUT</strong> повинна бути окремо живлена для керування сервоприводами для рульових пристроїв, елеронами тощо. Це можна зробити, під&#39;єднавши 8-контактний живильний (+) рейл <strong>FMU PWM-OUT</strong> до стабілізатора напруги (наприклад, ESC з обладнаною BEC або автономною BEC на 5 В або акумулятором LiPo 2S).</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Напруга шини живлення повинна бути відповідною для використаного сервоприводу!</p></div><table><thead><tr><th>PIN &amp; Connector</th><th>Функція</th></tr></thead><tbody><tr><td>I/O PWM Out</td><td>Підключіть сигнальні та земельні проводи двигуна тут.</td></tr><tr><td>FMU PWM Out</td><td>Підключіть сигнальні, позитивні та GND-проводи сервоприводу сюди.</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title"><strong>MAIN</strong> виходи у прошивці PX4 призначені для порту <strong>I/O PWM OUT</strong> на <em>Pixhawk 5X</em>, тоді як виходи <strong>AUX</strong> призначені для порту <strong>FMU PWM OUT</strong> на <em>Pixhawk 5X</em>. Наприклад, <strong>MAIN1</strong> відповідає виводу IO_CH1 <strong>I/O PWM OUT</strong>, а <strong>AUX1</strong> відповідає виводу FMU_CH1 <strong>FMU PWM OUT</strong>.</p></div><p>Схема роз&#39;ємів <em>Pixhawk 5X</em> для підключення живлення показана нижче. Роз&#39;єми живлення приймають цифровий сигнал I2C від модуля живлення PM02D для даних про напругу та силу струму. Лінії VCC повинні пропонувати принаймні 3A безперервного струму і за замовчуванням повинні мати напругу 5,2 В. Нижчий напруга 5V все ще прийнятний, але не рекомендується.</p><table><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1(red)</td><td>VCC</td><td>+5V</td></tr><tr><td>2(black)</td><td>VCC</td><td>+5V</td></tr><tr><td>3(black)</td><td>SCL</td><td>+3.3V</td></tr><tr><td>4(black)</td><td>SDA</td><td>+3.3V</td></tr><tr><td>5(black)</td><td>GND</td><td>GND</td></tr><tr><td>6(black)</td><td>GND</td><td>GND</td></tr></tbody></table><h2 id="радіоуправління" tabindex="-1">Радіоуправління <a class="header-anchor" href="#радіоуправління" aria-label="Permalink to &quot;Радіоуправління&quot;">​</a></h2><p>Для того щоб керувати транспортним засобом <em>вручну</em>, потрібна система радіоуправління (RC) (PX4 не потребує системи радіоуправління для автономних режимів польоту).</p><p>Вам потрібно <a href="./../getting_started/rc_transmitter_receiver.html">вибрати сумісний передавач/приймач</a> і <em>зв&#39;язати</em> їх таким чином, щоб вони взаємодіяли (ознайомтеся з інструкціями, що додаються до вашого конкретного передавача/приймача).</p><ul><li>Приймачі Spektrum/DSM підключаються до входу <strong>DSM/SBUS RC</strong>.</li><li>Приймачі PPM або SBUS підключаються до вхідного порту <strong>RC IN</strong>.</li></ul><p>PPM та PWM приймачі, які мають <em>окремий дріт для кожного каналу</em>, повинні підключатися до порту <strong>RC IN</strong> <em>через PPM перетворювач</em> <a href="http://www.getfpv.com/radios/radio-accessories/holybro-ppm-encoder-module.html" target="_blank" rel="noreferrer">на зразок цього</a> (PPM-Sum приймачі використовують єдиний сигнальний дріт для всіх каналів).</p><p>Для отримання додаткової інформації про вибір системи радіо, сумісність приймачів та зв&#39;язування вашої пари передавача/приймача дивіться: <a href="./../getting_started/rc_transmitter_receiver.html">Remote Control Transmitters &amp; Receivers</a>.</p><h2 id="телеметріине-радіо-опціонально" tabindex="-1">Телеметрійне радіо (Опціонально) <a class="header-anchor" href="#телеметріине-радіо-опціонально" aria-label="Permalink to &quot;Телеметрійне радіо (Опціонально)&quot;">​</a></h2><p><a href="./../telemetry/">Телеметричні радіостанції</a> можуть використовуватися для зв&#39;язку та управління транспортним засобом у польоті з наземної станції (наприклад, ви можете направляти БПЛА до певної позиції або завантажувати нове завдання).</p><p>Радіостанцію, розташовану на транспортному засобі, слід підключити до порту <strong>TELEM1</strong>, як показано нижче (якщо підключено до цього порту, додаткова конфігурація не потрібна). Інша радіостанція підключається до вашого комп&#39;ютера або мобільного пристрою наземної станції (зазвичай за допомогою USB).</p><p>Радіостанції також доступні для придбання на веб-сайті <a href="https://holybro.com/collections/telemetry-radios" target="_blank" rel="noreferrer">Holybro</a> .</p><h2 id="sd-карта-опціонально" tabindex="-1">SD-карта (Опціонально) <a class="header-anchor" href="#sd-карта-опціонально" aria-label="Permalink to &quot;SD-карта (Опціонально)&quot;">​</a></h2><p>Картки SD настійно рекомендується, оскільки вони потрібні для <a href="./../getting_started/flight_reporting.html">запису та аналізу даних польоту</a>, для виконання місій та для використання апаратного засобу UAVCAN-bus. Вставте картку (включену в комплект Pixhawk 5X) в <em>Pixhawk 5X</em>, як показано нижче.</p><img src="'+i+'" width="420px" title="Набір стандартів Pixhawk5x"><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Для отримання додаткової інформації див. <a href="./../getting_started/px4_basic_concepts.html#sd-cards-removable-memory">Основні концепції &gt; SD-карти (знімна пам&#39;ять)</a>.</p></div><h2 id="двигуни" tabindex="-1">Двигуни <a class="header-anchor" href="#двигуни" aria-label="Permalink to &quot;Двигуни&quot;">​</a></h2><p>Мотори/сервоприводи підключені до портів <strong>I/O PWM OUT</strong> (<strong>MAIN</strong>) та <strong>FMU PWM OUT</strong> (<strong>AUX</strong>) в порядку, вказаному для вашого апарату в <a href="./../airframes/airframe_reference.html">Довіднику планерів</a>.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Цей info містить зіставлення портів виводу до моторів/сервоприводів для всіх підтримуваних повітряних та наземних шасі (якщо ваше шасі не вказане в довіднику, то використовуйте &quot;загальний&quot; планер відповідного типу).</p></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Відображення не є однорідним для всіх конструкцій (наприклад, ви не можете покладатися на те, що ручка газу буде на тому ж вихідному порту для всіх повітряних конструкцій). Переконайтеся, що ви використовуєте правильне відображення для вашого транспортного засобу.</p></div><h2 id="інші-пристроі" tabindex="-1">Інші пристрої <a class="header-anchor" href="#інші-пристроі" aria-label="Permalink to &quot;Інші пристрої&quot;">​</a></h2><p>Підключення та конфігурація додаткових/менш поширених компонентів описано в темах для окремих <a href="./../peripherals/">периферійних пристроїв</a>.</p><h2 id="схема-розташування-виводів" tabindex="-1">Схема розташування виводів <a class="header-anchor" href="#схема-розташування-виводів" aria-label="Permalink to &quot;Схема розташування виводів&quot;">​</a></h2><p><img src="'+e+'" alt="Схема контактів Pixhawk 5X Pinout1"></p><p>Ви також можете завантажити схему розташування виводів <em>Pixhawk 5X</em> <a href="https://github.com/PX4/PX4-user_guide/blob/v1.15/assets/flight_controller/pixhawk5x/pixhawk5x_pinout.pdf" target="_blank" rel="noreferrer">тут</a> або <a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Holybro_Pixhawk5X_Pinout.pdf" target="_blank" rel="noreferrer">тут</a>.</p><h2 id="конфігурація" tabindex="-1">Конфігурація <a class="header-anchor" href="#конфігурація" aria-label="Permalink to &quot;Конфігурація&quot;">​</a></h2><p>Загальну інформацію про конфігурацію описано в: <a href="./../config/">Конфігурація автопілота</a>.</p><p>Особливу конфігурацію QuadPlane описано тут: <a href="./../config_vtol/vtol_quad_configuration.html">Конфігурація QuadPlane VTOL</a></p><h2 id="детальна-інформація" tabindex="-1">Детальна інформація <a class="header-anchor" href="#детальна-інформація" aria-label="Permalink to &quot;Детальна інформація&quot;">​</a></h2><ul><li><a href="./../flight_controller/pixhawk5x.html">Pixhawk 5X</a> (Сторінка огляду документа PX4)</li><li><a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Holybro_Pixhawk5X_Spec_Overview.pdf" target="_blank" rel="noreferrer">Огляд &amp; специфікація Pixhawk 5X</a> (Holybro)</li><li><a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Holybro_Pixhawk5X_Pinout.pdf" target="_blank" rel="noreferrer">Розводка Pixhawk 5X</a> (Holybro)</li><li><a href="https://docs.google.com/spreadsheets/d/1Su7u8PHp-Y1AlLGVuH_I8ewkEEXt_bHHYBHglRuVH7E/edit#gid=562580340" target="_blank" rel="noreferrer">Схема роз&#39;ємів для посилання на FMUv5X</a>.</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-011%20Pixhawk%20Autopilot%20v5X%20Standard.pdf" target="_blank" rel="noreferrer">Стандарт автопілота Pixhawk FMUv5X</a>.</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-010%20Pixhawk%20Autopilot%20Bus%20Standard.pdf" target="_blank" rel="noreferrer">Стандарт шин автопілота Pixhawk</a>.</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Стандарт роз&#39;ємів Pixhawk</a>.</li></ul>',56),c=[d];function m(P,k,b,g,f,_){return h(),l("div",null,c)}const v=s(p,[["render",m]]);export{q as __pageData,v as default};
