import{_ as t,a,b as r}from"./chunks/pixhawk6c_sd_slot.BP563C17.js";import{_ as e}from"./chunks/pixhawk5x_gps_front.BJK3Dtiq.js";import{_ as o,c as l,o as i,ab as s}from"./chunks/framework.CUflZczI.js";const x=JSON.parse('{"title":"Швидке підключення Holybro Pixhawk 6C","description":"","frontmatter":{},"headers":[],"relativePath":"uk/assembly/quick_start_pixhawk6c.md","filePath":"uk/assembly/quick_start_pixhawk6c.md"}'),h={name:"uk/assembly/quick_start_pixhawk6c.md"},n=s('<h1 id="швидке-підключення-holybro-pixhawk-6c" tabindex="-1">Швидке підключення Holybro Pixhawk 6C <a class="header-anchor" href="#швидке-підключення-holybro-pixhawk-6c" aria-label="Permalink to &quot;Швидке підключення Holybro Pixhawk 6C&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">PX4 не виробляє цей (або будь-який) автопілот. Зверніться до <a href="https://holybro.com/" target="_blank" rel="noreferrer">виробника</a> щодо підтримки апаратного забезпечення чи питань відповідності вимогам.</p></div><p>У цьому короткому посібнику показано, як увімкнути живлення польотного контролера <a href="./../flight_controller/pixhawk6c.html">Pixhawk 6C<sup>®</sup></a> та підключити до нього найважливіші периферійні пристрої.</p><h2 id="вміст-комплекту" tabindex="-1">Вміст комплекту <a class="header-anchor" href="#вміст-комплекту" aria-label="Permalink to &quot;Вміст комплекту&quot;">​</a></h2><p>Pixhawk 6C + PM02 + M8N GPS.</p><p><img src="'+t+'" alt="Pixhawk6c standard set"></p><h2 id="монтаж-та-орієнтація-контролера" tabindex="-1">Монтаж та орієнтація контролера <a class="header-anchor" href="#монтаж-та-орієнтація-контролера" aria-label="Permalink to &quot;Монтаж та орієнтація контролера&quot;">​</a></h2><p><em>Pixhawk 6С</em> можна змонтувати на раму за допомогою двосторонньої стрічки, включеної в комплект. Він повинен бути розташований якомога ближче до центру ваги вашого транспортного засобу, орієнтований верхньою стороною вгору зі стрілкою, що вказує вперед транспортного засобу.</p><img src="'+a+'" width="300px" title="Pixhawk6c - mounting orientation"><div class="info custom-block"><p class="custom-block-title">Якщо контролер не може бути змонтований у рекомендованому/стандартному положенні (наприклад, через обмеження місця), вам потрібно буде налаштувати програмне забезпечення автопілота з орієнтацією, яку ви фактично використовували: <a href="./../config/flight_controller_orientation.html">Орієнтація контролера польоту</a>.</p></div><h2 id="gps-компас-зумер-захиснии-вимикач-світлодіод" tabindex="-1">GPS + компас + зумер + захисний вимикач + світлодіод <a class="header-anchor" href="#gps-компас-зумер-захиснии-вимикач-світлодіод" aria-label="Permalink to &quot;GPS + компас + зумер + захисний вимикач + світлодіод&quot;">​</a></h2><p>Набір <em>Pixhawk6С</em> можна придбати з GPS M8N або M9N (10-контактний роз&#39;єм), який слід підключити до порту <strong>GPS1</strong>. Ці модулі GNSS мають вбудований компас, безпечний перемикач, дзвіночок та світлодіод.</p><p>Додатковий <a href="https://holybro.com/collections/gps" target="_blank" rel="noreferrer">GPS M8N або M9N</a> (6-контактний роз&#39;єм) можна придбати окремо і підключити до порту <strong>GPS2</strong>.</p><p>GPS/компас повинен бути <a href="./../assembly/mount_gps_compass.html">закріплений на рамі</a> якомога далі від іншої електроніки, так, щоб маркер напрямку був спрямований до передньої частини апарату (відокремлення компаса від іншої електроніки зменшить радіоперешкоди).</p><img src="'+e+'" width="200px" title="Pixhawk5x standard set"><div class="info custom-block"><p class="custom-block-title">Вбудований перемикач безпеки GPS-модуля за <em>замовчуванням</em> увімкнений (коли він увімкнений, PX4 не дозволить вам привести апарат в працездатний стан). Щоб вимкнути функцію безпеки, натисніть і утримуйте запобіжник протягом 1 секунди. Ви можете повторно натиснути запобіжник, щоб увімкнути функцію безпеки та перевести апарат в неробоче положення (це може бути корисно, якщо з якихось причин ви не можете перевести апарат в неробоче положення за допомогою пульта дистанційного керування або наземної станції).</p></div><h2 id="живлення" tabindex="-1">Живлення <a class="header-anchor" href="#живлення" aria-label="Permalink to &quot;Живлення&quot;">​</a></h2><p>Підключіть вихід обраного вами модуля живлення, що входить до стандартного комплекту, до одного з портів <strong>POWER</strong> <em>Pixhawk 6C</em> за допомогою 6-провідного кабелю.</p><p>Якщо використовується літак або ровер, <strong>FMU PWM-OUT</strong> повинно бути окремо живлене для керування сервоприводами для рульових пристроїв, елеронами тощо. Це можна зробити, під&#39;єднавши 8-контактний живильний (+) рейл <strong>FMU PWM-OUT</strong> до стабілізатора напруги (наприклад, ESC з обладнаною BEC або автономною BEC на 5 В або акумулятором LiPo 2S).</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Напруга на шині живлення повинна відповідати використовуваному сервоприводу!</p></div><table><thead><tr><th>PIN &amp; Connector</th><th>Функція</th></tr></thead><tbody><tr><td>I/O PWM Out</td><td>Підключіть сюди дроти Motor Signal і GND.</td></tr><tr><td>FMU PWM Out</td><td>Підключіть сигнальні, позитивні та GND-проводи сервоприводу сюди.</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">Виходи <strong>MAIN</strong> у прошивці PX4 призначені для порту <strong>I/O PWM OUT</strong> на <em>Pixhawk 6C</em>, тоді як виходи <strong>AUX</strong> призначені для порту <strong>FMU PWM OUT</strong> на <em>Pixhawk 6C</em>. Наприклад, <strong>MAIN1</strong> відповідає виводу IO_CH1 <strong>I/O PWM OUT</strong>, а <strong>AUX1</strong> відповідає виводу FMU_CH1 <strong>FMU PWM OUT</strong>.</p></div><p>Схема роз&#39;ємів <em>Pixhawk 6С</em> для підключення живлення показана нижче.</p><table><thead><tr><th>Пін</th><th>Signal</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1(red)</td><td>VDD</td><td>+5В</td></tr><tr><td>2(black)</td><td>VDD</td><td>+5В</td></tr><tr><td>3(black)</td><td>CURRENT</td><td>+3.3В</td></tr><tr><td>4(black)</td><td>VOLTAGE</td><td>+3.3В</td></tr><tr><td>5(black)</td><td>GND</td><td>GND</td></tr><tr><td>6(black)</td><td>GND</td><td>GND</td></tr></tbody></table><h2 id="радіоуправління" tabindex="-1">Радіоуправління <a class="header-anchor" href="#радіоуправління" aria-label="Permalink to &quot;Радіоуправління&quot;">​</a></h2><p>Для <em>ручного</em> керування вашим апаратом потрібна система дистанційного керування радіо (RC) (PX4 не вимагає наявності системи радіо для автономних режимів польоту).</p><p>Вам буде потрібно <a href="./../getting_started/rc_transmitter_receiver.html">вибрати сумісний передавач/приймач</a> та потім <em>зв&#39;язати</em> їх, щоб вони взаємодіяли (прочитайте інструкції, що додаються до вашого конкретного передавача/приймача).</p><ul><li>Приймачі Spektrum/DSM підключаються до входу <strong>DSM</strong>.</li><li>Приймачі PPM або SBUS підключаються до вхідного порту <strong>PPM/SBUS</strong>.</li></ul><p>Приймачі PPM і ШІМ, які мають <em>окремий дріт для кожного каналу</em>, повинні підключатися до порту *PPM/SBUS** через енкодер PPM* <a href="http://www.getfpv.com/radios/radio-accessories/holybro-ppm-encoder-module.html" target="_blank" rel="noreferrer">подібний до цього</a> (приймачі PPM-Sum використовують один сигнальний дріт для всіх каналів).</p><p>Для отримання додаткової інформації про вибір радіосистеми, сумісність приймача та налаштування пари передавач/приймач див.: <a href="./../getting_started/rc_transmitter_receiver.html">Пульт керування передавачів&amp; приймачів</a>.</p><h2 id="телеметричні-радіостанціі-опціонально" tabindex="-1">Телеметричні радіостанції (Опціонально) <a class="header-anchor" href="#телеметричні-радіостанціі-опціонально" aria-label="Permalink to &quot;Телеметричні радіостанції (Опціонально)&quot;">​</a></h2><p><a href="./../telemetry/">Телеметрійні радіомодулі</a> можуть використовуватися для зв&#39;язку та управління апаратом в польоті з наземної станції (наприклад, ви можете направляти БПЛА в певне положення або завантажувати нове завдання).</p><p>Бортову радіостанцію слід підключити до порту <strong>TELEM1</strong>, як показано нижче (у разі підключення до цього порту подальша конфігурація не потрібна). Інша радіостанція підключається до комп&#39;ютера або мобільного пристрою наземної станції (зазвичай через USB).</p><p>Радіостанції також доступні для придбання на веб-сайті <a href="https://holybro.com/collections/telemetry-radios" target="_blank" rel="noreferrer">Holybro</a> .</p><h2 id="sd-карта-опціонально" tabindex="-1">SD-карта (Опціонально) <a class="header-anchor" href="#sd-карта-опціонально" aria-label="Permalink to &quot;SD-карта (Опціонально)&quot;">​</a></h2><p>Наполегливо рекомендуємо використовувати SD-карти, оскільки вони необхідні для <a href="./../getting_started/flight_reporting.html">запису та аналізу даних польоту</a>, запуску місій та використання обладнання UAVCAN-шини. Вставте картку (з комплекту Pixhawk 6C) в <em>Pixhawk 6C</em>, як показано нижче.</p><img src="'+r+'" width="320px" title="Pixhawk6c SD"><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Для отримання додаткової інформації див. <a href="./../getting_started/px4_basic_concepts.html#sd-cards-removable-memory">Основні концепції &gt; SD-карти (знімна пам&#39;ять)</a>.</p></div><h2 id="двигуни" tabindex="-1">Двигуни <a class="header-anchor" href="#двигуни" aria-label="Permalink to &quot;Двигуни&quot;">​</a></h2><p>Двигуни/сервоприводи підключаються до портів <strong>I/O PWM OUT</strong> (<strong>MAIN</strong>) та <strong>FMU PWM OUT</strong> (<strong>AUX</strong>) у порядку, вказаному для вашого апарату в <a href="./../airframes/airframe_reference.html">Airframe Reference</a>.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>У цьому довіднику перераховані вихідні порти для мапування двигунів/сервоприводів для всіх підтримуваних повітряних і наземних каркасів (якщо ваш каркас не вказаний у довіднику, використовуйте &quot;загальний&quot; каркас відповідного типу).</p></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Відображення не є однаковим для різних фреймів (наприклад, ви не можете покладатися на те, що дросель буде на одному і тому ж виході для всіх повітряних конструкцій). Переконайтеся, що ви використовуєте правильне відображення для вашого транспортного засобу.</p></div><h2 id="інші-периферіині-пристроі" tabindex="-1">Інші периферійні пристрої <a class="header-anchor" href="#інші-периферіині-пристроі" aria-label="Permalink to &quot;Інші периферійні пристрої&quot;">​</a></h2><p>Підключення та конфігурація додаткових / менш поширених компонентів описано в темах для окремих <a href="./../peripherals/">периферійних пристроїв</a>.</p><h2 id="схема-розташування-виводів" tabindex="-1">Схема розташування виводів <a class="header-anchor" href="#схема-розташування-виводів" aria-label="Permalink to &quot;Схема розташування виводів&quot;">​</a></h2><ul><li><a href="https://docs.holybro.com/autopilot/pixhawk-6c/pixhawk-6c-pinout" target="_blank" rel="noreferrer">Holybro Pixhawk -6C Pinout</a></li></ul><h2 id="конфігурація" tabindex="-1">Конфігурація <a class="header-anchor" href="#конфігурація" aria-label="Permalink to &quot;Конфігурація&quot;">​</a></h2><p>Загальну інформацію про конфігурацію описано в: <a href="./../config/">Конфігурація автопілота</a>.</p><p>Конфігурація для QuadPlane описана тут: <a href="./../config_vtol/vtol_quad_configuration.html">Конфігурація QuadPlane VTOL</a></p><h2 id="додаткова-інформація" tabindex="-1">Додаткова інформація <a class="header-anchor" href="#додаткова-інформація" aria-label="Permalink to &quot;Додаткова інформація&quot;">​</a></h2><ul><li><a href="https://docs.holybro.com/" target="_blank" rel="noreferrer">Документація Holybro</a> (Holybro)</li><li><a href="./../flight_controller/pixhawk6c.html">Pixhawk 6C</a> (сторінка з оглядом документації PX4)</li><li><a href="./../power_module/holybro_pm02.html">PM02 Power Module</a></li><li><a href="./../power_module/holybro_pm06_pixhawk4mini_power_module.html">PM06 Power Module</a></li><li><a href="./../power_module/holybro_pm07_pixhawk4_power_module.html">PM07 Power Module</a></li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-010%20Pixhawk%20Autopilot%20Bus%20Standard.pdf" target="_blank" rel="noreferrer">Стандарт шин автопілота Pixhawk</a>.</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Стандарт роз&#39;ємів Pixhawk</a>.</li></ul>',51),d=[n];function c(p,m,b,P,k,u){return i(),l("div",null,d)}const w=o(h,[["render",c]]);export{x as __pageData,w as default};
