import{_ as t,a,b as r}from"./chunks/pixhawk6x_vehicle_front1.Dv6kulxv.js";import{_ as e}from"./chunks/pixhawk6x_wiring_diagram.Aap2Cb8p.js";import{_ as o}from"./chunks/pixhawk5x_gps_front.BJK3Dtiq.js";import{_ as i}from"./chunks/pixhawk5x_sd_slot.Cr4YwYis.js";import{_ as l,c as s,o as h,ab as n}from"./chunks/framework.CUflZczI.js";const q=JSON.parse('{"title":"Короткий посібник з підключення Holybro Pixhawk 6X","description":"","frontmatter":{},"headers":[],"relativePath":"uk/assembly/quick_start_pixhawk6x.md","filePath":"uk/assembly/quick_start_pixhawk6x.md"}'),d={name:"uk/assembly/quick_start_pixhawk6x.md"},p=n('<h1 id="короткии-посібник-з-підключення-holybro-pixhawk-6x" tabindex="-1">Короткий посібник з підключення Holybro Pixhawk 6X <a class="header-anchor" href="#короткии-посібник-з-підключення-holybro-pixhawk-6x" aria-label="Permalink to &quot;Короткий посібник з підключення Holybro Pixhawk 6X&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">PX4 не виробляє цей (чи будь-який інший) автопілот. Зверніться до <a href="https://holybro.com/" target="_blank" rel="noreferrer">виробника</a> щодо підтримки апаратного забезпечення чи відповідності вимогам.</p></div><p>Цей короткий посібник показує, як живити польовий контролер <a href="./../flight_controller/pixhawk6x.html">Pixhawk 6X<sup>®</sup></a> та під&#39;єднати його найважливіші периферійні пристрої.</p><h2 id="вміст-набору" tabindex="-1">Вміст набору <a class="header-anchor" href="#вміст-набору" aria-label="Permalink to &quot;Вміст набору&quot;">​</a></h2><h3 id="набір-pixhawk-6x-standard" tabindex="-1">Набір Pixhawk 6X Standard <a class="header-anchor" href="#набір-pixhawk-6x-standard" aria-label="Permalink to &quot;Набір Pixhawk 6X Standard&quot;">​</a></h3><p><img src="'+t+'" alt="Pixhawk 6x standard set"></p><h3 id="набір-pixhawk-6x-mini" tabindex="-1">Набір Pixhawk 6X Mini <a class="header-anchor" href="#набір-pixhawk-6x-mini" aria-label="Permalink to &quot;Набір Pixhawk 6X Mini&quot;">​</a></h3><p><img src="'+a+'" alt="Pixhawk 6x mini standard set"></p><h2 id="огляд-схеми-підключення" tabindex="-1">Огляд схеми підключення <a class="header-anchor" href="#огляд-схеми-підключення" aria-label="Permalink to &quot;Огляд схеми підключення&quot;">​</a></h2><p>На зображенні нижче показано, як під&#39;єднати найважливіші датчики та периферійні пристрої.</p><p><img src="'+e+'" alt="Pixhawk 6x Wiring Overview"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Додаткову інформацію про доступні порти можна знайти тут: <a href="./../flight_controller/pixhawk6x.html#connections">Pixhawk 6X &gt; Connections</a>.</p></div><h2 id="монтаж-та-орієнтація-контролера" tabindex="-1">Монтаж та орієнтація контролера <a class="header-anchor" href="#монтаж-та-орієнтація-контролера" aria-label="Permalink to &quot;Монтаж та орієнтація контролера&quot;">​</a></h2><p><em>Pixhawk 6X</em> можна змонтувати на раму за допомогою двосторонньої стрічки, включеної в комплект. Він повинен бути розташований якомога ближче до центру ваги вашого апарату, орієнтований верхньою стороною вгору зі стрілкою, що вказує вперед апарату.</p><img src="'+r+'" width="400px" title="Pixhawk6x"><div class="info custom-block"><p class="custom-block-title">Якщо контролер не може бути змонтований в рекомендованій/стандартній орієнтації (наприклад, через обмеження місця), вам потрібно буде налаштувати програмне забезпечення автопілота з орієнтацією, яку ви фактично використовували: <a href="./../config/flight_controller_orientation.html">Орієнтація контролера польоту</a>.</p></div><h2 id="gps-компас-перемикач-безпеки-світлодіод" tabindex="-1">GPS + компас + перемикач безпеки + світлодіод <a class="header-anchor" href="#gps-компас-перемикач-безпеки-світлодіод" aria-label="Permalink to &quot;GPS + компас + перемикач безпеки + світлодіод&quot;">​</a></h2><p>Набір <em>Pixhawk6X Standard Set</em> та <em>Pixhawk6X Mini Set</em> можна придбати з GPS M8N або M9N (10-контактний роз&#39;єм), який слід підключити до порту <strong>GPS1</strong>. Ці модулі GNSS мають вбудований компас, безпечний перемикач, дзвіночок та світлодіод.</p><p>Додатковий <a href="https://holybro.com/collections/gps" target="_blank" rel="noreferrer">GPS M8N або M9N</a> (6-контактний роз&#39;єм) можна придбати окремо і підключити до порту <strong>GPS2</strong>.</p><p>GPS/Компас слід <a href="./../assembly/mount_gps_compass.html">монтувати на раму</a> якомога подалі від інших електронних пристроїв, з напрямком вперед транспортного засобу (відокремлення компаса від інших електронних пристроїв зменшить втручання).</p><img src="'+o+'" width="200px" title="Pixhawk5x standard set"><div class="info custom-block"><p class="custom-block-title">Вбудований безпечний вимикач в GPS-модулі увімкнений <em>за замовчуванням</em> (коли включений, PX4 не дозволить вам готувати до польоту). Щоб вимкнути безпеку, натисніть і утримуйте безпечний вимикач протягом 1 секунди. Ви можете натиснути безпечний вимикач знову, щоб увімкнути безпеку та відключити транспортний засіб (це може бути корисно, якщо, з якихось причин, ви не можете вимкнути транспортний засіб за допомогою вашого пульта дистанційного керування або наземної станції).</p></div><h2 id="живлення" tabindex="-1">Живлення <a class="header-anchor" href="#живлення" aria-label="Permalink to &quot;Живлення&quot;">​</a></h2><p>Підключіть вихід модуля живлення <em>PM02D Power Module</em> (плати PM), що входить до стандартного комплекту, до одного з портів <strong>POWER</strong> <em>Pixhawk 6X</em> за допомогою 6-провідного кабелю. Порти PM02D та живлення на Pixhawk 6X використовують 6-контактний <a href="https://www.molex.com/molex/products/part-detail/pcb_receptacles/5024430670" target="_blank" rel="noreferrer">Роз&#39;єм плати CLIK-Mate Wire-to-Board PCB з кроком 2,00 мм</a> &amp; <a href="https://www.molex.com/molex/products/part-detail/crimp_housings/5024390600" target="_blank" rel="noreferrer">Корпус</a>.</p><p>Модуль живлення PM02D підтримує акумулятор <strong>2~6S</strong>, вхід до плати повинен бути підключений до вашого LiPo акумулятора. Зверніть увагу, що плата PM не постачає живлення до контактів + та - <strong>FMU PWM OUT</strong> та <strong>I/O PWM OUT</strong>.</p><p>Якщо використовується літак або рухомий об&#39;єкт, вихідна рейка <strong>FMU PWM-OUT</strong> повинна бути окремо живлена для керування сервоприводами для рульових пристроїв, елеронами тощо. Це можна зробити, під&#39;єднавши 8-контактний живильний (+) рейл <strong>FMU PWM-OUT</strong> до стабілізатора напруги (наприклад, ESC з обладнаною BEC або автономною BEC на 5 В або акумулятором LiPo 2S).</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Напруга шини живлення повинна бути відповідною для використаного сервоприводу!</p></div><table><thead><tr><th>PIN&amp; Connector</th><th>Функція</th></tr></thead><tbody><tr><td>I/O PWM Out</td><td>Тут під&#39;єднайте сигнал Motor та GND дроти.</td></tr><tr><td>FMU PWM Out</td><td>Під&#39;єднайте сигнальні, позитивні та GND дроти сюди.</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">Виходи <strong>MAIN</strong> у прошивці PX4 призначені для порту <strong>I/O PWM OUT</strong> на <em>Pixhawk 6X</em>, тоді як виходи <strong>AUX</strong> призначені для порту <strong>FMU PWM OUT</strong> на <em>Pixhawk 6X</em>. Наприклад, <strong>MAIN1</strong> відповідає виводу IO_CH1 <strong>I/O PWM OUT</strong>, а <strong>AUX1</strong> відповідає виводу FMU_CH1 <strong>FMU PWM OUT</strong>.</p></div><p>Схема роз&#39;ємів <em>Pixhawk 6X</em> для підключення живлення показана нижче. Роз&#39;єми живлення приймають цифровий сигнал I2C від модуля живлення PM02D для даних про напругу та силу струму. Лінії VCC повинні пропонувати принаймні 3A безперервного струму і за замовчуванням повинні мати напругу 5.2В. Нижчий напруга 5V все ще прийнятний, але не рекомендується.</p><table><thead><tr><th>Пін</th><th>Сигнал</th><th>Вольт</th></tr></thead><tbody><tr><td>1 (червоний)</td><td>VCC</td><td>+5V</td></tr><tr><td>2 (чорний)</td><td>VCC</td><td>+5V</td></tr><tr><td>3 (чорний)</td><td>SCL</td><td>+3.3V</td></tr><tr><td>4 (чорний)</td><td>SDA</td><td>+3.3V</td></tr><tr><td>5 (чорний)</td><td>GND</td><td>GND</td></tr><tr><td>6 (чорний)</td><td>GND</td><td>GND</td></tr></tbody></table><h2 id="радіоуправління" tabindex="-1">Радіоуправління <a class="header-anchor" href="#радіоуправління" aria-label="Permalink to &quot;Радіоуправління&quot;">​</a></h2><p>Для того щоб керувати транспортним засобом <em>вручну</em>, потрібна система радіоуправління (RC) (PX4 не потребує системи радіоуправління для автономних режимів польоту).</p><p>Вам потрібно <a href="./../getting_started/rc_transmitter_receiver.html">вибрати сумісний передавач/приймач</a> і <em>зв&#39;язати</em> їх таким чином, щоб вони взаємодіяли (ознайомтеся з інструкціями, що додаються до вашого конкретного передавача/приймача).</p><ul><li>Приймачі Spektrum/DSM підключаються до входу <strong>DSM/SBUS RC</strong>.</li><li>Приймачі PPM або SBUS підключаються до вхідного порту <strong>RC IN</strong>.</li></ul><p>Приймачі PPM та PWM, які мають <em>окремий провід для кожного каналу</em>, повинні підключатися до порту <strong>RC IN</strong> <em>через PPM кодер</em> <a href="http://www.getfpv.com/radios/radio-accessories/holybro-ppm-encoder-module.html" target="_blank" rel="noreferrer">як цей</a> (приймачі PPM-Sum використовують один сигнальний провід для всіх каналів).</p><p>Для отримання додаткової інформації про вибір радіосистеми, сумісність приймача та зв&#39;язок вашої передавача/приймача, див. статтю: <a href="./../getting_started/rc_transmitter_receiver.html">Пульт керування передавачів &amp; приймачів</a>.</p><h2 id="телеметричні-радіостанціі-опціонально" tabindex="-1">Телеметричні радіостанції (Опціонально) <a class="header-anchor" href="#телеметричні-радіостанціі-опціонально" aria-label="Permalink to &quot;Телеметричні радіостанції (Опціонально)&quot;">​</a></h2><p><a href="./../telemetry/">Телеметричні радіостанції</a> можуть використовуватися для зв&#39;язку та управління транспортним засобом у польоті з наземної станції (наприклад, ви можете направляти БПЛА до певної позиції або завантажувати нове завдання).</p><p>Бортову радіостанцію слід підключити до порту <strong>TELEM1</strong>, як показано нижче (у разі підключення до цього порту подальша конфігурація не потрібна). Інша радіостанція підключається до вашого комп&#39;ютера або мобільного пристрою наземної станції (зазвичай за допомогою USB).</p><p>Радіостанції також доступні для придбання на веб-сайті <a href="https://holybro.com/collections/telemetry-radios" target="_blank" rel="noreferrer">Holybro</a> .</p><h2 id="sd-карта-опціонально" tabindex="-1">SD-карта (Опціонально) <a class="header-anchor" href="#sd-карта-опціонально" aria-label="Permalink to &quot;SD-карта (Опціонально)&quot;">​</a></h2><p>Картки SD настійно рекомендується, оскільки вони потрібні для <a href="./../getting_started/flight_reporting.html">запису та аналізу даних польоту</a>, для виконання місій та для використання апаратного засобу UAVCAN-bus. Вставте картку (включену в комплект Pixhawk 6X) в <em>Pixhawk 6X</em>, як показано нижче.</p><img src="'+i+'" width="420px" title="Pixhawk5x standard set"><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Для отримання додаткової інформації див. <a href="./../getting_started/px4_basic_concepts.html#sd-cards-removable-memory">Основні концепції &gt; SD-карти (знімна пам&#39;ять)</a>.</p></div><h2 id="двигуни" tabindex="-1">Двигуни <a class="header-anchor" href="#двигуни" aria-label="Permalink to &quot;Двигуни&quot;">​</a></h2><p>Мотори/сервоприводи підключені до портів <strong>I/O PWM OUT</strong> (<strong>MAIN</strong>) та <strong>FMU PWM OUT</strong> (<strong>AUX</strong>) в порядку, вказаному для вашого апарату в <a href="./../airframes/airframe_reference.html">Довіднику планерів</a>.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Цей довідник містить зіставлення портів виводу до моторів/сервоприводів для всіх підтримуваних повітряних та наземних шасі (якщо ваше шасі не вказане в довіднику, то використовуйте &quot;загальний&quot; планер відповідного типу).</p></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Відображення не є однорідним для всіх конструкцій (наприклад, ви не можете покладатися на те, що ручка газу буде на тому ж вихідному порту для всіх повітряних конструкцій). Переконайтеся, що ви використовуєте правильне відображення для вашого транспортного засобу.</p></div><h2 id="інші-периферіині-пристроі" tabindex="-1">Інші периферійні пристрої <a class="header-anchor" href="#інші-периферіині-пристроі" aria-label="Permalink to &quot;Інші периферійні пристрої&quot;">​</a></h2><p>Підключення та конфігурація додаткових/менш поширених компонентів описано в темах для окремих <a href="./../peripherals/">периферійних пристроїв</a>.</p><h2 id="розпіновка" tabindex="-1">Розпіновка <a class="header-anchor" href="#розпіновка" aria-label="Permalink to &quot;Розпіновка&quot;">​</a></h2><ul><li><a href="https://docs.holybro.com/autopilot/pixhawk-6x/pixhawk-baseboard-pinout" target="_blank" rel="noreferrer">Розпиновка базової плати Holybro Pixhawk</a></li><li><a href="https://docs.holybro.com/autopilot/pixhawk-6x/pixhawk-mini-baseboard-pinout" target="_blank" rel="noreferrer">Розпиновка міні-базової плати Holybro Pixhawk</a></li></ul><h2 id="конфігурація" tabindex="-1">Конфігурація <a class="header-anchor" href="#конфігурація" aria-label="Permalink to &quot;Конфігурація&quot;">​</a></h2><p>Загальну інформацію про конфігурацію описано в: <a href="./../config/">Конфігурація автопілота</a>.</p><p>Конфігурація для QuadPlane описана тут: <a href="./../config_vtol/vtol_quad_configuration.html">Конфігурація QuadPlane VTOL</a></p><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="https://docs.holybro.com/" target="_blank" rel="noreferrer">Документи Holybro</a> (Holybro)</li><li><a href="./../flight_controller/pixhawk6x.html">Pixhawk 6X</a> (сторінка з оглядом документації PX4)</li><li><a href="./../power_module/holybro_pm02d.html">Модуль потужності PM02D</a></li><li><a href="./../power_module/holybro_pm03d.html">PM03D Модуль живлення</a></li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-012%20Pixhawk%20Autopilot%20v6X%20Standard.pdf" target="_blank" rel="noreferrer">Стандарт автопілота Pixhawk FMUv6X</a>.</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-010%20Pixhawk%20Autopilot%20Bus%20Standard.pdf" target="_blank" rel="noreferrer">Стандарт шин автопілота Pixhawk</a>.</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Стандарт роз&#39;ємів Pixhawk</a>.</li></ul>',58),c=[p];function m(k,b,P,x,g,u){return h(),s("div",null,c)}const v=l(d,[["render",m]]);export{q as __pageData,v as default};
