import{_ as a,c as e,o as t,ab as i}from"./chunks/framework.CUflZczI.js";const u=JSON.parse('{"title":"Ізоляція Вібрацій","description":"","frontmatter":{},"headers":[],"relativePath":"uk/assembly/vibration_isolation.md","filePath":"uk/assembly/vibration_isolation.md"}'),r={name:"uk/assembly/vibration_isolation.md"},o=i('<h1 id="ізоляція-вібраціи" tabindex="-1">Ізоляція Вібрацій <a class="header-anchor" href="#ізоляція-вібраціи" aria-label="Permalink to &quot;Ізоляція Вібрацій&quot;">​</a></h1><p>Ця тема показує, як визначити, чи рівні вібрацій занадто високі, і пропонує деякі прості кроки для покращення характеристик вібрацій.</p><h2 id="огляд" tabindex="-1">Огляд <a class="header-anchor" href="#огляд" aria-label="Permalink to &quot;Огляд&quot;">​</a></h2><p>Плати управління польотом з вбудованими акселерометрами або гіроскопами чутливі до вібрацій. Високі рівні вібрацій можуть викликати ряд проблем, включаючи зниження ефективності/продуктивності польоту, скорочення часу польоту та збільшення зносу транспортного засобу. У крайніх випадках вібрація може призвести до обрізання сигналу датчиків/відмов, що може спричинити за собою збої у вимірах та неконтрольовані польоти.</p><p>Добре спроектовані повітряні рами гасять/зменшують амплітуду певних структурних резонансів у місці кріплення автопілота. Додаткова ізоляція може знадобитися для зниження вібрацій до рівня, який чутливі компоненти можуть витримати (наприклад, деякі польотні контролери мають бути прикріплені до повітряної рами за допомогою якоїсь форми противібраційної піни/кріплення, тоді як інші ізольовані внутрішньо).</p><h2 id="аналіз-вібраціи" tabindex="-1">Аналіз Вібрацій <a class="header-anchor" href="#аналіз-вібраціи" aria-label="Permalink to &quot;Аналіз Вібрацій&quot;">​</a></h2><p><a href="./../log/flight_review.html#vibration">Аналіз Логів за допомогою Flight Review &gt; Вібрація</a> пояснює, як використовувати логи для підтвердження того, чи є вібрація ймовірною причиною проблем з польотом.</p><h2 id="основні-способи-усунення-вібраціи" tabindex="-1">Основні Способи Усунення Вібрацій <a class="header-anchor" href="#основні-способи-усунення-вібраціи" aria-label="Permalink to &quot;Основні Способи Усунення Вібрацій&quot;">​</a></h2><p>Декілька простих кроків, які можуть знизити вібрації:</p><ul><li>Переконайтеся, що все міцно прикріплено до транспортного засобу (шасі, мачта GPS тощо).</li><li>Використовуйте збалансовані пропелери.</li><li>Переконайтеся, що для пропелерів, моторів, регуляторів швидкості (ESC) та повітряної рами використовуються високоякісні компоненти. Кожен з цих компонентів може зробити значний вплив.</li><li>Використовуйте метод ізоляції вібрацій для монтажу автопілота. Багато польотних контролерів постачаються з <em>монтажною піною</em>, яку можна використовувати для цієї мети, тоді як інші мають вбудовані механізми ізоляції вібрацій.</li><li>Як <em>останній</em> захід, налаштуйте <a href="./../config_mc/filter_tuning.html">програмні фільтри</a>. Краще зменшити джерело вібрацій, аніж фільтрувати їх за допомогою програмного забезпечення.</li></ul><h2 id="джерела" tabindex="-1">Джерела <a class="header-anchor" href="#джерела" aria-label="Permalink to &quot;Джерела&quot;">​</a></h2><p>Деякі посилання, які ви можете знайти корисними:</p><ul><li><a href="http://www.vibrationdata.com/tutorials2/srs_intr.pdf" target="_blank" rel="noreferrer">Вступ до спектрів відповіді на удари &amp; вібрацію, Том Ірвайн</a> (безкоштовна стаття)</li><li><a href="https://books.google.ch/books?id=PwzDuWDc8AgC&amp;printsec=frontcover" target="_blank" rel="noreferrer">Динаміка та вібрація в практиці - Інженерний посібник, Дуглас Торбі</a> (попередній перегляд).</li></ul>',13),l=[o];function n(s,h,_,c,d,p){return t(),e("div",null,l)}const b=a(r,[["render",n]]);export{u as __pageData,b as default};
