import{_ as e,c as a,o,ab as i}from"./chunks/framework.CUflZczI.js";const d=JSON.parse(`{"title":"Комп'ютерний зір (оптичний потік, MoCap, VIO, уникання)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/computer_vision/index.md","filePath":"uk/computer_vision/index.md"}`),t={name:"uk/computer_vision/index.md"},r=i('<h1 id="комп-ютернии-зір-оптичнии-потік-mocap-vio-уникання" tabindex="-1">Комп&#39;ютерний зір (оптичний потік, MoCap, VIO, уникання) <a class="header-anchor" href="#комп-ютернии-зір-оптичнии-потік-mocap-vio-уникання" aria-label="Permalink to &quot;Комп&#39;ютерний зір (оптичний потік, MoCap, VIO, уникання)&quot;">​</a></h1><p>Техніки <a href="https://en.wikipedia.org/wiki/Computer_vision" target="_blank" rel="noreferrer">комп&#39;ютерного зору</a> дозволяють комп&#39;ютерам використовувати візуальні дані для розуміння їх оточення.</p><p>PX4 використовує системи комп&#39;ютерного зору (переважно запущені на <a href="./../companion_computer/README.html">супутніх комп&#39;ютерах</a>) для підтримки наступних функцій:</p><ul><li>Оцінка положення/швидкості: <ul><li><a href="./../sensor/optical_flow.html">Optical Flow</a> забезпечує 2D оцінку швидкості (за допомогою камери, спрямованої вниз, та датчика відстані, спрямованого вниз).</li><li><a href="./../computer_vision/motion_capture.html">Motion Capture</a> забезпечує 3D-оцінку положення за допомогою системи комп&#39;ютерного зору, яка є <em>зовнішньою</em> відносно апарату. Це переважно використовується для внутрішньої навігації.</li><li><a href="./../computer_vision/visual_inertial_odometry.html"> Візуальна інерційна одометрія (VIO)</a> забезпечує оцінку 3D-позиції та швидкості за допомогою бортової системи зору та IMU. Використовується для навігації, коли інформація про глобальне місцезнаходження відсутня або ненадійна.</li></ul></li><li>Уникнення/планування шляху: <ul><li><a href="./../computer_vision/obstacle_avoidance.html">Obstacle Avoidance</a> забезпечує повну навігацію навколо перешкод під час польоту за запланованим маршрутом (наразі підтримуються місії). Для цього використовується <a href="https://github.com/PX4/PX4-Avoidance" target="_blank" rel="noreferrer">PX4/PX4-Avoidance</a>, запущений на супутньому комп&#39;ютері.</li><li><a href="./../computer_vision/collision_prevention.html">Collision Prevention</a> використовується для зупинки транспортних засобів до того, як вони можуть зіткнутися з перешкодою (переважно під час польоту в ручному режимі).</li><li><a href="./../computer_vision/safe_landing.html">Safe Landing</a> допомагає транспортним засобам знаходити (і приземлятися на) рівну місцевість, вільну від стаціонарних перешкод.</li></ul></li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p><a href="./../complete_vehicles_mc/px4_vision_kit.html">PX4 Vision Autonomy Development Kit</a> (Holybro) - це надійний і недорогий набір для розробників, які працюють з комп&#39;ютерним зором на PX4. Він поставляється без попередньо встановленого програмного забезпечення, але включає приклад реалізації уникання перешкод для демонстрації можливостей платформи.</p></div><h2 id="додаткові-ресурси" tabindex="-1">Додаткові ресурси <a class="header-anchor" href="#додаткові-ресурси" aria-label="Permalink to &quot;Додаткові ресурси&quot;">​</a></h2><ul><li><a href="https://github.com/robin-shaun/XTDrone/blob/master/README.en.md" target="_blank" rel="noreferrer">XTDrone</a> - середовище моделювання ROS + PX4 v1.9 для комп&#39;ютерного зору. У посібнику <a href="https://www.yuque.com/xtdrone/manual_en" target="_blank" rel="noreferrer">XTDrone</a> є все необхідне для початку роботи!</li></ul>',7),l=[r];function n(c,s,_,p,m,h){return o(),a("div",null,l)}const f=e(t,[["render",n]]);export{d as __pageData,f as default};
