import{_ as e,a as t}from"./chunks/px4_arch_fc_companion.BWVvemTR.js";import{_ as a,c as r,o,ab as l}from"./chunks/framework.CUflZczI.js";const k=JSON.parse('{"title":"Архітектура системи PX4","description":"","frontmatter":{},"headers":[],"relativePath":"uk/concept/px4_systems_architecture.md","filePath":"uk/concept/px4_systems_architecture.md"}'),i={name:"uk/concept/px4_systems_architecture.md"},s=l('<h1 id="архітектура-системи-px4" tabindex="-1">Архітектура системи PX4 <a class="header-anchor" href="#архітектура-системи-px4" aria-label="Permalink to &quot;Архітектура системи PX4&quot;">​</a></h1><p>Нижче наведено огляд апаратного та програмного забезпечення на PX4 для двох &quot;типових&quot; систем PX4; перша має лише польотний контролер, а друга має польотний контролер і супутній комп&#39;ютер (також відомий як &quot;комп&#39;ютер політних завдань&quot;).</p><div class="info custom-block"><p class="custom-block-title"><a href="./../concept/architecture.html">Огляд архітектури PX4</a> надає інформацію про набір польотного програмного забезпечення та проміжного ПЗ. Зовнішні API охоплено в розділах <a href="./../ros/README.html">ROS</a> та <a href="https://mavsdk.mavlink.io/main/en/" target="_blank" rel="noreferrer">MAVSDK</a>.</p></div><h2 id="лише-польотнии-контролер" tabindex="-1">Лише польотний контролер <a class="header-anchor" href="#лише-польотнии-контролер" aria-label="Permalink to &quot;Лише польотний контролер&quot;">​</a></h2><p>На діаграмі нижче показано загальний огляд типової &quot;простої&quot; системи PX4 на основі польотного контролера.</p><p><img src="'+e+'" alt="Архітектура PX4 — система лише з політним контролером"></p><p>Апаратне забезпечення складається з</p><ul><li><a href="./../flight_controller/README.html">Політного контролера</a> (запускає набір польотного ПЗ PX4). Часто включає внутрішні ІВП, компас та барометр.</li><li><a href="./../peripherals/esc_motors.html">Електронного регулятора ходу двигунів</a> під&#39;єднаного до <a href="./../peripherals/pwm_escs_and_servo.html">виводів ШІМ</a>, <a href="./../dronecan/escs.html">DroneCAN</a> (DroneCAN дозволяє двонапрямну комунікацію, не в одному напрямку як показано) або іншої шини.</li><li>Датчиків (<a href="./../gps_compass/README.html">GPS</a>, <a href="./../gps_compass/README.html">компас</a>, датчики відстані, барометри, датчики оптичного потоку, ADSB транспондери і т. ін.) під&#39;єднані через I2C, SPI, CAN, UART тощо.</li><li><a href="./../camera/">Камера</a> або інше корисне навантаження. Камери можуть бути підключені до ШІМ виходів або за допомогою MAVLink.</li><li><a href="./../telemetry/README.html">Радіопередавачі телеметрії</a> для з&#39;єднання з комп&#39;ютером або ПЗ наземної станції.</li><li><a href="./../getting_started/rc_transmitter_receiver.html">Система радіо керування</a> для ручного керування</li></ul><p>Ліва частина діаграми показує набір програмного забезпечення, що по горизонталі (приблизно) вирівняно згідно з апаратними частинами діаграми.</p><ul><li>На комп&#39;ютері наземної станції зазвичай працює <a href="./../getting_started/px4_basic_concepts.html#qgc">QGroundControl</a> (або інше програмне забезпечення наземної станції). Він також може виконувати робототехнічне ПЗ <a href="https://mavsdk.mavlink.io/" target="_blank" rel="noreferrer">MAVSDK</a> або <a href="./../ros/README.html">ROS</a>.</li><li>Набір політного ПО PX4, що запущено на польотному контролері включає <a href="./../modules/modules_driver.html">драйвери</a>, <a href="./../modules/modules_communication.html">модулі каналів зв&#39;язку</a>, <a href="./../modules/modules_controller.html">контролери</a>, <a href="./../modules/modules_controller.html">спостерігачі</a> та інше <a href="./../modules/modules_main.html">проміжне ПЗ та системні модулі</a>.</li></ul><h2 id="польотнии-контролер-та-супутніи-комп-ютер" tabindex="-1">Польотний контролер та супутній комп&#39;ютер <a class="header-anchor" href="#польотнии-контролер-та-супутніи-комп-ютер" aria-label="Permalink to &quot;Польотний контролер та супутній комп&#39;ютер&quot;">​</a></h2><p>На діаграмі показано систему PX4, яка включає як політний контролер, так і супутній комп&#39;ютер (тут згадується як &quot;комп&#39;ютер політного завдання&quot;).</p><p><img src="'+t+'" alt="Архітектура PX4 - система з політним контролером і супутнім комп&#39;ютером"></p><p>Польотний контролер виконує звичайний набір ПЗ PX4, тоді як супутній комп&#39;ютер забезпечує просунуті функції наприклад <a href="./../computer_vision/obstacle_avoidance.html">уникнення об&#39;єктів</a> та <a href="./../computer_vision/collision_prevention.html">запобігання зіткненням</a>. Дві системи з&#39;єднані за допомогою швидкого послідовного або IP-з&#39;єднання і зазвичай взаємодіють за допомогою протоколу <a href="https://mavlink.io/en/" target="_blank" rel="noreferrer">MAVLink</a>. Зв&#39;язок з наземними станціями та хмарою зазвичай направляється через супутній комп&#39;ютер (наприклад, за допомогою <a href="https://github.com/mavlink-router/mavlink-router" target="_blank" rel="noreferrer">MAVLink Router</a> від Intel).</p><p>Системи PX4 зазвичай виконують ОС Linux на супутньому комп&#39;ютері (тому що проєкт <a href="https://github.com/PX4/PX4-Avoidance" target="_blank" rel="noreferrer">PX4/PX4-Avoidance</a> поставляє засновані на ROS бібліотеки уникнення розроблені для Linux). Linux є набагато кращою платформою для &quot;загальної&quot; розробки програмного забезпечення, ніж NuttX; у Linux багато розробників і вже написано багато корисного програмного забезпечення (наприклад для комп&#39;ютерного бачення, зв&#39;язку, інтеграції з хмарою, апаратні драйвери). Супутні комп&#39;ютери іноді працюють на Android з тієї ж причини.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Діаграма показує підключення до хмарних або наземних станцій через LTE, підхід, який був використаний в ряді систем заснованих на PX4. PX4 не надає програмного забезпечення для LTE та/або хмарної інтеграції (це потребує додаткової розробки).</p></div>',16),n=[s];function c(h,m,p,_,u,d){return o(),r("div",null,n)}const A=a(i,[["render",c]]);export{k as __pageData,A as default};
